(@tafseer_category_id, '', 'كان رَسولُ اللهِ ﷺ يَرْجُو أَنْ يكونَ المُسلمُونَ', '', 'رُبعَ أهلِ الجنَّةِ', '', 'ثُمنَ أهلِ الجنَّةِ', '', 'نِصفَ أهلِ الجنَّةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'وَصَفَ الله تعالى الأوثانَ بـ(الرِّجْسِ) في قوله: {فَاجْتَنِبُوا الرِّجْسَ مِنَ الْأَوْثَانِ} وفي هذا دَليلٌ على:', '', 'أنَّها رِجْسٌ مَعنوِيٌّ', '', 'نَجاسةِ أعيانِها', '', 'نَجاسةِ مُتَّبِعِيهَا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى "بَهيمةِ الأنعامِ":', '', 'الخيلُ والبَقَرُ والغنَمُ', '', 'الإبلُ والبَقَرُ والغَنَمُ', '', 'ما يُؤكَلُ لَحمُه مِن البَهائمِ الدَّاجِنَة والبَرِّيَّة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'لا يجوزُ تَسميةُ بعضِ الزُّهورِ بـ {عَبَّادِ الشَّمسِ}؛ لأنَّ:', '', 'لَفْظَ العبوديةِ خاصٌّ بالإنسانِ', '', 'الأشجارَ لا تَعبُدُ الشَّمسَ، إنَّما تَعبُدُ اللهَ عزَّ وجَلَّ', '', 'الزُّهُورَ تتبعُ النجومَ والكواكبَ وليسَ الشَّمْسَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَتَرَى الْأَرْضَ هَامِدَةً فَإِذَا أَنْزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ وَرَبَتْ} بُرهانٌ قاطِعٌ عَلى:', '', 'البعثِ', '', 'الإنباتِ', '', 'الحَشرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {ثُمَّ مَحِلُّهَا إِلَى الْبَيْتِ الْعَتِيقِ} دليل على:', '', 'وُجوبِ نَحْرِ الهَدْيِ في المسجدِ الحرامِ', '', 'أنَّ اسمَ البيتِ غَلَبَ على الحَرَمِ كلِّه فسُمِّيَ به', '', 'أنَّ البَيتَ العَتيقَ اسمٌ مِن أَسماءِ مِنى لأنَّها مَحِلُّ نَحرِ الهَديِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفاد من قَولِه تَعالَى: {وَطَهِّرْ بَيْتِيَ لِلطَّائِفِينَ وَالْقَائِمِينَ وَالرُّكَّعِ السُّجُودِ}:', '', 'أنه يُكرَهُ اقترافُ الكبائرِ عندَ بيتِ اللهِ الحرامِ', '', 'أنَّه لا يَجوزُ أن يُترَكَ عند بَيتِ اللهِ الحَرامِ قَذَرٌ مِنَ الأقذارِ، ولا نَجَسٌ مِنَ الأنجاسِ المعنويَّةِ ولا الحِسِّيَّةِ', '', 'أنه يُستحَبُّ اجتنابُ مَحظُوراتِ الإِحرامِ حالَ الحَجِّ والعُمرَةِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ} جاء في الافتتاحِ لسورتَيْن في القرآنِ الكريمِ، هُمَا:', '', 'النِّساءُ والحَجُّ', '', 'المائِدَة وَسَبَأ', '', 'يوسُف وطَه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {فَلْيَمْدُدْ بِسببٍ إلى السَّمَاءِ} معنى {بِسَبَبٍ}:', '', 'بِدُعاءٍ', '', 'بحَبلٍ', '', 'بِيَدَيْهِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {لِيَذْكُرُوا اسْمَ اللَّهِ عَلَى مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعَامِ} دليلٌ على:', '', 'أنَّ المَقصُودَ بالأَنعامِ كلُّ مَأكولِ اللَّحمِ', '', 'أنَّ القُربانَ يَجِبُ أن يكونَ مِنَ الأنعامِ', '', 'أنَّ القُربانَ لا يُشترطُ فيه الذَّبحُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى {بَوَّأْنَا} في قَولِه تَعالَى: {وَإِذْ بَوَّأْنَا لِإِبْرَاهِيمَ مَكَانَ الْبَيْتِ}:', '', 'هَيَّأْنا وبَيَّنَّا', '', 'بَنَيْنَا', '', 'أَخْرَجْنَا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ اللهِ تعالى: {ذَلِكَ بِمَا قَدَّمَتْ يَدَاكَ}، أي: بعَمَلِك، ولكِنَّه جرت عادةُ العَرَبِ أن تُضيفَ الأعمالَ إلى اليَدِ؛ لأنَّها:', '', 'مِن أَشرفِ الأعضاءِ فِي الإنسانِ', '', 'بها يأخُذُ الإِنسانُ كِتابَه يومَ القِيامَة', '', 'آلةُ أكثَرِ العَمَلِ، وبها يُزاوَلُ أكثرُ الأعمالِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى قوله: {ثَانِيَ عِطْفِهِ} أي:', '', 'ثَنَى إزارَه الَّذي يَلبسُه', '', 'رافِعًا حاجِبَيْهِ تَعَجُّبًا', '', 'لاوِيًا عُنُقَه تَكَبُّرًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'كانت بدايةُ خلقِ آدمَ عَليهِ السَّلامُ مِنْ:', '', 'نُطْفَةٍ', '', 'تُرابٍ', '', 'طِينٍ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ من قَولِه تَعالَى: {وَلْيُوفُوا نُذُورَهُمْ}:', '', 'ولْيُوفِ الحُجَّاجُ بما أوجَبوهُ على أنفُسِهم في الحَجِّ', '', 'وليوفِ الحُجَّاجُ بما عَاهَدوا عليه آبَاءَهُمْ', '', 'وليوفِ الحُجَّاجُ بِما أوجب رَبُّهُم عَليهِم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُؤخَذُ من قَولِه تَعالَى: {وَأَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجَالًا وَعَلَى كُلِّ ضَامِرٍ}:', '', 'أنَّ أَفضلَ الحَجِّ ما كان على الخَيلِ المُضمَّرةِ', '', 'عدمُ صحَّة حجِّ الماشي', '', 'جوازُ المَشيِ والرُّكوبِ في الحَجِّ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الفاء في قَولِه تَعالَى: {فَاجْتَنِبُوا}:', '', 'للسَّببيَّة', '', 'لِلاسْتِئْنَافِ', '', 'للتَّفريعِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ من قَولِه تَعالَى: {الْقَانِعَ}:', '', 'الفقيرُ الذي لا يسألُ النَّاسَ شيئًا', '', 'الجارُ الغنيُّ', '', 'السَّائِلُ الذي يَتعرَّضُ لِما في أيدِي النَّاسِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَلِكُلِّ أُمَّةٍ جَعَلْنَا مَنْسَكًا لِيَذْكُرُوا اسْمَ اللَّهِ عَلَى مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعَامِ فَإِلَهُكُمْ إِلَهٌ وَاحِدٌ فَلَهُ أَسْلِمُوا} دليلٌ عَلَى:', '', 'أنَّه لا يجوزُ في الأُضحِيَةِ إلا البَهائمِ فقط', '', 'أن ذِكرَ اللهِ عندَ ذَبحِ الأُضحِيَةِ شَرطٌ لا يَحِلُّ الذَّبحُ إلَّا به؛ ولا يَسقُط بالسَّهوِ', '', 'أنَّ ذَبْحَ الأُضحِيَّةِ وذِكْرَ اسمِ اللهِ عليها عِبادةٌ مَقصودةٌ بذاتِها، وأنَّها مِن توحيدِ اللهِ وتمامِ الاستِسلامِ له', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'نَزَل قَولُه تَعالَى: {هَذَانِ خَصْمَانِ اخْتَصَمُوا فِي رَبِّهِمْ} في:', '', 'الذين تبارَزوا يومَ بَدرٍ', '', 'بِلالِ بن رَباحٍ رضي الله عنه، وأُمَيَّةَ بنِ خَلَفٍ', '', 'أَهلِ الأَحزابِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفاد من قَولِه تَعالَى: {وَتَرَى النَّاسَ سُكَارَى وَمَا هُمْ بِسُكَارَى} أنَّ سَبَبَ السُّكرِ:', '', 'يكونُ منَ اللذَّة كما يكونُ مِن الخَمرِ', '', 'قد يكونُ مِن الأَلَم، كما يكونُ من اللَّذَّةِ', '', 'لا يكونُ إلَّا مِن الألمِ', '', '', 'b', 'medium');
