(@tafseer_category_id, '', 'قوله تعالى: {وَعَمِلُوا الصَّالِحَاتِ} فيه الردُّ على من يقولونَ: إنَّ الإنسانَ لا يعملُ، ولا يضافُ العملُ إليه إلا مجازًا. وَهُمْ:', '', 'الجَبْريَّة', '', 'المُعتزِلة', '', 'الخَوارج', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما وجهُ الإفرادِ في قَولِه: {يُخْرِجُكُمْ طِفْلًا} مع أنَّ المعنى نخرِجُكم أطفالًا؟', '', 'أَفْرَدَهُ باعتبارِ كلِّ واحدٍ منهم، أي: يُخرِجُ كُلَّ واحدٍ منكم طِفلًا', '', 'أَفْرَدَهُ باعتبارِ المجيءِ يَوْمَ القيامةِ فُرَادَى', '', 'أفردَهُ لأنَّ الجمعَ هُنَا جَمعُ قِلَّة', '', '', 'a', 'hard');
