(@tafseer_category_id, '', 'إِنَّمَا جُعِلَ الاسْتِئْذَانُ مِنْ أَجْلِ:', '', 'المحبَّةِ', '', 'السَّلامِ', '', 'البَصَرِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَلْيَشْهَدْ عَذَابَهُمَا طَائِفَةٌ مِنَ الْمُؤْمِنِينَ} دليل على:', '', 'إقامةِ حدِّ الجَلْدِ في السِّر', '', 'أنَّ حَدَّ الجَلد هذا يقام عَلانيةً غير سِرٍّ', '', 'وجوبِ سَتْرِ الزُّنَاة الذينَ ثبتَ زِنَاهُم بالبيِّنَة', '', '', 'b', 'medium');
