(@tafseer_category_id, '', 'سَبَبُ إفرادِ الشَّهادةِ الخامسةِ بالذِّكْرِ عَنْ سائرِ الشَّهَاداتِ الأربعةِ في اللِّعانِ في قَولِه تَعالَى: {وَالْخَامِسَةُ} هو:', '', 'أنَّ هذهِ الشهادةَ لا تأخذُ حُكْمَ الأَرْبَعَةِ السَّابِقَةِ', '', 'أَنَّ استِثنائِها هو مِن بابِ الاستِثناء المُنقَطِع', '', 'استِقلالُها بالفَحوى، ووِكادَتُها في إفادةِ ما يُقصَدُ بالشَّهادةِ مِن تَحقيقِ الخَبرِ وإظهارِ الصِّدقِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى قوله تعالى: {أَفَضْتُمْ}:', '', 'خُضتُم', '', 'تَحَوَّلتُم', '', 'نَزَلتُم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {يَا أَيُّهَا الَّذِينَ آَمَنُوا لَا تَدْخُلُوا بُيُوتًا غَيْرَ بُيُوتِكُمْ حَتَّى تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلَى أَهْلِهَا} مِنَ الآدابِ:', '', 'أنَّ الاستِئناسَ والسَّلامَ شَرطٌ لدُخولِ البُيوتِ لا يَصِحُّ بِدُونِهِما', '', 'أنَّ المرءَ لا يَنبغي أن يكونَ كَلًّا على غَيرِه، ولا يَنبغي له أن يُعرِّضَ نفْسَه إلى الكراهيةِ والاستِثقالِ', '', 'التَّنبِيهُ على ضَرورَةِ الإيمانِ لقَولِه: (آَمَنُوا)', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَن قَذَفَ نفسَه ثم رجع؛ فإنَّهُ:', '', 'يحدُّ لنفسِه', '', 'لا يحدُّ لنفسه؛ لأنه لم يَرْمِ أَحَدًا', '', 'يُحَدُّ بنِصفِ العَددِ تَعزِيرًا', '', '', 'b', 'medium');
