(@tafseer_category_id, '', 'المُرادُ بقَولِه تَعالَى: {يَأْتَلِ}:', '', 'يُعرِض', '', 'يَغضَب', '', 'يَحلِف', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {لَوْلَا إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِأَنْفُسِهِمْ خَيْرًا وَقَالُوا هَذَا إِفْكٌ مُبِينٌ} دليلٌ على:', '', 'أن الأصلَ في المسلمِ الغَفلةُ عن الإِفكِ والأفَّاكِينَ', '', 'وُجوبِ حَدِّ القَذفِ', '', 'أنَّ مَن عُرِف بالصلاحِ لا يُعدَلُ به عنه لخبرٍ مُحتَمَلٍ، وأنَّ القاذِفَ يُكذَّبُ شرعًا ما لم يأت بالشُّهداء', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'متى يُباحُ الحكمُ بالظَّنِّ على النَّاسِ؟', '', 'إذا دلَّتِ القَرائنُ على ذلك', '', 'إذا كانُوا مِن المُنافِقينَ صَرِيحِي النِّفاقِ', '', 'إذا ارتَضَوْا مَنهجَ الظَّنِّ حُكِمَ بِه عليهِم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن حقِّ المؤمنِ على أخيه إذا سَمِعَ مقالةَ شَرٍّ تُقالُ في حقِّه، :', '', 'أن  يَبنِي الأمرَ فيه على ظَنِّ الخيرِ، وأن يقولَ بِناءً على ظنِّه: هذا إفك مُبين', '', 'أن يُبلِّغ أخاهُ بما يُقالُ عنه حتَّى يَطلُبَ البَراءَةَ لنَفسِه', '', 'أن يُقاتِلَ دِفاعًا عن عِرضِ أَخيهِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سمَّى اللهُ تعالى اللِّعانَ شهادةً؛ مِن أَجْلِ:', '', 'أنه يُدْلِي به الزوجُ بين يدي الإمام', '', 'أنَّه يُقامُ في مجلسِ القَضَاءِ', '', 'أنه ينوبُ مناب الشُّهُود؛ حيث يقولُ: "أشهدُ باللهِ إنِّي لَمِنَ الصادقينَ فيما رميتُها به"', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ}:', '', 'الحثُّ على الرَّحمَةِ والرَّأفَةِ حالَ العُقوبَة', '', 'تَنبِيهٌ على عَدمِ التَّثريبِ على المَحدُودِ', '', 'الحثُّ على إقامةِ الحدود، والنهيُ عن تَعطيلِها، وأنَّه لا يجوزُ العفوُ عنها للإمامِ ولا لغَيرِه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ بقَولِه تَعالَى: {الْإِرْبَةِ}:', '', 'الحاجةُ إلى النِّساءِ', '', 'الإناءُ', '', 'الجاهُ والغِنَى', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ} دليلٌ على:', '', 'أن اللعان يشملُ ما قبْل الدُّخول وما بعده', '', 'أنَّ اللِّعان لا يكونُ إلا بين الرجلِ وامرأته التي دخلَ بِهَا', '', 'أنَّ اللعانَ لا يكونُ إلَّا قبلَ الدُّخولِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ضميرُ الغَيْبَةِ المذكورُ في قَولِه تَعالَى: {وَالْخَامِسَةُ أَنَّ لَعْنَةَ اللَّهِ عَلَيْهِ إِنْ كَانَ مِنَ الْكَاذِبِينَ} مع أن الزوج يأتي به بلفظ المتكلم يعني يقول: "أَنَّ لَعْنَةَ اللهِ عليَّ" لأنَّ:', '', 'هذا إيهامٌ للسَّامِع', '', 'فيهِ إشارةً إلى بُعدهِ عن الرَّحْمَة', '', 'هَذَا من باب التَّأدُّبِ في اللَّفظِ: أنَّ يُعَبَّرَ بضَميرِ الغَيْبةِ؛ لئلَّا يُضِيفَ المُتكلِّمُ اللَّعنةَ إلى نفسه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {يَعِظُكُمَ اللَّهُ أَنْ تَعُودُوا لِمِثْلِهِ أَبَدًا إِنْ كُنْتُمْ مُؤْمِنِينَ} دلالةٌ عَلى:', '', 'أنَّ المَوعِظةَ يَنتفِعُ بها كلُّ الناسِ', '', 'التَّحذِيرِ مِن تَأخِيرِ التَّوبَةِ', '', 'أنَّ المؤمنَ هو الذي ينتفعُ بالموعظةِ، أمَّا غيرُه فإنَّه لا ينتفعُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {الْغَافِلَاتِ}:', '', 'تشبيهٌ للنساءِ بالمُستَغرِق في النَّومِ', '', 'كِنايةٌ عن عدَمِ وُقوعِهنَّ فيما رُمِينَ به', '', 'مجازٌ عَلَاقَتُه السَّبَبِيَّةُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'حَدُّ الزِّنَا إنَّما يثبتُ بِشَهَادَةِ:', '', 'أربعة من الرِّجَالِ، ولا يثبتُ بشهادةِ النِّساء', '', 'رَجُلٍ وامرَأَتَين', '', 'اثنَينِ منَ العُدولِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تخصيصُ النِّسَاءِ بالذكرِ في قَولِه تَعالَى: {وَالَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ} من أجْلِ:', '', 'أنَّ الرِّجالَ لا يُشرعُ في حَقِّهِم حَدُّ القَذفِ', '', 'أنَّ القذفَ فيهنَ أشنَعُ وأنكَرُ للنُّفوسِ، ومِن حيث هُنَّ هَوَى الرِّجالِ؛ ففيه إيذاءٌ لهنَّ ولأزواجهن وقراباتِهنَّ', '', 'نفيِ الحُكْمِ عَنْ قَاذِفِ الرجالِ', '', '', 'b', 'hard');
