(@tafseer_category_id, '', 'في قَولِه تَعاَلى حِكَايَةً عن تحسُّر الكافرينَ في النار: {فَمَا لَنَا مِنْ شَافِعِينَ} دلالةٌ على:', '', 'تَعدُّدُ أَنواعِ الشَّفاعاتِ يومَ القِيامَة', '', 'انتِفاءُ الشَّفاعاتِ مُطلقًا يَومَ القيامةِ', '', 'انتفاءُ الشَّفاعةِ عن المكذِّبين للرُّسُلِ؛ وأنَّه لا يُشفَعُ لهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في تَقْدِيمِ تَقْوَى اللهِ تعالى على طاعةِ الرَّسولِ في قَولِه تَعاَلى: {فَاتَّقُوا اللَّهَ وَأَطِيعُونِ} فائدةٌ؛ وهي:', '', 'أنَّ تَقوَى الله هي الفَريضةُ وطاعةُ الرَّسولِ نافِلَة', '', 'أن تقْوَى اللهِ سببٌ لطاعةِ الرُّسُلِ', '', 'التَّأدُّب مع الله في الدُّعاءِ والطَّلَبِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المراد ُمن قَولِه تَعالَى: {فَكُبْكِبُوا}:', '', 'شُرْذِمُوا وتَفَرَّقُوا', '', 'رُفِعُوا', '', 'جُمِعوا وطُرِحوا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعاَلى: {فَأَخْرَجْنَاهُمْ مِنْ جَنَّاتٍ وَعُيُونٍ}:', '', 'إِشارَةٌ إلى الخُلودِ في الدارِ الآخِرَة', '', 'تَنبِيهٌ على أنَّ الجِنانَ لا تَصلُح إلا بعُيونِ الماء', '', 'تحذيرٌ للطُّغاةِ مِن أن تزولَ نِعَمُهم بسبَبِ طُغيانِهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعاَلى: {وَإِنَّهُ لَفِي زُبُرِ الْأَوَّلِينَ} دَلالةٌ عَلى:', '', 'أنَّ دِينَ الأَنبياءِ واحِدٌ وشَرائِعُهم مُختَلِفَة', '', 'أنَّ هذا القُرآنَ مَذكورٌ خبَرُه في كتُبِ الأنبياءِ السَّابقةِ المبشِّرةِ به، ممَّا يدُلُّ على صِحَّتِه', '', 'تَكرارِ مَوَاعِظِ القرآنِ وأَحْكَامِهِ في الشَّرائِع السَّابِقَةِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'السِّرُّ في قَولِ إبراهيمَ عَليهِ السَّلامِ: {وَإِذَا مَرِضْتُ فَهُوَ يَشْفِينِ}:', '', 'إِشارَةٌ إلى الرِّضا التَّام بأَقدارِ الله عزَّ وجلَّ', '', 'أن هذا من حُسْنِ الأدبِ مع اللهِ تعالى؛ فمِنَ الأدبِ إضافةُ نوعِ الحسَنةِ إلى الله، ونوعِ السَّيِّئةِ إلى النفْسِ', '', 'أنَّ كلَّ شَيءٍ  بَيَدِ الله تعالى حتى المَرَض والشِّفاء', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المرادُ من قولِه تَعَالَى: {تَلْقَفُ}:', '', 'تَحرِقُ', '', 'تَبتَلِعُ', '', 'تَقذِفُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {فَظَلَّتْ أَعْنَاقُهُمْ لَهَا خَاضِعِينَ} أصْلُ الكلامِ: "فظَلُّوا لها خاضعينَ"، فأُقحِمَتِ الأعناقُ لـ:', '', 'لبَيانِ مَوضعِ الخُضوعِ', '', 'للإشارةِ إلى العَجزِ المُنافِي للقُدرَةِ', '', 'للتَّأكيدِ على فِعلِ  الإعراضِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ بقَولِه تَعاَلى: {بَاخِعٌ}:', '', 'قَاتِلٌ', '', 'مَسْرُور', '', 'خَاشِع', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سورةُ الشُّعَراءِ منَ السُّوَرِ:', '', 'المكيَّة', '', 'المدنيَّة', '', 'المختلَف فيها', '', '', 'a', 'medium');
