(@tafseer_category_id, '', 'المُرادُ بقَولِه تَعاَلى: {الْأَرْذَلُونَ}:', '', 'أهلُ الضَّعَةِ والخَساسةِ', '', 'أَصحابُ الضِّياعِ', '', 'السَّابِقُونَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ} قال: "خَلَقَنِي" بلفظِ الماضي، و: "يَهْدِينِ" بلفظِ المستقبَلِ:', '', 'لتَقدُّمِ الخَلْقِ واستِمرارِ الهِدايةِ', '', 'لرِعايَةِ التَّرتِيبِ الزَّمَنِي في الأَحداثِ', '', 'لأنَّ احتِياجَ العِبادِ للهِدايَةِ أَعظمُ مِن احتِياجِهِم للخَلْقِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعاَلى: {فَنَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ * إِلَّا عَجُوزًا فِي الْغَابِرِينَ}:', '', 'أنَّ القُرْبَ منَ الأنبياءِ والأولياءِ لا يُغْني الإنسانَ شيئًا', '', 'أنَّ مَعِيَّة الله عزَّ وجلَّ مع المُؤمِنينَ ولو نَزلَ بهِم العَذابُ', '', 'الإتيانُ بلَفظِ الغابِرين للدَّلالةِ على عُتُوِّهِم في الكُفرِ', '', '', 'a', 'medium');
