(@tafseer_category_id, '', 'المقصودُ بقوله تعالى: {لِسَانَ صِدْقٍ}:', '', 'دَعوةٌ صادِقَةٌ', '', 'ذِكر وثناء حَسَن', '', 'تَوبَةٌ نَصوحٌ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعاَلى: {وَمَا أَهْلَكْنَا مِنْ قَرْيَةٍ إِلَّا لَهَا مُنْذِرُونَ} دليلٌ على:', '', 'أنَّ كُلَّ قريةٍ قَدْ جاءَها رُسُلٌ أو أَنبِياءُ', '', 'أنَّ الهَلاكَ إذا جاءَ لا يأتي لأَفرادٍ بأعيُنِهِم؛ إنَّما يأتِي عامًّا', '', 'أنَّ اللهَ تعالى لا يُعَذِّبُ أحدًا ولا يُهلِكُه إلَّا بذَنْبِه، وبعْدَ إرسالِ الرَّسولِ إليه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'من صِفاتِ الأُمَم الطَّاغِيَة التي ذُكِرَت في سُورةِ هُود:', '', 'اتِّخاذُ المَبانِي الفَخمةِ للفَخرِ والخُيَلاءِ والزِّينةِ، وقَهرُ العبادِ بالجَبَروتِ', '', 'التَّمادِي في شُربِ الخَمرِ واقتِرافِ الزِّنا', '', 'استِحقارُ الضُّعَفاءِ وظُلمُهم وهَضمُ حُقوقِهِم والسُّخرِيَة مِنهم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعاَلى: {إِنَّا مَعَكُمْ مُسْتَمِعُونَ} دليلٌ على:', '', 'إثباتُ المَعِيَّةِ للهِ عزَّ وجلَّ، وإثباتُ صفةِ السَّمع ِله أيضًا', '', 'عَدمِ جوازِ صِفةِ المَعِيَّة فِي حَقِّ اللهِ تعالى', '', 'أنَّ المَعِيَّة اسمٌ مِن أَسماءِ الله وصِفَةٌ مِن صِفاتِه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'من أهَمِّ المَقاصِدِ الَّتي تَهدِفُ لها سُورةُ الشُّعراءِ:', '', 'ذِكْرُ أحكامِ الجهادِ', '', 'التَّنويهُ بالقرآنِ الكريمِ، والتَّعريضُ بعَجزِ المشركينَ عن مُعارَضتِه', '', 'إقامةُ الأدِلَّةِ على وَحدانيَّةِ اللهِ تعالى وقُدرتِه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تَعالَى: {يَهِيمُونَ}:', '', 'يَمرَحون في غَفلَة', '', 'يَغِيبون عن النَّظَرِ', '', 'يَذهَبون على غيرِ قَصدٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'استعمالُ اسمِ الإشارةِ البعيدِ في قَولِه تَعاَلى: {تِلْكَ آَيَاتُ الْكِتَابِ الْمُبِينِ} فائدة؛ وهي:', '', 'التنبيهُ على بُعدِ منزلةِ المُشارِ إليه في الفَخامةِ', '', 'تشبيهُ الآياتِ بالنُّورِ الذي يأتي مِن بَعِيدٍ', '', 'كِنايةٌ عن بُعْدِ الكُفَّارِ عن هذه الآياتِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن دَقَائِقِ المُجادلةِ الَّتِي جَرَتْ بين موسى عَليهِ السَّلامُ، وفِرْعَون أنَّه:', '', 'أنَّ الاستِفسارَ مُقَدَّمٌ في المناظَراتِ', '', 'أنَّ الحُجَّة لا تُدحَضُ إلَّا بمِثلِها', '', 'أنَّ الصَّبرَ مُفتاحُ النَّصرِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما فائدةُ إدخالِ الأصنامِ التي كانت تُعبَدُ من دونِ اللهِ إلى النَّارِ مع عابدِيها؟', '', 'إشراكُ الجَميعِ في العَذابِ المُستَحقِّ للجُرْمِ الواحِد', '', 'أنَّ في ذلك إهانةً لعابديها؛ وبيانِ أنهَّا لا تنفَعُ', '', 'تَعذيبُها لاستِحقاقِها ذلك بإغواءِ بَني آدمَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'عبَّر اللهُ تعالى عن خُرورِ السَّحَرَةِ للسُّجُودِ بالإلقاءِ؛ وذلك في قَولِه: {فَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ}، وذلك للدَّلالةِ عَلى:', '', 'أنَّهُم سَقَطوا كأنَّهُم أُلْقُوا من مَكانٍ سَحيقٍ', '', 'للرِّعايَةِ على الفاصِلَةِ', '', 'أنه ذُكِر مع الإلقاءاتِ، فسُلِك به طريقُ المُشاكَلةِ', '', '', 'c', 'hard');
