(@tafseer_category_id, '', 'المُرادُ بقَولِه تَعاَلى: {مَصَانِعَ}:', '', 'أماكِنُ الصِّناعَة', '', 'ما يُصنَعُ مِن أَدواتِ الحَربِ', '', 'مَنازِل، وحُصون، وقُصور مُشَيَّدة', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ بقَولِه تَعاَلى: {زُبُرِ}:', '', 'أَساطِير', '', 'كتُب', '', 'قَصَص', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعاَلى: {إِذْ قَالَ لَهُمْ أَخُوهُمْ صَالِحٌ أَلَا تَتَّقُونَ}:', '', 'تَبْرِئَةُ صالحٍ عَليهِ السَّلامُ مِمَّا يَفعَلُهُ قَومُه', '', 'التَّنبيهِ على أنَّ صالحًا عَليهِ السَّلامُ تبرأ مِن نَسَبِهِم بسَببِ كُفرهِم', '', 'التَّنْبيهُ على أنَّه أَخوهُم في النَّسَبِ، وبِالتَّالي فهم يَعرِفون صِدقَه وأمانَتَه وشَفَقتَه عليهم', '', '', 'c', 'hard');
