(@tafseer_category_id, '', 'في قَولِه تَعالَى: {اللهُ يَبْدَأُ الخَلْقَ ثُمَّ يُعِيدُهُ} دلالةٌ عَلَى:', '', 'أنَّ البَعثَ ليس ابتداءَ خَلْقٍ، ولكنَّه إعادةٌ', '', 'أنَّ كلَّ إِنسانٍ يُخلَقُ مَرَّتَينِ', '', 'أنَّ البعثَ ابتِداءُ خَلْقٍ، وليس إعادةً', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنى "مُحْضَرُونَ" في قَولِه تَعالَى: {فَأُولَئِكَ فِي العَذَابِ مُحْضَرُونَ}:', '', 'مَأْتِيٌّ بهم إلى العذابِ', '', 'مُهانُونَ صَاغِرُونَ', '', 'مُضَيَّقٌ عليهِم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَقديمُ الخَبَرِ (وَلَهُ الحَمْدُ) في قَولِه تَعالَى: {وَلَهُ الحَمْدُ فِي السَّمَاوَاتِ وَالْأَرْضِ} دلالةٌ على:', '', 'لا يقومُ بحَقِّ الحَمدِ لله إلَّا الأنبياءُ والرُّسُلُ', '', 'أنَّ أَهلَ السَّمواتِ وأَهلَ الأَرضِ لم يَحمَدُوا الله كما يَليقُ بجَلالِه', '', 'أنَّه وَحدَهُ المستحقُّ لِأَنْ يُحْمَدَ على وجهِ الإطلاقِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى "يَرْبُو" في قَولِه تَعالَى: {وَمَا آتَيْتُمْ مِنْ رِبًا لِيَرْبُوَ فِي أَمْوَالِ النَّاسِ}:', '', 'يَضُرُّ', '', 'يَزدادُ', '', 'يَطمَعُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'جاءتِ الرَّوضةُ نَكِرةً في قَولِه تَعالَى: { فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ فِي رَوْضَةٍ يُحْبَرُونَ}:', '', 'لإبهامِ أمْرِها وتَفْخيمِه', '', 'لتَّأكيدِ الخِطابِ بالطَّريقِ البُرهانيِّ', '', 'للعُمومِ والشُّمُولِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصودُ بقَولِه تَعالَى: {وَهُم مِّنْ بَعْدِ غَلَبِهِم سَيَغْلِبُونَ}:', '', 'أي: أن الرُّومَ من بعدِ أَن غَلَبَهُمْ أهلُ فارسٍ سيَغلِبونَهم', '', 'أي: ستَغلِبُ الرُّومُ العربَ المُشرِكينَ بعدَما يَهزِمُونَهُم أوَّلَ مَرَّةٍ', '', 'أي: أنَّ أهلَ فارسٍ سيَغلِبونَ الروم مَرَّةً أُخرى', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'غَلَبَ الرُّومَ وَقَهَرَهُم:', '', 'العربُ المُشرِكون', '', 'المُسلِمون', '', 'أهلُ فارسٍ المُشرِكون', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى "يَصَّدَّعُونَ" :', '', 'يَتفَرَّقون فيَصِيرونَ فَريقًا في الجنَّةِ وفَريقًا في السَّعيرِ', '', 'يُزَلْزَلون حتَّى يَموتوا', '', 'يُضرَبون في رُؤوسِهم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قُرِئَ "لِلْعَالِمِينَ" بكسر اللَّامِ في قَولِه تَعالَى: {إِنَّ فِي ذَلِكَ لَآيَاتٍ لِلْعَالِمِينَ} أي:', '', 'عَالمُ الأَرضِ وعَالَمُ السَّماءِ', '', 'أَحبارُ اليَهُودِ', '', 'جَمع عالِمٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى تُظْهِرُونَ في قَولِه تَعالَى: {وَلَهُ الْحَمْدُ فِي السَّمَاوَاتِ وَالْأَرْضِ وَعَشِيًّا وَحِينَ تُظْهِرُونَ}:', '', 'تَدخُلون فِي وقتِ الظَّهيرةِ', '', 'تُظهِرون بعدَ الفَجرِ أوَّلَ النهارِ', '', 'تَنتَصِرون وتَظْهَرون على الكُفَّارِ', '', '', 'a', 'easy');
