(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ * بِنَصْرِ اللَّهِ}:', '', 'جوازُ فَرَحِ المُؤمِنين بانتِصارِ بعضِ الكُفَّارِ على بعضٍ', '', 'عَدَمُ مَشرُوعِيَّةِ الفَرَحِ بانتِصارِ بَعضِ الكُفَّارِ على بعضٍ', '', 'جَوازُ فرحِ المُؤمِنين بانتصارِ أهلِ الإسلامِ على أَهلِ الكُفرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المقصودُ بالأجَلِ المُسَمَّى في قَولِه تَعالَى: {أَوَلمْ يَتَفَكَّرُوا فِي أَنْفُسِهِمْ ما خَلَقَ اللهُ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إلا بالحقِّ وأجلٍ مُسَمًّى}:', '', 'أي: أنَّ كُلَّ نَفْسٍ لها أجلٌ مَعلومٌ', '', 'أي: أَنَّ للسَّماواتِ والأرضِ وَقتًا مُحدَّدًا عندَ اللهِ، تَنتَهي إليه، وتَفْنَى فيه', '', 'أي: أنَّ الغيبَ لَا يَعلَمُه أحدٌ إلَّا اللهُ', '', '', 'b', 'medium');
