(@tafseer_category_id, '', 'المَقصُودُ بـ"سُلطان" في قَولِه تَعالَى: {أَمْ أَنْزَلْنَا عَلَيْهِمْ سُلْطَانًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِهِ يُشْرِكُونَ}:', '', 'إِبْلِيسَ', '', 'جِبريل عَليهِ السَّلامُ', '', 'حُجَّةً', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {ثُمَّ إِذَا دَعَاكُمْ دَعْوَةً مِّنَ الأَرْضِ إِذَا أَنْتُمْ تَخْرُجُونَ}:', '', 'ردٌّ على المُرجِئةِ الَّذين يَقولون: إنَّ الإيمانَ قَولٌ فَقَط', '', 'ردٌّ على الجَهمِيَّةِ الذين يقولون: إنَّ اللهَ مَوجودٌ بذاتِه في كلِّ مكانٍ', '', 'حُجَّةٌ عَلَى المُعتزِلةِ والقَدَرِيَّةِ فِي إضافةِ الفِعلِ إلى فَاعِلِه', '', '', 'c', 'medium');
