(@tafseer_category_id, '', 'سورةُ الرُّومِ', '', 'مدنِيَّة', '', 'مكِّيَّة بالإجماعِ', '', 'مُختلَفٌ فيها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصودُ بقوله تعالى: {يُسْتَعْتَبُونَ}:', '', 'لا يُطلَبُ يومَ القِيامةِ منَ الظَّالِمين أن يَرجِعوا إلى ما يُرضِي اللهَ تَعالَى؛ حتى تُزالَ عنهم المُعاتبةُ والمُؤاخَذةُ', '', 'لا يَستَجيبُ خازنُ النَّارِ إلى كلامِ الظَّالمينَ في النَّار', '', 'لا أَحَدَ يَسْمَعُ عِتابَ الظَّالِمين بَعْضِهمْ بَعضًا', '', '', 'a', 'medium');
