(@tafseer_category_id, '', 'المَقصودُ بقَولِه تَعالَى: {وَلَا يَسْتَخِفَّنَّكَ الَّذِينَ لَا يُوقِنُونَ}. أي:', '', 'أَيْ: لا يُخَوِّفَنَّكَ -يَا مُحَمَّدُ-  أَعداءُ الله؛ فاللهُ حَافِظُكَ', '', 'لا يَسْتَفِزَّكَ -يا مُحمَّدُ- الَّذين لا يوقِنون بوَعدِ اللهِ، فيَحمِلوك على عدَمِ الصَّبْرِ وعَدَمِ تَبليغِ الحقِّ', '', 'لا يَصْرِفَنَّكَ يا محمد هُؤلاء المُشرِكُون عن الجُلوسِ مع المَساكِينِ والمَيْلِ إليهِم', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَإِذَا مسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُم مُنِيبينَ إِلَيْهِ}:', '', 'إِشارَةٌ إلى جَزَعِ الإنسانِ عندَ البَلوَى', '', 'دليلٌ على أنَّ مَعرِفَةَ الرَّبِّ في فِطرةِ كُلِّ إِنْسَانٍ', '', 'تَحذِيرٌ لبني آدمَ مِن مَسِّ الشَّيطانِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قُدِّمَ الجارُّ وَالمَجرورَ "بِهِ" عَلَى "يُشْرِكون" في قَولِه تَعالَى: {أَمْ أَنْزَلْنَا عَلَيْهِمْ سُلْطَانًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِهِ يُشْرِكون}:', '', 'لرِعايةِ الفَاصِلةِ وللتَّنْبيهِ عَلَى سَبَبِ إشْراكِهِم', '', 'لِحَصرِ استِحقاقِ العَذابِ عليهِم دُونَ غَيرهِم', '', 'لتَأكِيدِ الخِطابِ بالطَّريقِ البُرهانيِّ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى "يُحبَرُون" في قَولِه تَعالَى: {فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ فِي رَوْضَةٍ يُحْبَرُونَ}:', '', 'يَتَزاوَرُونَ', '', 'يُنَعَّمون', '', 'يَلبَسُون الحَبْرَ؛ أي: الحَريرَ', '', '', 'b', 'medium');
