(@tafseer_category_id, '', 'مَعنى: "وَهْنًا" في قولِه تَعالَى: {حَمَلَتْهُ أُمُّهُ وَهْنًا عَلَى وَهْنٍ}', '', 'عَزمٌ', '', 'شَفَقَة', '', 'ضَعْف', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفادَ قولُه تَعالَى: {فَأَنبَتْنَا فِيهَا مِن كُلِّ زَوْجٍ كَرِيمٍ}', '', 'إظهارَ تَعَدُّدِ نِعَمِ الله على بني آدمَ في البَرِّ والبَحرِ', '', 'أنَّ كلَّ نَوعٍ مِن النَّباتِ له صِنفَانِ', '', 'بيانَ قُدرةِ اللهِ عزَّ وجلَّ على تَصنيفِ هذا النباتِ معَ أنَّ أرضَه واحدةٌ وماءَه واحدٌ', '', '', 'c', 'easy');
