(@tafseer_category_id, '', 'مَعنى: "أَنَابَ" في قولِه تَعالَى: {وَاتَّبِعْ سَبِيلَ مَنْ أَنَابَ إِلَيَّ}', '', 'رجَعَ وتابَ', '', 'دَعَا', '', 'خَضَعَ وذَلَّ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مناسَبةُ قَولِه تَعالَى: {خَلَقَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَأَلْقَى فِي الأَرْضِ رَوَاسِيَ}', '', 'لما أنكَرَ المشرِكونَ قُدرةَ اللهِ عزَّ وجلَّ أراهُمُ اللهُ هنا بعضَ مَظاهِرِ قُدرتِه', '', 'لما اختتَمَ اللهُ تَعالَى الآيةَ السابقةَ بصِفَتَيِ العزَّةِ وهي غايةُ القُدرةِ والحكمةِ، وهي ثمرةُ العلمِ دلَّ عَليهِا بإتقانِ أفْعالِه وإحْكامِها', '', 'لما تساءلَ الناسُ عن خَلقِ السمَواتِ والأرضِ أنزَلَ اللهُ هذه الآيةَ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنى رَواسيَ في قولِه تَعالَى: {وَأَلْقَى فِي الأَرْضِ رَوَاسِيَ}', '', 'صخورٌ صَلبَة', '', 'هِضابٌ عالِيَة', '', 'جبالٌ ثوابِتُ', '', '', 'c', 'easy');
