(@tafseer_category_id, '', 'أفادَ قولُه تَعالَى: {إِنَّ فِي ذَلِكَ لآيَاتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ} أنَّ الإيمانَ نِصفَانِ:', '', 'علمٌ وعملٌ', '', 'صبرٌ وشكرٌ', '', 'استِسلامٌ وانقِيادٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مناسَبةُ قولِه تَعالَى: {وَمِنَ النَّاسِ مَن يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ عَن سَبِيلِ اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّخِذَهَا هُزُوًا أُولَئِكَ لَهُمْ عَذَابٌ مُّهِينٌ}', '', 'لما ذكَرَ اللهُ تَعالَى حالَ المُؤمِنين معَ القُرآنِ أعقَبَه بذِكرِ حالِ الكافِرينَ', '', 'لما بيَّنَ اللهُ تَعالَى أنَّ القرآنَ كتابٌ حكيمٌ يشمَلُ آياتٍ حكيمةً، بيَّنَ من حالِ الكفَّارِ أنَّهم يَترُكونَ ذلك ويَشتغِلونَ بغيرِه', '', 'لمَّا ذكَرَ اللهُ تَعالَى صَنيعَ المُشرِكين معَ النبيِّ ﷺ وإخراجَه من مَكَّةَ ذكَرَ هنا حالَهم معَ القرآنِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سورةُ لُقمانَ', '', 'كلُّها مَكِّيَّةٌ إلَّا خمسَ آياتٍ', '', 'مَدنِيَّةٌ', '', 'مَكِّيَّةٌ', '', '', 'c', 'easy');
