(@tafseer_category_id, '', 'في قَولِه تَعالَى: {تَنْزِيلُ الْكِتَابِ لَا رَيْبَ فِيهِ مِنْ رَبِّ الْعَالَمِينَ}:', '', 'بيان أنَّ اللهَ فِي كلِّ مكانٍ', '', 'إثباتُ عُلُوِّ اللهِ تعالى', '', 'نفيُ الجِهةِ عن اللهِ تعالَى', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {لِتُنْذِرَ قَوْمًا مَا أَتَاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ} يُستَفادُ منه:', '', 'أنَّ النبيَّ ﷺ غيرُ مُرسَلٍ لأَهلِ الكِتابِ لأنَّهم قد جاءَهُم مُوسَى وعِيسَى عليهِما السَّلامُ', '', 'أن النَّبِيَّ مُرسَلٌ لأهلِ الكتابِ أيضًا لِأَنَّهُم قد ضَلُّوا، وَلَمْ يأتِهم نذيرٌ مِن قَبْلِ محمَّدٍ بعدَ ضَلالِهم', '', 'أنَّ رِسالَةَ الرُّسُلِ تكونُ بالإِنذارِ قبلَ التَّبشِيرِ؛ لقَولِه: "لِتُنْذِرَ"', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ} تَقديمُ الجارِّ والمَجرورِ {لَكُمُ} على المَفعولِ الصَّريحِ:', '', 'للعُمومِ والشُّمولِ', '', 'لإِفادَةِ التَّوكيدِ بالخِطابِ البُرهانيِّ', '', 'للاهتِمامِ بالمُقدَّمِ والتَّشويقِ إلى المُؤخَّرِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ اللهِ تَعالَى: {الَّذِي أَحْسَنَ كُلَّ شَيْءٍ خَلَقَهُ} كَلِمة {أَحْسَنَ} تَأتي بمَعنَى:', '', 'عَلِمَ وأتقَنَ وأحكَمَ', '', 'أَفضَل وأَجمَل', '', 'أَتَمّ وأَوْفَى', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {قَلِيلًا مَا تَشْكُرُونَ} بَيانٌ لِكُفرِهم بتلك النِّعَمِ بطَريقِ الاعتِراضِ:', '', 'التَّكميلي', '', 'التَّذييليِّ', '', 'التَّوصيفي', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الخِطابُ المُوَجَّهُ للرَّسولِ ﷺ:', '', 'تَكلِيفٌ خاصٌّ بِه ﷺ مِن جِهَةِ الوَحيِ', '', 'مُوَجَّهٌ أَيضًا لأُمَّتِه ما لم يَقُمْ دَليلٌ على تَخصيصِه', '', 'المَقصُودُ مِنه عُمومُ أُمَّتِه ﷺ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَمَا جَعَلَ أَزْوَاجَكُمُ اللَّائِي تُظَاهِرُونَ} مَعنَى {تُظَاهِرُون}:', '', 'تُظاهِرُونَ مِن الظِّهارِ؛ وهو: تَركُ الرَّجُلِ الإنفاقَ على امرأَتِه', '', 'أي: تَقهَرُونَ وتَغلِبُونَ', '', 'الظِّهار: قَولُ الرَّجُلِ لامرَأتِه: أنتِ علَيَّ كظَهرِ أُمِّي', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', '{وَأَنْزَلَ الَّذِينَ ظَاهَرُوهُمْ مِنْ أَهْلِ الْكِتَابِ}، مَعنَى {ظَاهَرُوهُم}:', '', 'ظَهَرُوا لَهُم', '', 'خَذَلوهم', '', 'أعَانوهُم', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال ابنُ القَيِّمِ رحمَهُ الله: المرءُ كُلَّما كانت نِعمةُ اللهِ عليه أتَمَّ:', '', 'كان إِيمانُه أَكمَلَ، ويَقينُه أَشَدَّ', '', 'كانَ أَعظمَ بَلاءً', '', 'كانت عُقوبتُه إذا ارتكَبَ الجرائمَ أتَمَّ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مكارِمُ الأخلاقِ مُنحَصِرةٌ في:', '', 'الشَّفَقةِ على خَلقِ الله', '', 'جَميعُ ما سَبَق', '', 'التَّعظيمِ لأمرِ الله', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَهُوَ يَهْدِي السَّبِيلَ} يُستَفادُ مِنه:', '', 'أنَّ الإِيمانَ يَزيدُ ويَنقُص', '', 'أنَّ الهِدايَة لازِمَةٌ للمَحَبَّة', '', 'أنَّ طريقَ الحقِّ واحد', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَلَكِنْ مَا تَعَمَّدَتْ قُلُوبُكُمْ} أنَّ مَدارَ الأحكامِ والمؤاخَذةِ عليها هو:', '', 'الرُّوح', '', 'الجَسَد', '', 'القَلب', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تَعالَى: {مَا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ}:', '', 'جَميعُ ما سَبَق', '', 'ليس للإنسانِ قَلْبانِ حتَّى يُطيعَ بأحَدِهما، ويَعصيَ بالآخَرِ', '', 'ما خَلَق اللهُ لأيِّ رَجُلٍ قلبَين في صَدرِه؛ فيَعقِل بهما', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أسلَم دُونَ أن يَشعُرَ قَومُه، فاستَطاع النُّصحَ للمُسلِمينَ بالكَيدِ للمُشرِكين:', '', 'نُعَيم بنُ مَسْعُودٍ الأَشْجَعِيُّ', '', 'نُعَيمُ بنُ النَّحَّامِ الجُمَحِيُّ', '', 'نُعَيم بنُ سَعيدٍ الدَّوسِيُّ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى: {مَسْطُورًا} أي:', '', 'مَكتُوبًا مبَيَّنًا', '', 'في زَمَنٍ غابِرٍ', '', 'مُقَدَّرًا بِقَدَرِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَإِذْ زَاغَتِ الْأَبْصَارُ وَبَلَغَتِ الْقُلُوبُ الْحَنَاجِرَ} أنَّ خَوفَ الإنسانِ مِن المَخلوقِ الخَوفَ الطَّبيعيَّ:', '', 'يُعَدُّ شِركًا', '', 'لا يُعَدُّ شِركًا', '', 'يُعَدُّ نِفاقًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِي فِي قَلْبِهِ مَرَضٌ وَقُلْنَ قَوْلًا مَعْرُوفًا} أنَّ صَوتَ المرأةِ:', '', 'عَورَةٌ', '', 'ليس بعَورةٍ', '', 'مُنكَرٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَن القائِل: "إنَّ الإنسانَ إذا وصلَ إلى دَرَجةِ المعايَنةِ سَقَطت عنه التَّكاليفُ":', '', 'الشِّيعة', '', 'الصُّوفِية', '', 'الأشعريَّة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفادُ من قَولِه تَعالَى: {وَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيمَا أَخْطَأْتُمْ بِهِ}:', '', 'أنَّه لا يُؤاخَذُ أَحَدٌ بجَرِيرَةِ غَيرِه', '', 'أنَّ المَريضَ ليس عليه جُناحٌ فيما عَجَزَ عنهُ', '', 'أنَّ الخطَأَ مَرفوعٌ ولا إثمَ على المُخطِئِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تَعالَى: {يَا أَيُّهَا النَّبِيُّ قُلْ لِأَزْوَاجِكَ إِنْ كُنْتُنَّ تُرِدْنَ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحًا جَمِيلًا} فيه تخييرُه ﷺ نِساءَه بينَ الإقامةِ معه وفِراقِه، وأنَّ التخييرَ:', '', 'يُحسَب طَلقَةً واحِدةً', '', 'طَلاقٌ بائنٌ', '', 'ليس طَلاقًا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الصَّحابيُّ الذي تَبَنَّاُه النبيُّ صلَّى اللهُ عَليهِ وسلَّمَ، وكان يُدْعَى ابنَ مُحَمَّدٍ:', '', 'زَيدُ بنُ حارِثةَ رضي الله عنه', '', 'أَنسُ بنُ مالِكٍ رضي الله عنه', '', 'مُصعَبُ بنُ عُمَيرٍ رضي الله عنه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَإِذْ أَخَذْنَا مِنَ النَّبِيِّينَ مِيثَاقَهُمْ}. المِيثاقُ هو:', '', 'الإيمانُ الجازِمُ الذي لا شَكَّ فِيه', '', 'عقدٌ مؤكَّدٌ بيمينٍ وعهدٍ', '', 'كلُّ وَفاءٍ بينَ اثْنَينِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَمَا جَعَلَ أَدْعِيَاءَكُمْ} مَعنَى {أَدْعِيَاءَكُمْ}:', '', 'قَومُكُم وعَشِيرَتُكُم', '', 'مَن تبنَّيتُموهُم', '', 'مَن في جِوارِكُم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المقصود بقَولِه تَعالَى: {وَلَقَدْ كَانُوا عَاهَدُوا اللَّهَ مِنْ قَبْلُ لَا يُوَلُّونَ الْأَدْبَارَ} هُم:', '', 'أَهلُ الكِتابِ', '', 'أَصحابُ غَزوةِ بَدرٍ', '', 'المُنافِقون', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {يَا أَيُّهَا النَّبِيُّ اتَّقِ اللَّهَ} يُستفادُ منه:', '', 'تَوجيهُ الأمرِ لِمَن هو مُتَّصِفٌ به', '', 'أنَّ التَّقوَى واجِبةٌ في حَقِّ الأَنبياءِ والرُّسُلِ فقط لا عامَّة الخَلقِ', '', 'استِحبابُ مُخاطَبةِ النبيِّ ﷺ بلَفظِ النُّبُوَّةِ؛ لقولِه: "أَيُّهَا النَّبِيُّ"', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تَعالَى: {وَاتَّبِعْ مَا يُوحَى إِلَيْكَ مِنْ رَبِّكَ فيه} يُستفادُ منه:', '', 'عُلُوِّ مَنزِلَةِ جِبريلَ عليه السَّلامُ', '', 'زجْرٌ عنِ اتِّباعِ مراسِمِ الجاهِليَّةِ', '', 'تَنبِيهُ المُسلِمينَ إلى مُخالَفةِ أَهلِ الكتاب', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {فَلَا تَخْضَعْنَ بِالْقَوْلِ} هل هذه الآيةُ خاصَّةٌ بزَوجاتِ النبيِّ أم عامَّةٌ؟', '', 'خاصَّة بزَوجاتِ النبيِّ ﷺ', '', 'كانت خاصَّة ثم نَسَخَتهَا وعَمَّمَتْهَا آياتٌ أُخْرَى', '', 'عَامَّة لنساء الأُمَّة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {النَّبِيُّ أَوْلَى بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ} هذه الأولويَّةُ تتضَمَّنُ:', '', 'فَضلَ الإيمانِ ونِسبَةَ المُؤمِنينَ إلى النبيِّ ﷺ تَشرِيفًا لهُم', '', 'أن يكونَ الرَّسولُ أحَبَّ إلى العبدِ مِن نفْسِه', '', 'وُجوبَ حُبِّ الأَنصارِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَن هو الذي تَبنَّاهُ أبو حُذَيفةَ بنُ عُتبةَ، وأنكَحَه بنتَ أخيه هندَ بنتَ الوليدِ:', '', 'عِكْرِمةُ مَولَى أبي حُذَيفةَ', '', 'سَالِمٌ مَولَى أبي حُذَيفَةَ', '', 'نَافِعٌ مَولَى أبي حُذَيفةَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تَعالَى: {وَيُعَذِّبَ الْمُنَافِقِينَ إِنْ شَاءَ أَوْ يَتُوبَ عليهمْ} في تَعليقِ التَّعذيبِ على المَشيئةِ:', '', 'تَحذِيرٌ مِن عَذابِ الله عزَّ وجلَّ', '', 'تَنبيهٌ لهم بسَعةِ رَحمةِ اللهِ وأنَّه لا يَقطَعُ رَجاءَهم في السَّعيِ إلى مَغفرةِ ما أتَوْهُ بأنْ يَتوبوا', '', 'رَدٌّ على الخَوارِجِ الذين يَنفونَ إِرادةَ الله عزَّ وجل عن أَفعالِ العِبادِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تَعالَى: {وَأَزْوَاجُهُ أُمَّهَاتُهُمْ} هَلْ هذا يدُلُّ بدَلالةِ الالتِزامِ على أنَّه صلَّى اللهُ عَليهِ وسلَّمَ أبٌ لهم؟', '', 'لَا يَدُلُّ', '', 'نَعَم، وهيَ أُبُوَّةٌ حَقِيقِيَّة', '', 'نَعَم، وهي أُبُوَّة دينيَّة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تَعالَى: {صَيَاصِيهِمْ}:', '', 'حُصونُهم الَّتي تحَصَّنوا بها', '', 'بُيوتُهم التي يَختَبِئُونَ فيها', '', 'جَوَادُهُم التي يَركَبُونَها ويَتفاخَرُون بِها', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أهمِّ الموضوعاتِ الَّتي تناوَلَتْها سورةُ الأحزابِ:', '', 'ذِكرُ قِصَّةِ تَزويجِ جُوَيرِيةَ بِنتِ الحارِثِ رضيَ الله عنها مِنَ النَّبيِّ ﷺ', '', 'ذِكرُ قِصَّةِ تَزويجِ عائشةَ بِنتِ أبي بَكرٍ رضيَ الله عنها مِنَ النَّبيِّ ﷺ', '', 'ذِكرُ قِصَّةِ تزويج زَينبَ بنتِ جَحشٍ رضيَ الله عنها مِنَ النَّبيِّ ﷺ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَإِذْ أَخَذْنَا مِنَ النَّبِيِّينَ مِيثَاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ وَإِبْرَاهِيمَ وَمُوسَى وَعِيسَى ابْنِ مَرْيَمَ} في ذِكْرِ ضَميرِ محمَّدٍ ﷺ قبْلَ الأنبياءِ المذكورينَ:', '', 'أنَّ شأنَ الرُّسلِ واحِدٌ وأنَّ سُنَّةَ اللهِ فيهم مُتَّحِدةٌ', '', 'تَنبِيهٌ على تَرتِيبِ شَفاعَتِهِم يومَ القِيامَة', '', 'إشارةٌ إلى أُولِي العَزمِ مِن الرُّسُلِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَيَسْتَأْذِنُ فَرِيقٌ مِنْهُمُ النَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوْرَةٌ} فيه:', '', 'كأنَّ الرِّجالَ سِترٌ وحِفظٌ للبيوتِ', '', 'تَشبِيهٌ للنِّساءِ بالبُيوتِ في حُرمَتِها وامتِناعِها', '', 'أنَّ المُنافِقِينَ فِرَقٌ وأَحزابٌ؛ لقوله: "فَرِيقٌ"', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَلَا تُطِعِ الْكَافِرِينَ وَالْمُنَافِقِينَ} دلالةٌ على:', '', 'أنَّ النَّهي عن الشيء لا يدُلُّ على وقوعِه', '', 'أنَّه لا عَهْدٌ لكافِرٍ أو مُشرِكٍ أو مُنافِقٍ', '', 'وُجُوبِ مُخالَفَةِ الهَوَى', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَلَقَدْ كَانُوا عَاهَدُوا اللَّهَ مِنْ قَبْلُ لَا يُوَلُّونَ الْأَدْبَارَ وَكَانَ عَهْدُ اللَّهِ مَسْئُولًا} دَليلٌ على أنَّ عَهْدَ اللهِ:', '', 'المقصودُ مِنه مُطلَق الإيمان؛ وهو: تَوحِيدُ الله عزَّ وجلَّ', '', 'هو العَهدُ الذي أَخَذَهُ الله على بَنِي آدمَ وهُم في صُلبِ أَبيهِم', '', 'يَدخُلُ فيه ما عَقَده المَرءُ على نفْسِه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تَعالَى: {بَادُونَ فِي الْأَعْرَابِ}:', '', 'ظاهِرُوا النِّفاقِ', '', 'مِن أَصلِ الأَعرابِ ساكِنِي الصَّحَراءِ', '', 'مُقيمون في البادِيةِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَأَزْوَاجُهُ أُمَّهَاتُهُمْ} دليلٌ على أنَّ أُمومةَ المُؤمِنين هي:', '', 'لأزواجِه', '', 'لسَرارِيه', '', 'لأُمِّ وَلَدِه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَإِذْ أَخَذْنَا مِنَ النَّبِيِّينَ مِيثَاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ وَإِبْرَاهِيمَ وَمُوسَى وَعِيسَى ابْنِ مَرْيَمَ} تَخصيصُ هؤلاء الأنبياءِ بالذِّكْرِ -مع اندراجِهم في النَّبيِّينَ اندارجًا بَيِّنًا:', '', 'لأنَّ هَؤُلاءِ الأَنبياءِ فقط هُم الذين أَنزلَ اللهُ إليهم كُتُبًا', '', 'للإيذانِ بمَزيدِ مَزيَّتِهم وفَضْلِهم', '', 'لأنَّهم جميعًا لأَبٍ واحِدٍ وهو نُوح عليه السَّلام', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {قَدْ يَعْلَمُ اللَّهُ الْمُعَوِّقِينَ مِنْكُمْ} مَعنَى المعوِّقينَ:', '', 'المُثَبِّطون الصَّارِفون عن طَريقِ الخَيرِ', '', 'أصحابُ الإعاقاتِ', '', 'السَّاعونَ لإيذاءِ المُؤمِنِينَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {تَدُورُ أَعْيُنُهُمْ كَالَّذِي يُغْشَى عليه مِنَ الْمَوْتِ} المُبالَغةُ في وَصْفِ المُنافِقينَ:', '', 'بالرُّعبِ وإخلافِ الوعدِ', '', 'بالخَوفِ والجُبنِ', '', 'بالكَذِبِ والهَلَعِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه سُبحانَه: {إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا} فيه:', '', 'إِشارَةٌ إلى أنَّ مَعِيَّة الله مع المُؤمِنينَ', '', 'تَحذيرٌ مِن المُخالَفةِ', '', 'إثباتُ صِفَةِ القُدرةِ للهِ تعالى', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {إِذْ جَاءُوكُمْ مِنْ فَوْقِكُمْ} كان ذلك في غَزوةِ:', '', 'أُحُد', '', 'الخَندَقِ', '', 'بَني النَّضِير', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سورةُ الأحزابِ', '', 'مكِّيَّة', '', 'نَزَلت أَثناءَ الهِجرةِ', '', 'مدَنيَّة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {وَمَا زَادَهُمْ إِلَّا إِيمَانًا وَتَسْلِيمًا} فيه دليلٌ على صِحَّةِ مذهبِ أهلِ السُّنَّةِ والجماعةِ الَّذين يقولون: "إنَّ الإيمانَ يَزيدُ ويَنقُصُ"، وهو حُجَّةٌ على:', '', 'المعتزلةِ', '', 'المُرجِئةِ', '', 'الشِّيعةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', '{مَا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ} أصْلُ الجَوفِ هو:', '', 'القِشرَةُ الرَّقيقَةُ التي تَعلُو نَواةَ التَّمرِ', '', 'ما عَمُقَ مِن الأَرضِ', '', 'باطِنُ الإنسانِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'كانوا في أوَّلِ الإسلامِ يَتوارَثون فيما بيْنَهم؛ بسَبَبِ:', '', 'الهِجرةِ', '', 'حَقِّ الأُخوَّةِ الإيمانيَّةِ', '', 'جَميعُ ما سَبَق', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَأُولُو الْأَرْحَامِ بَعْضُهُمْ أَوْلَى بِبَعْضٍ فِي كِتَابِ اللَّهِ مِنَ الْمُؤْمِنِينَ وَالْمُهَاجِرِينَ} تخصيصُ المُهاجرينَ بالعطفِ يدُلُّ على:', '', 'أَنَّ المرادَ بالمُؤمِنين خُصوصُ الأنصارِ', '', 'أنَّ الحُكمَ خاصٌّ بهِم دُونَ غَيرِهم', '', 'أنَّهم أَوْصَلُ الناسِ أَرحامًا وأَحسَنُهم إلى ذَوِي القُربَى', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تَعالَى: {يَا أَيُّهَا النَّبِيُّ} لم يَقُلْ سُبحانَه في ندائِه: (يا محمَّدُ) كما قال في نداءِ غيرِه: (يا مُوسَى، يا عِيسَى، يَا دَاوُدَ):', '', 'تَحذِيرًا مِن مُخالَفَتِه ﷺ؛ فإنَّ مُخالفَتَه كُفرٌ', '', 'لأنَّ النبيَّ ﷺ عَرَبِيٌّ؛ والعَرَبُ تُحِبُّ الكُنَى', '', 'تشريفًا له ﷺ بفَضلِ هذا الوَصفِ، ولِيَعلَمَ النَّاسُ أنَّه رسولُ الله؛ لِيُلَقِّبوه بذلك، ويدعوه به', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {قُلْ مَنْ ذَا الَّذِي يَعْصِمُكُمْ مِنَ اللَّهِ إِنْ أَرَادَ بِكُمْ سُوءًا أَوْ أَرَادَ بِكُمْ رَحْمَةً} ذِكرِ "إرادةِ السُّوءِ" بلَفظِه حُجَّةٌ على:', '', 'الخَوارِجِ والشِّيعة', '', 'المُعتزِلةِ والقَدَريَّة', '', 'الأشاعِرَة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَأَنْزَلَ الَّذِينَ ظَاهَرُوهُمْ مِنْ أَهْلِ الْكِتَابِ مِنْ صَيَاصِيهِمْ} المراد بِهِم يَهودُ:', '', 'بني قُرَيْظَة', '', 'بني قَيْنُقَاع', '', 'بنِي النَّضير', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'جاء في قراءةٍ شاذَّةٍ: (وأزواجُه أُمَّهاتُهم وَهُوَ أَبٌ لَهُمْ). وهي مَرويَّةٌ عن:', '', 'ابنِ عَبَّاسٍ وأُبَيِّ بنِ كَعبٍ رضي الله عنهما', '', 'ابنِ مَسعُودٍ رضي الله عنه', '', 'أُسامةَ بنِ زَيدٍ رضي الله عنه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أزواجُ مُحمَّدٍ ﷺ هُنَّ أمَّهاتُ المُؤمِنين في:', '', 'البِرِّ والإحسانِ والمِيراثِ', '', 'السَّفَرِ بهنَّ كالمَحارمِ', '', 'حُرمةِ نِكاحِهنَّ، ووُجوبِ تعظيمِهنَّ وإكرامِهنَّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ اللهِ تَعالَى: {لَقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآَخِرَ وَذَكَرَ اللَّهَ كَثِيرًا} نبَّهَ على أنَّ الَّذي يَحمِلُ على التَّأسِّي به ﷺ إنَّما هو:', '', 'الصِّدقُ في الإيمانِ والخَوفُ مِنَ النِّفَاقِ', '', 'القُدوَةُ الحَسَنَةُ', '', 'تَحقِيقُ تَوحِيدِ الرُّبوبِيَّة', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'هل للرَّجُلِ أن يُناديَ زوجتَه ويُسَمِّيَها باسمِ مَحارِمِه؛ كقولِه: يا "أُمِّي" ، "يا أُختي" ونحوِه:', '', 'يُكرَه', '', 'يَحرُمُ', '', 'يُستحَبُّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يقولُ النبيُّ ﷺ: مَنِ ادَّعى إلى غَيرِ أبِيه وَهُوَ يَعلَمُ أنَّه غَيرُ أبِيهِ:', '', 'فقد باءَ بِغَضَبٍ مِن الله', '', 'فالجنَّةُ عَليهِ حَرامٌ', '', 'فهو كاذِبٌ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {ادْعُوهُمْ لِآَبَائِهِمْ} الأمرُ هُنَا:', '', 'للإباحَةِ', '', 'للنَّدبِ', '', 'للوُجوبِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'من صِفاتِ المُنافِقين، وهو أن يُذْكَرَ للإنسانِ ما يكونُ به الخوفُ والقلقُ:', '', 'الإرْجافُ', '', 'الإرْهاصُ', '', 'الإرْصادُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تَعالَى: {هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُونَ} القاعدةُ العامَّةُ: "أنَّ اللهَ سُبحانَه وتَعالَى يَذكُرُ النِّعَمَ مُضافةً إليه، ويَذكُرُ النِّقَمَ غالبًا":', '', 'مَبنيَّةً للمَفعول', '', 'مَبنيَّةً للفاعلِ', '', 'في سِياقِ العُمومِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {قُلْ مَنْ ذَا الَّذِي يَعْصِمُكُمْ مِنَ اللَّهِ} الاستِفهامُ فيها:', '', 'تَقريريٌّ', '', 'إنكاريٌّ', '', 'تَهَكُّميٌّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تَعالَى: {لَقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآَخِرَ} استدَلَّ الأصوليُّونَ بهذه الآيةِ على:', '', 'أنَّ التَّأسِّي يكونُ في فَضائلِ الأَعمالِ دُونَ العِباداتِ والفَرائضِ', '', 'أنَّ الأصلَ أنَّه أُسوةٌ للأمَّةِ في الأحكامِ، إلَّا ما دلَّ الدَّليلُ الشَّرعيُّ على الاختِصاصِ به', '', 'أنَّ النبيَّ ﷺ مَعصُومٌ عِصمَةً أَزَلِيَّة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {وَإِذْ زَاغَتِ الْأَبْصَارُ}:', '', 'فيه دَلالَةٌ على شِدَّةِ بَأسِهِم في القِتالِ', '', 'كِنايةٌ عنِ الحَيرةِ وشِدَّةِ الرَّوْعِ', '', 'إشارةٌ إلى وَهَنِ المُنافِقِينَ وتَخاذُلِهِم عن نُصرَةِ النبيِّ ﷺ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَإِذْ قَالَتْ طَائِفَةٌ مِنْهُمْ يَا أَهْلَ يَثْرِبَ} الثَّرْبُ هو:', '', 'الخرابُ والدَّمارُ', '', 'الأرضُ القاحِلَةُ', '', 'اللَّومُ والتَّعنيفُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَلَوْ دُخِلَتْ عليهمْ مِنْ أَقْطَارِهَا} مَعنَى {أَقْطَارها}:', '', 'جِبالُها التي تُحِيطُ بِها', '', 'أبوابُها', '', 'نَواحِيها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَالَّذِينَ سَعَوْا فِي آَيَاتِنَا مُعَاجِزِينَ أُولَئِكَ لَهُمْ عَذَابٌ مِنْ رِجْزٍ أَلِيمٌ}:', '', 'تَحذِيرٌ مِن الشِّرْكِ في الأَسماءِ والصِّفاتِ', '', 'حثٌّ على السَّعيِ في آياتِ الله؛ لِتَقريرِها وتَثبيتِها', '', 'بَيانُ أنَّ الآياتِ مِن عندِ الله؛ وهي تَتضَمَّنُ كَلامَ الله، ومُعجِزاتِ أَنبِيائِه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {إِلَّا قَالَ مُتْرَفُوهَا إِنَّا بِمَا أُرْسِلْتُمْ بِهِ كَافِرُونَ} يُستَفادُ مِنه:', '', 'كَراهَة وُرُودِ الشُّبُهاتِ', '', 'بَيانُ شِدَّةِ صَبرِ الرُّسُلِ', '', 'التَّحذيرُ مِن التَّرَفِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {مَا هَذَا إِلَّا رَجُلٌ يُرِيدُ أَنْ يَصُدَّكُمْ} (يَصُدَّكُمْ) أي:', '', 'يَصرِفَكم ويَمنعَكم', '', 'يَغِشَّكُم ويَخدَعَكُم', '', 'يَجعَلَكُم تَبَعًا لهُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سُمِّيَت سُورةُ سَبأٍ بهذا الاسمِ:', '', 'لأنَّها نَزلَت على النبيِّ صلَّى الله عليه وسلَّم وهو راجِعٌ مِن فَتح سَبأٍ', '', 'بِسَببِ وُرُودِ اسمِ سَبأٍ فيها خمسَ مرَّاتٍ', '', 'لاشتمالِها على قِصَّةِ سَبأٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما مَعنَى قَولِه تعالى: {وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيدُ}:', '', 'زال الباطِلُ وهلَك؛ فلم تَبْقَ منه بقيَّةٌ يُقبِلُ بها أو يُدبِرُ', '', 'إنَّ الباطِلَ ضَعِيفٌ سَرِيعٌ انقِضاؤُه', '', 'ولا يَنتَصِرُ الباطِلُ ولا يَنصُرُ غَيرَه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما المَقصُودُ بقَولِه تعالى: {وَجِفَانٍ كَالْجَوَابِ}:', '', 'قِصاعٌ كِبارٌ مثل الحَوضِ الضَّخمِ الَّذي يُجبَى فيه الماءُ', '', 'أَرضٌ مِن الزُّجاجِ الشَّفَّافِ يَجرِي تَحتَها الماءُ', '', 'قُصورٌ عالِيَةٌ ضَخمَةٌ مِثل الجِبالِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تعالى: {وَإِنَّا أَوْ إِيَّاكُمْ لَعَلَى هُدًى أَوْ فِي ضَلَالٍ مُبِينٍ} يُستَفادُ مِنه:', '', 'كَراهَةُ الجِدالِ والمِراءِ ولو كان في سَبيلِ الدَّعوَةِ إلى الله', '', 'التَّلطُّف في الدَّعوةِ إلى الله', '', 'أنَّ نَصِيبَ كلِّ امرِئٍ مِن الهُدى أو الضَّلالِ بحَسبِ عَملِه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِ الله تعالى: {وَالَّذِينَ سَعَوْا فِي آَيَاتِنَا مُعَاجِزِينَ} (مُعَاجِزِينَ) أي:', '', 'مُشاقِّينَ مُعانِدينَ', '', 'مُجتَهِدِينَ في مَعرِفَةِ آياتِ الله إِيمانًا وتَصدِيقًا', '', 'آمِنِينَ مُطمَئِنِّينَ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَلَوْ تَرَى إِذِ الظَّالِمُونَ مَوْقُوفُونَ} (مَوْقُوفُونَ) أي:', '', 'ناكِسُو رُؤُوسِهِم في ذِلَّةٍ وانكِسارٍ', '', 'مَمنُوعُونَ مِن الكَلامِ', '', 'محبوسونَ للحسابِ يومَ القيامةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قول الله تعالى: {رَجُلٍ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ} معنى (مُزِّقْتُمْ):', '', 'بَلِيتُم وتفرَّقت أجسادُكم', '', 'تَقَطَّعت أَرحامُكم', '', 'تَشَرَّدتُم في بِقاعِ الأَرضِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {إِنَّهُمْ كَانُوا فِي شَكٍّ مُرِيبٍ}معنى (مُرِيب):', '', 'شَدِيد', '', 'مانِعٌ لهم مِن الإِدراكِ والفَهْمِ', '', 'مُوقِعٌ للتُّهمةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قول الله تعالى: {يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا} معنى: (يَلِجُ):', '', 'يَدخُلُ', '', 'يَصعَدُ', '', 'يَتَنَزَّلُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَرَوَاحُهَا شَهْرٌ} أي: تسيرُ بالرَّواحِ مَسيرةَ شهرٍ، والرَّواحُ:', '', 'مِن شُروقِ الشَّمسِ إلى غُروبِها', '', 'مِن الزَّوالِ إلى اللَّيلِ', '', 'مِن مُنتَصَفِ اللَّيلِ  إلى آخِرِ اللَّيلِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أهمِّ الموضوعاتِ الَّتي تناولَتها سُورةُ سَبَأٍ:', '', 'ذكرُ قِصَّةِ نبيِّ الله هودٍ عليه الصَّلاةُ والسَّلامُ، ودعوتِه لقومِه', '', 'دعوةُ الكافِرينَ إلى التَّفكيرِ والتَّدبُّرِ في شأنِ دَعوةِ الرَّسولِ صلَّى الله عليه وسلَّم', '', 'بيانُ ما أعدَّه اللهُ تعالى للمُؤمِنينَ في الجنَّةِ، وما أعدَّه للكافِرينَ، وشَهادةِ جَوارحِهم عليهم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سُورةُ سبأٍ إحدى السُّوَرِ المُفتَتَحةِ بـ (الْحَمْدُ لله)، والسُّورُ المُفتَتحَةُ بالحمدلله في القُرآنِ:', '', 'ثَلاثُ سُورٍ', '', 'أَربعُ سُورٍ', '', 'خَمسُ سُورٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُ الله تعالى: {لَقَدْ كَانَ لِسَبَإٍ فِي مَسْكَنِهِمْ آَيَةٌ} المقصود بـ (آَيَة):', '', 'مُعجِزَةٌ ظاهِرَةٌ', '', 'جَنَّةٌ ونَعِيمٌ وخَيرٌ كَثيرٌ', '', 'عَلامةٌ وحجَّةٌ ودلالةٌ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تعالى: {إِنِّي بِمَا تَعْمَلُونَ بَصِيرٌ} فيه:', '', 'تَحذيرٌ مِن المُخالَفةِ', '', 'تَأكِيدٌ على أنَّ الأَعمالَ الصَّالِحَة لا تكونُ إلَّا عن بَصِيرَةٍ', '', 'إِثباتٌ لاسمِ الله: العَلِيم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {حَتَّى إِذَا فُزِّعَ عَنْ قُلُوبِهِمْ} (فُزِّعَ) أي:', '', 'أَصابَهُم الفَزعُ', '', 'أُزِيلَ الفَزَعُ', '', 'قُبِضَت أَرواحُهُم', '', '', 'b', 'easy');
