(@tafseer_category_id, '', 'في قَولِه تعالى {وَمَا لَهُ مِنْهُمْ مِنْ ظَهِيرٍ} (ظَهِير): أي:', '', 'مُؤمِن', '', 'مُعِين', '', 'شَفِيع', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {يَعْمَلُونَ لَهُ مَا يَشَاءُ مِنْ مَحَارِيبَ} المَحَارِيب: جمعُ مِحرابٍ، والمحرابُ هو:', '', 'الفِراشُ الذي يُصنَعُ مِن الحَريرِ الخالِصِ', '', 'الغرفةُ، والمسجدُ، وكُلُّ موضعٍ مُرتفِعٍ', '', 'المآذِنُ التي تكونُ فوقَ المَساجِد', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَقَالُوا مَا هَذَا إِلَّا إِفْكٌ مُفْتَرًى} مَعنَى (إِفْكٌ مُفْتَرًى) أي:', '', 'كَذِبٌ مُختَلَقٌ', '', 'سِحْرٌ عَظِيمٌ', '', 'أَساطِير الأَوَّلِينَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِ الله تعالى: {وَهُوَ الْحَكِيمُ الْخَبِيرُ}:', '', 'بَيانُ أنَّ ما ذُكِرَ مِن أَفعالٍ إنَّما هو ناتِجٌ عن قُدرَةِ الله تعالى المُطلَقَة', '', 'إِثباتُ صِفَةِ الرَّحمَةِ لله عزَّ وجلَّ', '', 'إثباتُ حُكمِ اللهِ سُبحانَه وتعالى الكَوْنيِّ والشَّرعيِّ', '', '', 'c', 'medium');
