(@tafseer_category_id, '', 'قولُه تعالى: {نَخْسِفْ بِهِمُ الْأَرْضَ} أي:', '', 'يَتِيهُونَ في الأَرضِ', '', 'نَرجُمُهُم بحِجارَةِ الأَرضِ', '', 'نُغَيِّبْهم في الأَرضِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {عَالِمِ الْغَيْبِ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ} مَعنَى (يَعْزُبُ):', '', 'يَستَقِلُّ', '', 'يَستَغنِي', '', 'يَغِيبُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سُورَةُ سَبأٍ', '', 'مَكِّيَّة', '', 'مُختَلفٌ فيها', '', 'مَدنِيَّة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المقصود بقول الله تعالى: {يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا}', '', 'أي: يَعلَمُ اللهُ ما يَنزِلُ مِن مَلائِكةِ السَّماءِ إلى الأَرضِ، ومَن يَمكُثُ مِنهم في الأَرضِ، ومَن يَصعَدُ مِنهم مرَّةً أُخرَى إلى السَّماءِ', '', 'أي: يَعلَمُ اللهُ كُلَّ ما يَتَنَزَّلُ مِن الوَحْيِ مِن السَّماءِ إلى الأَرضِ، وعلى مَن يَنزِلُ، وفي أَيِّ بُقعَةٍ يَنزِلُ', '', 'أي: يَعلَمُ اللهُ كُلَّ ما يَدخُلُ في الأرضِ ويَغيبُ فيها؛ كالمطَرِ، والبُذورِ، والأمواتِ،  وكُلَّ ما يَخرُجُ مِنها كأنواعِ النَّباتاتِ، والمعادِنِ، والكنوزِ، وغيرِ ذلك', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تعالى: {إِنَّ فِي ذَلِكَ لَآَيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ} أشار بصيغةِ المُبالَغةِ إلى:', '', 'أنَّ قَليلَ الصَّبرِ وقَليلَ الشُّكرِ لا أَجرَ فِيهِما', '', 'أنَّ الصَّبرَ والشُّكرَ شَرطانِ لصِحَّةِ الإيمانِ', '', 'أنَّ مَن ليس في طَبعِه الصَّبرُ فاتَه الشُّكرُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَقَلِيلٌ مِنْ عِبَادِيَ الشَّكُورُ} المرادُ بهذه الجملةِ هو:', '', 'الحثُّ على الشُّكرِ', '', 'التَّحذيرُ مِن الانغِماسِ في النِّعَمِ', '', 'إِثباتُ اسمِ الله: الشَّكُور', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {لَقَدْ كَانَ لِسَبَإٍ فِي مَسْكَنِهِمْ آَيَةٌ...} فيه:', '', 'بَيانُ مَنزِلَةِ أَهلِ سَبأٍ وإِتقانِهِم المَساكِن والعِمارَة', '', 'تَرغِيبٌ لأَهلِ الأَموالِ بإِخراجِ زَكاةِ أَموالِهم', '', 'تحذيرٌ لأهلِ المعاصي مِن أنْ تَزولَ نعمةُ الله تعالى عليهم بسَبَبِ معاصيهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المقصود يقوله تعالى: {بَلْ مَكْرُ اللَّيْلِ وَالنَّهَارِ} أي: مَكْرُكم في اللَّيلِ والنَّهارِ، والمكرُ هو:', '', 'الاحتِيالُ والخِداعُ', '', 'أَفحَشُ الكَذِبِ', '', 'القَتلُ على حِينِ غِرَّة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'استُدِلَّ بقَولِ الله تعالى: {قُلْ جَاءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيدُ} على استحبابِ هذا القولِ عندَ:', '', 'إِقامَة الحُدُودِ', '', 'إزالةِ المنكرِ', '', 'وَسوَسةِ الشَّيطانِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَلَوْ تَرَى إِذْ فَزِعُوا فَلَا فَوْتَ} (فَلَا فَوْتَ) أي:', '', 'فلا كُفرَ ولا تَكذِيبَ', '', 'فلا نجاةَ ولا هربَ', '', 'فلا احتِيالَ أو مُخادَعَة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تعالى:  {مِثْقَالُ ذَرَّةٍ} أي:', '', 'زِنةُ نملةٍ صغيرةٍ', '', 'مِكيالٌ تُوزَنُ بِه الحُبوبُ', '', 'حَبَّاتُ الرَّمْلِ الصِّغار', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تعالى: {قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ} فيه:', '', 'أنَّ قِلَّة الرِّزقِ أو كَثرةُ المالِ مُتعلِّقٌ بمَشيئةِ الله عزَّ وجلَّ، مِن غيرِ اختِصاصٍ بالفاسِقِ والصَّالحِ', '', 'أنَّ قِلَّة الرِّزقِ لا تكونُ إلَّا عُقوبَةً مُعجَّلةً في الدُّنيا', '', 'أنَّ سِعةَ الرِّزقِ مِن رِضا الله عزَّ وجلَّ عن العَبدِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى (يُخْلِفُهُ) في قَولِ الله تعالى: {وَمَا أَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُ}:', '', 'يُعطِيكُم خلَفَه وبدَلَه', '', 'يَدَّخِرُه لكُم في الآخِرَة', '', 'يَقبَلُه مِنكُم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بقَولِه تعالى: {يَرْجِعُ بَعْضُهُمْ إِلَى بَعْضٍ الْقَوْلَ}: أي:', '', 'يَتلاوَمون ويُحاوِرُ بعضُهم بعضًا', '', 'يَقتُل بَعضُهم بَعضًا', '', 'يَتَحاكَمُون فيما بينهُم', '', '', 'a', 'medium');
