(@tafseer_category_id, '', 'مَعنى قولِه تعالى: {وَقَدِّرْ فِي السَّرْدِ} :', '', 'زِدْ في عَددِ الدُّرُوعِ', '', 'تَمَهَّلْ في صَهْرِ الحَديدِ حتَّى يَسِيلَ ثم اصنَعهُ دُروعًا', '', 'اجعَلْ نَسْجَ الدُّروعِ على مِقدارٍ مَحدودٍ ونَسَقٍ مُتناسِبٍ مُحكَمٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنى قَولِه تعالى: {سَيْلَ الْعَرِمِ}:', '', 'مَلائِكةُ العَذابِ', '', 'السَّيلُ الغالِبُ الشَّدِيدُ', '', 'القَحْطُ الشَّدِيدُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أهَمِّ مَقاصِدِ سُورةِ سَبَأٍ:', '', 'بَيانُ طَريقِ العُبوديَّةِ، وبَيانُ أَحوالِ النَّاسِ مع هذا الطَّريقِ', '', 'ذِكْرُ خَلْقِ بني آدمَ، وانقِسامِهم، ومآلِهم يومَ القيامةِ', '', 'إبطالُ قَواعدِ الشِّركِ، والرُّدُّ على شُبَهِ المشركينَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا بِمَا أُرْسِلْتُمْ بِهِ كَافِرُونَ} (مُتْرَفُوهَا) أي:', '', 'المُنَعَّمونَ فيها', '', 'أَهلُها الذين يَعِيشُونَ فيها', '', 'سَادَتُها وكُبَراؤُها', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى قوله تعالى: {وَمَزَّقْنَاهُمْ كُلَّ مُمَزَّقٍ} أي:', '', 'قَطَّعْناهم في الأرضِ، فتفَرَّقوا في نواحٍ كثيرةٍ', '', 'سَلَّطْنَا عَليهِم أَعداءَهُم فاستَعبَدُوهُم', '', 'استَأْصَلْنَاهُم عن بَكرَةِ أَبِيهِم', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بقَولِه تعالى: {قُلْ إِنَّ رَبِّي يَقْذِفُ بِالْحَقِّ} أي:', '', 'قُلْ لهم -يا مُحمَّدُ-: إنَّ الله هو مَن يَنصُرُ الحَقَّ على الباطِلِ', '', 'قُلْ لهم -يا مُحمَّدُ-: إنَّ رَبِّي يُلقي الوَحيَ على رَسولِه', '', 'قُلْ لهم -يا مُحمَّدُ-: إنَّ الله قادِرٌ على أن يَقذِفَ عليكُم شُهُبًا مِن السَّماءِ بالحَقِّ فيُهلِكَكُم', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {أُولَئِكَ لَهُمْ عَذَابٌ مِنْ رِجْزٍ أَلِيمٌ} الرِّجزُ هو :', '', 'النُّحاسُ المُنصَهِرُ حتَّى يَسِيلَ', '', 'النَّجاسَةُ التي يُستَقذَرُ مِنها', '', 'سُوءُ العَذابِ وأَليمُه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تعالى عقِبَ ما ذكَرَهُ مِن تَسخِيرِ الجِنِّ لنبيِّ الله سُليمانَ: {اعْمَلُوا آَلَ دَاوُودَ شُكْرًا} وفي ذلك إشارةٌ إلى:', '', 'جَوازِ استِعمالِ السِّحْرِ في طاعَةِ الله', '', 'أنَّ الإنسانَ لا يَستغرِقُ في الدُّنيا، وأنَّه يجِبُ أنْ يَعمَلَ صالحًا', '', 'أنَّ الشُّكرَ يكونُ بالقَلبِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {أَفْتَرَى عَلَى اللَّهِ كَذِبًا أَمْ بِهِ جِنَّةٌ} (جِنَّةٌ) أي:', '', 'عُجْبٌ', '', 'جُنُونٌ', '', 'سِحْرٌ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَمَا بَلَغُوا مِعْشَارَ مَا آَتَيْنَاهُمْ فَكَذَّبُوا رُسُلِي فَكَيْفَ كَانَ نَكِيرِ} فيه:', '', 'بِشارَةٌ للمُؤمِنينَ بالتَّمكِينِ في الأَرضِ', '', 'تَرغِيبٌ في لُزُومِ الجَماعَةِ وذَمٌّ للتَّفرُّق', '', 'تَهديدٌ لِلَّذين كذَّبوا النبيَّ صلَّى الله عليه وسلَّم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {الْحَمْدُ لِلَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ} فيه:', '', 'بَيانُ أنَّ المَقصُودَ بالحَمدِ هو الإِقرارُ بتَوحِيدِ الرُّبوبِيَّة', '', 'إِثباتٌ لاسمِ الله: الحَمِيد', '', 'تَعريضٌ بكُفرانِ المشركينَ الَّذين حَمِدوا أشياءَ ليس لها أدْنى تَأثيرٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {إِنَّ فِي ذَلِكَ لَآَيَةً لِكُلِّ عَبْدٍ مُنِيبٍ} فيه:', '', 'حَثٌّ بليغٌ على التَّوبةِ والإنابةِ', '', 'تَقريرُ تَوحيدِ الألُوهِيَّة وتَوحيدِ الرُّبوبِيَّة', '', 'أنَّ العُبودِيَّةَ كلَّها تَتلخَّصُ في إِخلاصِ الدُّعاءِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {أَنِ اعْمَلْ سَابِغَاتٍ} (سَابِغَاتٍ): أي:', '', 'حِرابًا طِوالًا', '', 'دُروعًا واسِعةً طويلةً', '', 'سُيُوفًا حادَّةً قاطِعَةً', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَنَجْعَلَ لَهُ أَنْدَادًا} (أَنْدَادًا): أي:', '', 'أمثالًا ونُظَراءَ وشُرَكاءَ', '', 'أَقوالًا وأَفعالًا وأَحوالًا', '', 'أسماءً وصِفاتٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قول الله تعالى: {وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُ فَاتَّبَعُوهُ إِلَّا فَرِيقًا مِنَ الْمُؤْمِنِينَ} يُستَفادُ مِنه:', '', 'أنَّ الإيمانَ حاجزٌ عن اتِّباعِ الشَّيطانِ', '', 'أنَّ صِدقَ النِّيَّةِ لا يُصلِح العَملَ الفاسِدَ', '', 'أنَّ سُوءَ الظَّنِّ مِن أَعمالِ إِبليسَ عليه لَعنَةُ الله', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قول الله تعالى: {وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا} (يَعْرُجُ) أي:', '', 'يَصعَدُ ويَرقَى', '', 'يتَكوَّنُ فيها', '', 'يَتَنَزَّلُ في طَبَقاتِها', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَقَالُوا آَمَنَّا بِهِ وَأَنَّى لَهُمُ التَّنَاوُشُ} (التَّنَاوُشُ) أي:', '', 'التَّناوُل، وإِدراكُ ما يُريدونَ', '', 'الصِّدقُ في القَولِ', '', 'الرُّجوعُ إلى الدُّنيا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {مَا دَلَّهُمْ عَلَى مَوْتِهِ إِلَّا دَابَّةُ الْأَرْضِ}  (دَابَّةُ الْأَرْضِ) هي:', '', 'الدُّودُ الذي يَتكوَّنُ في جَسَدِ المَيِّتِ', '', 'الجَرادُ الذي يكونُ في القَمحِ', '', 'السُّوسٌ الذي يَنخِرُ الخَشَبَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما مَعنَى قَولِه تعالى: {وَأَسَلْنَا لَهُ عَيْنَ الْقِطْرِ}:', '', 'جَعَلنا لسُليمانَ عُيونَ الماءِ جارِيَةً مُسالَةً', '', 'فَتَحنا على سُليمانَ أَبوابَ الرِّزقِ والخَيرِ الكَثيرِ', '', 'أجرَينا لسُلَيمانَ عَينَ النُّحاسِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِ الله تعالى: {وَالَّذِينَ سَعَوْا فِي آَيَاتِنَا مُعَاجِزِينَ} أي:', '', 'والَّذين يُؤمِنُونَ بمُعجِزاتِ أَنبِياءِ الله', '', 'والَّذين يَسعَونَ في أَرضِ الله غيرَ عاجِزِينَ', '', 'والَّذين اجتَهَدوا في إبطالِ آياتِ الله تعالى، وصَدِّ النَّاسِ عنها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُنِيرِ} (الزُّبُر): أي:', '', 'المَلائِكَة', '', 'الكُتُب', '', 'المُعجِزات', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {وَحُمْرٌ مُخْتَلِفٌ أَلْوَانُهَا وَغَرَابِيبُ سُودٌ} (غَرَابِيبُ): جمْعُ غِربيبٍ، وهو:', '', 'الشَّديدُ السَّوادِ', '', 'الحِجارَةُ الصَّغيرَةُ', '', 'الغُرابُ الأَبقَع الذي يُخالِطُ سَوادَه بَياضٌ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ} أنَّ اللهَ عزَّ وجلَّ لا يَرفَعُ مِن الأعمالِ إلَّا ما كان صالِحًا، والعَمَلُ الصَّالحُ هو ما اشتمَلَ على:', '', 'مَراتِبِ الدِّينِ: الإِسلامِ، والإِيمانِ، والإِحسانِ', '', 'النِّيَّةِ، والمُداوَمَةِ', '', 'الإخلاصِ لله، والمتابَعةِ لِشَرعِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {هُوَ الَّذِي جَعَلَكُمْ خَلَائِفَ فِي الْأَرْضِ} فيه:', '', 'تَعلِيلٌ لِوُجُودِ الخَلْقِ باستِخلافِهِم في الأَرضِ', '', 'إِظهارٌ لقُدرَةِ الله تعالى الباهِرَة', '', 'بِشارةٌ للمؤمنينَ، وإنذارٌ للكافرين', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ} (جَهْدَ أَيْمَانِهِمْ) أي:', '', 'مُتَعمِّدِينَ كَذِبًا', '', 'غيرَ مُبالِينَ بالقَسَمِ', '', 'أقوى الأَيْمانِ وأغلَظَها', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {جَنَّاتُ عَدْنٍ}  أي: جَنَّاتُ الخُلدِ، وأصلُ (عدن): يدُلُّ على:', '', 'الخُلُودِ', '', 'الإِقامَةِ', '', 'النَّعِيمِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {جَاعِلِ الْمَلَائِكَةِ رُسُلًا أُولِي أَجْنِحَةٍ مَثْنَى وَثُلَاثَ وَرُبَاعَ} فيه:', '', 'أنَّ مِن المَلائكَةِ رُسُلًا وأَنبِياءَ مُكلَّفِين بالرِّسالَة', '', 'أنَّ المَلائكةَ يُباحُ لها التَّزَوُّجُ باثنَين وثَلاث وأَربَع مِثل بَنِي آدَمَ', '', 'إثباتُ أنَّ الملائكةَ أجسامٌ، وليسوا أرواحًا مُجرَّدةً مِن الجِسميَّةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما المَقصودُ بقَولِه تعالى: {وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَى}:', '', 'لا تُتبِع السَّيِّئةَ بسَيِّئةٍ أُخرَى واتبَعها بحَسنَةٍ تَمحُها', '', 'لا تَستَجلِبُ الطَّاعةُ إلَّا طاعَةً مِثلَها، ولا تَستَجلِبُ المَعصِيَةُ إلَّا مَعصِيَةً مِثلَها', '', 'لا تحمِلُ آثِمةٌ إِثْمَ أُخرى غيرِها', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بـ(فَاطِر): في قَولِ الله تعالى: {الْحَمْدُ لِلَّهِ فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ} أي:', '', 'خالِق ومُبتَدئ', '', 'قاهِر وغالِب', '', 'رازِق', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَلَا يَغُرَّنَّكُمْ بِاللَّهِ الْغَرُورُ} (الْغَرُورُ): أي: الشَّديدُ التَّغريرِ والخِداعِ، والمرادُ به:', '', 'الإِعجابُ بالرَّأيِ', '', 'الشَّيطانُ', '', 'النَّفسُ الأمَّارةُ بالسُّوءِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سُمِّيَت سُورةُ (فاطر) بهذا الاسمِ:', '', 'لذِكرِ اسمِ (فاطِر) في طليعتِها', '', 'لأنَّ هذا الاسمَ لم يُذكَر في غَيرِها', '', 'لذِكرِها قِصَّة قَومِ فاطرٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بـ(الْحَرُور) في قولِه تعالى: {وَلَا الظِّلُّ وَلَا الْحَرُورُ}:', '', 'شِدَّةُ الحَرِّ', '', 'جَوْفُ الشَّمسُ', '', 'الرِّياحُ البارِدَة', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {وَتَرَى الْفُلْكَ فِيهِ مَوَاخِرَ} (مَوَاخِرَ) أي:', '', 'جواريَ تَشُقُّ الماءَ وتَخرِقُه مع صَوتٍ', '', 'صَغيرَةَ الحَجمِ تَكادُ لا تُرَى', '', 'طافِيَةً على سَطحِ الماءِ', '', '', 'a', 'easy');
