(@tafseer_category_id, '', 'قولُه تعالى: {مَنْ كَانَ يُرِيدُ الْعِزَّةَ فَلِلَّهِ الْعِزَّةُ جَمِيعًا إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ} يُستَفادُ مِنه:', '', 'أنَّ كلَّ عَملٍ صالِحٍ مَقبُولٌ ولو كان قَليلًا', '', 'أنَّ المَعصِيةَ تُورِثُ الذُّلَّ', '', 'أنَّه لا عِزَّةَ في الدُّنيا مُطلقًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تعالى: {فَلَا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا}:', '', 'لا تَخدَعَنَّكم حياتُكم الدُّنيا والتَّمَتُّعُ بمَلَذَّاتِها والانشِغالُ بها', '', 'لا تَستَعجِلُوا أَرزاقَكُم في الدُّنيا فيَحمِلكُم ذلك على أن تَطلُبوها بمَعصِيَة الله', '', 'لا تَغتَرُّوا بِكَثرةِ أَموالِكُم في الدُّنيا فالأيَّامُ دُوَلٌ', '', '', 'a', 'easy');
