(@tafseer_category_id, '', 'في قَولِه تعالى: {سَائِغٌ شَرَابُهُ} مَعنَى (سَائِغٌ):', '', 'عَذْبٌ', '', 'حُلْوٌ', '', 'سَهْلٌ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ الله تعالى: {إِنَّمَا يَخْشَى اللَّهَ مِنْ عِبَادِهِ الْعُلَمَاءُ} يُستَفادُ منه:', '', 'أنَّ المَقصُودَ بالعُلماء هُم الأَنبياءُ والرُّسُلُ عليهم السَّلامُ', '', 'أنَّ تَمامَ الخَشيَة تكونُ مع تَمامِ العِبادَة', '', 'أنَّ العِلمَ التامَّ يَستلزِمُ الخَشيةَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَهُمْ يَصْطَرِخُونَ فِيهَا} (يَصْطَرِخُونَ) أي:', '', 'يَصرُخونَ بشِدَّةٍ', '', 'يُطرَحُونَ على وُجُوهِهِم', '', 'يُقَيَّدُونَ بالسَّلاسِلِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أهَمِّ مَقاصِدِ سُورةِ فاطِر:', '', 'التَّواضُعُ لله، المُستَلزِمُ لرَدِّ الأمرِ كُلِّه إليه', '', 'تحقيقُ العُبوديَّةِ، وتعظيمُ شَأنِ الرُّبوبيَّة', '', 'إثباتُ قُدرةِ اللهِ الكاملةِ، ومنها قدرتُه على البَعثِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {كَذَلِكَ النُّشُورُ} (النُّشُور) هو:', '', 'الحياةُ والمَرجِعُ', '', 'الخَلْقُ مِن العَدمِ', '', 'الإِنباتُ والزَّرعُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ} المَقصُودُ (بِذَاتِ الصُّدُورِ):', '', 'بِما يُخفِيهِ المُنافِقُونَ في صُدُورِهِم مِن كَيْدٍ للمُسلِمينَ', '', 'بالشِّركِ الخَفِيِّ، وهو الرِّياءُ', '', 'بالضَّمائِرِ والنِّيَّاتِ والأسرارِ', '', '', 'c', 'medium');
