(@tafseer_category_id, '', 'قال الله تعالى: {مَا يَفْتَحِ اللَّهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلَا مُمْسِكَ لَهَا وَمَا يُمْسِكْ فَلَا مُرْسِلَ لَهُ مِنْ بَعْدِهِ} ذكَرَ اللهُ في هذه الآيةِ انفِرادَه بالتَّدبيرِ والعَطاءِ والمَنعِ، وهذا يُوجِبُ:', '', 'التَّعَلُّقَ باللهِ تعالى والافتِقارَ إليه', '', 'تَقريرَ أنَّ رَحمَةَ الله تعالى تَسَعُ المُؤمِنَ والكافِرَ', '', 'انفِرادَ الله تعالى بالأُلوهِيَّة والرُّبوبِيَّة والأَسماءِ والصِّفاتِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {إِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا} الأمرُ باتِّخاذِه عدوًّا فيه تَنبيهٌ على:', '', 'أنَّ العَدواةَ الخَفِيَّة أَعظَمُ شَرًّا مِن العَداوَةِ الظَّاهِرَة', '', 'مُجاهَدَة النَّفسِ ومُخالَفَة الهَوَى', '', 'استِفراغِ الوُسْعِ في مُجاهَدتِه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {لَا إِلَهَ إِلَّا هُوَ فَأَنَّى تُؤْفَكُونَ} (تُؤْفَكُونَ): أي:', '', 'تَكفُرُونَ', '', 'تُصرَفونَ عن الحَقِّ', '', 'تُكَذِّبُونَ الرُّسُلَ', '', '', 'b', 'medium');
