(@tafseer_category_id, '', 'ما معنى قوله تعالى: {فَكَيْفَ كَانَ نَكِيرِ}:', '', 'فكيفَ كان إنكاري وعِقابي على تِلك الأُمَمِ المكَذِّبةِ', '', 'كيف سَوَّلَت لهُم أَنفُسُهم ألَّا يَأمُرُوا بالمَعرُوفِ ويَنهَوا عن المُنكَر', '', 'فكَيفَ كَذَّبُوا رُسُلِي وأَنكَرُوا رِسالَتَهُم رغمَ ما مَعهُم مِن الآياتِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {وَمِنَ الْجِبَالِ جُدَدٌ بِيضٌ} (جُدَدٌ) أي:', '', 'جِبالٌ مِن الثَّلجِ الأَبيَض', '', 'ضِخامٌ عَمالِيق', '', 'خُطوطٌ وطَرائِقُ تكونُ في الجِبالِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', '(يَحِيقُ) في قَولِ الله تعالى: {وَلَا يَحِيقُ الْمَكْرُ السَّيِّئُ إِلَّا بِأَهْلِهِ} مَعناهُ:', '', 'يَخدَعُ', '', 'يُمهِلُ ويُؤخِّرُ', '', 'يُحيطُ ويَنزِلُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {إِنَّمَا يَخْشَى اللَّهَ مِنْ عِبَادِهِ الْعُلَمَاءُ إِنَّ اللَّهَ عَزِيزٌ غَفُورٌ} قال: إنَّ الله عَزيزٌ غَفُورٌ بعدَ ذِكرِ الخَشيةِ: إشارةً إلى:', '', 'أنَّ اللهَ عزَّ وجَلَّ أهلٌ لأنْ يُخشَى؛ لأنَّه عزيزٌ', '', 'أنَّ العِلمَ يكونُ سَببًا في المَغفِرَة إذا عُمِلَ بِه', '', 'أنَّ خَشيَةَ الله عزَّ وجلَّ عَزِيزَةٌ وقَليلٌ فاعِلُها', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تعالَى: {وَمِنْ كُلٍّ تَأْكُلُونَ لَحْمًا طَرِيًّا} وصَفَه بالطراوةِ؛ للإشعارِ بلَطافتِه والتَّنبيهِ على:', '', 'أنَّه سَهلُ الأَكلِ للصَّغيرِ والكَبيرِ؛ فيكونُ غِذاءً للجَميعِ', '', 'أنَّ أَفضَلَ ما يُعالَجُ به هو الشِّواءُ', '', 'المسارعةِ إلى أكْلِه؛ لئلَّا يَتسارَعَ إليه الفَسادُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أَسماءِ سُورَةِ فاطِر:', '', 'سُورَةُ المَحَاجَّة', '', 'سُورَةُ السَّموات', '', 'سُورَةُ المَلائِكَة', '', '', 'c', 'medium');
