INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِ الله تعالى: {وَمِنْهُمْ سَابِقٌ بِالْخَيْرَاتِ بِإِذْنِ الله} قَولُه: (بِإِذْنِ الله) راجِعٌ إلى السَّابقِ إلى الخَيراتِ؛ وذلك:', '', 'لإِثباتِ أَفعالِ الله الاختِيارِيَّة', '', 'لِئَلَّا يَغترَّ بعَمَلِه', '', 'لعَدمِ إِغفالِ الإيمانِ بالقَدَرِ', '', '', 'b', 'hard');
