(@tafseer_category_id, '', 'المقصود بـ(سُنَّة الْأَوَّلِينَ) في قَولِ الله تعالى: {فَهَلْ يَنْظُرُونَ إِلَّا سُنَّةَ الْأَوَّلِينَ} أي:', '', 'السُّنَن التي كانت في شَريعَةِ الأَنبِياءِ مِن قَبلُ', '', 'العَذاب الَّذي نزَلَ بالكُفَّارِ', '', 'المَلائِكَة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {إِنَّمَا يَخْشَى اللَّهَ مِنْ عِبَادِهِ الْعُلَمَاءُ} إشارةٌ إلى عَمَلِ القَلبِ، وفي قَوله تعالى: {وَأَقَامُوا الصَّلَاةَ وَأَنْفَقُوا مِمَّا رَزَقْنَاهُمْ} إشارةٌ إلى :', '', 'قَولِ اللِّسانِ', '', 'إِيمانِ القَلبِ', '', 'عَمَلِ الجوارحِ', '', '', 'c', 'hard');
