(@tafseer_category_id, '', 'قَولُه تعالى: {فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ} قولُه: (يُنَبِّئُكُمْ) قِيلَ: إنَّ فيه إشارةً إلى:', '', 'اللُّطفِ والإحسانِ، حيثُ لم يقُلْ: يُؤاخِذُكم', '', 'شِدَّةِ الحِسابِ على الأَعمالِ يَومَ القِيامَة', '', 'قَولِ النبيِّ صلَّى الله عليه وسلَّم: (مَن نُوقِشَ الحِسابَ عُذِّبَ)', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {إِنَّ الَّذِينَ كَفَرُوا يُنَادَوْنَ لَمَقْتُ اللَّهِ أَكْبَرُ مِنْ مَقْتِكُمْ أَنْفُسَكُمْ}  المَقتُ هو:', '', 'القَهْرُ والغَلَبَةُ', '', 'العَذابُ الأَليمُ', '', 'البُغضُ الشَّديدُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {غَافِرِ الذَّنْبِ وَقَابِلِ التَّوْبِ شَدِيدِ الْعِقَابِ} يُقرَنُ هذان الوصْفانِ كثيرًا في مواضعَ متعدِّدةٍ مِن القرآنِ:', '', 'ليبقَى العبدُ بيْنَ الرَّجاءِ والخَوفِ', '', 'لنَصْبِ مِيزانِ العَدلِ بين مَغفِرَةِ الذُّنوبِ وشِدَّةِ العِقابِ', '', 'لأنَّ فيه رَدًّا على المُرجِئَةِ الذين يقُولونَ: إنَّ صِفاتِ الله كلُّها مَجازِيَّهٌ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بالرُّوحِ في قَولِه تعالى: {يُلْقِي الرُّوحَ مِنْ أَمْرِهِ عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ}:', '', 'الوَحْيُ', '', 'جِبريلُ عليهِ السَّلامُ', '', 'المَطَرُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سُمِّيَت سُورةُ غافِر بهذا الاسمِ:', '', 'لذِكْرِه في أوَّلِها', '', 'لذِكْرِها قِصَّة العَبدِ الصَّالحِ "غافِر"', '', 'لأنَّه تَكرَّرَ فيها إِحدَى عَشرةَ مرَّةً', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {ذِي الطَّوْلِ لَا إِلَهَ إِلَّا هُوَ إِلَيْهِ الْمَصِيرُ} المَقصُودُ بـ"الطَّوْل":', '', 'البَطْشُ والشِّدَّةُ', '', 'الرَّحمَةُ والرَّأفَةُ', '', 'الغِنى والفَضلُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بالعَشِيِّ في قَولِه تعالى: {وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْإِبْكَارِ}:', '', 'بعدَ صَلاةِ العِشاءِ', '', 'أَوَّلُ اللَّيلِ', '', 'آخِرُ النَّهارِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَقَالَ فِرْعَوْنُ يَا هَامَانُ ابْنِ لِي صَرْحًا} "صَرْحًا" أي:', '', 'سُورًا عَظِيمًا ضَخمًا', '', 'بِناءً عظيمًا مُرتَفِعًا', '', 'جَيشًا عَظِيمًا شَدِيدًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَأَنْذِرْهُمْ يَوْمَ الْآَزِفَةِ} "الْآَزِفَة" أي: القيامةِ، وأصلُ (أزف): يدُلُّ على:', '', 'الدُّنُوِّ والمُقارَبةِ', '', 'الخَوفِ والفَزَعِ', '', 'العَدلِ والقِسْطِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُ مُؤمنِ آلِ فِرعَونَ لقَومِه: {يَاقَوْمِ إِنِّي أَخَافُ عَلَيْكُمْ مِثْلَ يَوْمِ الْأَحْزَابِ * مِثْلَ دَأْبِ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ} يُستَفادُ منه:', '', 'أنَّ دِينَ جَميعِ الأَنبِياءِ هو الإِسلامُ', '', 'أنَّ أخبارَ الأنبياءِ مِن عَجائبَ وآياتٍ كانتْ مُنتَشِرةً مُتواتِرةً في العالَمِ', '', 'أنَّ كَلِمةَ الأَحزابِ تُطلَقُ على الكُفَّارِ خاصَّةً', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سورةُ غافِرٍ هي أوَّلُ سُوَرِ:', '', 'الحَوامِيمِ', '', 'المُفَصَّلِ', '', 'أَخواتِ هُودٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَأُفَوِّضُ أَمْرِي إِلَى اللَّهِ إِنَّ اللَّهَ بَصِيرٌ بِالْعِبَادِ * فَوَقَاهُ اللَّهُ سَيِّئَاتِ مَا مَكَرُوا} يُستَفادُ مِنه:', '', 'مَشرُوعِيَّةُ المَكرِ للأَعداءِ شَريطَةَ ألَّا يَحِلَّ حَرامًا أو يُحرِّمَ حَلالًا', '', 'أنَّ تَفويضَ الأَمرِ إلى الله يكونُ بفِعلِ الطَّاعاتِ وتَركِ المَنهِيَّاتِ', '', 'أنَّ صدق التَّوَكُّلَ على الله سَبَبٌ للوقايةِ مِن كُلِّ سُوءٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِي الْبِلَادِ} "يَغْرُرْكَ" أي: يَخدَعْك، والغِرَّةُ هي:', '', 'التَّمادِي في اللَّهْوِ', '', 'الأَخْذُ بِسُرعَةٍ', '', 'غفلةٌ في اليقظةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {مِثْلَ دَأْبِ قَوْمِ نُوحٍ } "دَأْبِ" أي:', '', 'فِعْلِ وعَمَلِ', '', 'عادةِ وشَأنِ', '', 'كُفْرِ وعِنادِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما المَقصُودُ بالضَّلالِ في قَولِه تعالى: {وَمَا دُعَاءُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ}:', '', 'الضَّياعُ والخُسرانُ', '', 'انحِرافٌ عن الحَقِّ', '', 'الزَّيْغُ والغَوايَةُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {إِنَّ اللَّهَ لَا يَهْدِي مَنْ هُوَ مُسْرِفٌ كَذَّابٌ} السَّرَفُ هو:', '', 'صَرفُ المَالِ في غير مَحِلِّهِ', '', 'تجاوزُ الحدِّ في كلِّ فِعلٍ يَفعَلُه الإنسانُ', '', 'الرُّكونُ إلى النِّعَمِ والتَّفاخُرُ بها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أَهمِّ مَقاصِدِ سُورَةِ غافِر:', '', 'بيانُ جوانبَ من التَّشريع الإسلاميِّ في العِباداتِ والأَحوالِ والمُعامَلاتِ', '', 'إبطالُ جَدَلِ الَّذينَ يُجادِلونَ في آياتِ الله', '', 'الإرشادُ إلى ما  يُوجِبُ السَّعادةَ؛ كالاستقامةِ على الدِّينِ، وعَدَمِ الرُّكونِ إلى الظَّالمينَ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَقَالَ مُوسَى إِنِّي عُذْتُ بِرَبِّي وَرَبِّكُمْ} فيه إِشارَةٌ إلى:', '', 'استِحبابُ أسلُوبِ التَّرغيبِ في الدَّعوَةِ إلى الله', '', 'أنَّ تَحقِيقَ تَوحِيدِ الرُّبوبِيةِ يَكفِي في النَّجاةِ مِن الشِّركِ', '', 'أنَّ الاجتماعَ على الدُّعاءِ واتِّفاقَ الأرواحِ على التَّوجُّهِ والطَّلبِ؛ أبلَغُ في إنجاحِ الحاجاتِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ بالعَقلِ في قَولِه تعالى: {هُدًى وَذِكْرَى لِأُولِي الْأَلْبَابِ}:', '', 'عَقلُ الرُّشدِ', '', 'عَقلُ الإِدراكِ', '', 'مُطلَقُ العَقلِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَحْمَةً وَعِلْمًا فَاغْفِرْ لِلَّذِينَ تَابُوا} يُستَفادُ مِنه:', '', 'أنَّ للتَّوبَةِ سِتَّةَ شُروطٍ حتَّى تُقبَل', '', 'أنَّ الدُّعاءَ مِن أَسبابِ تَوحِيدِ الألُوهِيَّةِ', '', 'أنَّ السُّنَّةَ في الدُّعاءِ أن يُبدَأَ فيه بالثَّناءِ على الله تعالى', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بالآثَارِ في قَولِه تعالى: {كَانُوا هُمْ أَشَدَّ مِنْهُمْ قُوَّةً وَآَثَارًا}:', '', 'ما تُرِكَ في الأرضِ مِنَ البِناياتِ والمَعالِمِ والحُصونِ', '', 'البَأسُ والشِّدِّةُ في المَعارِك خاصَّة', '', 'العِلمُ والمَعرِفَةُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المَقصُودُ بقَولِه تعالى: {تَقَلُّبُهُمْ فِي الْبِلَادِ}:', '', 'تَصَرُّفُهم في البِلادِ، وتَردُّدُهم في أسفارِهم سالِمينَ', '', 'كَثرَةُ أَحزابِهِم في البِلادِ وأَعوانِهِم على الكُفرِ', '', 'انتِصاراتِهِم في حُروبِهِم ودَفْعِهِم لأَعدائِهِم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {وَمَا يَتَذَكَّرُ إِلَّا مَنْ يُنِيبُ} "يُنِيبُ" أي:', '', 'يَصدُقُ النِّيَّةَ', '', 'يَجتَهِدُ في عِبادَةِ رَبَّهِ', '', 'يَرجِعُ ويَتوبُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {النَّارُ يُعْرَضُونَ عَلَيْهَا غُدُوًّا وَعَشِيًّا} فيه:', '', 'عَدمُ فَناءِ الشَّمسِ في الآخِرَة', '', 'كَراهَةُ الصَّلاةِ وَقتَ الشُّروقِ ووَقتَ الغُروبِ', '', 'إثباتُ عذابِ القَبرِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أَهمِّ مَوضُوعاتِ سُورَةِ غافِر:', '', 'دَعوةُ العِبادِ إلى إخلاصِ الطَّاعةِ له، وتَذكيرُهم بأهوالِ يومِ القيامةِ', '', 'بيانُ أنَّ اللَّهَ سُبحانَه مُطَلِّعٌ على سَرائرِ الخَلقِ وضَمائرِهم', '', 'إثباتُ البَعثِ والجزاءِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'جاء قَولُه تعالى: {مِثْلَ يَوْمِ الْأَحْزَابِ} بالإِفرادِ؛ فلم يقُل: (مِثلَ أيَّامِ الأحزابِ):', '', 'للإِشارَةِ إلى أنَّ هَلاكَهُم جميعًا كان بعَذابٍ واحِدٍ', '', 'للإشارةِ إلى أنَّ الله قادِرٌ على إهلاكِهم في أقَلِّ زَمانٍ', '', 'لتَأكِيدِ مَعنى اتِّحادِهِم في الكُفرِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {هُوَ الَّذِي خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ} النُّطفةُ هي:', '', 'الماءُ الصَّافي', '', 'الطِّينُ اليابِسُ', '', 'قِطعَةُ اللَّحمِ المُتجمِّدَةُ', '', '', 'a', 'medium');
