(@akida_category_id, '', 'كِتابةُ المَقاديرِ لها', '', 'أربعةٌ تَقاديرُ', '', 'ثَلاثةٌ تَقاديرُ', '', 'خَمسةٌ تَقاديرُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من مَقاديرِ اللهِ اليَوميَّةِ', '', 'تَجديدُ وتَفصيلُ الأُمورِ', '', 'كِتابةُ الرِّزقِ والأجَلِ لكُلِّ مَولودٍ', '', 'أن يُعِزَّ مَن يَشاءُ، ويُذِلَّ مَن يَشاءُ، ويُحييَ ويُميتَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'القَدرُ الَّذي هو عِلمُ اللهِ وقُدرَتُه ومَشيئَتُه، هو:', '', 'للمُطيعِ خيرٌ كلُّه وللعاصي شرٌ كلُّه', '', 'قد يَدخُلُه الشَّرُّ الجُزئيُّ من بابِ الحِكمَة', '', 'خَيرٌ كُلُّه، وكَمالٌ من كُلِّ وَجهٍ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَعنَى قَولِه تَعالَى: {لَوْلَا كِتَابٌ مِنَ اللهِ سَبَقَ لَمَسَّكُمْ فِيمَا أَخَذْتُمْ عَذَابٌ عَظِيمٌ}', '', 'لولا ما كَتَبه سابِقًا في اللَّوحِ المَحفوظِ أنَّه سيُحِلُّ لأُمَّةِ مُحمَّدٍ الغَنائمَ لعذَّبَهُم', '', 'لولا قَولُه: {وَمَا كَانَ اللهُ لِيُعَذِّبَهُمْ وَأَنْتَ فِيهِمْ} لَأَخَذَهمُ العَذابُ', '', 'لولا ما أُنزِلَ على لِسانِ رَسولِه منَ المَغفرةِ لأهلِ بَدرٍ لعَذَّبَكُم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُستحَبُّ في الدُّعاءِ أن:', '', 'يَجزِمَ في الطَّلَبِ، ولا يُعلِّقَ مَسألَتَه على المَشيئةِ', '', 'يَتوسَّلَ بالنَّبيِّ صلَّى اللهُ عليه وسلَّم', '', 'يُعلِّقَ مَسألتَه بمَشيئةِ اللهِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُه تَعالَى: {وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْدِيرًا}', '', 'التَّرتيبُ هنا تَرتيبٌ ذِكرِيٌّ، لا يَلزَمُ منه أن يَكونَ المُتأخِّرَ في اللَّفِظ مُتأخِّرًا في الرُّتبةِ', '', 'يُفيدُ أنَّ التَّقديرَ وَقَع بعدَ الخَلقِ', '', 'يُفيدُ أنَّ التَّقديرَ الواقِعَ بعدَ الخَلقِ هو التَّقديرُ العُمريُّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'التَّقديرُ الحَوليُّ في لَيلةِ القَدرِ', '', 'يَكونُ بكِتابةِ اللهِ تَعالَى لِمَا يَجري العامَ المُقبِلَ في اللَّوحِ المَحفوظِ', '', 'يَكونُ برَفعِ المَلائكةِ أعمالَ السَّنةِ الماضِيةِ للهِ تَعالَى', '', 'يَكونُ باستِنساخِ ما كُتِب في اللَّوحِ المَحفوظِ منَ الأحداثِ والأفعالِ والأحوالِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإرادةُ الكَونيَّةُ القَدَريَّةُ؛ هي:', '', 'الشَّامِلةُ لكُلِّ الحَوادِثِ الكَونيَّةِ', '', 'المُتضمِّنةُ للمَحبَّةِ والرِّضا', '', 'الشَّاملةُ لبَعضِ الحَوادثِ الكَونيَّةِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الشَّيءُ المُقدَّرُ الذي قَدَّره اللهُ تَعالَى على العِبادِ', '', 'قد يَدخُلُه الشَّرُّ الجُزئيُّ؛ فيَكونُ شرًّا من وَجهٍ، وخَيرًا من وَجهٍ آخَرَ', '', 'لا يَدخُلُه الشَّرُّ مُطلقًا', '', 'يَدخُلُه الشَّرُّ من كلِّ جانِبٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُه ﷺ: "ما مِنكُم مِن أحَدٍ إلَّا كُتِبَ مَكانُه منَ الجنَّةِ والنَّارِ"', '', 'ردٌّ على الجَهميَّةِ؛ فإنَّ التَّيسيرَ ضدُّ الجَبرِ', '', 'إثباتٌ لنَفيِ الاخْتيارِ عنِ العِبادِ', '', 'مُوافَقةٌ للقَدَريَّةِ في نَفيِ القَدَرِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'وَصَف اللهَ تَعالَى بأنَّه لا يَقدِرُ على الشُّرورِ والمَعاصي', '', 'أبُو الحَسَنِ الأشعَريُّ', '', 'واصِلُ بنُ عَطاءٍ', '', 'النَّظَّامُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'التَّغيِيرُ والتَّبديلُ الذي يَجرِي عَلى أعمالِ وأحوالِ بني آدَمَ وغَيرِهم، إنَّما يَكونُ:', '', 'في الفُروعِ والشُّعَبِ، كأعمالِ اليومِ واللَّيلةِ التي تَكتُبُها المَلائكةُ', '', 'في اللَّوحِ المَحفوظِ', '', 'في عِلمِ اللهِ الأزليِّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أخَذَ واصِلُ بنُ عَطاءٍ وعمرُو بنُ عُبيدٍ القَولَ في القَدَرِ عن', '', 'مَعبَدٍ الجُهَنيِّ', '', 'الجَهمِ بن صَفوان', '', 'غَيلانَ الدِّمَشقيِّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أبلَغُ آيةٍ في الرَّدِّ على القَدَريَّةِ النُّفاةِ؛ هي:', '', 'قَولُه تَعالَى: {وَإِذْ قِيلَ لَهُمُ اسْكُنُوا هَذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ}', '', 'قَولُه تَعالَى: {وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا * يُدْخِلُ مَنْ يَشَاءُ فِي رَحْمَتِهِ وَالظَّالِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًا}', '', 'قَولُه تَعالَى: {وَقُلْنَا يَا آدَمُ اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَا}', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المرادُ بالرِّضا بقَضاءِ اللهِ وقَدَرِه:', '', 'أن يَتلفَّظَ بألفاظِ الرِّضَا بلِسانِه، وإنْ وَجَد السَّخطَ في قَلبِه', '', 'أن يُحِبَّ العَبدُ المُصيبةَ ويَتمنَّى نُزولَها مرَّةً أُخرى', '', 'أن يَكونَ مُطمئِنًّا مُنشرِحَ الصَّدرِ بما قَضَى اللهُ، لا يَتألَّمُ نَفسِيًّا على الرَّغمِ من كُرهِه للمُصيبةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'تقديرُ أهلِ الجَنَّةِ وأهلِ النَّارِ يكون:', '', 'عندَ البَعثِ مِن القُبورِ', '', 'قبل خَلقِ الخلائِقِ', '', 'بعدَ نَفخِ الرُّوحِ في الجَنينِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال الكرمانيُّ: المَذهَبُ الحَقُّ أنَّه:', '', 'لا مشيئة لِلعبادِ وهُم مَجبُورُون تحتَ قَدَرِ الله عليهم', '', 'لا جَبرَ في أَفعالِ العِبادِ وإنما لهُم مَشيئَة', '', 'لا جَبرَ ولا قَدَرَ، بل أمرٌ بين الأمرَينِ؛ أي: بخَلقِ اللهِ وكَسبِ العَبدِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن ظَنَّ أنَّ التَّوكُّلَ يُغني عنِ الأسبابِ المَأمورِ بها؛ فهو:', '', 'كافِرٌ', '', 'ضالٌّ', '', 'مُشرِكٌ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُ بعضِ النَّاسِ: "هذا من سُخرِيةِ القَدَرِ"، أو "من سَذاجةِ القَدَرِ"', '', 'حرامٌ، وهو مُنكَرٌ منَ القَولِ، وكُفرٌ باللهِ واستِهزاءٌ به', '', 'مَكروهٌ', '', 'على خِلافِ الأَوْلَى', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإرادةُ والرِّضا', '', 'يَستَلزِمُ أحَدُهُما الآخَرَ', '', 'الإرادةُ تَستلزِمُ الرِّضَا، والرِّضا لا يَستَلزِمُ الإرادةَ', '', 'الإرادةُ لا تَستَلزِمُ الرِّضا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَعنى قَولِه تَعالَى: {كُلَّ يَوْمٍ هُوَ فِي شَأْنٍ}', '', 'يُظهِرُ شَأنًا من قُدرَتِه التي كَتَبها في الأزَلِ', '', 'يَكتُبُ مَقاديرَ العِبادِ كلَّ يَومٍ', '', 'منَ المُتَشابِهاتِ الَّتي لا يَعلَمُها أحَدٌ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ميَّز اللهُ أهلَ الجنَّةِ والنَّارِ', '', 'بَعدَ خَلقِ السَّمواتِ والأرضِ', '', 'بَعدَما خَلَق آدَمَ، واستَخرَجَ ذُرِّيَّتَه من صُلْبِه', '', 'بَعدَ كِتابةِ القَدَرِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإرادةُ الدِّينيَّةُ الشَّرعيَّةُ؛ هي:', '', 'الشَّامِلةُ لكُلِّ الحَوادِثِ الَكونيَّةِ', '', 'المُتضمِّنةُ للمَحبَّةِ والرِّضا', '', 'الشَّاملةُ لبَعضِ الحَوادثِ الكَونيَّةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَعنَى: "وقَضَيْنَا" في قَولِه تَعالَى: {وَقَضَيْنَا إِلَى بَنِي إِسْرَائِيلَ فِي الْكِتَابِ}', '', 'أمَرْنا', '', 'حَكَمْنا', '', 'أَعْلَمْنا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَعنَى "طَائِرَهُ" في قَولِه تَعالَى: {وَكُلَّ إِنْسَانٍ أَلْزَمْنَاهُ طَائِرَهُ فِي عُنُقِهِ}', '', 'عاقِبةُ تَشاؤمِه', '', 'المَلَكُ المُلازِمُ له', '', 'عَمَلُه وما قُدِّر عَليهِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المَلائكةُ يُؤمِّنونَ على دَعوةِ:', '', 'الَّذي يَدْعو لأخيهِ بظَهرِ الغَيبِ', '', 'الضُّعَفاءِ والمَساكينِ', '', 'المَظلومينَ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المَلائكةُ خَلقٌ من خَلقِ اللهِ تَعالى؛ وهم:', '', 'يَعصونَ، ثمَّ يَتوبونَ من فَورِهم', '', 'يَعصونَ كما يَعصي بَنو آدَمَ', '', 'لا يَعصونَ اللهَ تَعالى مُطلقًا؛ ويَدلُّ عليه قَولُه تَعالى: {‌لَا ‌يَعْصُونَ ‌اللَّهَ ‌مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ} [التحريم: 6]', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يَطوفُ بَعضُ المَلائكةِ في الطُّرُقاتِ يَبحَثونَ عن:', '', 'المُتحابِّينَ في الله', '', 'حِلَقِ الذِّكرِ ومَجالسِ العِلمِ', '', 'المُتصَدِّقينَ منَ المُؤمِنينَ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'اسمُ المَلَكِ المُوكَّلِ بالقَطرِ والنَّباتِ؛ هو:', '', 'مِيكائيلُ', '', 'إسْرافيلُ', '', 'جَبْرائيلُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'خَلَق اللهُ تَعالى المَلائكةَ:', '', 'بالتَّزامُنِ مَع وَقتِ خَلقِ آدمَ -عَليهِ السَّلامُ-', '', 'بعدَ خَلقِ آدمَ -عَليهِ السَّلامُ-', '', 'قبلَ خَلقِ آدمَ -عَليهِ السَّلامُ- بزَمانٍ', '', '', 'c', 'easy');
