(@akida_category_id, '', 'الكُفرُ الأصغَرُ يَترتَّبُ عليه:', '', 'الخُروجُ منَ المِلَّةِ', '', 'الخُلودُ في جَهنَّمَ', '', 'يَنقُصُ أصلَ الإيمانِ ويُضعِفُه، ولا يُخرِجُ صاحِبَه منَ المِلَّةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِه تَعالَى: {وَمِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الْآخِرِ وَمَا هُمْ بِمُؤْمِنِينَ} حُجَّةٌ على:', '', 'الأشاعِرةِ الَّذين قالوا بالتَّأويلِ', '', 'الكَرَّامِيَّةِ الَّذين قالوا: إنَّ الإيمانَ قَولٌ باللِّسانِ وإن لم يعتَقِدْ بالقَلْبِ', '', 'المُعتزِلةِ الَّذين قالوا بالمَنزلةِ بينَ المَنزِلتَينِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الأحكامُ يُعمَلُ فيها:', '', 'بالظَّواهِرِ، واللهُ يَتولَّى السَّرائرَ', '', 'بالخَيرِ دونَ الشَّرِّ', '', 'بالسَّرائرِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'تُعَدُّ مُوالاةُ المُشرِكينَ ومُظاهَرَتُهم من:', '', 'نَواقِضِ الإسلامِ', '', 'صَغائرِ الذُّنوبِ', '', 'نَواقِضِ الوُضوءِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن قال: إنَّ الفَلاسِفةَ أوِ الصُّوفيَّةَ، أوِ غيرهم: طريقتُهم أحسَنُ من طريقةِ مُحمَّدٍ ﷺ، فهذا:', '', 'مُرتكِبٌ لكَبيرةٍ دونَ الكُفرِ', '', 'فاسِقٌ، ولا يَخرُجُ منَ المِلَّةِ', '', 'كافِرٌ، وخارِجٌ عنِ المِلَّةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الكُفرُ يَكونُ:', '', 'بالإقرارِ باللِّسانِ', '', 'بالقَولِ والعَمَلِ والاعتِقادِ', '', 'بجُحودِ القَلبِ', '', '', 'b', 'hard');
