(@akida_category_id, '', 'مَن جَوَّز على الأنبياءِ الكَذِبَ فيما أتَوْا به؛ فهو:', '', 'كافِرٌ بإجماعٍ', '', 'فاسِقٌ جاهِلٌ', '', 'مُؤمِنٌ عاصٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن قَرأَ القرآنَ على ضَربِ الدُّفِّ؛ فإنَّه:', '', 'يَكفُرُ بهذا الفِعلِ', '', 'يَأثَمُ بذلك، ولا يَخرُجُ منَ المِلَّةِ', '', 'يُسجَنُ، ويُعزَّرُ أبدًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'إنكارُ وُجودِ الجِنِّ أوِ المَلائكةِ، هو:', '', 'إثمٌ ومَعصيةٌ، ولا يَرتَقي إلى دَرجةِ الكُفرِ', '', 'كُفرٌ أكبَرُ', '', 'نِفاقٌ عَمَليٌّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'التَّكفيرُ بأقوالٍ وأعمالٍ مُحتَمِلةِ الدَّلالةِ قبلَ التَّحقُّقِ من قَصدِ قائِلِها أو فاعِلِها، هو أمرٌ:', '', 'يَجوزُ إذا كان لازِمُ قَولِه يَدُلُّ عليه، أو يَؤُولُ إليه', '', 'جائزٌ شَرعًا', '', 'لا يَجوزُ مُطلقًا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'اتَّفَق أهلُ السُّنَّةِ على أنَّ النُّطقَ بالشَّهادَتينِ شَرطٌ', '', 'لزِيادةِ الإيمانِ', '', 'لكَماِل الإيمانِ', '', 'لصِحَّةِ الإيمانِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ما أسَرَّ أحَدٌ سَريرةً إلَّا أبْداها اللهُ على', '', 'أَعمالِه في الدُّنيا', '', 'فَلَتاتِ لِسانِه', '', 'صَحيفَتِه يومَ القِيامَة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولَ القَلبِ مُتعَلِّقُ التوحيدِ:', '', 'العَملي التَّقرِيريِّ', '', 'الخَبَريِّ الاعتِقاديِّ', '', 'الطَّلِبي الإِرادِيِّ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن تَعبيرات السّلَفِ في تقريرِ التَّلازُمِ بين الإيمانِ والعَمَلِ:', '', 'القَولُ شَرطٌ والعَملُ رُكنٌ', '', 'لا يَصلُحُ قَولٌ إلَّا بعَمَلٍ', '', 'النِّيَّةُ يُصدِّقُها العَملُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'إنْ عجَزَ الإنسانُ عن النُّطقِ لخَلَلٍ في لسانِه، أو لعَدَمِ التمَكُّنِ منه لمعاجَلةِ المنيَّةِ، أو لغيرِ ذلك؛ فإنَّه', '', 'يكونُ مُؤمِنًا بالاعتِقادِ من غيرِ لَفظٍ', '', 'يُعامَل مُعاملَة أَهلِ الكِتابِ', '', 'لا يَكونُ مؤمنًا بالاعتقادِ من غيرِ لفظٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الدَّليلُ على أنَّ التصديقَ بالقَلبِ لا ينفَكُّ عن الكُفرِ دونَ أن ينضَمَّ إليه الإقرارُ باللِّسانِ إذا كان مقدورًا عليه:', '', 'أنَّ الشَّهادتَينِ هُما قَولُ القَلبِ؛ ولا يَصلُح قَولٌ بلا نُطقٍ', '', 'أنَّ الإِقرارَ في لُغةِ العَربِ يَرجِعُ عُرفًا إلى قَولِ اللِّسانِ', '', 'أنَّ اللهَ عزَّ وجَلَّ أمَرَ بالقَولِ، فقال: {قُولُوا آمَنَّا بِاللَّهِ}', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُستَفادُ من قَولِ النَّبيِّ ﷺ: (أُمِرتُ أن أُقاتِلَ النَّاسَ حتى يقولوا: لا إلهَ إلَّا اللهُ، فإذا قالوها عَصَموا منِّي دِماءَهم وأموالَهم إلَّا بحَقِّها). أنَّ العِصمةَ المُزايِلةَ للكُفرِ تَثبُتُ:', '', 'بعَملِ الجَوارحِ', '', 'بالقَولِ', '', 'باعتِقادِ القَلبِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يَتفاوَتُ قائِلو كَلِمةِ التَّوحيدِ تفاوُتًا عظيمًا بحَسَبِ تفاوُتِ', '', 'ما في ألسِنَتِهم منَ قَولِ: لا إله إلا الله', '', 'ما في جَوارِحِهم منَ العَملِ', '', 'ما في قُلوبِهم منَ التَّوحيدِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'تَواترتِ الأحاديثُ بأنَّه (يَحرُمُ على النَّارِ مَن قال: لا إلهَ إلَّا اللهُ، ومَن شَهِدَ أنْ لا إلهَ إلَّا اللهُ، وأنَّ محمَّدًا رسولُ اللهِ) ولكِنْ جاءَت مُقيَّدةً', '', 'جَميعُ ما سَبَق', '', 'بالإخْلاصِ واليَقينِ', '', 'بمَوتٍ عليها', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لو تمزَّق القَلبُ بالمحبَّةِ والخَوفِ ولم يتعَبَّدْ بالأمرِ وظاهِرِ الشَّرعِ', '', 'لم يُنْجِه ذلك من النَّارِ', '', 'يُنْجِيه ذلك من النَّارِ', '', 'يَنفَعُه في الآخِرةِ ولا يَنفَعُه في الدُّنيا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'التَّصديقَ بمعناه الخَبَريِّ يساوي تمامًا عند السَّلَفِ والأئمَّةِ معنى:', '', 'الاستِسلامِ', '', 'العِلمِ أو المعرِفةِ', '', 'الإقرارِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لا بُدَّ أن يَشتمِلَ الإيمانُ في المَعنَى الاصطلاحيِّ على:', '', 'الاعتِقادِ فَقَط', '', 'الاعتِقادِ والقولِ والعَمَلِ', '', 'القَولِ والاعتِقادِ فَقَط', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُستَفادُ من بَعضِ آياتِ القُرآنِ أنَّ مقَرَّ الإيمانِ ومحَلَّه هو:', '', 'اللِّسانُ', '', 'الجَوارحِ', '', 'القَلبُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال ابنُ رَجَبٍ: من ترك الشَّهادتين:', '', 'خَرَج من الإسلامِ', '', 'لم يُسْلِم بَعدُ', '', 'فقد أَشركَ في الألوهِيَّة', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'خبَرُ اللهِ عن عُلَماءِ اليَهودِ أنَّهم يَعرِفونَ النَّبيَّ ﷺ، وما أُنزِلَ إليهِم كما يَعرِفونَ أبناءَهم، وفي هذا دليلٌ:', '', 'أنَّ الانقيادَ والاستِسلامَ والخضوعَ همُ الأساسُ، دونَ التَّصديقِ', '', 'على أنَّ التصديقَ والمَعرِفةَ فَقَط لا تنفَعُ صاحِبَها', '', 'على أنَّ التصديقَ والمَعرِفةَ تنفَعُ صاحِبَها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كُلُّ حَرَكةٍ بالجَوارِحِ لا تَكونُ إلَّا', '', 'بإرادةٍ قَلبيَّةٍ', '', 'بعدَ سُكونٍ', '', 'باتِّفاقِ الجَوارحِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ألَا إنَّ في الجَسَدِ مُضغةً إذا صَلَحَت صَلَح الجَسَدُ كُلُّه، وإذا فَسَدَت فَسَد الجَسَدُ كُلُّه، ألَا وهي', '', 'العَقلُ', '', 'القَلبُ', '', 'اللِّسانُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'إذا استقرَّ في القَلبِ التَّصديقُ والانقيادُ حَصَل له:', '', 'البِدءُ بالتَّكاليفِ', '', 'القَرارُ والطُّمَأنينةُ', '', 'الإيمانُ الخالِصُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُستَفادُ من قَولِ النَّبيِّ ﷺ: (الإيمانُ بِضعٌ وسَبعونَ -أو بِضعٌ وسِتُّونَ- شُعبةً، فأفضَلُها قَولُ: لا إلهَ إلَّا اللهُ، وأدْناها إماطةُ الأذى عنِ الطَّريقِ، والحياءُ شُعبةٌ من الإيمانِ) أنَّ الإيمانَ الحَقَّ:', '', 'هو الَّذي يتطابَقُ فيه العَقلُ والرُّوحُ على ما شَرَعه اللهُ', '', 'هو الَّذي يَتطابَقُ فيه القَلبُ واللِّسانُ والجَوارحُ على ما شَرَعه اللهُ', '', 'هو الَّذي يَتطابَقُ فيه القَلبُ والعَقلُ على ما شَرَعه اللهُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لو قامَ بظواهِرِ الإسْلامِ، وليس في باطِنِه حقيقةُ الإيمانِ', '', 'يَنفَعُه في الآخِرةِ ولا يَنفَعُه في الدُّنيا', '', 'لم يُنْجِه ذلك من النَّارِ', '', 'يُنْجِيه ذلك من النَّارِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُ النَّبيِّ ﷺ: «لا يَزْني الزَّاني حينَ يَزْني وهو مؤمِنٌ، ولا يَسرِقُ السَّارِقُ حين يَسرِقُ وهو مؤمِنٌ» معناهُ', '', 'أنَّ مَعاصِيَه وذُنوبَه حَتمًا ستُوصِلُه للكُفرِ', '', 'لا يفعَلُ هذه المعاصيَ وهو مُؤمنٌ، بل هو كافِرٌ باتِّفاقٍ', '', 'لا يفعَلُ هذه المعاصيَ وهو كامِلُ الإيمانِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'إذا قُرِن الإيمانُ بالإسْلامِ، فإنَّ الإيمانَ', '', 'في الجَوارحِ', '', 'في القَلبِ', '', 'في الرُّوحِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'هل يجوزُ التَّقليدُ في أبوابِ العقيدةِ، أم لا بُدَّ منَ الأخذِ بالدَّليلِ؟', '', 'لا يَجوزُ التَّقليدُ، بل لا بُدَّ من النَّظَرِ والدَّليلِ كما أمَرَت آياتُ التَّفكُّرِ', '', 'يَجوزُ التَّقليدُ في العَقائدِ فيما يُمكِنُ مَعرِفتُه بالدَّليلِ', '', 'يَجوزُ التَّقليدُ في العقائِدِ ممَّا لا يُمكِنُ مَعرفتُه بالدَّليلِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'إجماعُ أهلِ السُّنَّةِ على عَدَمِ التَّكفيرِ بالذَّنبِ إنَّما يرادُ به:', '', 'الصَّغائِر والكَبائِر وليس الشِّرك', '', 'الذَّنبُ غير المُخرِج مِن المِلَّة', '', 'فِعلُ المعاصي لا تَرْكُ مباني الإسلامِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُه تعالى: {وَمَا أَنْتَ بِمُؤْمِنٍ لَّنَا وَلَوْ كُنَّا صَادِقِينَ} مَعنى «بمُؤمنٍ» أي:', '', 'بمُقِرٍّ لنا', '', 'بمُؤيِّدٍ لنا', '', 'بمُصَدِّقٍ لنا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'التَّصديقُ بمَعْناه الخَبَريِّ -وهو مجرَّدُ أن يَقَعَ في القَلبِ نسبةُ الصِّدقِ إلى المخبِرِ والخَبَرِ من غيرِ إذعانٍ وقَبولٍ- يُساوي تمامًا عندَ السَّلَفِ والأئمَّةِ مَعنى', '', 'العِلمِ أو المعرِفةِ', '', 'التَّصوُّرِ أوِ المُلاحَظةِ', '', 'الشُّعورِ أوِ المُكابَدةِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال البَغَويُّ: إنَّ حقيقةَ الإيمانِ:', '', 'التَّصديقُ بالقَلْبِ', '', 'العَملُ بالجَوارحِ', '', 'القَولُ باللِّسانِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الاعتقادُ بالقَلبِ عندَ السَّلفِ لا بُدَّ فيه من:', '', 'الالتزامِ والانقيادِ والتَّسليمِ، ويُطلَقُ عليه (عَمَلُ القَلبِ)', '', 'المعرِفةِ والعِلمِ والتصديقِ، ويُطلَقُ عليه (قَولُ القَلبِ)', '', 'جَميعُ ما سَبَق معًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'تظهَرُ الأنوارُ يومَ القيامةِ للنَّاسِ بأيمانِهم وبينَ أيْديهم بحسَبِ ما في قُلوبِهم من', '', 'الإخلاصِ', '', 'نُورِ كلِمةِ التَّوحيدِ عِلمًا وعَمَلًا ومَعرِفةً وحالًا', '', 'تَوحيدِ الألُوهِيَّةِ وتَوحيدِ الرُّبوبِيَّةِ وتَوحيدِ الأَسماءِ والصِّفاتِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الشَّهادتانِ إذا لم يتكَلَّمْ بهما الإنسانُ مع القُدرةِ فهو', '', 'كافِرٌ باتِّفاقِ المُسلِمينَ', '', 'مُنافِقٌ باتِّفاقِ المُسلِمينَ', '', 'فاسِقٌ باتِّفاقِ المُسلِمينَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإيمانُ الشَّرعيُّ تصديقٌ مخصوصٌ، وهو ما يُسمِّيه السَّلَفُ بقَولِ القَلبِ، وهذا التصديقُ:', '', 'لا يَنفَعُ وَحدَه', '', 'يَنفَعُ في الآخِرةِ ولا يَنفَعُ في الدُّنيا', '', 'يَنفَعُ وَحدَه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِ النَّبيِّ ﷺ: «النُّجومُ أَمَنةُ السَّماءِ، فإذا ذهَبَتِ النُّجومُ أتى السَّماءَ ما تُوعَدُ»، مَعنى: «آمَنَةُ»', '', 'جَمعُ أمانةٍ وهي عَكسُ الخِيانةِ', '', 'جمعُ أَمينٍ، وهو الحافِظُ', '', 'جَمعُ أمانٍ وهي المسؤوليةُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'اختلَفَ أهلُ العِلمِ في تكفيرِ تارِكِ الأركانِ الأربعةِ التي سِوى الشَّهادَتَينِ، أو بَعضِها، معَ:', '', 'نُطقِه بالشَّهادَتينِ', '', 'جُحُودِها ونُكرانِها', '', 'الإقرارِ بوُجوبِها', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المعرفةُ التي اشتَرَطَها المعتَزِلةُ وأمثالُهم هي أن يَعرِفَ المرءُ أُصولَ دِينِه عن طريقِ:', '', 'التقليدِ', '', 'النَّظَرِ والدَّليلِ العَقليِّ وَحدَه', '', 'السَّماعِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال الآجُريُّ: لا يصِحُّ الدِّينُ إلَّا بالتصديقِ بالقَلْبِ، والإقرارِ باللِّسانِ،', '', 'والعَمَلِ بالجوارحِ', '', 'ورِضًا باللهِ ورَسولِه', '', 'والإخلاصِ في النِّيَّةِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِ اللهِ تَعالَى: {وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ} أجمَع المُفسِّرونَ على أنَّه أرادَ: صَلاتَكم إلى', '', 'بَيتِ المقدِسِ', '', 'الكَعبةِ المُشرَّفةِ', '', 'المَسجِدِ النَّبويِّ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قد وَرَدتِ النُّصوصُ بتَسميةِ الإيمانِ:', '', 'عَمَلًا', '', 'حُكمًا', '', 'قَولًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أجهَلُ النَّاسِ بالتَّوحيدِ مَن ظَنَّ أنَّ المطلوبَ بقَولِ: لا إلهَ إلَّا اللهُ هو', '', 'التلفُّظُ بها باللِّسانِ فَقَط', '', 'التلفُّظُ بها باللِّسانِ مع عَملِ الجَوارحِ والاعتِقادِ الجازِمِ', '', 'التلفُّظُ بها باللِّسانِ مع الاعتِقادِ الجازِمِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الكُفرُ منه كُفرُ إنكارٍ وجُحودٍ، ومنه كُفرُ إباءٍ وامتناعٍ، فمِثالُ الأخيرِ:', '', 'كُفرُ فِرعَونَ', '', 'كُفرُ النُّمرودِ', '', 'كُفرُ إبليسَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال اللهُ تَعالَى: {فَإِنَّهُمْ لَا يُكَذِّبُونَكَ وَلَكِنَّ الظَّالِمِينَ بِآيَاتِ اللَّهِ يَجْحَدُونَ} يُستفادُ منه أنَّ أساسَ دَعوةِ الأنبياءِ والرُّسُلِ هو', '', 'التَّصديقُ والقَبولُ', '', 'المعرفةُ والعِلمُ المجَرَّدُ (أي: قَولُ القَلبِ)', '', 'الخُضوعُ والاستسلامُ والانقيادُ (عمَلُ القَلبِ والجوارحِ)', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'جعَل سُفيانُ بنَ عُيَينةَ سُؤالَ: أمُؤمِنٌ أنت؟', '', 'كَبِيرَةً', '', 'شرِكًا', '', 'بِدعَةً', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'حقيقةُ الإيمانِ مُرَكَّبةٌ من قَولٍ وعَمَلٍ، والقَولُ قِسمانِ:', '', 'قَولُ القَلبِ، وهو الاعتقادُ، وقولُ اللِّسانِ', '', 'قَولُ القَلبِ، وهو الاعتقادُ، وقولُ الجوارحِ', '', 'قَولُ الجَوارحِ، وقَولُ اللِّسانِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كان أحدُ الأئمَّةِ يقولُ بزِيادةِ الإيمانِ؛ لأنَّه وَرَد النَّصُّ به، ويُتوقَّفُ في النُّقصانِ، ثمَّ رَجَع عنه بعدَ ذلك فمَن هو؟', '', 'الإمامُ أبو حَنيفةَ', '', 'الإمامُ الشَّافعيُّ', '', 'الإمامُ مالِكُ بنُ أنسٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ممَّن صنَّف في الإيمانِ مِن أئمَّةِ السَّلَفِ:', '', 'تَقِيُّ الدِّينِ الخَوارزمِيُّ', '', 'محمدُ بنُ شُراحِيلَ البَجليُّ', '', 'أبو عُبَيدٍ القاسِمُ بنُ سلَّامٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'القائلُ: «إنَّ الإيمانَ ليس بالتَّحَلِّي ولا بالتَّمَنِّي، إنَّما الإيمانُ ما وَقَر في القَلبِ، وصَدَّقه العَمَلُ»', '', 'الأعمَشُ سُلَيمانُ بنُ مِهرانَ', '', 'هارونُ الرَّشيدُ', '', 'الحَسَنُ البَصريُّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', '«تَركَ الفَرائِضِ ليس بمَنزلةِ رُكوبِ المَحارِمِ» هذه من القَواعِدِ التي اختَلَف فيها أهلُ السُّنَّةِ والجَماعةِ مع', '', 'المُرجِئةِ', '', 'الخَوارجِ', '', 'المُعتزِلةِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإيمانُ أصلٌ له شُعَبٌ متعَدِّدةٌ، وكُلُّ شُعبةٍ منها تُسَمَّى', '', 'إيمانًا', '', 'إِقرارًا', '', 'عَملًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'اخْتارَ شَيخُ الإسلامِ ابنُ تَيميَّةَ والعَلَّامةُ ابنُ عُثَيمين رَحِمَهما اللهُ تَعالَى أنَّ الأجوَدَ في مَعنَى الإيمانِ في اللُّغةِ هو:', '', 'الإقرارُ', '', 'التَّأمينُ', '', 'التَّصديقُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يكونُ الرَّجُلانِ مَقامُهما في الصَّفِّ واحِدًا وبينَ صلاتَيْهما كما بينَ السَّماءِ والأرضِ، لأن الأعمالَ تَتفاضَلُ:', '', 'بالنِّيَّاتِ', '', 'بتفاضُلِ ما في القُلوبِ', '', 'بعَدَدِها', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'تدخُلُ أعمالُ الجوارحِ عند السَّلَفِ في مُسَمَّى الإيمانِ دُخولًا أوَّلِيًّا، وذلك عند:', '', 'اعتِبار أنَّ للقَلبِ إقرارًا وقولًا وعَملًا', '', 'استِعمالِ الأَلفاظِ الدَّالَّةِ على أَحدِهِما', '', 'إطلاقِ لَفظِ الإيمانِ أو تجريدِه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن القائلُ: «لَقِيتُ أكثَرَ مِن ألفِ رَجُلٍ من العُلَماءِ بالأمْصارِ، فما رأيتُ أحدًا منهم يختَلِفُ في أنَّ الدِّينَ قَولٌ وعَمَلٌ»', '', 'البُخاريُّ', '', 'الشَّافِعيُّ', '', 'البَغَويُّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَملُ القَلبِ هو متعَلِّقُ التَّوحيدِ', '', 'الطَّلَبيِّ الإراديِّ', '', 'الاعتِقاديِّ', '', 'الخَبَريِّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'هلِ التَّقليدُ في المسائلِ العَقَديَّةِ كالتَّقليدِ في المسائِلِ العَمَليَّةِ؟', '', 'يجوزُ التَّقليدُ في الأَعمالِ دُونَ العَقائدِ', '', 'يجوزُ التَّقليدُ في العَقائدِ دُونَ الأَعمالِ', '', 'لا فَرقَ، فالذي لا يَستطيعُ الوُصولَ إلى الحَقِّ بنفْسِه ففَرضُه التَّقليدُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن القائلُ: «كان الإجماعُ من الصَّحابةِ والتَّابعينَ مِن بَعدِهم، ومَن أدرَكْناهم، يقولونَ: الإيمانُ قَولٌ وعَمَلٌ ونِيَّةٌ، لا يُجزئُ واحِدٌ من الثَّلاثِ إلَّا بالآخَرِ» هو:', '', 'البُخاريُّ', '', 'ابنُ عبدِ البَرِّ', '', 'الشَّافِعيُّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإيمانُ قولٌ وعَملٌ، وهذا العَمَلُ يَشمَلُ عَملَ الجَوارحِ والمُرادُ بها:', '', 'الإخلاصُ للهِ في جَميعِ العِباداتِ', '', 'جِنسُ العَمَلِ الصَّالحِ، أي: جِنسُ الامتِثالِ للأوامِرِ، والاجتنابِ للنَّواهي', '', 'اليَقينُ والقَبولُ', '', '', 'b', 'hard');
