INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'حقيقةُ الإيمانِ مُرَكَّبةٌ من قَولٍ وعَمَلٍ. والعَمَلُ قِسمانِ:', '', 'عَمَلُ اللِّسانِ، وعَمَلُ الجوارحِ', '', 'عَمَلُ القَلبِ، وهو نيَّتُه وإخلاصُه، وعَمَلُ الجوارحِ', '', 'عَمَلُ القَلبِ؛ وهو إيمانُه، وعَمَلُ اللِّسانِ؛ وهو كَلامُه وإِقرارُه', '', '', 'b', 'hard');
