(@akida_category_id, '', 'رُكنا العِبادةِ', '', 'الخَوفُ والرَّجاءُ', '', 'السَّمعُ والطَّاعةُ', '', 'كَمالُ الخُضوعِ وكَمالُ المَحبَّةِ', '', '', 'c', 'medium');
