(@akida_category_id, '', 'قال الحَسَنُ البَصريُّ: "هذا العَمودُ فأَينَ الطُّنُبُ؟" المُرادُ بالطُّنُبِ:', '', 'فِعلُ الواجِباتِ وتَركُ المُحرَّماتِ', '', 'أركانُ الإسلامِ', '', 'مَحاسِنُ الأخلاقِ', '', '', 'a', 'medium');
