(@akida_category_id, '', 'عن أبي هُرَيرةَ رَضِيَ اللهُ عنه أنَّ النَّبيَّ صلَّى اللهُ عليه وسلَّم قال: (يَتَقارَبُ الزَّمانُ، ويَنقُصُ العَمَلُ...) المَقصُودُ بنُقصانِ العَمَلِ:', '', 'نُقصانُ الأَجرِ لقِلَّةِ الإِخلاصِ وكَثرةِ الرِّياءِ', '', 'قِلَّةُ الرَّغبَةِ في الطَّاعاتِ واشتِغالُ النَّاسِ بالدُّنيا والسَّعيُ فيها', '', 'انعِدامُ البَركَةِ مِن الوَقتِ فلا يكادُ المَرءُ يَفعَلُ شَيئًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أوَّلُ أَشراطِ السَّاعةِ الصُّغرَى وُقوعًا:', '', 'بَعثَةُ النبيِّ صلَّى الله عليه وسلَّم', '', 'هِجرَةُ النبيِّ صلَّى الله عليه وسلَّم', '', 'مَوتُ النبيِّ صلَّى الله عليه وسلَّم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُؤخَذُ مِن حَديثِ النبيِّ صلَّى الله عليه وسلَّم: (كُنْ في الدُّنيا كأنَّكَ غَريبٌ أو عابرُ سَبيلٍ):', '', 'أنَّ الدُّنيا دارُ عُبورٍ والآخِرة دارُ القَرارِ', '', 'أنَّ الدِّينَ بَدأَ غَريبًا وسيعُودُ غَريبًا كما بَدأ', '', 'أنَ الدُّنيا سِجنُ المُؤمِنِ وجَنَّةُ الكافِر', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإيمانُ باليَومِ الآخِرِ:', '', 'سُنَّةٌ مُؤَكَّدةٌ', '', 'فَرضُ كِفايَة', '', 'رُكنٌ مِن أَركانِ الإيمانِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن المَعصُومِينَ مِن فِتنةِ القَبرِ وعَذابِه:', '', 'مَن ماتَ بداءِ البَطنِ', '', 'مَن ماتَت وزَوجُها عنها راضٍ', '', 'مَن أَحصَى أَسماءَ الله تعالى', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: (لا تَقومُ السَّاعةُ حَتَّى تَخرُجَ نارٌ مِن أرضِ الحِجازِ، تُضيءُ أعناقَ الإبِلِ ببُصْرَى) قال العُلماءُ: إنَّ هذه العَلامَة ظَهرَت عام:', '', '329هـ', '', '578هـ', '', '654هـ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم : (لا تَقومُ السَّاعةُ حتى تَظهَرَ الفِتَنُ، ويَكثُرَ الكَذِبُ) كَثرةُ الكَذبِ أمرٌ قد استفحَلَ بين الناسِ؛ ومن ذلك:', '', 'ظُهورُ السَّلاطِين الكذَّابِين', '', 'كَذبَة أبريل', '', 'استِحلالُ الكَذبِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أَسبابِ تَخصِيصِ اليَومِ الآخِرِ بمَزيدٍ مِن العِنايةِ والتَّعظيمِ في القُرآنِ:', '', 'شِدَّةُ إنكارِ المُشرِكينَ لِليَومِ الآخِرِ، وانحِرافُ عَقيدةِ الإيمانِ باليَومِ الآخِرِ لدى أهلِ الكِتابِ', '', 'أنَّ الإِيمانَ باليَومِ الآخِر لم يكُن في شَرائعِ الأَنبِياءِ قَبلَنا', '', 'إِبطالُ عَقائِدِ الجاهِليَّةِ، وخُرافاتِ أَهلِ الأَوثانِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كان انشِقاقُ القَمرِ:', '', 'في المَدينَةِ بعدَ الهِجرَةِ', '', 'في مكَّة قبلَ الهِجرَةِ', '', 'أَثناءَ هِجرَةِ النبيِّ صلَّى الله عليه وسلَّم من مكَّة إلى المَدينَةِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رسول الله صلَّى الله عليه وسلَّم: (اعْدُد سِتًّا بين يَديِ السَّاعةِ: مَوْتِي، ثم فَتحُ بَيتِ المَقدِسِ، ثم مُوتَانٌ يَأخذُ فيكم كَقُعاصِ الغَنمِ...) فَسَّرَ العُلماءُ المَوتانَ بأنَّه:', '', 'الزَّلازِلُ والبَراكِينُ والحَوادِثُ الكَونِيَّة الكَبيرَة', '', 'السِّحْرُ', '', 'طاعُونُ عَمْواس', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من ثَمَراتِ الإيمانِ باليَومِ الآخِرِ:', '', 'شُكرُ اللهِ تعالى على عنايتِه ببني آدَمَ', '', 'محبَّةُ الرُّسُلِ وتوقيرُهم، والثَّناءُ عليهم بما يَليقُ بهم', '', 'الانكِفافُ عَنِ المَعاصي', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قول تعالى: {فَارْتَقِبْ يَوْمَ تَأْتِي السَّمَاءُ بِدُخَانٍ مُبِينٍ} المَقصُودُ بالدُّخانِ الذي هو مِن عَلاماتِ السَّاعَةِ:', '', 'خَيالٌ يُخيَّلُ للنَّاسِ مِن شِدَّةِ الجُوعِ كما حَدَثَ لقُريشٍ', '', 'النَّارُ التي خَرجَت بالحِجازِ فأَضاءَت أَعناقَ الإِبِلِ بِبُصْرَى', '', 'دُخانٌ ظاهِرٌ واضِحٌ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أَصلُ يَأجوجَ ومَأجوجَ:', '', 'قَبِيلٌ مِن نَسْلِ إِبليسَ لهُما خَواصُّ الجِنِّ وأَشكالُ الإِنسِ', '', 'قَبِيلتانِ مِن البَشرِ من ولَدِ آدَمَ وحَوَّاءَ', '', 'خَلْقٌ خاصٌّ لا يَعلمُ حَقيقَتَهُم إلا اللهُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'تكونُ التَّوبةُ مَقبولةً عند الله تعالى حين تتوفَّرُ شُروطُها، ومنها:', '', 'أن تكونَ في الرَّخاءِ لا في الشِّدَّةِ', '', 'أن يَسبِقَها عَملٌ صالِحٌ', '', 'أن تكونَ قَبلَ حُضورِ الموتِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن ماتَ فأَكلَتُه السِّباعُ أو الحِيتانُ في الماءِ ولم يُدفَن في قَبرٍ:', '', 'يُفتَنُ بعدَ مَوتِه مِثل فِتنَة القَبرِ', '', 'يُفتَنُ يومَ القِيامَة بعدَ حَشرِ الخَلائقِ', '', 'لا تَجرِي عليه فِتنَةُ القَبرِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: (إذا ضُيِّعَتِ الأمانةُ فانتَظِرِ السَّاعةَ) كيف تكونُ إِضاعَةُ الأَمانَة؟', '', 'بالطَّمعِ؛ حيث يَفشُو الطَّمعُ في الناسِ فلا يكادُ أَحدٌ يُؤدِّي الأَمانَة', '', 'بمَوْتِ العُلماءِ', '', 'بإسنادِ الأَمرِ إلى غيرِ أَهلِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'سَكَراتُ الموتِ هي: شِدَّتُه وغَشْيَتُه التي تُصِيبُ المُحتَضَرَ بسَبَبِ:', '', 'نَزعِ الرُّوحِ', '', 'حُضُورِ المَلائكَةِ', '', 'عَذابِ القَبرِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المَقصُودُ بفِتنَةِ القَبرِ في حَديثِ النبيِّ صلَّى الله عليه وسلَّم: (أُوحِيَ إلَيَّ أنَّكُم تُفتَنونَ في قُبورِكُم):', '', 'فِتنَةُ المَلَكينِ مُنكَرٍ ونَكيرٍ', '', 'فِتنَةُ عَذابِ القَبرِ', '', 'فِتنَةُ ضَمَّةِ القَبرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أَهمِّيةِ مَعرِفَة عَلاماتِ السَّاعة:', '', 'تَحذيرُ الله تعالى لِعِبادِه عن الغَفلةِ والرُّكونِ إلَى المُلهِيَاتِ', '', 'مَعرِفَة حَقيقَةِ نِهايَة العالَم وفَناءِ الدُّنيا', '', 'الاستِبشارُ بظُهورِ الدِّينِ وتَمكينِ المُؤمِنينَ في الأَرضِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الإيمانُ باليَومِ الآخِرِ يَتضمَّن:', '', 'الإيمانَ بأَشراطِ السَّاعَةِ', '', 'الإيمانَ بالأَنبِياءِ والرُّسُلِ', '', 'الإيمانَ بالكُتُبِ المُنزَّلَةِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَن أنسِ بن مالِكٍ رَضِيَ اللهُ عَنه قال: قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: (إنَّ من أشراطِ السَّاعةِ الفُحشَ والتَّفَحُّشَ) نصَّ ابنُ الأثير على أن الفُحش هو:', '', 'كُلُّ ما يَشتَدُّ قُبحُه مِنَ الذُّنوبِ والمَعاصي', '', 'كلُّ ما عُدَّ مِن الكَبائرِ وجاءَت فيه عُقوبَةٌ صَريحَة', '', 'ما خالَفَ أَصلَ الفِطرَةِ مِثل اللُّواط', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أَرواحُ المُؤمِنينَ بعدَ المَوتِ تكونُ:', '', 'طُيورًا تَعْلُقُ في شَجرِ الجنَّة', '', 'قَنادِيلَ مُعلَّقةً في سَقفِ الجنَّة', '', 'سابِحَةً تحتَ العَرشِ في أعلى الجنَّة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المراد في قولِ رَسول الله صلَّى اللهُ عليه وسلَّم: (لا تَقومُ السَّاعةُ حَتَّى تَأخُذَ أمَّتي بأخذِ القُرونِ قَبلَها):', '', 'اليَهودُ والنَّصارَى', '', 'المَجُوسُ', '', 'أَهلُ الجاهِليَّةِ الأُولَى', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَن عائشةَ رَضِيَ الله عَنها أنَّ النَّبيَّ صلَّى اللهُ عليه وسلَّم قال: أوَّلُ النَّاسِ هلاكًا:', '', 'أَشَدُّهُم إِيمانًا', '', 'قُرَيشٌ', '', 'بَنُو إِسرائِيلَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ما هي أعظَمُ الفِتَنِ في تاريخِ البَشريَّةِ قاطِبةً؟', '', 'فِتنَةُ الهَرْجِ (القَتلِ)', '', 'فِتنَةُ يَأجُوجَ ومَأجُوجَ', '', 'فِتنَةُ المَسيحِ الدَّجَّالِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في آخِرِ الزَّمانِ تُهدَمُ الكَعبَةُ على يَدِ من؟', '', 'ذو السُّويقَتينِ الحَبَشي', '', 'ذو الخَلَصَةِ طاغِيَة دَوْسٍ', '', 'الجَهْجَاه القَحطانِي', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: ليَشرَبنَّ ناسٌ من أمَّتي الخَمرَ يُسَمُّونَها:', '', 'المَاءَ', '', 'بغَيرِ اسمِها', '', 'نَبِيذًا', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُسَنُّ عند حُضُورِ المَوتِ:', '', 'الإكثارُ مِن قِراءَةِ القُرآنِ لحُضُورِ المَلائكَةِ', '', 'توجيهُ المُحتَضَرِ إلى القِبلةِ', '', 'تَلقِينُ المَيِّتِ: لا إلهَ إلَّا اللهُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عن النَّبيِّ صلَّى اللهُ عليه وسلَّم أنه قال (يَتَقارَبُ الزَّمانُ، ويَنقُصُ العَمَلُ، ويُلقى الشُّحُّ) قال المظهريُّ إنَّ المُرادَ بإلقاءِ الشُّحِّ أن:', '', 'يُتَلَقَّى الشُّحُّ ويُتَعَلَّمُ ويُتَواصى عليه ويُدعى إليهِ', '', 'يُلقى البُخلُ في القُلوبِ حَتَّى يُحِبُّوا الْمالَ', '', 'يَبخَلَ العالِمُ بعِلمِه، ويَبخَلَ الصَّانِعُ بصِناعَتِه، ويَبخَلَ الغَنيُّ بمالِه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَالَ النَّبِىُّ صلَّى اللهُ عليه وسلَّم: (لَا تَقُومُ السَّاعَةُ حَتَّى يُقْبَضَ الْعِلْمُ، وَتَكْثُرَ الزَّلَازِلُ، وَيَتَقَارَبَ الزَّمَانُ، وَتَظْهَرَ الْفِتَنُ) المَقصُودُ بظُهُورِ الفِتَنِ:', '', 'كَثرَةُ الحُروبِ ومَوتُ الرِّجالِ وسِيادَةُ النِّساءِ', '', 'خُروجُ يَأجُوجَ ومَأجُوجَ', '', 'كَثرَةُ الفِتَنِ واشتِهارُها وعَدَمُ التَّكاتُمِ بها', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'انشِقاقُ القَمَرِ كانَ آيةً على شَيئينِ:', '', 'صِدْقِ الرَّسولِ صلَّى الله عليه وسلَّم، واقتِرابِ السَّاعةِ', '', 'اقتِرابِ السَّاعةِ، ونِهايَةِ تَكذِيبِ قُريشٍ للنبيِّ صلَّى الله عليه وسلَّم', '', 'صِدْقِ النبيِّ صلَّى الله عليه وسلَّم، وزَوالِ مُلكِ فارِس والرُّوم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من أدِلَّةِ القُرآنِ على وُقوعِ سَكَراتِ المَوتِ؛ قَولُ الله تعالى:', '', '{حَتَّى إِذَا جَاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُونَ}', '', '{أَيْنَمَا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِي بُرُوجٍ مُشَيَّدَةٍ}', '', '{وَلَوْ تَرَى إِذِ الظَّالِمُونَ فِي غَمَرَاتِ الْمَوْتِ وَالْمَلائِكَةُ بَاسِطُو أَيْدِيهِمْ أَخْرِجُوا أَنْفُسَكُمُ}', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'البُشرَى التي تُبشِّرُها المَلائِكةُ للمُؤمِنينَ في قولِ الله تعالى: {أَلَّا تَخَافُوا وَلَا تَحْزَنُوا وَأَبْشِرُوا بِالْجَنَّةِ الَّتِي كُنْتُمْ تُوعَدُونَ} تكونُ عندَ:', '', 'النَّفْخِ في الصُّورِ', '', 'القِيامَةِ', '', 'الاحتِضارِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: (لا تَقومُ السَّاعةُ حَتَّى يَتَباهى النَّاسُ في الْمَساجِدِ) المَقصُودُ مِن التَّباهِي في المَساجِد:', '', 'جَعْلُها أماكِن للسَّمَرِ والمُفاخَرَةِ والتَّباهِي بَدلًا مِن جَعْلِها للعِبادَة', '', 'زَخرفَتُها وتَزيِينُها وتَشيِيدُ بِنائِها', '', 'اتِّخاذُ مَنابِرها للشِّعرِ والشُّعراءِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُ النَّبيِّ صلَّى اللهُ عليه وسلَّم: (إنَّ بَعدَكم قَومًا  يَشهَدونَ ولا يُستَشهَدونَ): قِيلَ: إنَّه مَحمولٌ على:', '', 'شَهادةِ الزُّورِ', '', 'الإِشراكِ بالله', '', 'عُقوقِ الوَالدَينِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ضَمَّةُ القَبرِ تكونُ:', '', 'لجَميعِ العِبادِ مُؤمِنهِم وكافِرهِم', '', 'للمُؤمِنينَ فقط', '', 'للكافِرينَ فقط', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن الذين لا تَشمَلُهم فِتنةُ القَبرِ:', '', 'الشُّهداءُ', '', 'العُلماءُ', '', 'البارُّ بوَالِدَيهِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَن أبي مالِكٍ الأشعَريِّ أنَّه سَمِعَ النَّبيَّ صلَّى اللهُ عليه وسلَّم يَقولُ: (ليَكونَنَّ في أمَّتي أقوامٌ يَستَحِلُّونَ الحِرَ) المقصود بـ(الحِر):', '', 'الخَمر', '', 'الزِّنا', '', 'الرِّبا', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المَهدِي هو:', '', 'رجلٌ يَخرُجُ بعدَ نُزولِ عيسى عليه السَّلامُ', '', 'رجلٌ يَخرُجُ قَبلَ نُزولِ عيسى عليه السَّلامُ', '', 'عيسى عليه السَّلام', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَن أبى هُرَيرةَ رَضِيَ الله عَنه عَنِ النَّبي صلَّى اللهُ عليه وسلَّم قال: إذا اقتَرَبَ الزَّمانُ:', '', 'لا يَبقَى على ظَهرِ الأَرضِ كافِرٌ', '', 'لَم تَكُد رُؤيا الْمُسْلِمِ تَكذِبُ', '', 'رُفِعَت الأَقلامُ وجَفَّت الصُّحُفُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِ الرَّسولِ صلَّى اللهُ عليه وسلَّم: (مِن أشراطِ السَّاعةِ أن يُلتَمَسَ العِلمُ عِندَ الأصاغِرِ) ذكر ابنُ عثيمين أن الأصاغرَ هم:', '', 'صغارُ السنِّ فقط', '', 'الأصاغرُ في العلمِ', '', 'الأصاغرُ في العلمِ وفي السنِّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أَخبرَ النبيُّ صلَّى الله عليه وسلَّم أنَّ الفُراتَ سَوفَ يَحسِرُ عن جَبلٍ مِن ذَهبٍ، وأَوْصَى مَن حَضَرَهُ:', '', 'ألَّا يأخُذَ مِنه شَيئًا', '', 'ألَّا يَتركَهُ للفُرسِ والرُّومِ', '', 'أن يُقاتِلَ دُونَه فإنَّه مَالُ الله', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عن رَسولِ الله صلَّى اللهُ عليه وسلَّم أنَّه قال: بينَ يَديِ السَّاعةِ مُوْتانٌ شَديدٌ، وبَعدَه سَنَواتُ:', '', 'الفِتَنِ', '', 'الزَّلازِلِ', '', 'القَحْطِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'هل يَحضُر الشَّيطانُ عندَ مَوتِ العَبدِ المُؤمِنِ؟', '', 'لا يَحضُر؛ بسَببِ وُجودِ مَلائكَةِ المَوتِ', '', 'يَحضُر حتَّى لا يَفلِتَ مِنه العَبدُ؛ فيُضِلَّه في اعتِقادِه، أو يحول بَينَه وبَينَ التَّوبةِ', '', 'يَحضُر في صُورَةِ مَلَكٍ فيَختَبِرَ إِيمانَ العَبدِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ممَّا أخبَر بهِ النبيُّ صلَّى الله عليه وسلَّم عن آخِرِ الزَّمانِ: أن يأتِيَ على النَّاسِ زَمانٌ لا يُبالِي المَرءُ:', '', 'بما أخذَ الْمالَ أمِن حَلالٍ أم من حِرامٍ', '', 'بما تَركَ مِن دِينِه لإِصلاحِ دُنياهُ', '', 'بأن ينَكحَ أُمَّهُ وأُختَهُ وعَمَّتَه وخالَتَهُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: (لا تَقومُ السَّاعةُ حَتَّى يُقبَضَ العِلمُ، وتَكثُرَ الزَّلازِلُ، ويَتَقارَبَ الزَّمانُ) مِن أَقوالِ أَهلِ العِلمِ في مَعنَى تَقارُبِ الزَّمانِ:', '', 'تَقارُبُ الشَّمسُ والقَمرُ لأنَّهُما آلَتَا الزَّمانِ', '', 'كَثرةُ الفِتَنِ التي تُلهِي المُؤمِن فلا يَكادُ يَشعُر بالزَّمانِ', '', 'قِلَّةُ البَركةِ في الزَّمانِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'سَكَراتُ المَوتِ تَكونُ:', '', 'للإِنسِ والجِنِّ دُونَ البَهائمِ', '', 'لجَميعِ المَخلُوقاتِ', '', 'لِبَنِي آدمَ فقط', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن خَصائِصِ الأَنبياءِ عندَ المَوتِ:', '', 'أنَّهم لا تَأخُذُهُم سَكَراتِ المَوتِ', '', 'قَبضُ الله سُبحانَه أَرواحَهُم بنَفسِه', '', 'تَخييرُهُم بَينَ البَقاءِ في الدُّنيا وبَينَ المَوتِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من عَلاماتِ السَّاعة الصُّغرَى:', '', 'إِمارَةُ النِّساءِ', '', 'تَوَقُّفُ الجِزيَةِ والخَراجِ', '', 'استِحلالُ المُسلِمينَ أَكلَ الخِنزيرِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ثَبتَ في الحَديثِ الصَّحيحِ أنَّ مِن أَشراطِ السَّاعةِ: خُروجَ الدَّجَّالِينَ أَدعِياءِ النُّبُوَّة، وأنَّ عَدَدهُم:', '', 'ثَلاثُونَ دجَّالًا', '', 'خَمسَةٌ وأَربعُونَ دجَّالًا', '', 'سَبعُونَ دجَّالًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عن رَسولِ الله صلَّى اللهُ عليه وسلَّم أنَّه قال عن أَشراطِ السَّاعةِ: (إنَّ بينَ يَدَيها فِتنةً وهرْجًا.. ويُلقَى بينَ النَّاسِ التَّناكُرُ) مَعنَى التَّناكُر:', '', 'لا يَكادُ أحَدٌ أن يَعرِفَ أحَدًا', '', 'لا يُنكِرُونَ مُنكرًا ولا يَعرِفُونَ مَعرُوفًا', '', 'يُنكِرُ الرَّجلُ وَلدَهُ، ويُنكِرُ الوَلدُ آبَاهُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَذهَبُ أَهلِ السُّنَّةِ والجَماعَةِ في الأَرواحِ أنَّها:', '', 'باقِيةٌ لا تَفنَى', '', 'فانِيَةٌ لا تَبقَى', '', 'مُعَلَّقةٌ بعدَ المَوتِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَن أبي هُرَيرةَ رَضِيَ الله عَنه، عَنِ النَّبيِّ صلَّى اللهُ عليه وسلَّم قال: لا تَذهَبُ الأيَّامُ واللَّيالي حَتَّى يَملِكَ رَجُلٌ يُقالُ لَه:', '', 'الجَهْجَاهُ', '', 'قَحْطانُ', '', 'شَاهَانْ شَاهْ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أَخبرَ الصَّادِقُ المَصدُوق صلَّى الله عليه وسلَّم أنَّه: لا تَقومُ السَّاعةُ حَتَّى تَعودَ أرضُ العَرَبِ:', '', 'للجاهِلِيَّةِ الأُولَى', '', 'فتَسُوقُها قُريشٌ بِعَصاهَا', '', 'مُروجًا وأنهارًا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى اللهُ عليه وسلَّم: (مِن أشراطِ السَّاعةِ أن يُرفَعَ العِلمُ ويَثبُتَ الجَهْلُ) رَفعُ العِلمِ معناه:', '', 'مَوتُ العُلماءِ', '', 'مَحْوُهُ مِن الصُّدورِ', '', 'نِسيانُ القُرآنِ الكَريمِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَن زينَبَ بنتِ جَحشٍ رَضِيَ الله عَنها أنَّ رَسولَ الله صلَّى اللهُ عليه وسلَّم دَخَلَ عليها يَومًا فَزِعًا يَقولُ: (لا إلَه إلَّا اللهُ، ويْلٌ للعَرَبِ من شَرٍّ قَدِ اقتَرَبَ) المَقصُودُ بالشَّرِّ هو:', '', 'الرِّدَّةُ التي حَدثَت في عَهدِ أبي بَكرٍ رَضِيَ اللهُ عنه', '', 'مقتلُ عمرَ بنِ الخطَّابِ رَضيَ اللهُ عنه', '', 'الثَّغرَةُ التي فُتِحَت مِن رَدْمِ يَأجوجَ ومَأجوجَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الواجِبُ على العَبدِ الاستِعدادُ لِلمَوتِ قَبلَ نُزولِه وذلك يكون بـ:', '', 'الإِيمانِ بالبَعثِ بعدَ المَوتِ وأنَّ الله يَبعَثُ مَن في القُبورِ', '', 'الرَّضا بالقَضاءِ والقَدَرِ', '', 'المُبادَرةِ إلى الأَعمالِ الصَّالِحةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن عَقيدةِ أَهلِ السُّنَّةِ والجَماعَةِ في الصَّحابَةِ رضي اللهُ عنهم:', '', 'اعتِقادُ أنَّهم أَفضلُ البَشرِ بعدَ النبيِّ صلَّى الله عليه وسلَّم', '', 'جَوازُ التَّوسُّلِ بهِم إلى الله عزَّ وجلَّ', '', 'وُجوبُ مَحبَّتهِم رضي اللهُ عنهم', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لَمَّا خَشِيَ اختِلافَ النَّاسِ في القُرآنِ جَمَعَ القُرآنَ عَلى رَسمٍ واحِدٍ للمُصحَفِ:', '', 'مُعاوِيَةُ بنُ أبي سُفيانَ رضي الله عنه', '', 'عُثمانُ بنُ عفَّانَ رضي الله عنه', '', 'أُبَيُّ بنُ كَعبٍ رضي الله عنه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال أبو زُرعةَ الرَّازي: إذا رَأيتَ الرَّجُلَ يَنتَقِصُ أحَدًا مِن أصحابِ رَسولِ اللهِ صلَّى اللهُ عليه وسلَّم فاعلَمْ أنَّه:', '', 'كافِرٌ', '', 'زِنْديقٌ', '', 'مُنافِقٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أثنى اللهُ تبارك وتعالى عَلى أصحابِ رَسولِ اللهِ صلَّى اللهُ عليه وسلَّم في:', '', 'القرآنِ فقط', '', 'القُرآنِ والتوراةِ فقط', '', 'القُرآنِ والتَّوراةِ والإنجيلِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من الذي قال عن أَصحابِ النبيِّ صلَّى اللهُ عليه وسلَّم: "حُبُّهم دينٌ وإيمانٌ وإحسانٌ، وبُغْضُهم كُفرٌ ونِفاقٌ وطُغيانٌ"؟', '', 'ابنُ عبدِ البَرِّ', '', 'سُفيانُ الثَّورِيُّ', '', 'الطَّحاوِيُّ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'بُويِعَ أبو بكرٍ رضي الله عنه بالخِلافَةِ مِن قِبَلِ المُهاجِرينَ والأَنصارِ في:', '', 'دارِ الأَرقَمِ بنِ أبي الأَرقَمِ', '', 'سَقيفةِ بني ساعِدةَ', '', 'مَسجِدِ النبيِّ صلَّى الله عليه وسلَّم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الرَّاجِحُ مِن أَقوالِ أَهلِ العِلمِ أنَّ أفضلَ زوجاتِ النبيِّ صلَّى اللهُ عليه وسلَّم هي:', '', 'أمُّ المُؤمِنينَ سَودَةُ بِنتُ زَمعَةَ رَضِيَ الله عنها', '', 'أمُّ المُؤمِنينَ خَديجَةَ رَضِيَ الله عنها', '', 'أمُّ المُؤمِنينَ عائشةُ رَضِيَ الله عنها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن فَضائِلِ أهلِ البَيتِ التي ذُكِرَت في القُرآنِ الكريمِ:', '', 'اصطِفاءُ الله لهُم بالوَحْيِ والرِّسالَةِ دُونَ غَيرِهِم', '', 'تَطهِيرُهُم مِن الرِّجْسِ', '', 'تَفضِيلُهم بنِصفِ الفَيْءِ مِن الغَنائمِ خصيصةً لهُم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن فَضائِلِ أزواجِ النَّبِيِّ صلَّى اللهُ عليه وسلَّم:', '', 'حَملُهُنَّ شَرفَ النُّبُوَّةِ بعدَ مَوتِ النبيِّ صلَّى الله عليه وسلَّم', '', 'أنَّهُنَّ خيرُ نِساءِ العالِمينَ', '', 'مُضاعَفةُ الأجرِ لهُنَّ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من الذي استَخلَفهُ رَسولُ الله صلَّى الله عليه وسلَّم على المَدينَةِ حين خَرجَ لغَزوَةِ تَبُوكَ؟', '', 'عليُّ بنُ أبي طالِبٍ رضي اللهُ عنه', '', 'أبو هُريرةَ رضي اللهُ عنه', '', 'ابنُ أُمِّ مَكتُومٍ رضي اللهُ عنه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من هي المْرأَةُ التي بَشرَّها النبيُّ صلَّى الله عليه وسلَّم بالجنَّة؟', '', 'أُمُّ شَريكٍ العامِريَّة رَضِيَ اللهُ عنها', '', 'أُمِّ عَطِيَّةَ نُسَيْبَةُ بنتُ كَعبٍ الأنصارِيَّةُ رَضِيَ اللهُ عنها', '', 'أُمُّ سُلَيم بِنتُ مِلحانَ رَضِيَ اللهُ عنها', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِنَ الآياتِ القُرآنيَّةِ في بَيانِ فَضلِ الصَّحابةِ رَضِيَ اللهُ عنهم؛ قَولُ الله تعالى:', '', '{النَّبِيُّ أَوْلَى بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ وَأَزْوَاجُهُ أُمَّهَاتُهُمْ وَأُولُو الْأَرْحَامِ بَعْضُهُمْ أَوْلَى بِبَعْضٍ فِي كِتَابِ اللَّهِ}', '', '{مُحَمَّدٌ رَسُولُ اللَّهِ وَالَّذِينَ مَعَهُ أَشِدَّاءُ عَلَى الْكُفَّارِ رُحَمَاءُ بَيْنَهُمْ}', '', '{وَكَأَيِّنْ مِنْ نَبِيٍّ قَاتَلَ مَعَهُ رِبِّيُّونَ كَثِيرٌ فَمَا وَهَنُوا لِمَا أَصَابَهُمْ فِي سَبِيلِ اللَّهِ وَمَا ضَعُفُوا وَمَا اسْتَكَانُوا وَاللَّهُ يُحِبُّ الصَّابِرِينَ}', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عرَّفَ ابنُ حَجَرَ الصَّحابِيَّ بأنَّه:', '', 'مَنِ اجتَمَعَ بالنَّبيِّ صلَّى اللهُ عليه وسلَّم مُؤْمِنًا ولَو لحظةً، حتَّى لو ارتدَّ بعدَ ذلك', '', 'مَن لَقِيَ النَّبيَّ صلَّى اللهُ عليه وسلَّم مُؤْمِنًا به، ومات عَلى الإسلامِ', '', 'كُلُّ مَن رَأى رَسولَ اللهِ صلَّى اللهُ عليه وسلَّم وقَد أدرَكَ الحُلُمَ، فأسلَمَ وعَقَلَ أمرَ الدِّينِ ورَضِيَه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لَمَّا نَزَلَ قولُه تعالى: {فَقُلْ تَعَالَوا نَدْعُ أَبْنَاءَنَا وَأَبْنَاءَكُمْ} دَعاهم رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم وقال: (اللَّهُمَّ هَؤُلاءِ أهلي):', '', 'عَلِيٌّ وفاطِمةُ وحَسَنٌ وحُسَينٌ', '', 'أبو بكرٍ وعُمرُ', '', 'حَمزَةُ والعبَّاسُ وجَعفرٌ وطَلحَةُ والزُّبيرُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'سُئِلَتْ عائِشةُ رضي الله عنها: مَن كانَ رَسولُ الله صلَّى اللهُ عليه وسلَّم مُستَخلِفًا لوِ استَخلَفَ؟ قالت: أبو بَكْرٍ، فقيلَ لها: ثُمَّ مَن؟ قالت: عُمَرُ، فقيلَ لها: من بَعْدَ عُمَرَ؟ فقالت:', '', 'عبدُالرحمنِ بنُ عَوفٍ رضي اللهُ عنه', '', 'أبو عُبَيدةَ بنُ الجَرَّاحِ رضي اللهُ عنه', '', 'سَعدُ بنُ مُعاذٍ رضي اللهُ عنه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كانَ رَضِيَ اللهُ عنه مِمَّن استِجابَ لله والرَّسولِ من بَعدِ ما أصابَهمُ القَرحُ يَومَ أُحُدٍ:', '', 'الزُّبيرُ بنُ العَوَّامِ رضي الله عنه', '', 'أَنسُ بنُ مَالكٍ رضي الله عنه', '', 'حَسَّانُ بنُ ثابِتٍ رضي الله عنه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لم يَستَخلِف عُمرُ بنُ الخطَّابِ رضي الله عنه أَحدًا بَعدَه صَراحَةً؛ لكنَّه جَعلَ الأَمرَ شُورَى بينَ سِتَّة نَفرٍ؛ وهُم:', '', 'خالدٌ، والزُّبَيرُ، وطَلحَةُ، وأبو عُبيدَةَ، وعُثمانُ، وسَعيدُ بنُ زَيدٍ، رضي الله عنهم أجمعين', '', 'عليٌّ، وعُثمانُ، والزُّبَيرُ، وطَلحةُ، وسَعدُ بنُ أبي وقَّاصٍ، وعبدُ الرحمنِ بنُ عَوفٍ، رضي اللهُ عنهم أجمعين', '', 'عليٌّ، ومُعاوِيَةُ، والزُّبَيرُ، وطَلحَةُ، وجَعفرٌ، وسَعدُ بنُ عُبادَة، رضي الله عنهم أجمعين', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن عَقيدةِ أهلِ السُّنَّةِ فيما  شَجَرَ بينَ الصَّحابةِ رَضِيَ اللهُ عنهمُ:', '', 'التَّأوِيلُ بأنَّ كلَّ فِرقَةٍ كانت على الحَقِّ', '', 'القَولُ بعِصمَتِهِم رَضِيَ اللهُ عنهمُ، وأنَّهم مَغفُورٌ لهُم بصَريحِ آياتِ كِتابِ الله عزَّ وجلَّ', '', 'الإمساكُ عَمَّا شَجرَ بَينهُم رَضِيَ اللهُ عنهمُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'نزل فيه قولُه تعالى: {وَإِنْ جَاهَدَاكَ عَلَى أَنْ تُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفًا}:', '', 'سَعدُ بنُ أبي وقَّاصٍ رضي اللهُ عنه', '', 'أبو هُريرةَ رضي اللهُ عنه', '', 'طَلحةُ بنُ عُبيدِالله رضي اللهُ عنه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن هو الذي رآهُ النبيُّ صلَّى الله عليه وسلَّم في المنامِ وعليه قَميصٌ يَجُرُّهُ؟', '', 'عبدُالله بن عَمرٍو رضي الله عنه', '', 'الزُّبيرُ بن العَوَّامِ رضي الله عنه', '', 'عُمرُ بنُ الخطَّابِ رضي الله عنه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عن أبي سَعيدٍ الخُدْريِّ رَضِيَ اللهُ عنه قال: قال رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم: والذي نفسي بيَدِه، لا يُبغِضُنا أهْلَ البَيتِ رجلٌ إلَّا:', '', 'أدخَلَه اللهُ النَّارَ', '', 'أَبغَضَهُ اللهُ', '', 'مَحرُومٌ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال زيد بن أرقم رضي الله عنه : إن آل بيت النبي صلى الله عليه وسلم هم:', '', 'بَنُو هاشِمٍ، وبَنُو المُطَّلِبِ، وبَنُو حَمزَةَ', '', 'أَخوالُ رَسولِ الله صلَّى الله عليه وسلَّم وأَعمامُه', '', 'آلُ عليٍّ، وآلُ عَقيلٍ، وآلُ جَعفرٍ، وآلُ عبَّاسٍ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'شَهِدَ النبيُّ صلَّى الله عليه وسلَّم لأَصحابِ الشَّجرَةِ الذين بايَعُوا تحتَ الشَّجرَةِ بـ:', '', 'أنَّ الله قد غَفَرَ لهُم ما تَقدَّم مِن ذُنوبِهِم', '', 'أن لا أَحَدَ مِنهُم يَدخُلُ النَّارَ إن شاءَ اللهُ', '', 'أنَّ مَنزِلَتَهُم في الجنَّةِ في السَّماءِ السَّادِسَةِ مع نَبِيِّ الله مُوسى عليه السلامُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِ الله تعالى: {وَاذْكُرْنَ مَا يُتْلَى فِي بُيُوتِكُنَّ مِنْ آيَاتِ اللَّهِ وَالْحِكْمَةِ} فَضِيلَةٌ لزَوجاتِ النبيِّ صلَّى الله عليه وسلَّم؛ وهي:', '', 'تَكلِيفهُنَّ بتَبليغِ الوَحيِ', '', 'تِلاوَتهُنَّ كِتابَ الله عزَّ وجلَّ وسُنَّةَ رَسولِه صلَّى الله عليه وسلَّم', '', 'الاصطِفاءُ الإلهي لهُنَّ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قوله تعالى: {وَالَّذِينَ جَاؤُوا مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ} قال القُرطُبيُّ: هَذِه الآيةُ دَليلٌ عَلى:', '', 'استِحبابِ الاستِغفارِ للمُؤمِنينَ والمُؤمِناتِ', '', 'وُجوبِ مَحَبَّةِ الصَّحابةِ', '', 'أنَّ الوَلاءَ والبَراءَ يكونُ لأُخُوَّةِ الدِّينِ فقط دونَ النَّسبِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن الأَحادِيثِ التي كانت فيها إِشارَةٌ إلى صِحَّةِ إمامةِ عَلِيٍّ رضي الله عنه؛ قولُه صلَّى اللهُ عليه وسلَّم:', '', 'خيرُ النَّاسِ قَرنِي، ثُم الذين يَلُونَهُم، ثُم الذين يَلُونَهُم، ثُم الذين يَلُونَهُم', '', 'لَأُعْطِيَنَّ هذِه الرَّايَةَ غَدًا رَجُلًا يَفْتَحُ اللهُ علَى يَدَيْهِ، يُحِبُّ اللَّهَ ورَسولَه، ويُحِبُّهُ اللهُ ورَسولُهُ', '', 'وَيْحَ عَمَّارٍ! تقتُلُه الفِئةُ الباغيةُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن فَضائلِ خَديجةَ رضي اللهُ عنها:', '', 'أنَّها دُفِنَت مع النبيِّ صلَّى الله عليه وسلَّم في قَبرٍ واحِدٍ', '', 'أنَّ النَّبِيَّ صلَّى اللهُ عليه وسلَّم لم يتزوَّجِ  عليها حتَّى ماتَت', '', 'أنَّ الوَحيَ لم يَنزِل على رَسولِ الله صلَّى الله عليه وسلَّم في لِحافِ امرأَةٍ غيرها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن فَضائِله: أنَّه وَقَى النبيَّ صلَّى الله عليه وسلَّم يومَ أُحُدٍ مِن ضَربَةِ سَيفٍ فشُلَّت يَدُه رضي اللهُ عنه:', '', 'الزُّبيرُ بنُ العوَّامِ رضي الله عنه', '', 'طَلحةُ بنُ عُبَيدِ الله رضي اللهُ عنه', '', 'عبدُالرَّحمنِ بنُ عَوفٍ رضي الله عنه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِه تعالى: {وَلَا يَأْتَلِ أُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبَى} أجمَعَ المُفَسِّرونَ عَلى أنَّ المُرادَ مِن قَولِه: "أُولُو الفَضْلِ" هو:', '', 'أبو بكرٍ رضي اللهُ عنه', '', 'عَبدُالرحمنِ بنُ عَوفٍ رضي الله عنه', '', 'زَيدُ بنُ حارِثَة رضي الله عنه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'هل تَدخُلُ زَوجاتُ النَّبِيِّ صلَّى اللهُ عليه وسلَّم في آلِ البَيتِ؟', '', 'يَدخُلُ مِنهُنَّ فقط مَن كانت مِن بَنِي هاشِمٍ أو بَنِي المُطَّلِبِ', '', 'لا يَدخُلنَ', '', 'نعم؛ يَدخُلنَ كُلُّهُنَّ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قالت أُمُّ المُؤمِنينَ عائِشةَ رضي الله عنها: ما رَأَيْتُ امْرَأَةً أَحَبَّ إلَيَّ أَنْ أَكُونَ في مِسْلَاخِهَا مِن:', '', 'سَودةَ بِنتِ زَمعَةَ', '', 'أُمِّ سَلمَة', '', 'مَيمُونَة بِنتِ الحارِثِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من الذي دعا له النَّبِيُ صلَّى اللهُ عليه وسلَّم بالعِلمِ والحِكمةِ والفِقهِ في الدِّينِ؟', '', 'عبدُ الله بنُ عُمرَ رضي اللهُ عنه', '', 'عبدُ الله بنُ عبَّاسٍ رضي اللهُ عنه', '', 'عبدُ الله بنُ عَمرٍو رضي اللهُ عنه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'نَزلَ قَولُ الله تعالى {رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ وَأَعَدَّ لَهُمْ جَنَّاتٍ تَجْرِي تَحْتَهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ذَلِكَ الْفَوْزُ الْعَظِيمُ} في:', '', 'السَّابِقينَ الأوَّلينَ مِن المُهاجِرينَ والأنصارِ', '', 'مَن آمَنَ بالنبيِّ صلَّى الله عليه وسلَّم مِن أَهلِ الكِتابِ', '', 'أَهلِ الصُّفَّة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن فَضائِلِهم أنَّهم أوَّلُ بَيتٍ هاجَرَ إلى رَسولِ الله صلَّى الله عليه وسلَّم:', '', 'آلُ ياسِرٍ رضي اللهُ عنهُم', '', 'أبو سَلمَة وأُمُّ سَلمَة رضي اللهُ عنهم', '', 'بَنُو عَقيلٍ رضي اللهُ عنهُم', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أَعْظَمِ اللَّوازِمِ التي تترتَّبُ على السَّبِّ والطَّعنِ في صحابةِ رَسولِ الله صلَّى اللهُ عليه وسلَّم:', '', 'الطَّعنُ في آلِ بَيتِ رَسولِ الله صلَّى الله عليه وسلَّم', '', 'تَركُ الدُّعاءِ والاستِغفارِ لهُم كما جاء في القُرآنِ', '', 'الشَّكُّ في القُرآنِ الكَريمِ والأحاديثِ النَّبَويَّةِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أَدِلَّةِ السُّنَّةِ النَّبوِيَّةِ على فَضلِ الصَّحابَةِ رَضِيَ الله عنهُم؛ قَولُه صلَّى الله عليه وسلَّم:', '', 'إنَّ للهِ عِبادًا يُجلِسُهم يَومَ القِيامةِ على مَنابِرَ من نورٍ ، يَغشَى وُجوهَهُمُ النُّورُ', '', 'يُدْعَوْنَ يَومَ القِيامَةِ غُرًّا مُحَجَّلِينَ مِن آثارِ الوُضُوءِ', '', 'إنَّ خَيرَكم قَرني، ثُمَّ الَّذينَ يَلُونَهم، ثُمَّ الَّذينَ يَلُونَهم، ثُمَّ الَّذينَ يَلُونَهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لمَّا تُوفِّيَ رَسولُ الله صلَّى الله عليه وسلَّم وخافَ أبو بكرٍ مِن اختِلافِ المُهاجِرينَ والأَنصارِ في أَمرِ الخِلافَةِ رَشَّحَ لهم أبو بكرٍ رضي الله عنه رَجُلَينِ للخِلافَةِ؛ وهُما:', '', 'عُثمانُ بنُ عفَّانَ والزُّبَيرُ بنُ العَوَّامِ رضي الله عنهُما', '', 'عبدُالرحمنِ بنُ عَوفٍ وسَعدُ بنُ عُبادَة رضي الله عنهُما', '', 'عُمرُ بنُ الخطَّابِ وأبو عُبيدَةَ بنُ الجَرَّاحِ رضي اللهُ عنهُما', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من الذي صلَّى خَلفَهُ النبيُّ صلَّى اللهُ عليه وسلَّم مَأمُومًا؟', '', 'أُبَيُّ بنُ كَعبٍ رضي اللهُ عنه', '', 'عبدُالرحمنِ بنُ عَوفٍ رضي اللهُ عنه', '', 'عبدُالله بنُ مَسعُودٍ رضي اللهُ عنه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن فَضائلِ عائِشةَ رضي اللهُ عنها:', '', 'أنَّها أوَّلُ مَن آمَنَت بالنبيِّ صلَّى الله عليه وسلَّم مِن النِّساءِ', '', 'أنَّها رَأَت جِبريلَ عليه السَّلامُ في صُورَتِه الحَقيقِيَّة مَرَّتينِ', '', 'أنَّ الوَحيَ لم يَنزِل على رَسولِ الله صلَّى الله عليه وسلَّم في لِحافِ امرأَةٍ غيرها', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من القائل: (كُلُّ مَن صَحِبَه سَنةً أو شَهرًا أو يَومًا أو ساعةً ورَآهُ فهوَ مِن أصحابِه)؟', '', 'أبو حَنِيفَةَ النُّعمان', '', 'مَالِكُ بنُ أَنَسٍ', '', 'أحمَدُ بنُ حَنبَلٍ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أَدِلَّةِ القُرآنِ الكَريمِ على عَدالَةِ الصَّحابَةِ رضي الله عنهم؛ قولُ الله تعالى:', '', 'إِنَّ الَّذِينَ قَالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقَامُوا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ * أُولَئِكَ أَصْحَابُ الْجَنَّةِ خَالِدِينَ فِيهَا جَزَاءً بِمَا كَانُوا يَعْمَلُونَ', '', 'وَإِذَا سَمِعُوا مَا أُنْزِلَ إِلَى الرَّسُولِ تَرَى أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ يَقُولُونَ رَبَّنَا آمَنَّا فَاكْتُبْنَا مَعَ الشَّاهِدِينَ', '', 'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ أُوْلَئِكَ يَرْجُونَ رَحْمَتَ اللَّهِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'الصَّحابةُ عَدَدٌ كثيرٌ، ولا يُمكِنُ الجَزمُ بحَصْرِهِم عَلى وَجهِ التَّحديدِ، لكِنْ قيلَ عَلى وَجهِ التَّقريبِ: أنَّهم يَبلُغونَ:', '', 'مِئَةً وخَمسِينَ ألفًا', '', 'مِئةً وأربَعةَ عَشَرَ ألفًا', '', 'مِئةَ أَلفٍ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'وزيرَا رَسولِ الله صلَّى اللهُ عليه وسلَّم هُما:', '', 'مُعاذُ بنُ جَبلٍ وأبو مُوسى الأَشعريُّ رَضِيَ اللهُ عنهُما', '', 'أبو بكرٍ وعُمرُ رَضِيَ اللهُ عنهُما', '', 'عليُّ بنُ أبي طالِبٍ وعبدُالله بنُ عبَّاسٍ رَضِيَ اللهُ عنهُما', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من التي قال فيها رَسولُ الله صلَّى الله عليه وسلَّم: (هي أفضَلُ بَناتي؛ أُصِيبَت فِيَّ)؟', '', 'أُمُّ كُلثُوم رضي اللهُ عنها', '', 'رُقَيَّةُ رضي اللهُ عنها', '', 'زَينبُ رضي اللهُ عنها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من التي قال لها النبيُّ صلَّى الله عليه وسلَّم: (هذا جِبريلُ يُقرِئُكِ السَّلامَ)؟', '', 'عائِشةُ بِنتُ أبي بَكرٍ رضي اللهُ عنهما', '', 'جُويرِيةُ بِنتُ الحارِثِ رضي اللهُ عنها', '', 'صَفِيَّةُ بِنتُ حُيَيٍّ رضي اللهُ عنها', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أَسلَمَ سَعدُ بنُ أبي وقَّاصٍ رضي الله عنه ومَكَثَ أُسبُوعًا وإنَّه:', '', 'خُمسُ الإِسلامِ', '', 'ثُلثُ الإِسلامِ', '', 'رُبعُ الإِسلامِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كانَ من أشهَرِ كُتَّابِ النَّبيِّ صلَّى اللهُ عليه وسلَّم، وهوَ الَّذي كَتَبَ صُلْحَ الحُدَيبيَةِ:', '', 'عليُّ بنُ أبي طالِبٍ رضي اللهُ عنه', '', 'جَعفرُ بنُ أبي طالِبِ رضي اللهُ عنه', '', 'زَيدُ بنُ ثابتٍ رضي اللهُ عنه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كانت خِلافَةُ عُمرَ رضي الله عنه بـ:', '', 'اجتِماعِ أَهلِ بَدرٍ عليه وتَولِيَتهِم له رضي اللهُ عنهم أَجمَعِينَ', '', 'باستِخلافِ أبي بكرٍ له رضي اللهُ عنهُما', '', 'تَرشِيحِ عبدِالرحمنِ بنِ عَوفٍ له للخِلافَةِ ومُبايعتُه له على المَلأِ بعدَ صَلاةِ الجُمعَةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'لَمَّا استُشهِدَ عَلِيٌّ رَضِيَ اللهُ عنه، بُويِعَ بالخِلافةِ من بَعْدِه:', '', 'الزُّبَيرُ بنُ العَوَّامِ رضي اللهُ عنه', '', 'ابنُه الحَسَنُ رضي اللهُ عنه', '', 'طَلحَةُ بنُ عُبيدِ الله رضي اللهُ عنه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كانت أَكثرَ زَوجاتِ النبيِّ صلَّى الله عليه وسلَّم صَدَقَةً:', '', 'عائِشةُ بِنتُ أبي بَكرٍ رضي اللهُ عنها', '', 'مَيمُونَة بِنتِ الحارِثِ رضي اللهُ عنها', '', 'زَينبُ بِنتُ جَحشٍ رضي اللهُ عنها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من الذي قال له النبيُّ صلَّى اللهُ عليه وسلم: (أشبَهْتَ خَلْقِي وخُلُقِي)؟', '', 'المِسْوَرُ بنُ مَخرمَةَ رضي اللهُ عنه', '', 'دَحْيَةُ الكَلبيُّ رضي اللهُ عنه', '', 'جَعفَرُ بنُ أبي طالِبٍ رضي اللهُ عنه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من المقصودُ بقَولِ النبيِّ صلَّى اللهُ عليه وسلَّم: (إنَّ ابني هذا سَيِّدٌ، ولعَلَّ اللهَ أن يُصلِحَ به بين فئتينِ عَظيمتينِ مِنَ المُسلِمين)؟', '', 'الحَسَنُ بن عليٍّ رضي اللهُ عنهما', '', 'إبراهيمُ بنُ النبيِّ صلَّى اللهُ عليه وسلَّم', '', 'الحُسَينُ بنُ عليٍّ رضي اللهُ عنهما', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'كانت مِن أَعظَمِ النِّساءِ بَرَكةً على قَومِها؛ حيث أُعتِقَ بسَبَبِها مائةُ أهلِ بَيتٍ من الأَسْرِ:', '', 'زَينبُ بِنتُ جَحشٍ رضي اللهُ عنها', '', 'أُمُّ حَبِيبَة بِنتُ أبي سُفيانَ رضي اللهُ عنها', '', 'جُوَيريَةُ بِنتُ الحارِثِ رضي اللهُ عنها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى الله عليه وسلَّم فيهِم: (لا يُحِبُّهم إلَّا مُؤْمِنٌ، ولا يُبغِضُهم إلَّا مُنافِقٌ):', '', 'ألُ بَيتِ النبيِّ صلَّى الله عليه وسلَّم', '', 'المُهاجِرُونَ', '', 'الأنصارُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم: (إنَّكم تَلْقَونَ بَعْدِي فِتنةً واختِلافًا) فقال قائِلٌ مِنَ النَّاسِ: فمَن لنا يا رَسولَ اللَّهِ؟ قال: (عليكم بالأمينِ وأصحابِه) وهوَ يُشيرُ إلى:', '', 'عُثمانَ بنِ عفَّانَ رضي اللهُ عنه', '', 'أبي عُبيدَةَ بنِ الجَرَّاحِ رضي اللهُ عنه', '', 'عبدِالله بنِ مَسعُودٍ رضي اللهُ عنه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عَرَّفَ الغَزالِيُّ العَدالةَ بأنَّها:', '', 'استِقامةُ السِّيرةِ والدِّينِ', '', 'أَداءُ الفَرائضِ والتَّجافِي عن المُحرَّماتِ', '', 'فِعلُ الأَوامِرِ وتَركُ النَّواهِي', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن الذين بَشَّرَهُم النبيُّ صلَّى الله عليه وسلَّم بالجنَّةِ غير العَشرَةِ المُبشَّرِين:', '', 'سُراقَةُ بنُ مالِكٍ رَضِيَ اللهُ عنه', '', 'تَمِيمٌ الدَّارِيُّ رَضِيَ اللهُ عنه', '', 'عُكَّاشةُ بنُ مِحصَنٍ الأسْديُّ رَضِيَ اللهُ عنه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يقولُ صلَّى اللهُ عليه وسلَّم: (بَينا أنا نائِمٌ إذ رَأيتُ قَدَحًا أُتِيتُ به فيه لبَنٌ، فشَرِبتُ مِنهُ حَتَّى إنِّي لأرى الرِّيَّ يَجري في أظفاري، ثُمَّ أعطيتُ فَضْلي عُمَرَ بنَ الخَطَّابِ) قالوا: فما أوَّلتَ ذلك يا رَسولَ اللَّهِ؟ فقال:', '', 'الوّحيُ', '', 'العِلمُ', '', 'الدِّينُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من أَوَّل مَن أَعْلَنَ مبايَعَةَ عُثمانَ بنِ عفَّانَ رضي اللهُ عنه على الخِلافَةِ؟', '', 'عليُّ بنُ أبي طالِبٍ رضي اللهُ عنه', '', 'عبدُالرحمنِ بنُ عَوفٍ رضي اللهُ عنه', '', 'سَعيدُ بنُ زَيدٍ رضي اللهُ عنه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يُستَدلُّ بقَولِ الله تعالى: {وَالَّذِينَ جَاؤُوا من بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ} على:', '', 'الأمرِ بالدُّعاءِ والِاستِغفارِ لِأصحابِ رَسولِ اللهِ صلَّى اللهُ عليه وسلَّم', '', 'عِصمَةِ الصَّحابَةِ رضي اللهُ عنهم', '', 'فَضلِ أَهلِ بَدرٍ رِضوانِ الله عليهِم جَميعًا وسَبقِهِم في نصرة رَسولِ الله صلَّى اللهُ عليه وسلَّم', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال العِرباضِ بنِ سارِيةَ: صَلَّى بنا رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم ذاتَ يَومٍ ثُمَّ أقبَلَ عَلينا فوعَظَنا.. فقال قائِلٌ: يا رسولَ اللَّهِ، كأنَّ هذه مَوعِظةُ مُودِّعٍ فماذا تَعهَدُ إلينا؟ فقال أُوصيكُم:', '', 'بتَقوَى الله والسَّمعِ والطَّاعةِ', '', 'بإِقامِ الصَّلاةِ، وإِيتاءِ الزَّكاةِ والرِّضا بقَضاءِ الله', '', 'بالجَماعَةِ؛ فمَن شَذَّ عن الجَماعَةِ شَذَّ في النَّارِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من خُطورةِ البِدعةِ وآثارِها السَّيِّئةِ:', '', 'أنَّ صاحِبَها مَلْعونٌ على لسانِ الشَّريعةِ', '', 'أنَّ أَصلَ البِدَعِ في الدِّينِ هُم النَّصارَى فمَن تَشَبَّه بهِم فهو مِثلهُم', '', 'أنَّها تَعدِلُ الكُفرَ بالله عزَّ وجلَّ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن القائِلُ: (الاقتِصادُ في السُّنَّةِ أحسَنُ من الاجتِهادِ في البِدْعةِ)؟', '', 'عبدُالله بنُ مَسعودٍ رَضيَ اللهُ عنه', '', 'أبو إِسحاقَ الشاطِبيُّ', '', 'مالِكُ بنُ أَنسٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من أدِلَّةِ القُرآنِ الكَريمِ على ذَمِّ البِدَعِ: قولُ الله تعالى:', '', '{قُلْ مَا كُنْتُ بِدْعًا مِنَ الرُّسُلِ}', '', '{وَأَنَّ هَذَا صِرَاطِي مُسْتَقِيمًا فَاتَّبِعُوهُ وَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيلِهِ}', '', '{وَلَا تَقْعُدُوا بِكُلِّ صِرَاطٍ تُوعِدُونَ}', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُ النبيِّ صلَّى اللهُ عليه وسلَّم لحُذيفةَ بن اليَمانِ: (تَلزَمُ جَماعةَ المُسلِمينَ وإمامَهم) قال الطِّيبيُّ: المُرادُ بالجَماعةِ:', '', 'جَماعةُ الفُقهاءِ وعُلماءُ المُسلِمينَ', '', 'الصَّحابةُ ومن بَعدَهم من التابِعينَ وتابِعيِ التابِعينَ', '', 'السَّوادُ الأعظَمُ مَعَ الإمامِ الجامِعِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُه تعالى: {أَقِيمُوا الدِّينَ وَلَا تَتَفَرَّقُوا فِيهِ} قال الشَّوكانيُّ: لَمَّا أمرَهم سُبحانَهُ بإقامةِ الدِّين، نَهاهُم عن:', '', 'الاختِلافِ فيه', '', 'ضَلالاتِ البِدَعِ', '', 'ما يَمنَعُهُم مِن إِقامَةِ ذلك الدِّينِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'البِدْعَةُ في اللُّغَةِ هي:', '', 'الخُرُوجُ عن أَصلِ الطَّريقِ', '', 'السَّبْقُ والتَّفَرُّدُ', '', 'كُلُّ ما اختُرِعَ على غيرِ مِثالٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال ابنُ حَجَرٍ: المُحْدَثاتُ جَمعُ مُحْدَثةٍ، والمُرادُ بها:', '', 'ما أُحدِثُ ولَيسَ لَهُ أصلٌ في الشَّرعِ', '', 'الزِّيادَةُ أو النَّقصُ في شَعائرِ الإسلامِ الظَّاهِرَة', '', 'كلُّ بِدعَةٍ تَعلَّقت بعِبادَةٍ مِن العِباداتِ أو بعادَةٍ مِن العاداتِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'العِبادةُ لا تَكونُ عَمَلًا صالِحًا إلَّا إذا تَحَقَّقَ فيها شَرطانِ:', '', 'خُلوصُها مِن الشِّركِ والرِّياءِ', '', 'النِّيَّةُ والطَّهارَة', '', 'الإخلاصُ والمُتابَعة', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المُلاحَظِ في القُرآنِ الكَريمِ أنَّ الآياتِ الَّتي جاءَت في ذَمِّ الفُرقةِ أكثَرُ عَدَدًا من الآياتِ الَّتي جاءَت في الحَثِّ على الجَماعةِ؛ وذلك:', '', 'لأنَّ شَرَّ الفُرقَةِ يُذهِبُ بخَيرِ الجَماعَة', '', 'لأنَّ الجَماعةَ هيَ الأصلُ والفُرقةُ أمرٌ حادِثٌ', '', 'أنَّ القُرآنَ نَزلَ والعَربُ تَسودُهُم الفُرقَةُ والجاهِليَّة فَراعَى حَالَهُم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من أهمِّ الأسبابِ الدَّاعيةِ إلى الاجتِماعِ ونَبِذِ التفَرُّقِ والاختِلافِ:', '', 'العَملُ بالسَّمعِ والطَّاعةِ؛ مع النُّصحِ لأئِمَّةِ المُسلمِينَ وعامَّتِهِم', '', 'تَحقِيقُ مَراتِبِ العُبودِيَّة: الإِسلام والإِيمان والإِحسان', '', 'القيامُ بالأمرِ بالمَعروفِ والنَّهيِ عنِ المُنكَرِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُه صلَّى اللهُ عليه وسلَّم: (ألا إنِّي أوتيتُ القُرآنَ ومِثلَه مَعَهُ)  يَعني:', '', 'التَّورَاةَ والإنجِيلَ', '', 'السُّنَّةَ', '', 'كلامَ رَبِّ العالِمينَ في غيرِ القُرآنِ (الأحادِيث القُدسِيَّة)', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أدِلَّةِ القُرآنِ الكَريمِ على لُزومِ الجَماعةِ: قولُ الله تعالى:', '', '{وَاعْتَصِمُوا بِحَبْلِ اللَّهِ جَمِيعًا وَلَا تَفَرَّقُوا}', '', '{ لَوْ أَنْفَقْتَ مَا فِي الْأَرْضِ جَمِيعًا مَا أَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَلَكِنَّ اللَّهَ أَلَّفَ بَيْنَهُمْ}', '', '{انْفِرُوا جَمِيعًا}', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'يقول النبي صلَّى اللهُ عليه وسلَّم: عليكم بالجَماعةِ وإيَّاكُم والفُرقةَ؛ فإنَّ:', '', 'يَدَ الله مع الجَماعَة', '', 'الشَّيطانَ مَعَ الواحِدِ، وهو من الاثنين أبعَدُ', '', 'الفُرقَةَ مِن الشَّيطانِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُه صلَّى اللهُ عليه وسلَّم: (من أحدَثَ في أمرِنا هذا ما لَيسَ منه فهو رَدٌّ) معناه أي:', '', 'مَن تَكلَّمَ في كِتابِ الله بغير عِلمٍ فقد ارتَدَّ عن مِلَّةِ الإسلامِ', '', 'مَن غَلَبَهُ الحَدَثُ فصَلاتُه مَردُودَةٌ عليه حتَّى يَتوضَّأَ', '', 'من اختَرَعَ في الشَّرعِ ما لا يَشهَدُ لَهُ أصلٌ من أصولِه فهو مَفسوخٌ لا يُعمَلُ به', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أوصى رَسولُ الله صلَّى اللهُ عليه وسلَّم مَن رَأى مِن أميرِه شيئًا يَكرَههُ:', '', 'أن يَصبِر', '', 'أن يَنصَحَهُ في غَيرِ مَلأٍ', '', 'أنْ يَدعُوَ له بالسَّدادِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال النَّوَويُّ: أمَّا قَولُه صلَّى اللهُ عليه وسلَّم: (والتارِكُ لدينِه المُفارِقُ للجَماعةِ) قال العُلماءُ: يَتَناوَلُ أيضًا كُلَّ خارِجٍ عنِ الجَماعةِ بـ:', '', 'بحَقٍّ أو بغيرِ حَقٍّ', '', 'بكَلِمَةٍ أو بِسَيْفٍ', '', 'ببِدعةٍ أو بَغْيٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُ الله سُبحانَه: {وَلَا تُطِعْ مَنْ أَغْفَلْنَا قَلْبَهُ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوَاهُ} جَعَلَ الأمرَ مَحصورًا بَينَ أمرَينِ:', '', 'غَفلَةِ القَلبِ واتِّباعِ الهَوَى', '', 'السَّمعِ والطَّاعَةِ', '', 'اتِّباعِ الذِّكْرِ واتِّباعِ الهوى', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال أحمَدُ بن أبي الحَواريِّ: من عَمِل عَمَلًا بلا اتِّباعِ سُنَّةِ رَسولِ الله صلَّى اللهُ عليه وسلَّم:', '', 'جاءَ يومَ القِيامَةِ وصَحِيفَتُه صِفرًا', '', 'فباطِلٌ عَمَلُه', '', 'فقد شَابَهَ رُهبانَ النَّصارَى', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن الأَمثِلَةِ التي ضَربَها الشَّاطِبيُّ للبِدَع في وَضعِ الحُدودِ:', '', 'قِيامُ لَيلَةِ النِّصفِ مِن شَعبانَ', '', 'اتِّخاذُ يَومِ وِلادةِ النَّبيِّ صلَّى اللهُ عليه وسلَّم عيدًا', '', 'نَذْرُ الصِّيامِ قائِمًا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المَعروفُ هو كُلُّ ما عَرَفَهُ الشَّرعُ وأقَرَّه، والمُنكَرُ هو:', '', 'كُلُّ ما أنكَرَه الشَّرعُ وحَرَّمَه', '', 'ما كان في العُرفِ غير مَعرُوفٍ واشتَهَرَ بين الناس نَكارَتُهُ', '', 'كلُّ ما عَظُمَ قُبحُهُ مِن الذُّنوبِ والآثامِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ما يَجري عليه حُكمُ المَكروه مِن البِدَعِ لا يَعني به كراهيَّةَ:', '', 'التَّحذِيرِ', '', 'التَّنزِيهِ', '', 'التَّحريمِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى الله عليه وسلَّم: (عليكم بِسُنَّتي وسُنَّةِ الخُلفاءِ المَهْديِّين الرَّاشِدين، تَمَسَّكوا بها وعَضُّوا عليها بالنَّواجِذِ) العَضُّ كِنايةٌ عن:', '', 'الاجتِهادِ في الدَّعوَةِ إلى السُّنَّةِ ونَشْرِها بين النَّاسِ', '', 'شِدَّةِ مُلازَمةِ السُّنَّةِ والتمَسُّكِ بها', '', 'حِفظِ السُّنَّةِ  وتَدوِينِها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قول الله تعالى: {وَأَنَّ هَذَا صِرَاطِي مُسْتَقِيمًا فَاتَّبِعُوهُ} قال الشَّاطِبيُّ: الصِّراطُ المُستَقيمُ هو سَبيلُ الله الَّذي دَعا إليه، وهو:', '', 'سَبِيلُ: لا إلهَ إلَّا الله', '', 'الإِسلامُ', '', 'السُّنَّةُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'حَثَّ رَسولُ الله صلَّى الله عليه وسلَّم على لُزُومِ الجَماعَةِ وقال: مَن فارَقَ الجَماعةَ قِيدَ شِبرٍ:', '', 'فليسَ مِنَّا', '', 'فقد خَلعَ رِبْقةَ الإسلامِ من عُنُقِه', '', 'فعليه لَعنَةُ الله والمَلائكةِ والنَّاسِ أِجمعِينَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', '(طَريقةٌ في الدِّينِ مُختَرَعةٌ تُضاهي الشَّرعيَّةَ، يُقصَدُ بالسُّلوكِ عليها المُبالَغةُ في التعَبُّدِ للهِ سُبحانَه) هذا التعريفُ يختصُّ بـ:', '', 'التَّبَتُّل', '', 'الرَّهْبانِيَّة', '', 'البِدعَة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولَ اللهِ صلَّى اللهُ عليه وسلَّم: ثَلاثُ خِصالٍ لا يَغِلُّ عليهِنَّ قَلبُ مُسلِمٍ أبَدًا: إخلاصُ العَمَلِ للهِ، ومُناصَحةُ ولاةِ الأمرِ، و:', '', 'لُزومُ الجَماعةِ', '', 'أن تُحِبَّ لأَخيكَ ما تُحِبُّ لنَفسِك', '', 'مُوالاةُ المُؤمِنينَ والبَراءَةُ مِن الكافِرينَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من خُطورةِ البِدعةِ وآثارِها السَّيِّئةُ أنَّها مانِعةٌ من شَفاعةِ مُحَمَّدٍ صلَّى اللهُ عليه وسلَّم؛ والدَّليلُ على ذلك:', '', 'قولُه صلَّى اللهُ عليه وسلَّم: (مَن سَنَّ في الإسلامِ سُنَّةً سَيِّئةً، كان عليه وزرُها، ووِزْرُ من عَمِلَ بها)', '', 'قولُ الله تعالى حِكايَةً عن أَهلِ النَّارِ: {فَمَا لَنَا مِنْ شَافِعِينَ وَلَا صَدِيقٍ حَمِيمٍ}', '', 'قولُه صلَّى اللهُ عليه وسلَّم: (يُجاءُ برجالٍ من أمَّتي، فيُؤخَذُ بهم ذاتَ الشِّمالِ، فأقولُ: يا رَبِّ أصحابي! فيقالُ: لا تدري ما أحدَثوا بَعْدَك)', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قَولُ الله تعالى: {وَلَا تَكُونُوا كَالَّذِينَ تَفَرَّقُوا وَاخْتَلَفُوا} المَقصُودُ بِه:', '', 'عَربُ الجاهِليَّة', '', 'فارِسُ والرُّومُ', '', 'أَهلُ الكِتابِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'إذا كانتِ البِدعةُ لا يَقتَصِرُ أثَرُها على المُبتَدِعِ بَل يَتَعَدَّاهُ إلى غَيرِه؛ كانت بِدعَةً:', '', 'جُزئِيَّة', '', 'كُلِّيَّة', '', 'ذاتِيَّة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال اللَّهُ عَزَّ وجَلَّ: {وَمَنْ أَضَلُّ مِمَّنِ اتَّبَعَ هَوَاهُ بِغَيْرِ هُدًى مِنَ الله} قال الشَّاطبِيُّ هذا شَأنُ المُبتَدِعِ، فإنَّه اتَّبَعَ هواهُ بغَيرِ هدًى من اللَّه، وهُدَى الله هو:', '', 'القُرآنُ', '', 'صِراطُه المُستقِيمُ', '', 'الإِسلامُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَولِ النبيِّ صلَّى اللهُ عليه وسلَّم: (إيَّاكُم ومُحْدَثاتِ الأمورِ): المُرادُ بالأُمُور:', '', 'أُمورُ الدِّينِ والدُّنيا', '', 'أمورُ الدِّينِ فقط', '', 'أُمورُ الدُّنيا فقط', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أكمل الحديث التالي: قال رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم: إنَّ أمَّتي سَتَفتَرِقُ على ثِنْتَينِ وسَبْعينَ فِرقةً كُلُّها في النَّارِ إلَّا واحِدةً، وهيَ:', '', 'في الجنَّة', '', 'الحَنِيفِيَّة', '', 'الجَماعةُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن أَمثِلَةِ البِدَع الإضافِيَّة:', '', 'القَولُ بارتِفاعِ التكاليفِ عِندَ الوُصولِ إلى مَرحَلةِ اليَقينِ', '', 'إفرادُ شَهرِ رَجَبٍ بالصَّومِ', '', 'تَخصيصُ أَماكِنَ مُعيَّنَةٍ للعِبادَةِ بلا استِنادٍ إلى خَبرٍ صَحيحٍ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مَن القائِلُ: (سَنَّ رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم وولاةُ الأمرِ من بُعدِه سُنَنًا، الأخذُ بها تَصديقٌ لكِتابِ اللَّهِ، واستِكمالٌ لطاعةِ الله)؟', '', 'عُمرُ بنُ عَبدِ العَزيز', '', 'أَحمدُ بنُ حَنبَل', '', 'ابنُ القَيِّم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال البَربَهاريُّ: اعلَمْ أنَّ النَّاسَ لم يَبتَدِعوا بدعةً قَطُّ حَتَّى:', '', 'تَركوا من السُّنَّةِ مِثلَها', '', 'يَقعُوا في الشِّركِ بسَببِها', '', 'يُنزِلُ اللهُ عليهم ذُلًّا لا يَرفعُه عنهُم إلَّا أن يَترُكُوها', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال اللهُ سُبحانَه: {إِنَّمَا الْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا بَيْنَ أَخَويْكُمْ} نَصَّت هذه الآيةُ الكَريمةُ على مَبدَأٍ عَظيمٍ من مَبادِئِ الإسلامِ، وهو:', '', 'النَّهيُ عن قِتالِ المُسلِمينَ', '', 'التآخِي في الله', '', 'الإِصلاحُ في الأَرضِ وعِمارتُها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال علي القاري: تَحصيلَ السَّعاداتِ الحَقيقيَّةِ بَعدَ مُجانَبةِ كُلِّ صاحِبٍ يُفسِدُ الوَقتَ، وكُلِّ سَبَبٍ يَفتِنُ القَلبَ: مَنُوطٌ بـ:', '', 'بالزُّهدِ في الدُّنيا', '', 'بمُخالَفَةِ الهَوَى', '', 'باتِّباعِ السُّنَّة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من أدِلَّةِ من السُّنَّةِ على ذَمِّ البِدَعِ: قولُه صلَّى اللهُ عليه وسلَّم:', '', 'من أحدَثَ في أَمْرِنا هذا ما لَيسَ منه، فهو رَدٌّ', '', 'كُلُّ أُمَّتي يَدْخُلُونَ الجَنَّةَ إِلَّا مَن أَبَى', '', 'لا عَدْوَى ولا طِيَرَةَ، ولا هامَةَ ولا صَفَرَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'مِن الأَمثِلَةِ التي ضَربَها الشَّاطِبيُّ للبِدَع في التِزامِ الكَيفِيَّاتِ:', '', 'الاقتِصارِ من المَأكَلِ والمَلبَسِ على صِنفٍ دونَ صِنفٍ', '', 'الذِّكرُ بهَيئةِ الاجتِماعِ على صَوتٍ واحِدٍ', '', 'الاختِصاءِ في الانقِطاعِ للعِبادةِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ اللهِ صلَّى اللهُ عليه وسلَّم: (إنَّما هلَكَ من كان قَبلَكم باختِلافِهم في الكِتابِ) المَقصُودُ بالكِتابِ:', '', 'القُرآنُ', '', 'الكِتابُ المُنَزَّلُ على نَبيِّهم', '', 'التَّورَاةُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'المَعاصيَ منها صَغائِرُ ومنها كبائِرُ، وأعظمُ الكَبائِرِ تكونُ واقعةً في:', '', 'الحاجيَّاتِ', '', 'التكميليَّاتِ', '', 'الضَّروريَّاتِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من الذي عَرَّفَ البِدعَةَ بأنَّها: "ما لم يَأذَنِ اللهُ به ولم يَكُنْ عليه أمرُ النَّبيِّ صلَّى اللهُ عليه وسلَّم ولا أصحابِه)؟', '', 'الإمامُ الشَّافِعيُّ', '', 'ابنُ حَجرٍ العَسقلانِيُّ', '', 'حافِظٌ الحَكَميُّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ما لا يُضاهِي الأُمورَ المَشروعةَ يكون:', '', 'بِدعَةً حَسَنَة', '', 'ليس بِدعة', '', 'داخلًا في عُمومِ شِرْكِ الرُّبوبِيَّة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من القائل: "أمرَ اللَّهُ المُؤمنينَ بالجَماعةِ، ونَهاهم عن الاختِلافِ والفُرقةِ، وأخبَرَهم أنَّه إنَّما هلَكَ من كان قَبلَهم بالمِراءِ والخُصوماتِ في دينِ الله"؟', '', 'عبدُالله بنُ عبَّاسٍ رضي اللهُ عنهُما', '', 'عليُّ بنُ أبي طالبٍ رضي اللهُ عنه', '', 'عُمرُ بنُ الخطَّابِ رضي اللهُ عنه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'في قَول النبيِّ صلَّى اللهُ عليه وسلَّم: (دُعاةٌ على أبوابِ جَهنَّمَ من أجابَهم إليها قَذَفوهُ فيها) مَثَّلَ لهُم النَّوويُّ بـ:', '', 'المُرجِئَةِ والجَهمِيَّة', '', 'الخَوارِجِ والقَرامطة', '', 'المُعتَزِلَة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ما ذا تسمَّى البدعة التي لم يَدُلَّ عليها دَليلٌ شَرعيٌّ لا من كِتابٍ ولا من سُنَّةٍ ولا من إجماعٍ، ولا استِدلالٍ مُعتَبَرٍ عِندَ أهلِ العِلمِ لا في الجُملةِ ولا في التفصيلِ؟', '', 'البِدعَةُ الإِضافِيَّة', '', 'البِدعَةُ الاعتِقادِيَّة', '', 'البِدعَةُ الحَقيقِيَّة', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُه تعالى: {وَمَنْ أَضَلُّ مِمَّنِ اتَّبَعَ هَوَاهُ بِغَيْرِ هُدًى مِنَ الله} الآيةَ عَيَّنَت -باعتبارِ الاتِّباعِ وعدمِهِ للأحكامِ الشَّرعيَّةِ- طَريقَين:', '', 'القُرآن والسُّنَّة', '', 'القَبول والرَّد', '', 'الشَّريعَة والهَوَى', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'من خُطورةِ البِدعةِ وآثارِها السَّيِّئةِ أنَّه يُخشَى على صاحِبِها الفِتْنةُ؛ والدَّليلُ على ذلك:', '', 'قَولُ الله تعالى: {كُلَّ مَا رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيهَا}', '', 'قَولُ الله تعالى: {فَلْيَحْذَرِ الَّذِينَ يُخالِفُونَ عَنْ أَمْرِهِ أنْ تُصِيبَهُمْ فِتْنةٌ أَوْ يُصِيبَهُمْ عَذابٌ أَلِيمٌ}', '', 'قَولُ الله تعالى: {وَاتَّقُوا فِتْنَةً لَا تُصِيبَنَّ الَّذِينَ ظَلَمُوا مِنْكُمْ خَاصَّةً}', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال النبيُّ صلَّى اللهُ عليه وسلَّم: من دَعا إلى ضَلالةٍ:', '', 'فعَليهِ لَعنَةُ الله والمَلائِكَةِ والنَّاسِ أَجمعِينَ', '', 'حُشِرَ يومَ القِيامةِ مع فِرعونَ وهامانَ وقارُونَ وأُبيِّ بنِ خَلفٍ', '', 'كان عليه من الإثمِ مِثلُ آثامِ من تَبِعَه لا يَنقُصُ ذلك من آثامِهم شَيئًا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال رَسولُ الله صلَّى الله عليه وسلَّم: مَن يَعِشْ منكُم بَعْدي فسيرَى اختِلافًا كَثيرًا، فعَليكُم:', '', 'بأَنفُسِكُم، لا يَضُرُّكُم مَن ضَلَّ بَعدَكُم', '', 'بالمَدِينَةِ؛ فمَن صَبَرَ علَى لَأْوَائِهَا، كُنْتُ له شَفِيعًا، أَوْ شَهِيدًا يَومَ القِيَامَةِ', '', 'بِسُنَّتي وسُنَّةِ الخُلفاءِ المَهْديِّين الرَّاشِدين', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'أَدخَلَ ابنُ عُثَيمين في تَعريفِ البِدعةِ: ما أُحدِثَ في الدِّينِ على خِلافِ ما كان عليه النَّبيُّ صلَّى اللهُ عليه وسلَّم وأصحابُه مِن:', '', 'عادَةٍ أو عِبادَةٍ', '', 'شَعيرَةٍ أو قُربَى', '', 'عَقيدةٍ أو عَمَلٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال ابنُ عُثَيمين: إنَّ المُتابَعةَ لا تَتَحَقَّقُ إلَّا إذا كان العَمَلُ موافِقًا للشَّريعةِ في:', '', 'أُمورٍ خَمسَة: العِلم، واليَقِين، والقَبُول، والانقِياد، والمَحَبَّة', '', 'أُمورٍ ثَلاثَة: النِّيَّة، والإِخلاص، والمُداوَمَة', '', 'أمورٍ سِتَّة: السَّبَب، والجِنْس، والقَدْر، والكَيفِيَّة، والزَّمان، والمَكان', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قولُه صلَّى اللهُ عليه وسلَّم (لا ألفَيَنَّ أحَدَكُم مُتَّكِئًا على أريكَتِه يَأتيه الأمرُ من عِندي فيَقولُ: لا نَدري، ما وجَدْنا في كِتابِ اللهِ اتَّبَعْناهُ) فيه تَحذِيرٌ من:', '', 'هَجرِ القُرآنِ قِراءَةً وتَدبُّرًا وعَمَلًا', '', 'تَركِ العَملِ بسُنَّتِه صلَّى اللهُ عليه وسلَّم', '', 'الشِّبَعِ ومِلءِ البَطنِ المُفضِي إلى الكَسلِ والفُتُورِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال ابنُ هُبَيرةَ: قَولُه صلَّى الله عليه وسلَّم: (وشَرَّ الأمورِ مُحدَثاتُها) يَعني:', '', 'كُلَّ ما أُحدِثَ بَعدَهُ في كُلِّ شيءٍ إذا كان مُخالِفًا لِمَا شَرعَه صلَّى اللهُ عليه وسلَّم', '', 'إنَّ أشَرَّ الأُمُورِ كَثرَةُ الحَديثِ، والكَلامُ بغيرِ عِلمٍ وبلا طائِلٍ', '', 'ما يكونُ غَرِيبًا مِن كلِّ شَيءٍ في الدِّينِ والدُّنيا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال ابنُ عُمَرَ رَضيَ اللهُ عنه فيهِم: (فإذا لَقيتَ أولَئِكَ فأخبِرْهم أنِّي بَريءٌ منهم وأنَّهم بُرَآءُ منِّي):', '', 'أهلُ القَدَرِ', '', 'اليَهودُ والنَّصارَى', '', 'المُرتَدُّونَ في عَهدِ أبي بكرٍ رضِيَ اللهُ عنه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'ضابِطُ البِدَعِ المُكَفِّرةُ هو:', '', 'الزِّيادَةُ أو النُّقصانُ في أَصلِ الشَّعائرِ الظَّاهِرَةِ كالصَّلاةِ والحَجِّ', '', 'الإحداثُ في دِينِ الله ما ليس منه بغيرِ مُستَندٍ مِن كِتابٍ ولا سُنَّةٍ', '', 'إنكارُ أمرِ مُجْمَعٍ عليه مُتَواتِرٍ من الشَّرعِ مَعلومٍ من الدِّين بالضَّرورةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'بماذا  أوصَى رَسولُ الله صلَّى اللهُ عليه وسلَّم حُذيفةَ بن اليَمانِ عندما سَألَهُ عن الشَّرِّ؟', '', 'أن يَلحَقَ بالشَّامِ فإنَّها خِيرَةُ الله مِن أَرضِه', '', 'أن يَضرِبَ بِسَيفِه مَن يَشُقُّ جَماعَة المُسلِمينَ', '', 'أن يَلزمَ جَماعَةَ المُسلمِينَ وإِمامَهُم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'عن أبي هُريرةَ رَضيَ اللهُ عنه أنَّ النَّبيَّ صلَّى اللهُ عليه وسلَّم قال: من خَرجَ من الطَّاعةِ وفارَقَ الجَماعةَ فمات:', '', 'فمَا مِن تَوبَةٍ يَستَدرِكُها', '', 'فقد وُكِلَ حِسابُه إلى الله', '', 'مات مِيتةً جاهِليَّةً', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@akida_category_id, '', 'قال اللَّهُ تعالى: {وَمَا يَنطِقُ عَنِ الْهَوَى إِنْ هُوَ إِلَّا وَحْيٌ يُوحَى} فالسُّنَّةُ النَّبَويَّةُ جُزءٌ من الوَحيِ. قال السَّمعانيُّ: كُلُّ ما ثَبَتَ بالسُّنَّةِ:', '', 'كان وَحْيًا مِن الله', '', 'فكَأنَّه ثابِتٌ في الكِتابِ', '', 'فهو قُرآنٌ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الصُّرَعة هو:', '', 'المُصابُ بالصَّرَعِ', '', 'الَّذي يَتمتَّعُ بقوَّةٍ بَدنيَّةٍ يَستطيعُ بها أنْ يَصرَعَ الآخَرين ويَغلِبَهُم', '', 'الذي يَغلِبُه النَّاسُ ويَصرَعُونه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لا يؤمِنُ عبدٌ حتَّى يُؤْمِنَ بأربعٍ: يشهَدُ أن لا إلَهَ إلَّا اللَّهُ، وأنِّي مُحمَّدٌ رسولُ اللَّهِ بَعثَني بالحقِّ، ويُؤمنُ بالموتِ:', '', 'وبالبَعثِ بعدَ الموتِ، ويؤمِنُ بالقَدرِ', '', 'ويُؤمِنُ بأنَّ ما أصابَه لم يَكن ليخطئَه وما أخطأَه لم يَكن ليصيبَه', '', 'ويُؤمِنُ باليومِ الآخِر', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'القَتَّاتُ هو:', '', 'الَّذي يُعدِّدُ على النَّاسِ نِعَمَ اللهِ', '', 'مَنْ تَسمَّعَ إلى حَديثِ شَخصٍ، فنَقَله إلى غيرِه بقَصْدِ الإفسادِ بيْنَهُما، ويُطلَقُ عليها النَّميمةُ', '', 'الَّذي يَأكُلُ القاتَ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى النبيُّ ﷺ أنْ يُقِيمَ الرَّجُلُ أخَاهُ مِن مَقْعَدِهِ:', '', 'ويَجْلِسَ فِيه', '', 'حَمِيَّةً للجاهِليَّة', '', 'ثم يَنهَرهُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: ليسَ الشَّدِيدُ بالصُّرَعَةِ، إنَّما الشَّدِيدُ الذي يَمْلِكُ نَفْسَهُ:', '', 'ويُمسِكُ لِسانَه', '', 'عِنْدَ الغَضَبِ', '', 'عن الشِّركِ بالله', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: مَنِ اسْتَطَاعَ البَاءَةَ فَلْيَتَزَوَّجْ؛ فإنَّه أغَضُّ لِلْبَصَرِ، وأَحْصَنُ لِلْفَرْجِ، ومَن لَمْ يَسْتَطِعْ:', '', 'فليَدَّخِر نَفسَه للحُورِ العِينِ', '', 'فَعليهِ بالدُّعاءِ', '', 'فَعلَيهِ بالصَّوْمِ فإنَّه له وِجَاءٌ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الهَامَةُ هي:', '', 'كلُّ ما له سَمٌّ', '', 'صَغائرُ الذُّنوبِ', '', 'الَّتي تُلِمُّ بالإنسانِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنَّ اللَّهَ يَغارُ، وغَيْرَةُ اللَّهِ:', '', 'أن تُشرِكُوا به شَيئًا', '', 'أنْ يَسُبَّ الرجُلُ أباهُ وأُمَّه', '', 'أنْ يَأْتِيَ المُؤْمِنُ ما حَرَّمَ اللَّهُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إِذَا مَاتَ الإنْسَانُ انْقَطَعَ عنْه عَمَلُهُ إِلَّا مِن ثَلَاثَةٍ: إِلَّا مِن صَدَقَةٍ جَارِيَةٍ، أَوْ عِلْمٍ يُنْتَفَعُ بِه أو:', '', 'عَينِ ماءٍ لابنِ السَّبيلِ', '', 'فَرسٍ تَرَكَهُ في سَبيلِ الله', '', 'وَلَدٍ صَالِحٍ يَدْعُو له', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: "مَن نَزَلَ مَنْزِلًا، ثُمَّ قالَ: أَعُوذُ بكَلِمَاتِ اللهِ التَّامَّاتِ مِن شَرِّ ما خَلَقَ:', '', 'لا يُصيبُه شَرٌّ مِن الجُمعَةِ إلى الجُمعَةِ', '', 'لَمْ يَضُرَّهُ شيءٌ حتَّى يَرْتَحِلَ مِن مَنْزِلِهِ ذلكَ', '', 'لا يُخيفُه شَيطانٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كَلِماتُ اللهِ التَّامَّاتُ هي:', '', 'كَلِماتُ كلِّ ما أنزَلَه اللهُ على رَسولِه', '', 'كَلِماتُ اللهِ الكامِلَةُ الَّتي لا يَدخُلُها نَقصٌ ولا عَيبٌ', '', 'كَلِماتُ كُلِّ أنواعِ الذِّكرِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ النبيُّ ﷺ يُعَوِّذُ الحَسَنَ والحُسَيْنَ، ويقولُ: أَعُوذُ بكَلِمَاتِ اللَّهِ التَّامَّةِ، مِن كُلِّ شيطَانٍ وهَامَّةٍ:', '', 'ومِن كلِّ دابَّةٍ سامَّةٍ', '', 'ومِنْ كُلِّ عَيْنٍ لَامَّةٍ', '', 'ومِن شَرِّ حاسِدٍ وحاقِدٍ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لا فَرَعَ ولا:', '', 'استِقسَامَ', '', 'جاهِليَّةَ', '', 'عَتِيرَةَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: مَن يُرِدِ اللهُ به خيرًا:', '', 'يَجعَل غِناهُ في قَلبِه', '', 'يَرزُقه مِن حيثُ لا يَحتسِب', '', 'يُفَقِّهْهُ في الدِّينِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا نَسِيَ المُسلِمُ أن يَذكُرَ اللهَ في أوَّلِ الطَّعامِ فإنَّه يَقولُ:', '', 'باسمِكَ اللَّهُمَّ', '', 'باسمِ اللهِ في أوَّلِه وآخرِه', '', 'الحَمدُ للهِ الَذي أطعَمَني وسَقاني', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"العَتِيرةُ" هي:', '', 'دابَّةٌ كانوا يَتشاءَمُون بها في الجاهِليَّة', '', 'صَنَمٌ كان لقَبيلَةِ هوازن', '', 'ذَبيحةٌ كانوا يَذْبَحونَها في رَجَبٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'بَوائِقُه تَعنِي:', '', 'الغَدَراتِ', '', 'السِّبابَ والشَّتائمَ', '', 'الشُّرُور والظُّلمُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'العَرَضُ هو:', '', 'النَّفَقاتُ', '', 'ما يُنتفَع به من متاعِ الدُّنيا سِوى النَّقدين', '', 'المَصائبُ والأمراضُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: تُنْكَحُ المَرْأَةُ لأَرْبَعٍ: لِمَالِهَا، وَلِحَسَبِهَا، وَلِجَمَالِهَا، وَلِدِينِهَا، فَاظْفَرْ:', '', 'بذَاتِ الجَمالِ تَقَرُّ عَينُك', '', 'بذَاتِ الحَسَبِ يكُن لك جِوار', '', 'بذَاتِ الدِّينِ تَرِبَتْ يَدَاك', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: سِبابُ المُؤمِنِ فُسوقٌ:', '', 'وقِتالُه كُفرٌ', '', 'وضَرْبُه شِركٌ', '', 'وحُبُّه فَرضٌ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إِذَا انْتَعَلَ أَحَدُكُمْ:', '', 'فليَحمَد الله', '', 'فليَقُل: باسمِ الله', '', 'فَلْيَبْدَأْ باليَمِينِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إذا تَثاءَبَ أحَدُكُمْ في الصَّلاةِ، فَلْيَكْظِمْ ما اسْتَطاعَ، فإنَّ:', '', 'الهَوامُّ تَعلَقُ في الفَمِ', '', 'الشَّيْطانَ يَدْخُلُ', '', 'ذلك أَدعَى للخُشُوعِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الإيمانُ والحِكمةُ يَمانِيةٌ، يَعنِي:', '', 'مَصدَرُهُما حُبُّ التَّيامُنُ', '', 'تُنسَبُ إلى أهْلِ اليَمَنِ؛ لانْقيادِهم إلى الإيمانِ مِن غَيرِ تَكلُّفٍ', '', 'يُنسَبانِ إلى اليَمينِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَرْنَا الشَّيطانِ، يَعنِي:', '', 'جانِبَي رَأسِه', '', 'وَسْوَسَتُه وَغَيُّه', '', 'مُؤخَّر أُذُنَيهِ مِن فَوق', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما مَعنَى قَولِ عُمرَ بنِ أبي سَلمَة: "وكَانَتْ يَدِي تَطِيشُ في الصَّحْفَةِ":', '', 'أي: يُحرِّكُها في جَوانبِ إناءِ الطَّعامِ؛ لِيَلتَقِطَه', '', 'أي: كان يَأكُل بشَراهةٍ ولم يَتمهَّل', '', 'أي: كانت يَدِي تَعبَثُ بالأَوراقِ والصُّحُفِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِه: "خيارُكُم لنسائِهِم" المَقصودُ بالنِّساءِ:', '', 'النِّساءُ المُسلِماتِ غَيرُ المُحرَّماتِ', '', 'كُلُّ النِّساءِ', '', 'أهلُه مِن النِّساءِ كزَوجتِه وبَناتِه وأخواتِه وقَريباتِه؛ لأنَّهُنَّ مَحَلُّ الرَّحْمةِ لضَعفِهنَّ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مِصداقُ قَولِه: "أنْفِقْ أُنْفِقْ عَلَيْكَ" من القُرآنِ الكَريمِ، هو قَولُه تعالى:', '', '{وَمَا عِنْدَ اللَّهِ خَيْرٌ لِلْأَبْرَارِ} [آل عمران: 198]', '', '{وَمَا أَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُ} [سبأ:39]', '', '{مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللَّهِ بَاقٍ} [النحل: 96]', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: إذَا صَلَّى أحَدُكُمْ إلى شيءٍ يَسْتُرُهُ مِنَ النَّاسِ فأرَادَ أحَدٌ أنْ يَجْتَازَ بيْنَ يَدَيْهِ،', '', 'فَلْيَدْفَعْهُ', '', 'فَلْيَتْرُكهُ', '', 'فَلْيُكرِمهُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'خِيارُكم، يَعنِي:', '', 'أفضَلُكم وأحسَنُكم', '', 'الطَّامِعين في الثَّوابِ', '', 'المُحبِّين للخَيرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: مَن كانَ له ذِبْحٌ يَذْبَحُهُ فإذا أُهِلَّ هِلالُ ذِي الحِجَّةِ:', '', 'فلا يَأْخُذَنَّ مِن شَعْرِهِ ولا مِن أظْفارِهِ شيئًا حتَّى يُضَحِّيَ', '', 'فليُمسِكها على العَلَفِ حتَّى تَسمُن', '', 'فَلْيَذبَح ذَبِيحَتَه وليُضَحِّ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: أكْملُ المؤمنينَ إيمانًا أحسنُهُم خلقًا،  وخيارُكُم:', '', 'في الجاهِلِيَّةِ خِيارُكُم في الإسلامِ', '', 'أكثَرُكُم ذِكرًا للهِ', '', 'خِيارُكُم لنِسائِهِم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَعنى الفَدَّادين:', '', 'الكافِرون', '', 'النَّاعِقونَ الذين يُغيِّرُون خَلْقَ الله', '', 'الَّذين تَعلو أصواتُهم في إبلِهِم وخَيلِهم وحُروثِهم ونحْوِ ذلك', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'التَّرجُّلُ، يَعنِي:', '', 'أنْ يَصيرَ الغُلامُ رَجُلًا', '', 'لُبْسَ الحِذاءِ في الرِجْلِ', '', 'تَسريحَ شَعرِ الرَّأسِ بالمُشطِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَجْرِ النَّبيِّ ﷺ، يَعنِي:', '', 'في تَربيتِه وتحْتَ رِعايتِه', '', 'الجُلوسُ على فَخِذِ النبيِّ ﷺ', '', 'الحياةُ على هَدْيِ النبيِّ ﷺ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَديثِ: "مَن يُرِدِ اللهُ به خيرًا يُفَقِّهْهُ في الدِّينِ" جاءَت كَلِمةُ "خَيرًا" نَكِرةً لـ:', '', 'ليَشمَلَ القَليلَ والكثيرَ', '', 'للتَّنزيهِ', '', 'للحَصرِ والقَصرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ لِعُمرَ بنِ أبي سَلَمةَ رَضِيَ اللهُ عنه: يا غُلَامُ:', '', 'سَمِّ اللَّهَ، وكُلْ بيَمِينِكَ', '', 'اتَّقِ اللهَ يجعَل لك مَخرجًا', '', 'احمَدِ اللهَ يكُن لك عَونًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن أنس رضي الله عنه: أنَّهُ رَأَى رَسولَ اللَّهِ ﷺ شَرِبَ لَبَنًا، وعَنْ يَسَارِهِ أبو بَكْرٍ، وعَنْ يَمِينِهِ أعْرَابِيٌّ، فأعْطَى الأعْرَابِيَّ فَضْلَهُ، ثُمَّ قالَ:', '', 'الغَريبُ أوَّلًا', '', 'الأيْمَنَ فَالأيْمَنَ', '', 'كَبِّرِ كَبِّرِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الحِكمةُ هي:', '', 'القَولُ اللَّيِّنُ', '', 'مَعرِفَةُ بَواطنِ الأُمورِ مِن ظَواهِرها', '', 'العِلمِ المُشتَمِلِ على مَعرِفةِ اللهِ تعالَى', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: السَّكِينَةُ والْوَقارُ في:', '', 'أصْحابِ الشَّاءِ', '', 'قِيامِ اللَّيلِ', '', 'ذِكرِ الله وما وَالاهُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ النبيُّ ﷺ يُعْجِبُهُ:', '', 'التَّيَمُّنُ', '', 'تَمرُ العَجوَةِ', '', 'أنْ يَبدأَ يَومَه بركعَتينِ خَفيفَتينِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنِّي رَأيتُ كأنَّ عَمودَ الكِتابِ انتُزِعَ مِن تَحتِ وِسادتي، فأَتبَعْتُه بَصري، فإذا هوَ نورٌ ساطِعٌ، عُمِدَ به إلى الشَّامِ، ألَا:', '', 'وإنَّ الإيمانَ إذا وَقَعَتِ الفِتنُ بِالشَّامِ', '', 'فانتَظِروا السَّاعةَ', '', 'فاعتَبِروا يا أُولي الألبابِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصُود بالأيَّامِ في قَولِ النبيِّ ﷺ: "ما العَمَلُ في أيَّامٍ أفْضَلَ منها في هذه"', '', 'أيامُ عَشرِ ذي الحجَّة', '', 'الأَشهُر الحُرُم', '', 'العَشرُ الأَواخِرُ مِن رَمضان', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"عَنان السَّماءِ" يَعني:', '', 'المَطَر', '', 'السَّحاب', '', 'الفَضاء الواسِع', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: "قالَ اللَّهُ تبارَكَ وتعالى: يا ابنَ آدمَ إنَّكَ ما دَعَوتَني ورَجَوتَني:', '', 'بذُنوبٍ كالجِبالِ غَفَرتُ لك', '', 'أَعطَيتُكَ مَسأَلتَكَ ولا يَنقُص مِن مُلكِي شَيئٌ', '', 'غَفَرتُ لَكَ على ما كانَ فيكَ ولا أُبالِي', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"المُزَفَّتْ" يعني:', '', 'الوِعاءَ المَطْلِيَّ بالزِّفْتِ', '', 'الإناءَ المَصنُوع منَ الخَشَبِ', '', 'الإناءَ  الذي فيه شَيءٌ مِن الذَّهَبِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول رسول الله ﷺ: أنا زَعيمُ بيتٍ في رَبَضِ الجنةِ  لِمَن:', '', 'اتَّقَى اللهَ في السِّرِّ والعَلانِيَة', '', 'صَلَّى باللَّيلِ والناسُ نِيامٌ', '', 'تَرَك المِراءَ وإن كان مُحِقًّا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'لا يَأخُذُ غَيرُ المُحْرمِ من أظفارِه وشَعرِه شَيئًا إذا بَدَأت عَشْرُ ذي الحِجَّةِ وأراد أن يُضحِّيَ:', '', 'احتِرامًا للأُضحِيَّةِ وتَشبُّهًا بالمُحْرِمِ', '', 'اتِّباعًا لشَريعَةِ نبيِّ الله إبراهيمَ عليه السلامُ التي لم تُنسَخ', '', 'حتَّى يَتحَلَّلَ مع الحُجَّاجِ يومَ النَّحرِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: "قالَ اللَّهُ عزَّ وجلَّ: أنْفِقْ:', '', 'أُنْفِقْ عَلَيْكَ', '', 'فمَا أَنفَقتُم من شَيءٍ فهو يُخْلِفُهُ', '', 'فالحَسنةُ بعَشرِ أَمثالِها واللهُ يُضاعِفُ لمَن يَشاءُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'منَ الآنِيةِ التي وَرَد النَّهيُ عن شُربِ ما يَتخَمَّرُ فيها:', '', 'آنِيةُ النُّحاسِ والحَديدِ والخَشَبِ', '', 'الدُّبَّاءُ والحَنْتَمُ والمُزَفَّتُ', '', 'السِّقاءُ المَصنُوع مِن الجِلد', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"شُغزُبًّا" يعني:', '', 'قَوِيًّا غَليظَ اللَّحْمِ', '', 'ذا شَعَرٍ وزَغَبٍ', '', 'طَويلًا وجَميلًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"بقرابِ الأرضِ" تعني:', '', 'بمكانٍ وَسطَ الأرضِ', '', 'بمِثلِ الأرضِ', '', 'بمِلْء الأرضِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"سَحَّاءُ اللَّيلَ والنَّهارَ" تَعنِي:', '', 'تَكثُرُ فيها العَظَمةُ', '', 'كثيرةُ العَطاءِ في كُلِّ الأوقاتِ', '', 'تُغَطِّي اللَّيلَ والنَّهارَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنِّي كُنتُ نُهيتُكم عن لُحومِ الأضاحيِّ أنْ تُمسِكوا بعدَ ثلاثةِ أيَّامٍ:', '', 'إلَّا لحَاجَةٍ', '', 'حتَّى لا يَبقَى في المَدينَةِ جائِعٌ', '', 'فكُلوا ما بَدا لكُم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'التَّيَمُّنُ هو:', '', 'الابتداءُ باليَمينِ', '', 'الحَلِفُ بالأَيمانِ', '', 'التَّفاؤُلُ والبِشْرُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"رَبَض الجنَّةِ" يعني:', '', 'نواحِيَها وأطرافَها', '', 'أشجارَها', '', 'أنهارَها', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: الإيمَانُ يَمَانٍ هَا هُنَا، ألَا إنَّ القَسْوَةَ وغِلَظَ القُلُوبِ في:', '', 'الجَبَّارِينَ', '', 'المُتَكبِّرِينَ', '', 'الفَدَّادِينَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصُود ببِلادِ الشَّامِ:', '', 'الجَزيرةُ العربية', '', 'بَيتُ المَقدِسِ', '', 'البِلادُ التي تَقَعُ إلى الشَّمالِ من الجَزيرَةِ العَرَبيَّةِ، وتَضُمُّ حالِيًا سُورِيَا والأُرْدُنَّ وفِلَسْطِينَ ولُبْنانَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ماذا أثبَتَ قَولُه: "يَدُ اللَّهِ مَلْأَى".', '', 'أنَّ اللهَ يحبُّ العبدَ المُلِحَّ في الدُّعاءِ', '', 'أنَّ يدَ اللهِ هي قُدرتُه ورِزقُه لعباده', '', 'إثباتُ صِفةِ اليَدِ للهِ سبحانه على ما يَليقُ بكمالِه وجلالِه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'العَمَلُ في أيَّامٍ عَشرِ ذي الحِجَّةِ، يُقصَدُ به:', '', 'الإنفاقُ في سَبيلِ اللهِ', '', 'كلُّ أعْمالِ البِرِّ', '', 'الصَّومُ عامَّة وصَومُ يَومِ عَرفةَ خاصَّة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جُملةُ "لا تَغِيضُها نَفَقَةٌ" معناها:', '', 'لا تَنقُصُها نفَقةٌ مهما عَظُمَت', '', 'لا تَمنَعُ النَّفقةَ', '', 'لا تَترُك نَفقةً حتى تُضاعِفَها', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: "إذا الْتَقَى المُسْلِمانِ بسَيْفَيْهِما:', '', 'فعلَى ابنِ آدمَ الأوَّل كِفلٌ مِن دَمِّهما', '', 'فالْقاتِلُ والْمَقْتُولُ في النَّارِ', '', 'فلا تتْركوهُما للشَّيطانِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'معنى (خَوَلُكم) في حديث: "إخْوَانُكُمْ خَوَلُكُمْ، جَعَلَهُمُ اللَّهُ تَحْتَ أيْدِيكُمْ":', '', 'النِّساءُ التي تحتَ أَيديكُم مِن زَوجاتٍ وأَخواتٍ وغَيرهنَّ', '', 'خَدَمُكم وعَبيدُكم الَّذين يَلُونَ أُمورَكم ويُصلِحونها', '', 'أهلُ بَيتِكُم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الرُّكنُ الثَّاني الَّذي بُنِي عَليهِ الإسلامُ هو:', '', 'شَهادَةُ أنَّ محمدا رَسولُ الله', '', 'حَجُّ البَيتِ لمَن استَطاعَ إليه سَبيلًا', '', 'إقامةُ الصَّلاةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَفْيُ النَّبيِّ ﷺ: "لَا يُؤْمِنُ أحَدُكُمْ حتَّى يُحِبَّ لأخِيهِ ما يُحِبُّ لِنَفْسِه":', '', 'نفْيُ كَمالِ الإيمانِ', '', 'نَفْيُ أصلِ الإيمانِ', '', 'نَفْيُ زِيادةِ الإيمانِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُسْلِمُ مَن سَلِمَ المُسْلِمُونَ مِن لِسَانِهِ ويَدِهِ، والمُهَاجِرُ:', '', 'مَن هَجَرَ ما نَهَى اللَّهُ عنْه', '', 'مَن هَجَرَ أَرضَه ليَعبُدَ اللهَ', '', 'الَّذي يَهجُرُ أخاهُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إلقاءُ السَّلامِ عَلى كلِّ مُسلِمٍ من أفضَلِ أعمالِ الإسلامِ؛ لأنَّه:', '', 'اسمٌ من أسماءِ اللهِ', '', 'تحيَّةُ الإسلامِ لعمومِ المسلِمينَ', '', 'أحَبُّ الأعمالِ إلى اللهِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الصَّلاةُ المكتوبةُ هي:', '', 'الفَجرُ، والظُّهرُ، والعَصرُ، والمَغربُ، والعِشاءُ', '', 'النَّوافِلُ', '', 'الضُّحى', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'بُنِيَ الإسْلَامُ علَى:', '', 'خَمْسٍ', '', 'الجِهادِ في سَبيلِ الله', '', 'حُسنِ الخُلُقِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: "يقولُ اللَّهُ تَعَالَى: أخْرِجُوا مِنَ النَّارِ مَن كانَ في قَلْبِهِ مِثْقَالُ حَبَّةٍ مِن خَرْدَلٍ مِن إيمَانٍ...". فما هو الخَرْدَلُ؟', '', 'نَباتٌ مَعروفٌ يضرب به المثل في الشَّيءَ القَليلَ البليغَ في القِلَّةِ', '', 'شَجرٌ ذوُ ساقٍ طَويلةٍ وأَوراقُه عَريضَةٌ مُتفَرِّعَة', '', 'مَعدِنٌ يُكالُ به المِثقالُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قالَ رَسولُ اللَّهِ ﷺ عن الوَحيِ: (أحْيانًا يَأْتِينِي مِثْلَ صَلْصَلَةِ الجَرَسِ)؛ فما هي الصَّلْصَلةُ؟', '', 'هو الصَّوتُ الَّذي يخرُجُ مِنَ الحديدِ عندَ تَدارُكِ الضَّربِ عَليهِ', '', 'السَّلاسِلُ عندما تَهتَزُّ فتصدرُ صَوتًا', '', 'صون الطِّينِ عندما يكون صَلْصالًا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جُملةُ: "مَن أعتَقَ رقبةً مُسلِمةً" مَعناها:', '', 'مَن أنجَي مُسلِمًا منَ القَتلِ', '', 'مَن أعتَقَ عبدًا أو أَمَةً', '', 'مَن أَعتَقَ أَمَتَهُ المُسلِمَة ثم تَزوَّجَها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'خُصَّ خُلُقُ الحياءِ بالذِّكْرِ في قَولِه ﷺ: "والحَيَاءُ شُعْبَةٌ مِنَ الإيمَانِ":', '', 'لأنَّه مِن الوَحيِ العام', '', 'لأنَّهُ خُلُقُ العُبَّادِ والنُّسَّاكِ', '', 'لِكَوْنِه أمْرًا خُلُقيًّا ربَّما يُذهَلُ العَقلُ عن كَوْنِه مِن الإيمانِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ رَسولُ اللَّهِ ﷺ يُعالِجُ مِنَ التَّنْزِيلِ:', '', 'شِدَّةً', '', 'وَحْيًا', '', 'ثِقلًا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول النبي ﷺ: قارِبُوا وسَدِّدُوا، واعْلَمُوا :', '', 'أنَّه لَنْ يَنْجُوَ أحَدٌ مِنكُم بعَمَلِهِ', '', 'أنَّ الجنَّة طَيِّبَة التُّربَة وغِراسُها: سبحان الله والحمدُ لله', '', 'أنِّي لا أُغنِي عنكُم مِن الله شَيئًا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قوله تعالى: {جَمْعَهُ وَقُرْآنَهُ}، جَمْعُه يَعنِي:', '', 'تَجميعَ الوَحيِ فلا يَتشابَه عليكَ', '', 'ضَمَّ السُّوَرِ إلى بَعضِها', '', 'جَمْعَه في صَدرِك فلا تَنْسَى منه شَيئًا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'خُصَّتِ الغَنَمُ بأنَّها خيرُ مالِ المُسْلِمِ عِندَما يَفِرُّ بدينِه ويَهيمُ في الجِبالِ؛ لأنَّها:', '', 'فيها مِن السَّكينةِ والبَرَكةِ', '', 'لها منَ القُدرةِ والصَّبرِ على تَحمُّلِ الجوعِ والعَطَشِ', '', 'لا تَشرُد ولا تَجمحُ على أَهلِها', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان الوَحيُ ثَقيلًا على النَّبيِّ ﷺ:', '', 'لضَعْفِ القُوَّةِ البشريَّةِ عن تحمُّلِ ذلك الوارِدِ العظيمِ مِن الجَنابِ الجَلِيلِ', '', 'لضَخامةِ المَلَكِ الذي يَنزِلُ بالوَحيِ', '', 'لكَثرةِ ما يُوحِي إليه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'شُعْبَةٌ مِنَ الإيمَانِ تَعنِي:', '', 'خَصلةٌ مِن خِصالِ الإيمانِ', '', 'دَليلٌ على كَمالِ الإيمانِ', '', 'رُكنٌ مِن أَركانِ الإيمانِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول النبي ﷺ: أُمِرْتُ أنْ أُقَاتِلَ النَّاسَ حتَّى:', '', 'تَخرُجَ الظّعينةُ مِن صَنعاءَ إلى حَضرموت لا تَخشَى إلَّا الله', '', 'يَرجِعُوا مِن الشِّركِ إلى الحَنِيفِيَّةِ الأُولَى', '', 'يَشْهَدُوا أنْ لا إلَهَ إلَّا اللَّهُ، وأنَّ مُحَمَّدًا رَسولُ اللَّهِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"يُوشِكُ أنْ يَكونَ خَيْرَ مَالِ المُسْلِمِ غَنَمٌ يَتْبَعُ بهَا شَعَفَ الجِبَالِ ومَوَاقِعَ القَطْرِ" فما معنى شَعَف الجبالِ؟', '', 'سُهولُها', '', 'رُؤوسُها وأعاليها', '', 'وِدْيانُها وقِيعانُها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'تأويل الرُّؤَى، يَعنِي:', '', 'الحِفظَ والرِّوايةَ للرُّؤيا', '', 'إِسقاطُ الرُّؤيا حَقيقةً في الواقِع', '', 'تَعبيرَ المَنامِ وتَفسيرَهُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جُملةُ "ألَا إنَّ القوَّةَ الرَّميُ"، تعني:', '', 'أنَّ القُوَّةَ الحَقيقِيَّة في الرِّجالِ هي الصَّبرُ وَقتَ الحَربِ', '', 'أنَّ أَفضلَ طَريقةٍ لرَمْيَ الجَمَراتِ الرميُ بقُوَّةٍ', '', 'أنَّ الرَّميَ هو مِنَ القوَّةِ الحقيقيَّةِ في الحَربِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'على أيِّ شيءٍ يَدُلُّ حَديثُ: "لَا يُؤْمِنُ أحَدُكُمْ، حتَّى أكُونَ أحَبَّ إلَيْهِ مِن والِدِهِ ووَلَدِهِ والنَّاسِ أجْمَعِينَ"؟', '', 'أنَّه كلَّما زادَ حُبُّ العَبدِ للنبيِّ ﷺ زادَ حُبُّه لِوَالِدِهِ ووَلَدِهِ والنَّاسِ أجْمَعِينَ', '', 'أنَّ مَحبَّةَ النَّبيِّ ﷺ من أُصولِ الإيمانِ', '', 'أنَّ مَحبَّةَ النَّبيِّ ﷺ مِن كَمالِ الإيمانِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان الإيمانُ أفضَلَ الأعمالِ وأعظَمَها عندَ اللهِ أجرًا وثوابًا؛ لأنَّه:', '', 'أفضَلُ ما يَتوصَّلُ به الإنسانُ إلى مَحبَّةِ النَّبيِّ ﷺ', '', 'شرْطٌ في صحَّةِ جميعِ العِباداتِ الشَّرعيَّةِ', '', 'أَصلُ حُسنِ الخُلُقِ الذي اختَصرَ النبيُّ ﷺ دَعوتَه لإتمامِها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَواقِعُ القَطْرِ، هي:', '', 'بُطونُ الأوديةِ والصَّحارى', '', 'مَواضِع وُجودِ الطَّعامِ', '', 'أَماكنُ العَيشِ في الخَلواتِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'من فوائدِ حَديثِ: "إخْوَانُكُمْ خَوَلُكُمْ، جَعَلَهُمُ اللَّهُ تَحْتَ أيْدِيكُمْ":', '', 'النَّهيُ عن عُقوقِ الإخوةِ أو الإساءَةِ إليهِم', '', 'الحثُّ على الإحسانِ إلى الرَّقيقِ والخَدَمِ', '', 'أنَّ النِّعَمَ تَدومُ بالشُّكرِ وتَذهبُ بالبَطَرِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: "أُرِيتُ النَّارَ فَإِذَا أكْثَرُ أهْلِهَا:', '', 'النِّسَاءُ', '', 'أهلُ الكتابِ', '', 'يأجُوجُ ومأجُوجُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'سَأَلَ رَجُلٌ النَّبيَّ ﷺ: أيُّ الإسْلَامِ خَيْرٌ؟ فقالَ ﷺ:', '', 'تَشهَدُ لا إله إلا الله وأنِّي رَسولُ الله', '', 'أن تَتقِي اللهَ حيثُما كنتَ وتَتبِعَ السَّيئةَ الحَسنةَ تَمحُها', '', 'تُطْعِمُ الطَّعَامَ وتَقْرَأُ السَّلَامَ علَى مَن عَرَفْتَ ومَن لَمْ تَعْرِفْ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: إخْوَانُكُمْ خَوَلُكُمْ، جَعَلَهُمُ اللَّهُ:', '', 'تَحْتَ أيْدِيكُمْ', '', 'مِن مَتاعِكم', '', 'عَونًا لكُم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُسلِمُ في قَولِ النبيِّ ﷺ: "المُسْلِمُ مَن سَلِمَ المُسْلِمُونَ مِن لِسَانِهِ ويَدِهِ" يُقصَدُ به:', '', 'الكامِلُ الجامعُ لخِصالِ الإسلامِ', '', 'مَن شَهِدَ للهِ بالتَّوحيدِ', '', 'مَنْ أَسلَم وَجهَه لله مُخلِصًا له الدِّين', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أنواعُ الحَياءِ:', '', 'نَوعٌ واحِدٌ، وهو فِطرةٌ فَطَر اللهُ النَّاسَ عَليها', '', 'ثلاثة: قَوليٌّ، وعَمَليٌّ، وقَلبيٌّ', '', 'نَوعانِ: ما كان خُلُقًا وجِبلَّةً غيرَ مُكتسَبٍ. وما كان مُكتسَبًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إطعامُ الطَّعامِ هو:', '', 'شُكرُ الله عزَّ وجلَّ بعدَ الأكلِ والشُّربِ', '', 'الإكثارُ مِن إطعامِ الناسِ الطَّعامَ، ويَدخُلُ فيه الصَّدقةُ والهَديَّةُ والضِّيافةُ والوليمةُ', '', 'أن تُحافِظَ على النِّعمَة بعَدمِ الإسرافِ فيها أو إِهدارِها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: بيْنَا أنَا نَائِمٌ، رَأَيْتُ النَّاسَ يُعْرَضُونَ عَلَيَّ وعليهم:', '', 'أَثَرُ السَّفَرِ', '', 'تاجُ الوقارِ', '', 'قُمُصٌ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ لسَعدٍ رضي الله عنه: يا سَعْدُ، إنِّي لَأُعْطِي الرَّجُلَ، وغَيْرُهُ أحَبُّ إلَيَّ منه:', '', 'إنَّما أنا قاسِمٌ واللهُ هو المُعطي', '', 'خَشْيَةَ أنْ يَكُبَّهُ اللَّهُ في النَّارِ', '', 'بأَمرٍ مِن الله، ولا حَظَّ لي فيهِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: الإِيمَانُ بضْعٌ وسِتُّونَ شُعْبَةً، والحَيَاءُ:', '', 'هو الإيمانُ', '', 'أَعلَى شُعَبِ الإيمانِ', '', 'شُعْبَةٌ مِنَ الإيمَانِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الحجُّ المَبرورُ، هو:', '', 'الَّذي يَجمَعُ بينَ العُمرةِ والحَجِّ في نُسُكٍ واحِدٍ', '', 'الَّذي يُؤدِّيه الإنسانُ وهو بارٌّ بوالِدَيهِ', '', 'الذي أُدِّيتْ أركانُه وكان خالِصًا لوجهِ اللهِ تعالَى، وخالِصًا مِن الرِّياءِ والسُّمعةِ والمالِ الحرامِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الفِتَنُ المَقصودةُ في قَولِه ﷺ: "يَفِرُّ بدِينِهِ مِنَ الفِتَنِ" هي:', '', 'الفِتَنُ الَّتي يَختلِطُ فيها الحقُّ بالباطلِ', '', 'فِتنَةُ النِّساءِ وفِتنةُ المالِ', '', 'خُروجُ الكَذَّابين والدَّجَّالين آخِرَ الزَّمانِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'لماذا قال المُسلِمون لِلنَّبيِّ ﷺ: "إنَّا لَسْنَا كَهَيْئَتِكَ يا رَسولَ اللَّهِ، إنَّ اللَّهَ قدْ غَفَرَ لكَ ما تَقَدَّمَ مِن ذَنْبِكَ وما تَأَخَّرَ"؟', '', 'لأنَّهُم كانوا لشِدَّةِ حِرصِهم على الطَّاعاتِ يُريدونَ الاجتهادَ في العملِ، فطلَبوا الإذنَ في الزِّيادةِ', '', 'تَواضُعًا مِنهم للنبيِّ ﷺ تَعظيمًا لمَكانَتِه عِندهُم', '', 'اعتِرافًا منهم بأنَّ اللهَ سُبحانَه قد كرَّم نَبِيَّه فغَفَر له ذُنوبَه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ رَسولُ اللَّهِ ﷺ إذَا أمَرَ الصَّحابةَ أَمرًا، أمَرَهُمْ مِنَ الأعْمَالِ:', '', 'دِينًا وَسَطًا', '', 'أَحسَنَها', '', 'بما يُطِيقُونَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'سَبَبُ تَقديمِ اللِّسانِ في حَديثِ: "المُسْلِمُ مَن سَلِمَ المُسْلِمُونَ مِن لِسَانِهِ ويَدِهِ" هو:', '', 'لأنه أشرف ما في الوجه فبه تنطق الشهادتان وذكر الله وغير ذلك', '', 'لأنَّ الإيذاءَ به أكثرُ وأسهلُ، وأشدُّ نِكايةً، ويعُمُّ الأحياءَ والأمواتَ جميعًا', '', 'لأنَّ اللِّسانَ هو قائِدُ الجَوارحِ؛ وله تَقولُ: إنَّما نحن بِك، فإنْ استَقمتَ استَقَمنا، وإنْ اعوَجَجتَ اعوَجَجنا', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يَتحقَّقُ الإيمانُ الكامِلُ للمُؤمِنِ حين يُحِبُّ:', '', 'تَقوَى الله وحُسنَ الخُلُقِ', '', 'لأخيهِ ما يُحِبُّ لنفْسِه', '', 'المَوتَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الحَياءُ الَّذي يَكونُ شُعبةً من شُعَبِ الإيمانِ، هو:', '', 'الحياءُ مِن اللهِ تعالى', '', 'الحَياءُ النَّفْعيُّ', '', 'حَياءُ الخَوفِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'سُئِلَ رَسولُ اللَّهِ ﷺ: أيُّ العَمَلِ أفْضَلُ؟ فَقالَ: إيمَانٌ باللَّهِ ورَسولِهِ. قيلَ: ثُمَّ مَاذَا؟ قالَ: الجِهَادُ في سَبيلِ اللَّهِ قيلَ: ثُمَّ مَاذَا؟ فقال ﷺ:', '', 'تُؤدِّي زَكاةَ مالِك طَيِّبَةً بها نَفسُك', '', 'خَمسُ صَلواتٍ في اليومِ واللَّيلَة', '', 'حَجٌّ مَبْرُورٌ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُهَاجِرُ الحَقُّ هو:', '', 'مَن هجَرَ أَرضَه لله خالِصًا مِن قَلبِه', '', 'مَن يَترُكُ الحَلالَ خَشيةَ الوُقوعِ في الحَرامِ', '', 'مَن هَجَرَ ما حرَّم اللهُ تعالَى عَليه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'من علاماتِ مَحبَّةِ المُسلِمِ لِلنَّبيِّ ﷺ:', '', 'امتثالَ أوامرِه ﷺ وتَقديمُ طاعتِه على أيِّ داعٍ آخَر', '', 'رَفعُ الصَّوتِ أَثناءَ الأَذانِ عند: أَشهدُ أنَّ محمدا رَسولُ الله', '', 'الطَّوافُ بقَبرِه ﷺ والتَّمسُّح بجُدارنِه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"مَنْ لا يَرْحَمْ" يَعنِي:', '', 'يَستجْلِبِ الرَّحماتِ منَ النَّاسِ', '', 'لا يَطلُبِ الرَّحمةَ منَ الله', '', 'لا يَتعطَّفْ على غيرِه، ولا يَرأَفْ بهم، إنْسانًا كان أو حَيَوانًا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِه ﷺ: "فَإِذَا فَعَلُوا ذلكَ عَصَمُوا مِنِّي دِمَاءَهُمْ وأَمْوَالَهُمْ إلَّا بحَقِّ الإسْلَامِ، وحِسَابُهُمْ علَى اللَّهِ" ماذا أفادَتْ عِبارةُ: "إلَّا بحقِّ الإسلامِ"؟', '', 'أن الإسلامَ يَعصِمُ دِماءَهم وأموالَهم، إلَّا إذا ارتكَبوا جَريمةً يَستحِقُّون عليها القتلَ', '', 'أنَّ للإسلامِ حُقوقًا كما أنَّ للمُسلمِينَ حُقوقًا', '', 'أنَّ مَن فَعلَ ما نهَى النبيُّ ﷺ عنه فقد بَرِأَ مِن حَقِّ الإسلامِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديثِ:  مَن مَاتَ يُشْرِكُ باللَّهِ شيئًا', '', 'لا عُذرَ له عندَ الله', '', 'فقد حَبِطَ عَملُه', '', 'دَخَلَ النَّارَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'تَنشَأُ مَحبَّةُ اللهِ سُبحانَه وتَعالَى في قَلبِ المُسلِمِ من:', '', 'أن يُحِبَّ العَبدُ ما رَزَقه اللهُ به', '', 'إِقامَةِ الصَّلاةِ وإيتاءِ الزَّكاةِ وصَومِ رَمضانَ وحَجِّ البَيتِ', '', 'مَعرفةِ أسمائِه وصِفاتِه، والتَّفكُّرِ في مَصنوعاتِه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: "مَن لا يَرْحَمْ لا يُرْحَمْ ، ومَن لا يَغْفِرْ لا يُغْفَرْ له، ومَن لا يَتُبْ:', '', 'لا يُتَبْ عَليهِ', '', 'فإثمُه على نَفسِه', '', 'يَدخُل النارَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إيتاءُ الزَّكاةِ، يَعنِي:', '', 'تَطهيرَ القَلبِ مِن التَّعلُّقِ بالدُّنيا', '', 'تَزكِيةَ النَّفِس بالزُّهدِ في المالِ', '', 'إخراجَ الزَّكاةِ المفروضةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مِن الأُمورِ التي يَجِدُ بها المُؤمِنُ حَلاوةَ الإيمان:', '', 'قَولُ "لا إله إلا الله" مُوقِنًا بِها', '', 'تَقوَى الله عزَّ وجلَّ في السِّرِّ والعَلانيةِ', '', 'أنْ يَكونَ اللَّهُ ورَسولُهُ أحَبَّ إلَيْهِ ممَّا سِوَاهُمَا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَعنى الإيمانِ بالنبيِّ ﷺ هو:', '', 'اتباعُه وتَعظيمُه وتوقيرُه وأنَّه خاتمُ الأنبياءِ', '', 'كَثرةُ الصَّلاةِ عليه ﷺ عند ذِكرِ اسمِه', '', 'الإقرارُ بالشَّهادتَينِ وإِقامةُ الصَّلاةِ وإيتاءِ الزَّكاةِ وصَومِ رَمضان وحَجُّ البَيتِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِ عائشةَ رضي الله عنها: "كانَ رَسولُ اللَّهِ ﷺ إذَا أمَرَهُمْ أمَرَهُمْ مِنَ الأعْمَالِ بما يُطِيقُونَ" دَلالةٌ على:', '', 'حِرصِ النبيِّ ﷺ على تعليمِ أُمَّتِه الإخلاصَ في العَملِ', '', 'أنَّ مِن السُّنَّةِ الاقتصادَ في النَّوافلِ، ومُلازَمةَ ما يُمكِنُ الدَّوامُ عَليهِ', '', 'أنَّ الاجتِهادَ في الطَّاعاتِ مَكروهٌ لِما فيه مِن التَّكلُّف', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَعنَى الحَياءِ عُمومًا، هو:', '', 'خُلُقٌ يبعَثُ صاحبَه على اجتنابِ القَبيحِ', '', 'التَّحرُّجُ منَ الأعمالِ الظَّاهِرَة', '', 'الخَوفُ منَ الوُقوعِ في الشَّرِّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَستفيدُ من حَديثِ: "المُسْلِمُ مَن سَلِمَ المُسْلِمُونَ مِن لِسَانِهِ ويَدِهِ..." أن:', '', 'المُؤمِنَ مُطالَبٌ بالأعمالِ الظَّاهِرةِ أما الباطِنَة فحِسابُها على الله', '', 'الظَّواهرَ لا يَعبَأُ اللهُ تعالَى بها إذا لم تُؤيِّدْها الأعمالُ الدَّالَّةُ على صِدقِها', '', 'الإسلامُ شُعَبٌ ودَرجاتٌ مِثل الإيمانِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الجَمعُ بينَ إطعامِ الطَّعامِ وإفشاءِ السَّلامِ من أفضَل ِأعمالِ الإسلامِ بعْدَ الإيمانِ وأداءِ الأركانِ؛ لأنَّ:', '', 'بهما يَجتمِعُ الإحسانُ بالقولِ والفعلِ', '', 'إفشاءَ السَّلامِ مع كَثرةِ الطَّعامِ من عُمومِ الوَحيِ الذي لم يُنسَخ', '', 'جَميعَ مَراتِبِ الدِّينِ اجتَمعَت فيهِما', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يعد ركن الحج من أركانُ الإسلامِ:', '', 'البدَنيَّة الماليَّة', '', 'اللِسانِيَّةٌ العَمَليَّةٌ', '', 'القَلبيَّة الماليَّة', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'لماذا كان حُبُّ المُسلِمين للأنصارِ عَلامةً على صِدقِ الإيمانِ؟', '', 'لأنَّ الأنصارَ أصدَقُ النَّاسِ إيمانًا', '', 'لِمَا كان مِن حُسنِ وَفائِهم بما عاهَدوا اللهَ سُبحانه وتعالَى عَليهِ؛ مِن إيواءِ نبيِّه ﷺ، ونَصرِه على أعدائِه', '', 'لأنَّ الله عزَّ وجلَّ اصطَفاهُم للنبيِّ ﷺ كما اصطَفَى الحَواريِّينَ لعِيسى عليه السلامُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: ثَلَاثٌ مَن كُنَّ فيه وجَدَ حَلَاوَةَ الإيمَانِ: -وذكر منها- وأَنْ يَكْرَهَ أنْ يَعُودَ في الكُفْرِ كما يَكْرَهُ', '', 'أنْ يُقْذَفَ في النَّارِ', '', 'أن تُضرَبَ عُنُقُه', '', 'أن تُؤتَى مَحارِمهُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"الغَدوةُ: هي:', '', 'أوَّلُ النَّهارِ', '', 'وَقْتُ العَصرِ', '', 'آخِرَ اللَّيلِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثَ: أَرْبَعٌ مَن كُنَّ فيه كانَ', '', 'مُشرِكًا', '', 'مُنَافِقًا خَالِصًا', '', 'مُؤمِنًا مُقسِطًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: "إِذَا أسْلَمَ العَبْدُ فَحَسُنَ إسْلَامُهُ:', '', 'وُضِعَ له القَبولُ في الأَرضِ', '', 'أَعتقَ اللهُ به سَبعِينَ رَقبةً مِن أَهلِ النارِ', '', 'يُكَفِّرُ اللَّهُ عنْه كُلَّ سَيِّئَةٍ كانَ زَلَفَهَا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُصبِحُ الكَذِبُ من علاماتِ النِّفاقِ إذا:', '', 'إذا لم يَتحَرَّ  الإنسانُ الصِّدقَ في نَقلِ الكَلامِ', '', 'بأنْ يَشتهِرَ ذلك الإنسانُ بالكذبِ في الحديثِ', '', 'متَى كَذَبَ الإنسانُ ولو مرَّةً', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: مَن يَقُمْ لَيْلَةَ القَدْرِ، إيمَانًا واحْتِسَابًا:', '', 'كُتِبَ له أَجرُ خَمسِينَ ألفِ سَنَةٍ', '', 'غُفِرَ له ما تَقَدَّمَ مِن ذَنْبِهِ', '', 'كان حَقًّا على الله أنْ يُرضِيَه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قيامُ لَيلةِ القَدرِ إيمانًا واحتِسابًا، يَعنِي:', '', 'إيمانًا بأنَّها خَيرُ لَيلةٍ، واحتِسابًا لعَددِ الرَّكعاتِ فيها', '', 'مُخلِصًا لله عزَّ وجلَّ في صِدقِ التَّوكُّلِ عليه سُبحانه', '', 'تَصديقًا بفضْلِ هذه اللَّيلةِ، وفضْلِ العملِ فيها، وابتغاءً لوجْهِ اللهِ في عِبادتِه، مُحتسِبًا جَزيلَ الأجرِ في قِيامِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَولُه: "اللَّهمَّ لا مانعَ لِمَا أعطيْتَ ولا مُعطِيَ لما مَنعْتَ" يَعنِي:', '', 'لا يجودُ بالعَطاءِ إلَّا أنتَ ولا يَمنعُ العَطاءَ إلا تَأخيرًا', '', 'لا يستطيعُ أحدٌ أنْ يمنعَ ما أردْتَ إعطاءَه ولا أنْ يُعطِيَ مَن أردْتَ مَنْعَه', '', 'لا يَحبِسُ رِزقَ العِبادِ شَيءٌ ولا يَمنعُ عنهُم المَطرَ إلَّا ذُنوبُهم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِ النبيِّ ﷺ "فَسَدِّدُوا وقَارِبُوا" التَّسديدُ يَعنِي:', '', 'الإِخلاصُ في المَسألَةِ', '', 'العملُ بالقصدِ والتَّوسُّطُ في العِبادةِ', '', 'الاجتِهادُ في الطَّاعاتِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"وقارِبوا" يَعنِي:', '', 'إنْ لم تَستطيعوا الأخْذَ بالأكملِ، فاعمَلوا بما يَقرُبُ منه', '', 'الزَمُوا قَارِبَ النَّجاةِ؛ وهي تَقوَى الله عز وجل', '', 'صِلُوا أقارِبَكم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: آيَةُ المُنَافِقِ ثَلَاثٌ: إذَا حَدَّثَ كَذَبَ، وإذَا وعَدَ أخْلَفَ:', '', 'وإذَا اؤْتُمِنَ خَانَ', '', 'وإذا سافَرَ أَبعَدَ', '', 'وإذا حارَبَ فَزِعَ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: إنَّ الدِّينَ يُسْرٌ، ولَنْ يُشَادَّ الدِّينَ أحَدٌ إلَّا غَلَبَهُ، فَسَدِّدُوا وقَارِبُوا:', '', 'وأَبْشِرُوا', '', 'وأَدلِجُوا', '', 'وأَحسِنُوا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'خَرجَ رَسولُ اللَّهِ ﷺ يُخْبِرُ المُسلِمينَ بلَيْلَةِ القَدْرِ:', '', 'فجاءَهُ خَبرُ وَفاةِ حَمزةَ رضي الله عنه', '', 'فكَسفَتِ الشَّمسُ', '', 'فَتَلَاحَى رَجُلَانِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"إنَّ الدِّينَ يُسْرٌ" يَعنِي:', '', 'واسِعٌ رِزقُه مَيسُورٌ خَيرُه', '', 'يُفرِحُ المُسلِمَ ويَسُرُّه', '', 'مُيسَّرٌ مُسهَّلٌ في عَقائدِه وأخلاقِه، وفي أفعالِه وتُروكِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الفُجورُ في الخُصومةِ، يَعنِي:', '', 'التَّعيِيرُ بالآباءِ والأُمَّهاتِ والتَّنابُذ بالأَلقابِ', '', 'أنْ يَخرُجَ عنِ الحقِّ عمْدًا، حتَّى يَصيرَ الحقُّ باطلًا، والباطلُ حقًّا', '', 'المُخاصَمَة المُفضِيَة إلى السِّبابِ والتَّلاعُن', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الدُّلجةُ، هي:', '', 'سَيرُ آخرِ اللَّيل', '', 'الجَريُ في السَّيرِ', '', 'العَمَلُ بإخلاصٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جَبلُ أُحُدٍ، هو:', '', 'الجبلُ الذي كلَّم اللهُ عليه مُوسى عليه السلامُ', '', 'أَحَدُ جِبالِ مكَّة كان النبيُّ ﷺ يَتعبَّدُ فيه', '', 'جَبَلٌ على مَشارِفِ المدينةِ مِن الجِهةِ الشَّماليَّةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"آيةُ المُنافِقِ" تَعنِي:', '', 'شَكْلَه', '', 'أفعالَه', '', 'عَلاماتُه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَديثِ: "لَمَّا نَزَلَتْ: {الَّذِينَ آمَنُوا ولَمْ يَلْبِسُوا إيمَانَهُمْ بظُلْمٍ} الظُّلمُ هو:', '', 'الشِّركُ', '', 'الذُّنوبُ والمَعاصِي', '', 'ظُلمُ النَّفسِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديثِ: "مَن قَامَ رَمَضَانَ إيمَانًا واحْتِسَابًا غُفِرَ له ما تَقَدَّمَ مِن ذَنْبِهِ" المَقصُود بقِيامِ رمضان:', '', 'أن يَقومَ العَبدُ بما فَرضَ اللهُ عليه ويَنتهِي عمَّا نَهاهُ اللهُ عنه', '', 'القِيامُ بأَركانِ الصِّيامِ تامَّة على وَجهِها الصَّحيحِ', '', 'صَلاةُ التَّراويحِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول رسول الله ﷺ: مَنِ اتَّبَعَ جَنَازَةَ مُسْلِمٍ، إيمَانًا واحْتِسَابًا، وكانَ معهُ حتَّى يُصَلَّى عَلَيْهَا ويَفْرُغَ مِن دَفْنِهَا، فإنَّه يَرْجِعُ مِنَ الأجْرِ:', '', 'بقِيرَاطَيْنِ', '', 'وقد غُفِرَ له ما تَقدَّم مِن ذَنبِه', '', 'بخيرٍ ممَّا طَلعَت عليه الشَّمسُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"المُشتَبِهاتُ" هي:', '', 'الأَحكامُ التَّكلِيفيَّة الخَمسَة وهي: الحلالُ والحرامُ والمندوبُ والمكرُوه والمُباحُ', '', 'كَبائرُ الذُّنوبِ التي تَوعَّدَ اللهُ فاعِليهَا بالعَذابِ الشَّديدِ', '', 'الأمورُ الَّتي تكونُ غيرَ واضحةِ الحُكمِ مِن حيثُ الحِلُّ والحُرمة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث:  إنَّ مِنَ الشَّجَرِ شَجَرَةً لا يَسْقُطُ ورَقُها" الشَّجرةُ هي:', '', 'شَجرةُ الغَرقَد', '', 'النَّخلَةُ', '', 'شَجرةُ الزَّقُّومِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"وإنَّ حِمى اللهِ مَحارمُه" تعني:', '', 'المعاصي الَّتي حرَّمها على عِبادِه', '', 'ما أحماهُ اللهُ بالنَّارِ لمن خالَفَ أَمرَه', '', 'ما حَماهُ اللهُ منَ السُّقوطِ منَ السَّماءِ إلى الأرضِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِ النبيِّ ﷺ: "يُكَفِّرُ اللَّهُ عنْه كُلَّ سَيِّئَةٍ كانَ زَلَفَهَا" "زَلَفَهَا" تعني:', '', 'قدَّمها وعَمِلَها قبْل إسلامِه', '', 'تَزلَّفَ بها، أي تَقرَّب بها', '', 'راقَبَ اللهَ فيها', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'النَّصيحةِ لكلِّ مُسلمٍ ومُسلمةٍ تَكونُ:', '', 'بالحِرصِ على مَنفعتِهما، وإيصالِ الخيرِ إليهما، ودفْعِ الشَّرِّ عنهما', '', 'العَمَلُ على زِيادَتِهِما في الطَّاعاتِ', '', 'بالتَّعاونِ على البِرِّ والتَّقوَى دُونَ الإثمِ والعُدوانِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديثِ: إِذَا أنْفَقَ الرَّجُلُ علَى أهْلِهِ يَحْتَسِبُهَا:', '', 'كانت له بكُلِّ نَفقَةٍ مِثل جَبلِ أُحُدٍ', '', 'كأنَّما أَعتَقَ عَشرَ رِقابٍ مِن وَلدِ إِسماعيلَ', '', 'فَهو له صَدَقَةٌ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: الحَلَالُ بَيِّنٌ، والحَرَامُ بَيِّنٌ، وبيْنَهُما:', '', 'بَيِّناتٌ', '', 'الآياتُ', '', 'مُشَبَّهَاتٌ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"الذَّرَّةُ" هي:', '', 'الواحِدَة مِن نَباتِ الذُّرَةِ', '', 'ما يُنثَرُ من رَذاذِ الماء', '', 'واحدةُ الذَّرِّ، وهو: صِغارُ النَّملِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول جَرِيرُ بنَ عبدِ اللَّهِ رضي الله عنه: أتَيْتُ النبيَّ ﷺ فقُلتُ: أُبَايِعُكَ علَى الإسْلَامِ، فَشَرَطَ عَلَيَّ:', '', 'ألَّا أرجِعَ عن دارِ الهِجرَةِ ما بَقِيَ مِن عُمُرِي', '', 'والنُّصْحِ لِكُلِّ مُسْلِمٍ', '', 'الإيمانَ بالقَدرِ خَيرِه وشَرِّه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول جرير رضي الله عنه: بَايَعْتُ رَسولَ اللَّهِ ﷺ علَى إقَامِ الصَّلَاةِ، وإيتَاءِ الزَّكَاةِ:', '', 'والنُّصْحِ لِكُلِّ مُسْلِمٍ', '', 'وعَدَمِ الإشراكِ باللهِ', '', 'وصَومِ رَمَضانَ وحجِّ البَيتِ إنْ استَطعتُ إليه سَبيلًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: ومَن كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا أوِ امْرَأَةٍ يَتَزَوَّجُهَا:', '', 'فقد خَسِرَ خُسرانًا مُبينًا', '', 'فلا هِجرةَ له', '', 'فَهِجْرَتُهُ إلى ما هَاجَرَ إلَيْهِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"الأعقابُ" هي:', '', 'كلُّ ما انثَنَى منَ الطَّريقِ', '', 'ما أصابَ الأرضَ مِن مُؤخَّرِ الرِّجلِ إلى مَوضعِ الشِّراكِ', '', 'جَمعُ عاقِب وهو الجَمَلُ الصَّغيرُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثَ: "الأعْمَالُ بالنِّيَّةِ، ولِكُلِّ امْرِئٍ:', '', 'جَنَّةٌ أو نارٌ', '', 'ما نَوَى', '', 'على قَدرِ سَعْيِه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: ويْلٌ لِلْأَعْقَابِ:', '', 'مِنَ النَّارِ', '', 'ثُمَّ وَيلٌ لهُم', '', 'ألَا يَتَّقون الله عزَّ وجلَّ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثَ: أنَّ النبيَّ ﷺ أوَّلَ ما قَدِمَ المَدِينَةَ نَزَلَ علَى:', '', 'أخْوَالِهِ مِنَ الأنْصَارِ', '', 'بَيتِ أَعمامِة مِن بنِي عبدِ الدَّارِ', '', 'سَقيفَةِ بنِي ساعِدَة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"الحِمَى: هو:', '', 'المكانُ الَّذي جَعَله الملِكُ لرَعْيِ مَواشِيه', '', 'الجِوارُ الذي كانت تَعتَنِقُه العَربُ مِن حِمايَةٍ وكَفالَة', '', 'المَكانُ شَديدُ الحَرارةِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ الرَّسولُ ﷺ: مَنِ اتَّقَى المُشَبَّهَاتِ:', '', 'فقد حَقَّقَ تَقوَى الله عزَّ وجلَّ', '', 'أنَجاه اللهُ مِن النَّارِ وأَدخَلهُ الجنَّة', '', 'اسْتَبْرَأَ لِدِينِهِ وعِرْضِهِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول النبي ﷺ: "يَخْرُجُ مِنَ النَّارِ مَن قالَ لا إلَهَ إلَّا اللَّهُ، وفي قَلْبِهِ:', '', 'إيمانٌ باللهِ ورَسولِه', '', 'وزْنُ شَعِيرَةٍ مِن خَيْرٍ', '', 'دَغَلٌ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثَ: أنَّ رَجُلًا مِنَ اليَهُودِ قالَ له: يا أمِيرَ المُؤْمِنِينَ، آيَةٌ في كِتَابِكُمْ تَقْرَؤُونَهَا، لو عَلَيْنَا مَعْشَرَ اليَهُودِ نَزَلَتْ:', '', 'لَاتَّخَذْنَا ذلكَ اليومَ عِيدًا', '', 'لكُنَّا بها أَفرح مِن الوالدِ بوَلدِه', '', 'لاكتَفَينا بها وما فَارَقناها', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: لا يَمَلُّ اللَّهُ:', '', 'مِن الأعمالِ الصَّالحَةِ', '', 'مَسأَلتَكُم ودُعاءَكُم', '', 'حتَّى تَمَلُّوا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جُملةُ: "كان يَتخَوَّلُهم بالمَوعِظةِ" تَعنِي:', '', 'يُكثِرُ المَوعِظةَ لهم', '', 'لا يَعِظُهم إلَّا قَليلًا نادِرًا حتَّى لا يَنفِرُوا', '', 'يَتعهَّدُهم بالمَوعظةِ في بعضِ الأيَّامِ دونَ بعضٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان رَسولُ اللَّهِ ﷺ في حَجَّةِ الوَدَاعِ واقَفًا لِلنَّاسِ يَسْأَلُونَهُ وهو في:', '', 'مِنًى', '', 'عَرفَةَ', '', 'مُزدَلِفَةَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنَّ اللَّهَ لا يَقْبِضُ العِلْمَ انْتِزَاعًا يَنْتَزِعُهُ مِنَ العِبَادِ، ولَكِنْ:', '', 'يَرفعُ العِلمَ مِن الصُّدورِ حتَّى لا يَبقَى عالِمٌ', '', 'يُشرِبهُم الهَوَى؛ فإذا فَعلُوا ضَلُّوا وأَضَلُّوا', '', 'يَقْبِضُ العِلْمَ بقَبْضِ العُلَمَاءِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إنَّ مِن أشْراطِ السَّاعَةِ: أنْ يُرْفَعَ العِلْمُ، ويَثْبُتَ الجَهْلُ:', '', 'ويَنتشِرَ الفُحشُ', '', 'ويُشْرَبَ الخَمْرُ، ويَظْهَرَ الزِّنا', '', 'ويموتُ الخيرُ في القُلوبِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: يَسِّرُوا ولا تُعَسِّرُوا:', '', 'واعلَمُوا أنَّ الجنَّةَ تحتَ ظِلالِ السُّيوفِ', '', 'وبَشِّرُوا ولا تُنَفِّرُوا', '', 'وسَدِّدُوا وقارِبوا', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: يقولُ رَسولُ اللَّهِ ﷺ قالَ: بيْنَا أنَا نَائِمٌ، أُتِيتُ بقَدَحِ لَبَنٍ، فَشَرِبْتُ حتَّى إنِّي لَأَرَى الرِّيَّ يَخْرُجُ:', '', 'في أظْفَارِي', '', 'بينَ يَدَيَّ', '', 'مِن فَمِي', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ نَقْشُ خاتَمِ رَسولِ الله ﷺ:', '', 'مُحَمَّدٌ رَسولُ اللَّهِ', '', 'النبيُّ الأُمِّيُّ', '', 'لا إله إلا الله', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: كانَ النبيُّ ﷺ يَتَخَوَّلُنا بالمَوْعِظَةِ في الأيَّامِ:', '', 'كَراهَةَ السَّآمَةِ عَلَيْنا', '', 'حفِظَها مَن حفِظَها، ونسِيَها مَن نسِيَها', '', 'حتَّى كَأنَّا رَأْيُ عَيْنٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'بلادُ البَحرينِ قَديمًا كانت:', '', 'مَملكةً بيْن البَصرةِ وعُمَانَ تابعةً لمَملكةِ فارسَ', '', 'بلادَ ما وراءَ النَّهرِ وتَشملُ بُخارَى وخَوارزمَ وأوزبكستانَ', '', 'عاصِمةَ الخِلافَةِ العبَّاسِيَّة في العِراقِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'سُمِّيتِ الخَمرُ خَمرًا لأنَّها:', '', 'يُوضَعُ عَلَيها خِمارٌ حتَّى تَختَمِرَ', '', 'تُغطِّي العقْلَ', '', 'تُصدِرُ صَوتَ الخَريرِ عندَ الغَلَيانِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثَ: أنَّ رَسولَ اللَّهِ ﷺ بَعَثَ بكِتَابِهِ رَجُلًا وأَمَرَهُ أنْ يَدْفَعَهُ إلى عَظِيمِ البَحْرَيْنِ فَدَفَعَهُ عَظِيمُ البَحْرَيْنِ إلى:', '', 'قَيصَر', '', 'المُقَوقِس', '', 'كِسْرَى', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الغِبطةُ، هي:', '', 'ما يَغتَبِطُ به الناسُ مِن الأَموالِ والمَتاعِ والوِلدان', '', 'أنْ يَرى المرءُ نِعمةً عندَ غَيرِه، فيَتمنَّى مِثلَها لنَفْسِه دونَ زَوالِها عن أخيه', '', 'الفَرحُ بفَرَحِ الآخَرين والحُزنُ بحُزنِهِم', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"التَّيسيرُ" ضِدُّه', '', 'التَّعسيرُ', '', 'التَّسهيلُ', '', 'الإسرارُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: "لَا حَسَدَ إلَّا في اثْنَتَيْنِ: رَجُلٌ آتَاهُ اللَّهُ مَالًا فَسُلِّطَ علَى هَلَكَتِهِ في الحَقِّ:', '', 'ورَجُلٌ آتَاهُ اللَّهُ الحِكْمَةَ فَهو يَقْضِي بهَا ويُعَلِّمُهَا', '', 'ورجلٌ خرجَ بنفسِهِ ومالِهِ فلمْ يرجعْ مِنْ ذلكَ بشيءٍ', '', 'ورَجُلٌ ذَكَرَ اللَّهَ خَالِيًا فَفَاضَتْ عَيْنَاهُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: يُقْبَضُ العِلْمُ، ويَظْهَرُ الجَهْلُ والفِتَنُ، ويَكْثُرُ الهَرْجُ"  الهَرْجُ هو:', '', 'الرِّبا', '', 'الفَواحِشُ', '', 'القَتْلُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '"التَّبشيرُ" عَكْسُ', '', 'التَّيسيرِ', '', 'التَّكبيرِ', '', 'النِّذارةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن ابنِ عبَّاسٍ رضي الله عنهُما قال: ضَمَّنِي رَسولُ اللَّهِ ﷺ وقالَ: اللَّهُمَّ عَلِّمْهُ:', '', 'الكِتابَ', '', 'السُّنَّة', '', 'الفِراسَة', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كَتَبَ النبيُّ ﷺ كِتَابًا، فقِيلَ له: إنَّهُمْ لا يَقْرَؤُونَ كِتَابًا إلَّا مَخْتُومًا، فَاتَّخَذَ:', '', 'إِداوَةً وأُدُمًا وصَنعَ لنَفسِه خاتَمًا ﷺ', '', 'كاتِبًا مِن الأَنصارِ معهُ خاتَمٌ', '', 'خَاتَمًا مِن فِضَّةٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الحَسَدُ، هو:', '', 'أنْ يَتمنَّى المرءُ زَوالَ النِّعمةِ عن أخيهِ سواءٌ حصَلتْ للحاسدِ أو لا', '', 'الخَوفُ مِن الفَاقَةِ، وهي الفَقرُ', '', 'وَساوِسُ الشَّياطين', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قوله صلى الله عليه وسلم: "افعَلْ ولا حرَجَ" يَدُلُّ عَلى:', '', 'البِرِّ', '', 'التَّيسيرِ', '', 'الإخلاصِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِه ﷺ: «عَزْمَةً مِن عَزَماتِ ربِّنا»:', '', 'أمْرًا حقًّا ثابتًا للهِ', '', 'غَضبٌ مِن غَضبِ الله عزَّ وجل على حُرماتِه', '', 'رُخصَةً منه تَبارَك وتَعالَى تَصدَّق بها على عِبادِه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المَقصودُ بالقَزَعِ في حَديثِ ابْنِ عُمَرَ: «أَنَّ رَسُولَ اللَّهِ ﷺ نَهَى عَنِ القَزَعِ»؟', '', 'حَلقُ شَعرِ إبْطٍ دونَ الإبْطِ الآخَرِ', '', 'حَلقُ شَعرِ اللِّحيةِ', '', 'حَلْقُ بَعضِ شَعرِ الرَّأسِ وترْكُ بَعضِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنَّ الدُّنْيَا حُلْوَةٌ خَضِرَةٌ، وإنَّ اللَّهَ مُسْتَخْلِفُكُمْ فِيهَا، فَيَنْظُرُ:', '', 'ما استِخلافُكم فيها', '', 'إلى قُلوبِكم لا إلى صُوَرِكُم', '', 'كيفَ تَعمَلون', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ أُمِّ عَطِيَّةَ رَضِيَ اللهُ عَنهَا-: «كُنَّا نُنْهَى أنْ نُحِدَّ علَى مَيِّتٍ فَوْقَ ثَلَاثٍ، إلَّا علَى زَوْجٍ؛ أرْبَعَةَ أشْهُرٍ وعَشْرًا»:', '', 'النَّهيُ عن الحِدادِ لأنَّه مِن عاداتِ الجاهِليَّة', '', 'الحَثُّ على المَودَّةِ والرَّحمةِ بين الزَّوجينِ حتَّى بعدَ المَوتِ', '', 'عُلوُّ مَنزِلةِ الزَّوجِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لَا تَجْعَلُوا بُيُوتَكُمْ مَقَابِرَ، إِنَّ الشَّيْطَانَ يَنْفِرُ مِنَ الْبَيْتِ الَّذِي:', '', 'تُقْرَأُ فِيهِ سُورَةُ الْبَقَرَةِ', '', 'يُكفَلُ فيه يَتيمٌ', '', 'يُذكَرُ فيه اسمُ اللهِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الزَّكاةُ عِبادةٌ مالِيَّةٌ', '', 'واجِبةٌ في كُلِّ مالٍ بلَغَ النِّصابَ وحالَ عليه الحَوْلُ', '', 'واجِبةٌ في حَقِّ الرِّجالِ الأَحرارِ المُكلَّفِينَ', '', 'فَرضُ كِفايَةٍ إنْ قامَ بها البَعضُ سَقطَت عن الجَميعِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: الدُّنْيَا سِجْنُ الْمُؤْمِنِ:', '', 'وجَنَّةُ المُنافِقِ', '', 'وجَنَّةُ النِّساءِ', '', 'وَجَنَّةُ الْكَافِرِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِه ﷺ في وَصفِ الشُّجاعِ الأقرَعِ: «له زَبيبَتانِ»:', '', 'نُقطتانِ سَوْداوانِ فَوقَ عَيْنَيه', '', 'عِنَبَتانِ طافِيَتانِ على عَينَيهِ', '', 'حاجِبانِ ثَقيلانِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ جابِرٍ رَضِي اللهُ عَنهُ في وَقتِ العِشاءِ: «والعِشاء: أحيانًا يُؤَخِّرُها وأحيانًا يُعَجِّلُ، إذا رَآهُمُ اجتَمَعُوا عَجَّل، وإذا رَآهُم أبطَؤوا أخَّرَ»', '', 'أنَّ العِشاءَ لا وقْتَ لها', '', 'مَشروعِيَّةُ تَأخيرِ صَلاةِ العِشاءِ بحَسَبِ اجتِماعِ المُصَلِّينَ', '', 'أنَّ تَعجيلَ العِشاءِ خَيرٌ من تَأخيرِها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِه ﷺ: «النَّاسُ مَعادِنُ»:', '', 'أُصولٌ للخَيرِ والشَّرِّ بِحَسَبِ ما جَعلَهمُ اللهُ مُستعدِّينَ لَه؛ كمَعادنِ الفِضَّةِ والذَّهبِ', '', 'يُشبِهون المَعادِنَ في صَلابَتِها', '', 'يَميلون إلى التَّعدينِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المُرادُ بفَيحِ جَهنَّمَ المَذكورِ في حَديثِ: «إنَّ شِدَّةَ الحَرِّ مِن فَيْحِ جَهَنَّمَ»:', '', 'حَميمُها', '', 'حَرُّها وفَوَرانُها', '', 'زَمهَريرُها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'العَبدُ إذا لم يَشكُرِ النِّعمةَ ويؤَدِّ حَقَّ اللهِ فيها، تكونُ:', '', 'نِقمةً ووبالًا عليه يومَ القِيامةِ', '', 'ذُخرًا له يَومَ القِيامةِ', '', 'مَشاعًا للمِسكينِ وابنِ السَّبيلِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'لا فضْلَ لِعربيٍّ على أعْجَميٍّ إلَّا:', '', 'بالتَّقوَى', '', 'الأنصارَ', '', 'رَجلًا خَرجَ بنَفسِه ومَالِه فلم يَرجِع مِن ذلك بشَيءٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: مَا تَرَكْتُ بَعْدِي فِتْنَةً أَضَرَّ عَلَى الرِّجَالِ مِنَ:', '', 'حُبِّ الدُّنيا وكَراهِيَةِ المَوتِ', '', 'فِتنَةِ المالِ', '', 'النِّساءِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إِنَّ اليَهُودَ، وَالنَّصَارَى لا يَصْبُغُونَ:', '', 'فَخَالِفُوهُمْ', '', 'لِحاهُم', '', 'فلا يَضرُّونَكُم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان من هَديِ النَّبيِّ ﷺ أنَّه لا يَعجَلُ بالدُّعاءِ على أحَدٍ ما دامَ:', '', 'لم يَغدِر', '', 'يُحسِنُ ذِكرَه', '', 'يَطمَعُ في إجابَتِهم إلى الإسلامِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يَرجِعُ السَّبَبُ في  تَفضيلِ بَعضِ القَبائلِ الوارِدِ ذِكرُها في حَديثِ: «قُرَيْشٌ، وَالأَنْصَارُ، وَجُهَيْنَةُ، وَمُزَيْنَةُ، وَأَسْلَمُ، وَأَشْجَعُ، وَغِفَارُ مَوَالِيَّ، لَيْسَ لَهُمْ مَوْلًى دُونَ اللَّهِ وَرَسُولِهِ» إلى:', '', 'سَبقِهِم إلى الإسلامِ  ونُصرَتِهِم للهِ ورَسولِه', '', 'كَثرةِ عَدَدِهم', '', 'تَأليفِ قُلوبِهِم للإسلامِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لا يَقْعُدُ قومٌ يَذكُرون اللهَ عزَّ وجلَّ:', '', 'إلَّا كانت خُطاهُم إِحداها تَحُطُّ خَطيئَةً والأُخرَى تَرفعُ دَرجَة', '', 'إلَّا حَفَّتْهُمُ المَلائكةُ، وغَشِيَتْهُمُ الرَّحمةُ، ونَزَلَت عَليهِمُ السَّكينةُ، وذَكَرَهمُ اللهُ فيمَن عِندَه', '', 'إلَّا استَوجَبوا الجَنَّةَ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أعظَمُ سورةٍ في كِتابِ اللهِ عزَّ وجلَّ من حَيثُ الأجرُ والثَّوابُ؛ هي:', '', 'الفَتحُ', '', 'النِّساءُ', '', 'الفاتِحةُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصودُ بـ(الزَّهرَاوانِ)؛ هما:', '', 'القُرآنُ والسُّنَّةُ', '', 'أبو بكرٍ وعُمر رضي الله عنهُما', '', 'سورةُ البَقَرةِ وآلِ عِمرانَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لَيسَ المُؤمِنُ بالطَّعَّانِ ولا اللَّعَّانِ ولا الفاحِشِ ولا:', '', 'السَّبَّابِ', '', 'المُسيءِ', '', 'البَذيءِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال النبيُّ ﷺ: «المَدِينَةُ حَرَمٌ ما بيْنَ عَيْرٍ إلى ثَوْرٍ» «عَيْر، وثَوْر» هُما:', '', 'اسمٌ لقَبِيلَتَيْنِ تَسكُنانِ أطرافَ المَدينةِ', '', 'اسمٌ لبِئرَينِ ماءٍ بالمدينةِ', '', 'اسمُ جَبَلَينِ بالمدينةِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لا تَلاعَنوا بلَعنةِ اللهِ ولا بغَضَبِ اللهِ ولا:', '', 'بالنَّارِ', '', 'بالوَيلِ', '', 'بالثُّبورِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِه ﷺ: «مَن جرَّ ثوبَهُ خُيَلاءَ لم ينظُرِ اللَّهُ إليهِ يومَ القيامةِ» المقصودُ بالنَّظرِ هُنا:', '', 'نَظَرُ حِسابٍ', '', 'نَظَرٌ خاصٌّ', '', 'نَظرٌ عامٌّ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن ابْنِ عُمَرَ قالَ: لعَنَ النَّبيُّ ﷺ مَن مَثَّل بالحَيَوانِ» مَعنَى «مَثَّل بالحَيَوانِ»:', '', 'قَلَّد الحَيَوانَ في حَرَكاتِه وتَصَرُّفاتِه حتَّى يُؤذِيَه', '', 'قَطَعَ بعضَ أجزائِه كالأُذنِ والأنفِ وغيرِها', '', 'أشارَ له بالسِّكِّينِ أنَّه سيَذبَحُه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال النبيُّ ﷺ: البَسوا من ثِيابِكُمُ البياضَ فإنَّها:', '', 'لِباسُ المَلائِكَة', '', 'أَستَر لِسَوآتِكُم', '', 'مِن خَيرِ ثِيابِكُم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: «إنَّ خيرَ أَكْحالِكُمُ الإثمِدُ: يَجلُو البَصَرَ، ويُنبِتُ الشَّعرَ» معنى «الإثمِد»:', '', 'حَجَرٌ أَملَس له رائِحةٌ طَيِّبَةٌ', '', 'حَجَرٌ أسودُ يميلُ إلى الحُمرةِ', '', 'حَجَرٌ أحمَرُ يُؤتَى به مِن العِراقِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنَّ شِدَّةَ الحَرِّ مِن فَيْحِ جَهَنَّمَ، فإذا اشْتَدَّ الحَرُّ:', '', 'فأَقِيموا الصَّلاةَ', '', 'فاجتَنِبوا الصَّلاةَ', '', 'فأبْرِدوا بالصَّلاةِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: يَسِّرُوا وَلَا تُعَسِّرُوا:', '', 'وَبَشِّرُوا وَلاَ تُنَفِّرُوا', '', 'فَكُلٌّ مُيَسَّرٌ لِمَا خُلِقَ لهُ', '', 'إنَّ أَكرمَكُم عندَ الله أَتقاكُم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالثَّغامةِ في حَديثِ: «أُتِيَ بأبي قُحافةَ يومَ فتْحِ مكَّةَ ورَأسُه ولِحيتُه كالثَّغامَةِ بَياضًا»:', '', 'شَجرةٌ بَيضاءُ الثَّمَرِ والزَّهْرِ', '', 'جَبَلٌ أبيَضُ بمَكَّةَ', '', 'طائرٌ أبيَضُ كَبيرٌ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى رَسولُ اللهِ ﷺ أَنْ يُقْتَلَ شيءٌ مِنَ الدَّوَابِّ صَبْرًا. مَعنَى «صَبْرًا»:', '', 'يُحْبَسُ الحَيوانُ الحيُّ حتَّى يَموتَ', '', 'جَميعُ ما سَبَق', '', 'أَنْ يُحبَسَ في مَكانٍ فيُجعَلَ هَدفًا يُرمَى عليه حتَّى يَموتَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'وَقتُ أداءِ صَلاةِ الفَجرِ هو:', '', 'قبلَ طُلوعِ الشَّمسِ قِيدَ رُمحٍ', '', 'إذا صارَ ظِلُّ كُلِّ شِيءٍ مِثلَه', '', 'ما لمْ يَطلُعْ قرنُ الشَّمسِ الأوَّلُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حديثِ أمِّ خالِدٍ: «ثُمَّ قَالَ رَسولُ اللَّهِ ﷺ: أبْلِي وأَخْلِقِي، ثُمَّ أبْلِي وأَخْلِقِي، ثُمَّ أبْلِي وأَخْلِقِي» مَعنى «أبْلِي وأَخْلِقِي»', '', 'هو دُعاءٌ لها بكَثرةِ المالِ والعِيالِ', '', 'الْبَسي وتَخَلَّقي بالأخْلاقِ الحَسَنةِ', '', 'الْبَسِي وعِيشي إلى أنْ يَصِيرَ ما تَلْبَسينَه خَلَقًا بَالِيًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَديثِ عُثمانَ بنِ عبدِ اللهِ بنِ مَوْهَبٍ قال: أَرسَلَني أَهْلي إِلى أُمِّ سَلَمةَ بقَدَحٍ مِن ماءٍ، وكانَ إذا أَصابَ الإِنسانَ عَينٌ أو شَيءٌ بَعَث إِلَيها:', '', 'مِحْضَبَه', '', 'يَسْتَشْفِعُها أنْ تَدعُو له', '', 'بِرُقْيَةٍ تُرقِيهَا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَن هُم الذين كانوا يَلْعَبُونَ عِنْدَ النبيِّ ﷺ بحِرَابِهِمْ:', '', 'الأنصارُ', '', 'المُهاجِرون', '', 'الحَبَشَةُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ  البَراءُ بن عازِبٍ رضي الله عنه: «كانَ النبيُّ ﷺ مَرْبُوعًا، بَعِيدَ ما بيْنَ المَنْكِبَيْنِ» مَعنَى «مَرْبوعًا»:', '', 'كَتِفُه مُرَبَّعةٌ', '', 'مُتوسِّطُ القامةِ بين الطَّويلِ والقَصيرِ', '', 'مُتوسِّطُ العَرضِ بين الرَّفيعِ والعَريضِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا قَسَم شَخصٌ شَيئًا قَليلًا بين أصحابِه؛ فإنَّ السُّنَّةَ:', '', 'أن يَقسِمَه بالتَّساوي', '', 'ألَّا يَأخُذَ لنَفسِه نَصيبًا', '', 'أن يُعطِيَ الكَبيرَ أكثَرَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن عَبدِ اللهِ بنِ عَمرٍو رَضِيَ اللهُ عَنهُما قال: رَأَى النبيُّ ﷺ عَلَيَّ ثَوْبَيْنِ مُعَصْفَرَيْنِ، فَقالَ: أَأُمُّكَ أَمَرَتْكَ بهذا؟ قُلتُ: أَغْسِلُهُمَا، قالَ: بَلْ:', '', 'أَحْرِقْهُمَا', '', 'اعْطِهِما امرأَتَك', '', 'تَصدَّق بِهِما', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا أكَلَ شَخصٌ في رُفقةٍ فإنَّ عَليهِ أن يأكُلَ:', '', 'مِمَّا يَليهِ', '', 'ما يَشتَهيهِ', '', 'ممَّا يَأكلُون ولا يَزيد', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالكَتَمِ في قَولِ النَّبيِّ ﷺ: «إِنَّ أَحْسَنَ مَا غُيِّرَ بِهِ هَذَا الشَّيْبُ الْحِنَّاءُ وَالْكَتَمُ»', '', 'نَباتٌ يُصبَغُ به، وصِبغُه أصفَرُ، أو أسودُ يَميلُ إلى الحُمرةِ', '', 'الفُلفُل الأَحمَر بعدَما يُنقَع يَتحوَّل صِباغًا أَحمرَ يُصبَغُ بهِ', '', 'نَوعٌ مِن السّلقِ أصفرُ اللَّونِ يُعطِي صَفارًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'من هم الذين لا يَكونُون شُفَعَاءَ وَلَا شُهَدَاءَ، يَومَ القِيَامَةِ:', '', 'النَّمَّامُونَ', '', 'الطَّعَّانون', '', 'اللَّعَّانُونَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ عن الحَجَرِ الأَسودِ: واللهِ لَيَبْعَثَنَّهُ اللهُ يومَ القِيامَةِ له:', '', 'عَيْنانِ يُبصِرُ بهما، ولِسَانٌ يَنطِقُ به، يَشهَدُ على مَنِ استَلَمَهُ بحَقٍّ', '', 'وَجهانِ كِلاهُما أبيَضُ منَ اللَّبَنِ', '', 'يَدانِ يَشهَدُ على مَنِ استَلَمَه بحَقٍّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كَنَّى النَّبيُّ ﷺ عنِ السَّفَرِ بشَدِّ الرِّحالِ في قَولِه: «لا تُشَدُّ الرِّحالُ إلَّا إلى ثَلاثةِ مَساجِدَ»:', '', 'أنَّ الرِّحالَ أشرَفُ ما يَركَبُه بَنو آدَمَ للسَّفَرِ', '', 'وذلك أنَّ النَّهيَ مُختَصٌّ برُكوبِ الإبِلِ', '', 'لأنَّه لازِمُه، وخرَجَ ذِكرُها مَخرَجَ الغالبِ في رُكوبِ المسافرِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن عَبدِ اللهِ بنِ عبَّاسِ رَضِيَ اللهُ عَنهُما: نَهَى رَسولُ اللهِ ﷺ عن كُلِّ ذِي نَابٍ مِنَ السِّبَاعِ، وَعَنْ:', '', 'الدُّبَّاءِ، والحَنْتَمِ، والنَّقيرِ، والمُزَفَّتِ', '', 'كُلِّ ذِي مِخلَبٍ مِنَ الطَّيْرِ', '', 'أَكلِ مالِ اليَتيمِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «أَخْرَجَ إلَيْنَا أَنَسٌ نَعْلَيْنِ جَرْدَاوَيْنِ» معنى «جَرْدَاوَيْنِ»:', '', 'جَديدانِ فيهِما شَعرٌ', '', 'مَصنُوعانِ مِن الجَرْدَوانِ وهو نَباتٌ عَريضُ الأَوراقِ يَنمُو في جَزيرةِ العَربِ', '', 'بَالِيانِ مُجرَّدانِ عَنِ الشَّعرِ لقِدَمِهما', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: إنَّ العَبدَ إذا لَعَن شَيئًا صَعِدَتِ اللَّعنةُ إلى السَّماءِ:', '', 'فتُغلَقُ أبوابُ السَّماءِ دُونَها', '', 'فتَعُودُ كأَعظَمِ ما تكونُ وتَرجِعُ إلى صاحِبِها', '', 'فيَتَلَقَّفُها كلُّ مَلَكٍ بينَ السَّماءِ والأَرضِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'حَديثُ أبي مَسعودٍ البَدرِيِّ رَضِيَ اللهُ عَنهُ: «لا تُجزِئُ صلاةُ أحَدِكُم حتَّى يُقيمَ ظَهرَهُ في الرُّكوعِ والسُّجودِ» يَدُلُّ على:', '', 'أنَّ الصَّلاةَ نَوعانِ: مُجزِئَةٌ، وغيرُ مُجزِئَة', '', 'أنَّ إِقامةَ الظَّهْرِ في الصَّلاةِ رُكنٌ لا تَصِحُّ الصَّلاةُ بدُونِه', '', 'أنَّ الطُّمأنينةَ في الصَّلاةِ رُكنٌ من أركانِ الصَّلاةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: «لمَّا قَدِم رَسولُ اللهِ ﷺ المدينةَ، لَعِبَتِ الحَبشةُ لقُدومِه فَرَحًا بذلك»، لَعِب الحَبشَةُ بـ:', '', 'بحِرابِهم', '', 'بسِهامِهم', '', 'بسُيوفِهم', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِ النَّبيِّ ﷺ: «نِعْمَ الأُدُمُ الخَلُّ»:', '', 'التَّنبيهُ على فائدةِ الخَلِّ للأبدانِ', '', 'إِباحَةُ التَّمتُّع بما أَحَلَّ اللهُ مِن المَأكَلِ والمَشرَبِ', '', 'الاقتِصادُ في المأْكَلِ، ومنْعُ النَّفسِ عَن ملاذِّ الأطعِمةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لا يَنْبَغِي لِصِدِّيقٍ أنْ يَكونَ:', '', 'مُرائِيًا', '', 'لَعَّانًا', '', 'حَريصًا', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ المُسيءِ صَلاتَه:', '', 'أنَّ قِراءةَ الفاتِحةِ غيرُ واجِبةٍ', '', 'أنَّ البَسملةَ رُكنٌ مِن أَركانِ الصَّلاةِ', '', 'الأمرُ بالطُّمَأنينةِ في الصَّلاةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «أَخْرَجَ إلَيْنَا أَنَسٌ نَعْلَيْنِ جَرْدَاوَيْنِ لهما قِبَالَانِ» مَعنَى «قِبَالَانِ»', '', 'طَرفانِ؛ طَرفٌ عندَ الأَصابِع، وطَرفٌ عن العَقِبِ', '', 'ما يُشَدُّ به سَيرُ النَّعلِ', '', 'غِطاءَانِ مِن الشَّعرِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: لا تُشَدُّ الرِّحَالُ إِلَّا إِلَى ثَلاثَةِ مَسَاجِدَ: مَسْجِدِ الحَرَامِ، وَمَسْجِدِي:', '', 'وَمَسْجِدِ الأَقْصَى', '', 'ومَسجِدِ قُباء', '', 'والمَسجِد الذي أُسِّسَ على التَّقوَى', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ النبيُّ ﷺ: اسْتَكْثِرُوا مِنَ النِّعَالِ:', '', 'فلا يَدرِي أَحدُكم متَى تُسلَبُ النِّعمَة', '', 'فإنَّ الرَّجُلَ لا يَزَالُ رَاكِبًا ما انْتَعَلَ', '', 'فإنَّها مِن سُنَنِ المُرسَلِينَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الأمرُ في حَديثِ: «إِذَا وقَعَ الذُّبَابُ في شَرَابِ أحَدِكُمْ، فَلْيَغمِسْهُ ثُمَّ لِيَنْزِعْهُ؛ فإنَّ في إحْدَى جَنَاحَيْهِ دَاءً، والأُخْرَى شِفَاءً»', '', 'للإرشادِ لا للوُجوبِ', '', 'للكَراهةِ', '', 'للوُجوبِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديِث أنَسِ بنِ مالِكٍ رَضِيَ اللهُ عَنهُ قال: «كانَ رَسولُ اللهِ ﷺ في بَعضِ أسفَارِهِ وغُلامٌ أسوَدُ يُقالُ له: أنجَشَةُ يَحْدُو...»:', '', 'مَشروعِيَّةُ السَّفَرِ بالنِّساءِ', '', 'مَشروعِيَّةُ الحُداءِ', '', 'مَشروعِيَّةُ اتِّخاذِ الغِلمانِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'النِّفاقُ المَذكورُ في الحَديثِ: «إنَّ المُختَلِعاتِ والمُنتَزِعاتِ هنَّ المنافِقاتُ» هو:', '', 'نِفاقٌ قَوليٌّ', '', 'نِفاقٌ عَمَليٌّ', '', 'نِفاقٌ اعتِقادِيٌّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كَنَّى النَّبيُّ ﷺ عنِ النِّساءِ بالقَواريرِ في قَولِه: «رُوَيْدَكَ يا أنْجَشَةُ سَوْقَكَ بالقَوَارِيرِ»:', '', 'لشَبَهِهِنَّ بالقَواريرِ في صَفاءِ قُلوبِهنَّ', '', 'لِضَعْفِ بِنْيَتِهِنَّ ورِقَّتِهِنَّ ولَطَافَتِهِنَّ', '', 'لأنَّهُنَّ يَكفُرنَ العَشيرَ مِثلَما تَكفُر القَواريرُ الشَّرابَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقولُ رَسولُ الله ﷺ: الْمُسْتَبَّانِ ما قالا فَعَلَى البادِئِ، ما لَمْ:', '', 'يَفتَرِقا', '', 'يَعْتَدِ المَظْلُومُ', '', 'يَقُولا زُورًا', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: «إذَا انْقَطَعَ شِسْعُ أَحَدِكُمْ فلا يَمْشِ في الأُخْرَى حتَّى يُصْلِحَهَا» مَعنَى «شِسعُ»:', '', 'مُقدِّمةُ النَّعلِ التي تَلِي الأَصابِعَ', '', 'أحَدُ سُيورِ النِّعالِ', '', 'الجِلدُ الذي في أَسفَلِ النَّعلِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان المُصَدِّقُ -عامِلُ الصَّدَقةِ- يَأتي ليَجمَعَ صَدَقاتِ الماشِيةِ من أربابِها، عِندَ:', '', 'أَسواقِهِم', '', 'مَبارِكها التي تبيتُ فيها', '', 'أماكنِ الشُّربِ حيثُ تُجمَعُ الحيَواناتُ للسَّقيِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: الرَّاحِمُونَ يَرْحَمُهُمُ الرَّحْمَنُ:', '', 'إنَّ رَحمةَ الله سَبقَت غَضَبَهُ', '', 'وكلُّ بَني آدَمَ خَطَّاءٌ، وخيرُ الخطَّائين التَّوابُون', '', 'ارْحَمُوا مَنْ فِي الأَرْضِ يَرْحَمْكُمْ مَنْ فِي السَّمَاءِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى النَّبِيُّ ﷺ عَنْ نَتْفِ الشَّيْبِ، وَقَالَ:', '', 'إنَّه شَرَفُ المُؤمِنِ', '', 'إِنَّهُ نُورُ المُسلِمِ', '', 'إنَّه يُنِيرُ للمُؤمنِ الصِّراطَ يَومَ القِيامةِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن عائشةَ رَضِيَ اللهُ عَنها قالت: «لقَدْ كانَ رَسولُ اللَّهِ ﷺ يُصَلِّي الفَجْرَ، فَيَشْهَدُ معهُ نِسَاءٌ مِنَ المُؤْمِنَاتِ مُتَلَفِّعَاتٍ في مُرُوطِهِنَّ» مَعنَى «مُرُوطِهِنَّ»:', '', 'ثَوبٌ يَستُرُ أسفَلَ المَرأةِ يُشبِهُ الإزارَ', '', 'الثَّوبُ مِنَ الصُّوفِ أو غيرِه، وهو يُشبِهُ المِلحَفةَ', '', 'أثَواب كانت تُصنَع مِن الحَريرِ تُغطِّي سائِرَ الجَسَدِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: أَسْفِرُوا بِالفَجْرِ، فَإِنَّهُ:', '', 'أعظَمُ للأجرِ', '', 'أنسَأُ للأجَلِ', '', 'أتْقَى للنَّفسِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ ابْنِ عَبَّاسٍ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: أَهْوَنُ أَهْلِ النَّارِ عَذَابًا أَبُو طَالِبٍ، وَهُوَ:', '', 'في الدَّركِ الأسفَلِ منها', '', 'مُنْتَعِلٌ بِنَعلَيْنِ يَغْلي مِنهُمَا دِمَاغُه', '', 'في لِحافٍ مِن نارٍ ظَانًّا أنَّه أَشدُّ أَهلِ النارِ عَذابًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أعظَمُ آيةٍ في كِتابِ اللهِ عزَّ وجلَّ؛ هي:', '', 'آيةُ الدَّينِ', '', 'آيةُ الكُرسِيِّ', '', 'آيَةُ الميزانِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'سورةُ "قُلْ هُوَ اللهُ أَحَدٌ" تَعدِلُ:', '', 'ثُلُثَ القُرآنِ', '', 'رُبُعَ القُرآنِ', '', 'نِصفَ القُرآنِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى رَسولُ اللهِ ﷺ عن بَيْعِ الغَرَرِ. ما معنى (بَيْع الغَرَرِ):', '', 'كلُّ بَيعٍ اشتَمَل على أيِّ نَوعٍ من أنواعِ الخِداعِ، أو كان مَجهولًا أو مَعجوزًا عنه', '', 'بَيعُ ما يُستعمَلُ في المُحَرَّماتِ كالعِنَبِ إذا عُصِرَ خَمرًا', '', 'بَيعُ الثَّمَرِ بالتَّمرِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جُملةُ: "ألَا إنَّ القوَّةَ الرَّميُ"، تَعني:', '', 'أنَّ رَمْيَ الجَمَراتِ بقُوَّةٍ تُظهِرُ شَجاعةَ المُسلِمِ', '', 'أنَّ الرَّميَ هو منَ القُوَّةِ الحَقيقيَّةِ في الحَربِ', '', 'أنَّ ضَرْبَ الأعداءِ بالأحجارِ يُقَوِّي عَزيمةَ المُحارِبين', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: انْهَكُوا الشَّوَارِبَ:', '', 'وَأَعْفُوا اللِّحَى', '', 'واذكُرُوا اسمَ الله', '', 'ولا تَحِفُّوا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَغافيرُ: هو', '', 'نَوعٌ من الحَلْواءِ طَيِّبةُ الرَّائحةِ', '', 'صَمغٌ حُلوٌ له رائحةٌ كَريهةٌ', '', 'شَجَرٌ من شَجَرِ البادِيةِ له ثِمارٌ طَيِّبةُ الطَّعمِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقَولُ النَّبيُّ ﷺ: وجُعِلَ قُرَّةُ عَينِي:', '', 'أن أَكونَ عَبدًا شَكُورًا', '', 'في الصَّلاةِ', '', 'حُبُّ الأَنصارِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ عائشةَ رَضِيَ اللهُ عَنها: «لقَدْ كانَ رَسولُ اللَّهِ ﷺ يُصَلِّي الفَجْرَ، فَيَشهَدُ معهُ نِسَاءٌ مِنَ المُؤْمِنَاتِ...»', '', 'وُجوبُ شُهودِ النِّساءِ للصَّلاةِ في جَماعةٍ', '', 'مَشروعيَّةُ خُروجِ النِّساءِ للصَّلاةِ في المَساجِدِ باللَّيلِ', '', 'إِباحَةُ خُروجِ المَرأةِ للصَّلاةِ دُونَ إِذنِ زَوجِها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُوزَنُ العَبدُ عِندَ اللهِ تَعالَى بمِعيارِ:', '', 'التَّقوَى', '', 'الوَرعِ', '', 'الصَّلاةِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسُولُ اللَّهِ ﷺ: لاَ صَلَاةَ بَعْدَ الصُّبْحِ حَتَّى تَرْتَفِعَ الشَّمْسُ، وَلَا صَلَاةَ بَعْدَ:', '', 'العَصْرِ حَتَّى تَغِيبَ الشَّمْسُ', '', 'المَغرِبِ حتَّى يَسقُطَ الشَّفَقُ', '', 'الظُّهرِ حتَّى يَصيرَ ظِلُّ كُلِّ شَيءٍ مِثلَه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن خَبَّابِ بنِ الأرَتِّ رَضِيَ اللهُ عَنهُ، قال: شَكَوْنا إِلَى رَسولِ اللهِ ﷺ الصَّلاةَ في الرَّمْضاءِ:', '', 'فقال: أَبرِدُوا بالصَّلاةِ', '', 'فلم يُشْكِنا', '', 'فأمَرَنا بالسُّجودِ على ثِيابِنا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: إِذَا دَخَلَ رَمَضَانُ:', '', 'فُتِّحَتْ أَبْوَابُ الجَنَّةِ، وَغُلِّقَتْ أَبْوَابُ جَهَنَّمَ وَسُلْسِلَتِ الشَّيَاطِينُ', '', 'فاجتَهِدُوا في الدُّعاءِ', '', 'لا يَزالُ عليكُم مِن الله رَقيبٌ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: دَخَلَتِ امْرَأَةٌ النَّارَ فِي هِرَّةٍ:', '', 'أَحرَقَتها بالنَّارِ؛ فهي تَتقَلَّبُ في نارِ جَهنَّم بُكرةً وعَشِيَّة', '', 'رَبَطَتْهَا، فَلَمْ تُطْعِمْهَا، وَلَمْ تَدَعْهَا تَأْكُلُ مِنْ خَشَاشِ الأَرْضِ', '', 'ضَرَبَتْها حتَّى المَوتِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: «مَثَلُ الَّذِي يَقْرَأُ القُرْآنَ، وَهُوَ حَافِظٌ لَهُ مَعَ السَّفَرَةِ الكِرَامِ البَرَرَةِ، وَمَثَلُ الَّذِي يَقْرَأُ، وَهُوَ يَتَعَاهَدُهُ، وَهُوَ عَلَيْهِ شَدِيدٌ:', '', 'مع النَّبِيِّينَ والصِّدِّيقِينَ والشُّهداء', '', 'فنِعمَ ما يَلقَى الله به غَدًا', '', 'له أجْرانِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى رَسولُ اللهِ ﷺ يَومَ خَيْبَرَ عن لُحُومِ الحُمُرِ الأهْلِيَّةِ، وَأَذِنَ في لُحُومِ:', '', 'البِغالِ', '', 'الخَيْلِ', '', 'الحُمُرِ الإنْسِيَّة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: أَيُّما رَجُلٍ قالَ لأخِيهِ: يا كافِرُ، فقَدْ باءَ بها:', '', 'أحَدُهُما', '', 'هُو', '', 'آثِمًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قام بشِراءِ صُهَيبٍ وأعتَقَه:', '', 'زَيدُ بنُ عَمرِو بنِ نُفَيلٍ', '', 'عَبدُ المُطَّلِبِ', '', 'ابنُ جُدْعانَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن عَائِشَةَ قالَت: «لقَدْ رَأَيْتُ رَسولَ اللَّهِ ﷺ يَوْمًا علَى بَابِ حُجْرَتي والحَبَشَةُ يَلْعَبُونَ في المَسْجِدِ، ورَسولُ اللَّهِ ﷺ يَسْتُرُنِي برِدَائِهِ، أنْظُرُ إلى لَعِبِهِمْ» يُستَفادُ منَ الحَديثِ:', '', 'مَشرُوعِيَّة اللَّعِب للزَّوجةِ إذا كانت صَغيرة', '', 'إِباحَة اللَّعِب ولكن في غيرِ المَسجِد', '', 'حُسنُ خُلُقِه الكَريمِ، وجَميلُ مُعاشَرتِه لِأهلِه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا قالَتْ: يَرْحَمُ اللَّهُ نِسَاءَ المُهَاجِرَاتِ الأُوَلَ؛ لَمَّا أنْزَلَ اللَّهُ: {وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلَى جُيُوبِهِنَّ}:', '', 'بادَرْنَ بالحِجابِ حتَّى كأنَّهُنَّ الغِربانِ', '', 'شَقَقْنَ مُرُوطَهُنَّ فَاخْتَمَرْنَ بهَا', '', 'ضَربَت كلُّ امرأَةٍ مِنهُنَّ خِمارِها على جَيْبِها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «إنَّ رَسولَ اللَّهِ ﷺ دَخَلَ عَلَيَّ مَسْرُورًا تَبْرُقُ أسارِيرُ وجْهِهِ» معنى «أسارِيرُ وجْهِهِ»:', '', 'لمَعانُ العَيْنِ الذي يكونُ عندما يُسَرُّ الإنسانُ', '', 'التَّكاسيرُ التي تَكونُ في الذَّقَنِ', '', 'الخُطوطُ الَّتي في الجَبْهةِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عنِ المُغيرةِ رَضِيَ اللهُ عَنهُ قال: كُنْتُ مع النبيِّ ﷺ في سَفَرٍ، فَقالَ: يا مُغِيرَةُ:', '', 'خُذِ الإدَاوَةَ', '', 'لا يَزالُ لِسانُك رَطبًا بذِكرِ الله', '', 'اتَّقِ اللهَ يَجعَل لكَ مَخرجًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن عائشةَ رَضِيَ اللهُ عنها قالت: كانَ رَسولُ اللَّهِ ﷺ يُحِبُّ:', '', 'تَمرَ المَدينَةِ', '', 'الحَلْوَاءَ والعَسَلَ', '', 'الخُبزَ المُرَقَّقَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان الحَبَشَةُ يَلْعَبُونَ عِنْدَ النبيِّ ﷺ بحِرَابِهِمْ.. مَن هُم الحَبَشَةُ:', '', 'هُم جِنسٌ منَ السُّودانِ', '', 'اسمٌ يُطلَق على العَبيدِ', '', 'الغِلمانُ الصِّغارُ الذين قارَبُوا الحُلُمَ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: لَوْ كَانَتِ الدُّنْيَا تَعْدِلُ عِنْدَ اللَّهِ جَنَاحَ بَعُوضَةٍ:', '', 'مَا سَقَى كَافِرًا مِنْهَا شَرْبَةَ مَاءٍ', '', 'ما أَخرجَ آدمَ مِن الجنَّة', '', 'لَعَجَّلَ العَذابَ على أَهلِ الدُّنيا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: «دَخَلْتُ علَى عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا وعَلَيْهَا دِرْعُ قِطْرٍ، ثَمَنُ خَمْسَةِ دَرَاهِمَ، فَقالَتْ: ارْفَعْ بَصَرَكَ إلى جَارِيَتي انْظُرْ إلَيْهَا؛ فإنَّهَا تُزْهَى أنْ تَلْبَسَهُ في البَيْتِ» معنى «تُزْهَى»:', '', 'تَتكبَّرُ وتَأنَفُ', '', 'تُزَيَّنُ لعُرْسِها', '', 'تَصفَرُّ به حَياءً', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن قُرَّةَ بنِ إياسٍ رَضِيَ اللهُ عَنهُ قال: «أتيتُ رَسولَ اللَّهِ ﷺ في رَهْطٍ مِن مُزَيْنةَ، فَبايَعْناهُ، وإنَّ قَميصَهُ لمُطلَقُ الأزرارِ قالَ: فبايعتُهُ، ثمَّ أدخَلتُ يَدَيَّ في جَيبِ قَميصِهِ فمَسِسْتُ الخاتمَ» مَعنَى «الخاتَم» هو', '', 'خاتَمُ النُّبُوَّةِ الَّذي كان في مُؤخَّرِ كَتِفِ النَّبيِّ ﷺ', '', 'الخاتَمُ الَّذي أعَدَّه النَّبيُّ ﷺ مَهرًا لامرأةٍ من مُزَينةَ', '', 'الخاتَمُ الَّذي كان يَختِمُ به النَّبيُّ ﷺ كُتُبَه ورَسائلَه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أسَرَ الرُّومُ صُهَيبًا، ثُمَّ اشتَراهُ رَجُلٌ من قَبيلةِ:', '', 'قُضاعةَ', '', 'كَلبٍ', '', 'هُزيل', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«كانَ النبيُّ ﷺ يُعْجِبُهُ التَّيَمُّنُ، في تَنَعُّلِهِ، وتَرَجُّلِهِ، وطُهُورِهِ، وفي شَأْنِهِ كُلِّهِ» معنى (التَّيَمُّن):', '', 'البِدءُ باليَمينِ', '', 'الفَألُ الحَسَن', '', 'اليُمْنُ والبِشارَة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَديثِ عائِشةَ أنَّ النبيَّ ﷺ قال: «إنَّ هذِه الأقْدامَ بَعْضُها مِن بَعْضٍ». مَن يَقصِدُ النبيُّ ﷺ:', '', 'العبَّاسَ بنَ عبدِ المُطَّلِب وابنَه عبدَالله رضي الله عنهما', '', 'عُمرَ بنَ الخطَّابِ وعبدَالله بنَ عُمرَ رضي الله عنهما', '', 'زَيدَ بنَ حارِثَةَ وأُسامَةَ بن زَيدٍ رضي الله عنهما', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما معنى الإدَاوَةَ:', '', 'سِكِّينٌ صَغيرٌ تُذبَحُ به الغَنَمُ', '', 'وِعاءٌ صَغيرٌ يوضَعُ فيه الماءُ لِلوُضوءِ ونحوِه', '', 'المَحْبَرةُ التي يكونُ فيها المِدادُ الذي يُكتَب بِه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'دِحيةُ الكَلبِيُّ هو أحَدُ أصحابِ النَّبيِّ؛ وكان:', '', 'يُضحِكُ النبيَّ ﷺ فيَقولُ له النبيُّ ﷺ: والله إنِّي لأُحِبُّك', '', 'مَريضًا مُزمِنًا فشَكَا إلى النبيِّ ﷺ وسَألَهُ أن يَدعُو الله له', '', 'جَميلًا فكان جِبريلُ يأْتِي إلى رَسولِ اللهِ ﷺ على صورَةِ دِحْيَةَ غالِبًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'بيْنَا الحَبَشَةُ يَلْعَبُونَ عِنْدَ النبيِّ ﷺ بحِرَابِهِمْ، دَخَلَ عُمَرُ فأهْوَى إلى الحَصَى فَحَصَبَهُمْ بهَا، فَقالَ له النبي ﷺ:', '', 'أَبَيْنَ يَدَيْ رَسولِ الله ذلكَ يا عُمر', '', 'أَحسَنتَ يا عُمر', '', 'دَعْهُمْ يا عُمَرُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: لَمَّا خَلَقَ اللَّهُ الخَلْقَ كَتَبَ فِي كِتَابِهِ وَهُوَ يَكْتُبُ عَلَى نَفْسِهِ وَهُوَ وَضْعٌ عِنْدَهُ عَلَى العَرْشِ إِنَّ:', '', 'عَذابي هو العَذابُ الأليمُ', '', 'رَحْمَتِي تَغْلِبُ  غَضَبِي', '', 'الجَنَّةَ لا يَدخُلُها إلَّا المُتَّقونَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ اللهِ ﷺ: «السَّاعِي على الأَرمَلَةِ، والمِسكِينِ، كَالمُجاهِدِ في سَبيلِ اللهِ» مَعنَى السَّاعِي على الأرمَلَةِ، والمِسكينِ:', '', 'عائِلهُم الذي يَعولُهم وتجبُ عليه نَفقَتُهم', '', 'الَّذي يَقضي لهم حَوائجَهُم ويُحسِنُ إليهم', '', 'الوالي الذي يَعيشُون في وِلايَتِه فيَتكفَّلُهم مِن بَيتِ مَالِ المُسلمِينَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المُرادُ بكَفتِ الشَّعرِ الوارِدِ في حَديثِ عَبدِ اللهِ بنِ عبَّاسٍ رَضِيَ اللهُ عَنهُما: «ولَا نَكْفِتَ الثِّيَابَ والشَّعَرَ»', '', 'قَصُّه وتَقصيرُه', '', 'سَدْلُه وإرسالُه مِن غير رَبطٍ', '', 'رَبْطُه بحيثُ لا يَستَرْسِلُ ويَنسابُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الرُّكنانِ اللَّذانِ تَرَك النَّبيُّ ﷺ استِلامَهُما؛ هما:', '', 'رُكنُ الحَجَرِ الأسوَدِ، والرُّكنُ الشَّامِيُّ', '', 'الرُّكنُ الشَّاميُّ والعِراقيُّ اللَّذانِ يَلِيانِ الحِجْرَ', '', 'الرُّكنُ اليَمانِيُّ والرُّكنُ  العِراقيُّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا شَرِبَ شَخصٌ اللَّبَنَ، أو أكَلَ طَعامًا فيه دَسَمٌ، فإنَّه:', '', 'يُستحَبُّ أن يُنظِّفَ فَمَه منه قبلَ أن يُصلِّيَ', '', 'لا يَجوزُ له أن يَتمَضمَضَ لأنَّ في ذلك إِهدارًا للنِّعمَة', '', 'تَحرُمُ عَليهِ الصَّلاةُ حتَّى يَتوَضَّأَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المقَصودُ بقَولِ المَرأةِ "فَامَّرَقَ شَعرُها":', '', 'تَغيَّر لَونُه إثْرَ مَرَضٍ', '', 'تَشابَك وكَثُر فيه القَملُ', '', 'خَرَجَ شَعَرُها مِن مَوضعِه فتَمزَّق وتَقطَّعَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'معنى (لَا يَفْرَكْ) في قولِه ﷺ: «لَا يَفْرَكْ مُؤْمِنٌ مُؤْمِنَةً، إنْ كَرِهَ منها خُلُقًا رَضِيَ منها آخَرَ».', '', 'لا يُبْغِض', '', 'لا يُقبِّح', '', 'لا يَضرِب', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«ليسَ المُؤمِنُ بالطَّعَّانِ ولا اللَّعَّانِ ولا الفاحِشِ ولا البَذيءِ» مَعنَى «الفاحِشِ»', '', 'جَميعُ ما سَبَق', '', 'فاحِشِ القوْلِ وبذِيءِ اللِّسانِ', '', 'السَّليطِ الَّذي لا حَياءَ له', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كلُّ ثَوبٍ مَخيطٍ مُستمسِكٍ على الجَسَدِ بدونِ شَدٍّ لا يَلبَسُه', '', 'الرَّجُلُ حالَ صَلاتِه', '', 'الصَّبيُّ المُميِّزُ', '', 'المُحرِمُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «أنَّ عَبْدَ المَلِكِ بنَ مَرْوَانَ بَعَثَ إلى أُمِّ الدَّرْدَاءِ بأَنْجَادٍ مِن عِندِهِ» معنى «بأَنْجَادٍ»', '', 'ما يُزيَّنُ بهِ البَيتُ مِن الأَمتعَةِ', '', 'الأواني الواسِعَة التي كانت تُصنَع في هَجَر', '', 'جمعُ نِجاد؛ نَوعٌ مِن الخُيولِ العَرَبِيَّةِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى رَسولُ اللَّهِ ﷺ عن بَيْعَتَيْنِ المُنَابَذَةِ، والمُلَامَسَةِ. مَعنَى «المُنابذةِ»:', '', 'أن يَجعَلَ إلْقاءَ السِّلعةِ إيجابًا للبَيعِ؛ فمتى أَلْقى إليه ثوبًا أو غيرَه التَزَمَ المُشترِي بشِرائِه', '', 'أن يَتنابَذَ البائعُ والشَّاري بالألقابِ والسِّبابِ', '', 'أن يَضَع كلُّ واحِدٍ منَ البائعِ والشَّاري ما يُريدُ في قَدَحِ نَبيذٍ ثم يَنبِذَه لصاحِبِه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: اعْتدلُوا في السُّجودِ، ولا يَبسُطنَّ أحَدَكُم:', '', 'جَناحَيْهِ ولا يُجافيهما', '', 'ذِراعَيه انبِساطَ الكَلبِ', '', 'ثَوبَه على فَخِذِه كأنَّه التُّرْس', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'حُكمُ الهِجرةِ واجِبةٌ في حَقِّ:', '', 'الرِّجالِ دُونَ النِّساء والأطفالِ', '', 'كلِّ مُسلِمٍ مُكلَّفٍ حُرٍّ مُقيمٍ', '', 'في حقِّ مَن أسلَمَ في دارِ الكفْرِ، ولم يَأمَنْ على دِينِه، وقدَرَ على الخُروجِ منها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'اسمُ اليَهودِيِّ الَّذي سَحَر النَّبيَّ ﷺ؛ هو:', '', 'فِنحاصُ', '', 'كَعبُ بن الأشَرفِ', '', 'لَبيدُ بنُ الأعصَمِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى رَسولُ اللَّهِ ﷺ عن بَيْعَتَيْنِ المُنَابَذَةِ، والمُلَامَسَةِ. معنى «المُلامَسةِ» هي:', '', 'أنْ يَبِيعَ البائعُ شيئًا إلى المُشترِي على أنَّه متى لَمَسَه؛ فقد تَمَّ البَيعُ', '', 'أن يَلمَسَ البائعُ الثَّمَنَ ويَلمَسَ المُشتَري البِضاعةَ أثناءَ التَّبادُلِ', '', 'أن يَلمَسَ كلٌّ منَ البائعِ والمُشتَري بَعضَهمُ البَعضَ أثناءَ عَمليَّةِ البَيعِ إيجابًا وقَبولًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ النبيُّ ﷺ يُعْجِبُهُ التَّيَمُّنُ، في تَنَعُّلِهِ، وتَرَجُّلِهِ، وطُهُورِهِ، وفي شَأْنِهِ كُلِّهِ. مَعنَى «وتَرَجُّلِه»:', '', 'مَشْيُه على رِجْلِه', '', 'تَسريحُ شَعرِ رَأسِه بالمُشطِ', '', 'نُزولُه من على ظَهرِ فَرَسِه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المُرادُ بكَفتِ الثِّيابِ الوارِدِ في حَديثِ عبدِ اللهِ بنِ عَبَّاسٍ رَضِيَ اللهُ عَنهُما: «ولَا نَكْفِتَ الثِّيَابَ والشَّعَرَ»:', '', 'قَطْعُها وتَمزيقُها', '', 'ضَمُّ بَعضِها فوقَ بَعضٍ بحيثُ لا تَنْسدِلُ', '', 'سَدْلُها وإرسالُها', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المُرادُ بالشِّكالِ في الحَديثِ: «أنَّ رَسولَ اللهِ ﷺ كان يَكرَهُ الشِّكالَ منَ الخَيلِ»', '', 'الخَيلُ الذي نَزَا عليها البَغلُ', '', 'أنْ يَكونَ الفَرَسُ في رِجْلِه اليُمنَى وفي يَدِه اليُسرَى بَيَاضٌ، أو في يَدِه اليُمنَى ورِجْلِه اليُسرَى', '', 'أن يَكونَ الفَرَسُ أدهَمَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهَى النَّبِيُّ ﷺ أَنْ يَلْبَسَ المُحْرِمُ ثَوْبًا مَصْبُوغًا:', '', 'بِوَرْسٍ أَوْ بِزَعْفَرَانٍ', '', 'بالحِنَّاءِ', '', 'بالكَتَمِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بنَهيِ النَّبيِّ ﷺ الوارِدِ في حَديثِ أَنَسٍ، قَالَ: كُنَّا عِنْدَ عُمَرَ فَقَالَ: «نُهِينَا عَنِ التَّكَلُّفِ»', '', 'المُبالَغةُ في إكرامِ الضِّيفانِ', '', 'أن يَتكَلَّفَ الإنسانُ ما لا عِلمَ له به', '', 'التَّكلُّف في مُجاهَدةُ النَّفسِ والهَوى والشَّيطانِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِه ﷺ: «لا جَلَبَ»', '', 'لا تَحصدُوا الثَّمرَ مِن الشَّجرِ قبلَ نُضوجِه واستِوائِه', '', 'لا تَجلِبوا البِضاعةَ إلى الأسْواقِ قبل أَوانِ بَيْعِها', '', 'لا يَطلُبُ العامِلُ على الصَّدقَةِ من أربابِ الأموالِ جَلْبَها إليه من أماكِنِ رَعْيِها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: لَا سَبَقَ إِلَّا فِي:', '', 'خُفٍّ أَوْ فِي حَافِرٍ أَوْ نَصْلٍ', '', 'الدُّبَّاءِ والحَنْتَمِ والْمُزَفَّتِ والنَّقِيرِ', '', 'سُقْيَا الماءِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا ذَبَح المُسلِمُ شَيئًا مِمَّا يُباحُ أكْلُه بغَيرِ السِّكِّينِ الحَديدِيَّةِ؛ كأنْ يَذبَحَه بحَجَرٍ له حَدٌّ قاطِعٌ، فإنَّه:', '', 'يَصِحُّ أكلُه', '', 'يُؤكَلُ إذا أُدرِكَ قبلَ أن يَلفِظَ أنفاسَه، فيُذبَح بسِكِّينٍ', '', 'يَكونُ مَيتةً لا يَحِلُّ أكْلُه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أَوَّلُ فِتنةٍ وَقَعَتْ في بَني إسرائيلَ كانتْ بِسَبَبِ:', '', 'المالِ', '', 'النِّساءِ', '', 'الضِّياعِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يَكونُ طَعامُ الوَليمةِ في العُرسِ بـ:', '', 'أيِّ شَيءٍ يَقدِرُ عليه المسلِمُ وقتَ الزَّواجِ دونَ إسْرافٍ وفخْرٍ', '', 'ذَبيحَةٍ مِن بَهائمِ الأَنعامِ', '', 'ما يُقِرُّهُ العُرفُ في كلِّ قُطرٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إنَّه ممَّا شاء اللهُ تَعالَى وقَضَى بحِكمَتِه البالِغةِ أنْ يَجعَلَ الدُّنيا دارَ:', '', 'حِسابٍ وجَزاءٍ', '', 'ابتِلاءٍ واختِبارٍ', '', 'نَعيمٍ مُقيمٍ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الصَّدَقاتُ أوِ الزَّكَواتُ حينَ يُعطِيها المُتَصَدِّقُ؛ فهي إنَّما تَقَعُ في:', '', 'رِياضِ الجنَّة', '', 'يَدِ اللهِ تَعالَى أوَّلًا', '', 'المَلَكِ المُوَكَّلِ بالصَّدَقاتِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كان المُؤذِّنونَ قَديمًا يُؤَذِّنون منَ الأماكِنِ المُرتَفِعةِ؛ وذلك:', '', 'لِكَي يَصِلَ الصَّوتُ إلى أبعَدِ مَدًى يُمكِنُ أنْ يَصِلَ إليه', '', 'إشعارًا بعُلُوِّ مَرتَبَتِهِم في الآخِرَة', '', 'لأنَّه أرفَعُ لدَرَجاتِهم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: ما مِنْ يَومٍ أكثَرَ من أن يُعتِقَ اللهُ فيه عَبيدًا مِنَ النَّارِ مِن يَومِ:', '', 'الفِطرِ', '', 'عَرَفةَ', '', 'النَّحرِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ عُثمانَ بنِ أبي العاصِ رَضِيَ اللهُ عَنهُ: «يا رسولَ اللَّهِ، اجعَلْني إمامَ قَومي؟ فقالَ: أنتَ إمامُهُم، واقتدِ بأضعفِهِم واتَّخذْ مُؤَذِّنًا لا يَأخُذُ علَى أذانِهِ أجرًا»:', '', 'مَشروعِيَّةُ طَلَبِ إمامةِ الدِّينِ والدُّنيا', '', 'النَّهيُ عن أَخذِ الأَجرِ على تَعليمِ القُرآنِ', '', 'مَشروعِيَّةُ طَلَبِ الإمامةِ في الخَيرِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'العَقيقةُ؛ هي:', '', 'الذَّبِيحَةُ الَّتِي تُذبَحُ عنِ المَولودِ فِي يومِ سابِعِه', '', 'الذَّبيحةُ التي تُذبَحُ للضِّيفانِ', '', 'الذَّبيحةُ التي تُذبَحُ للعُرسِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُستَنبَطُ من حَديثِ: «إنَّ اللَّهَ قد أبدلَكُم بِهِما خَيرًا مِنهُما يَومَ الأَضحَى ويَومَ الفِطرِ»:', '', 'أنَّه لا بَأسَ أن يَحتَفِلَ المُسلِمون ببَعضِ أعيادِ الجاهِلِيَّةِ', '', 'النَّهْيُ عن الاحتِفالِ بالأعيادِ الجاهليَّةِ', '', 'أنَّه يَحرُمُ اللَّعِبُ يَومَ العيدِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول النبي ﷺ: إنَّ اللهَ يَرفَعُ بهذا الكِتابِ أقوامًا، ويَضَعُ به:', '', 'فُجَّارًا', '', 'آخَرينَ', '', 'أَقوامًا', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يقول النبي ﷺ: أَنَا أَوْلَى النَّاسِ بابْنِ مَرْيَمَ، وَالأنْبِيَاءُ أَوْلَادُ:', '', 'عَلَّاتٍ', '', 'آدمَ عليهِ السَّلامُ', '', 'آبائِهِم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: «مِنْ حَقِّ الإِبِلِ أَنْ:', '', 'لا تُحَمَّل ما لا تطيقُ', '', 'يَرفُقَ بها أصحابُها', '', 'تُحلَبَ على الماءِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: لَيُحَجَّنَّ البَيْتُ وَلَيُعْتَمَرَنَّ بَعدَ خُرُوجِ:', '', 'الدَّجَّالِ', '', 'المَسيحِ عِيسَى ابنِ مَريَمَ', '', 'يَأجُوجَ ومَأجُوجَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصودُ بالكُراعِ في حَديث: «لَوْ دُعِيتُ إلى ذِراعٍ أوْ كُراعٍ لَأَجَبْتُ»:', '', 'لَحمُ الظَّهرِ', '', 'قَوائمُ الحيوانِ وأطْرافُه ممَّا دونَ الرُّكبةِ', '', 'كَتِفُ الشَّاةِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نادَى رَجلٌ النَّبيَّ ﷺ فقال: "يا خَيرَ البَرِيَّةِ" فرَدَّ عليه ﷺ قائِلًا:', '', 'أحسنت', '', 'ذَاكَ إبرَاهِيمُ عَليهِ السَّلامُ', '', 'إنَّما أنا نَفَرٌ مِن قَومِي', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ممَّا اختَصَّ اللهُ تَعالَى به نَبِيَّنا مُحمَّدًا ﷺ على سائرِ النَّبِيِّين:', '', 'التَّيمُّمُ عِندَ انعِدامِ الماءِ، أو عدَمِ القُدرةِ على استِخدامِه', '', 'أنَّه أُمِر بتَبليغِ رِسالةِ اللهِ تَعالَى إلى النَّاسِ', '', 'أنَّه فُرِضَت عَليهِ الصَّلاةُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أمَرَ الإسْلامُ المُزَكِّين بإخْراجِ زَكاةِ أموالِهِم مِن:', '', 'ما يُحِبُّونَه من أموالِهِم؛ من أوسَطِه', '', 'أشرَفِ المالِ وأغْلاهُ ثَمَنًا', '', 'أَقَلِّ المالِ وأَدناهُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَن صلَّى رَكعةً مِن الفَجرِ ثمَّ طَلَعَتِ الشَّمسُ قبلَ أن يُتِمَّ صَلاتَه:', '', 'فإنَّه تَسقُطُ عنه الفَريضةُ، ولا يَكونُ مُدرِكًا للوَقتِ', '', 'فقد أدرَكَ الوقتَ وصحَّتْ صَلاتُه', '', 'فقد فاتَهُ الوَقتُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالرَّمضاءِ في حَديثِ خَبَّابٍ رَضِيَ اللهُ عَنهُ: «شَكَوْنا إلى رَسولِ اللهِ ﷺ الصَّلاةَ في الرَّمْضاءِ...»:', '', 'الرَّمْلُ الذي اشتدَّتْ حَرارتُه', '', 'شِدَّةُ البَردِ', '', 'تَعذيبُ قُرَيشٍ وأذاهُم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الحَرورِيَّةُ نِسبةً إلى:', '', 'حَروراءَ، وهي مَوضعٌ قَريبٌ مِن الكوفةِ', '', 'حَرورَة، وهي قَريَة مِن قُرَى دِمَشقَ', '', 'حَرُونِيَّة، جَبلٌ عَظِيمٌ بين العِراقِ والشَّامِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رسولُ الله ﷺ: إذا استَجمَرَ أحدُكُم:', '', 'فَلْيُتبِعْ ذلك الماءَ', '', 'فلْيتَوَضَّأْ', '', 'فَلْيوتِرْ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «أنَّ النَّبيَّ ﷺ أمَرَ رَجُلًا حينَ أمَرَ المُتَلاعِنَينِ أن يَتلاعَنَا أن يَضَعَ يدَهُ على فِيهِ عِندَ الخامِسةِ، يقولُ: إنَّها مُوجِبةٌ»، مَعنَى «إنَّها مُوجِبةٌ»:', '', 'مُوجِبةٌ للحَدِّ في الدُّنيا', '', 'مُوجِبةٌ للَعْنةِ اللهِ وإحْقاقِها عليه إنْ كان مِنَ الكاذِبينَ', '', 'يَعني: واجِبةً عَليهِ إنْ أرادَ إتمامَ اللِّعانِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديث: «لا تَلاعَنوا بلَعنةِ اللهِ ولا بغَضَبِ اللهِ ولا بالنَّارِ» مَعنَى «ولا بالنَّارِ»:', '', 'لا تَلعَنوا أحَدًا بالنَّارِ', '', 'لا تَدعوا على أحدٍ بدُخولِ النارِ', '', 'أي لا تَسُبُّوا النارَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «أنَّ رَسولَ اللهِ ﷺ نَهَى عن لُقَطةِ الحَاجِّ» مَعنَاه:', '', 'أنَّ لُقطةَ الحاجِّ وَقفٌ يَنتفِعُ بها المِسكينُ وابنُ السَّبيلِ', '', 'أنَّ لُقَطةَ الحاجِّ لا تُملَكُ مُباشرةً، ولا تُؤخَذُ لِتُعَرَّفَ سَنَةً ثُمَّ تُملَكُ، وإنَّما تُعَرَّفُ دائمًا', '', 'أنَّ لُقطَةَ الحاجِّ كغَيرِها مِنَ اللُّقَطاتِ؛ تُعرَّفُ سَنَةً ثُمَّ تُتَملَّكُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «أنَّ النبيَّ ﷺ خَرَجَ وعليه قَبَاءٌ مِن دِيبَاجٍ مُزَرَّرٌ بالذَّهَبِ». الدِّيباجُ هو:', '', 'نَوْعٌ نَفيسٌ مِن الصُّوفِ', '', 'نَوْعٌ نَفيسٌ مِن الحَريرِ', '', 'نَوْعٌ نَفيسٌ مِن القُطنِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَديثِ المِسوَرِ بنِ مَخرَمةَ أنَّ أباه قال: «يا بُنَيَّ، ادْعُ لِي النَّبيَّ ﷺ، فأعْظَمْتُ ذلكَ، فَقُلتُ: أدْعُو لكَ رَسولَ اللَّهِ ﷺ؟! فَقالَ: يا بُنَيَّ، إنَّه ليسَ بجَبَّارٍ، فَدَعَوْتُهُ، فخَرَجَ» معنى «ليس بجَبَّارٍ»:', '', 'أي ليس بَخيلًا وإنما كَريمٌ يَقرِي الضَّيفَ', '', 'ليس من صِفاتِه الكِبْرُ الَّذي يمنَعُه من إجابةِ مَن دَعاه', '', 'يَجبُرُ المِسكِينَ والضَّعيفَ وابنَ السَّبيلِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديثِ: «لَيسَ المُؤمنُ بالطَّعَّانِ ولا اللَّعَّانِ ولا الفاحشِ ولا البَذيءِ» مَعنَى «لَيسَ المُؤمِنُ»', '', 'أي يَرتِفعُ الإيمانُ عن العَبدِ إن فَعلَ ذلك', '', 'لا يَكونُ العبدُ مُؤمِنًا إنْ فَعَل ذلك', '', 'لا يَكونُ العبدُ كامِلَ الإيمانِ، أو ليس من أخلاقِ المُؤمِنِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رسولُ الله ﷺ: ذاقَ طَعمَ الإيمانِ مَن رَضيَ باللهِ رَبًّا، وبالإسلامِ دينًا:', '', 'وبالمُؤمِنينَ أَولِياءَ', '', 'وبالجَنَّةِ مَنزِلًا', '', 'وبمُحَمَّدٍ رَسولًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهى رَسولُ اللهِ ﷺ عن المُزابَنةِ والمُحاقَلةِ. مَعنَى المُزابَنةِ', '', 'بَيعُ الثَّمَرِ في رُؤوسِ النَّخلِ قبلَ جَنْيِه', '', 'بَيعُ العِنَبِ لاتِّخاذِه خَمرًا', '', 'بَيعُ التَّمرِ بالزَّبيبِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما معنى: «جَمَل أوْرَق»', '', 'الجَمَلُ الَّذي فيه صَفارٌ', '', 'الجَمَلُ الَّذي فيه بَياضٌ', '', 'الجَمَلُ الَّذي فيه سَوادٌ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: إِنَّ الرِّفْقَ لَا يَكُونُ فِي شَيْءٍ إِلَّا:', '', 'كان عَدْلًا وافِيًا', '', 'زَانَهُ', '', 'أَتَمَّهُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا كانَتِ القِبلةُ في جِهةِ المَشرِقِ بالنِّسبةِ لمَوطِنِ بَعضِ الأشخاصِ، فعَليِه إذا أراد قَضاءَ حاجَتِه أن:', '', 'يَستَقبِلَ جِهةً أُخرى لا يَكونُ فيها استِقبالُ القِبلةِ ولا استِدبارُها', '', 'يَستَقبِلَ جِهةَ المَغرِبِ', '', 'يَستَقبِلَ جِهةَ المَشرِقِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أُهْدِيَ إلى النبيِّ ﷺ فَرُّوجُ حَرِيرٍ، فَلَبِسَهُ، فَصَلَّى فِيهِ، ثُمَّ انْصَرَفَ، فَنَزَعَهُ نَزْعًا شَدِيدًا كَالكَارِهِ له، وقَالَ: لا يَنْبَغِي هذا:', '', 'للمُؤمِنين', '', 'لنا مَعشَر الرِّجالِ', '', 'لِلمُتَّقينَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديثِ: «ليسَ المؤمنُ بالطَّعَّانِ ولا اللَّعَّانِ ولا الفاحِشِ ولا البَذيءِ» مَعنَى «الطَّعَّانِ»:', '', 'الَّذي يَضرِبُ بالسَّيفِ، ويَطعَنُ إخوانَه المُؤمِنين', '', 'الَّذي يُؤذِي المُسالِمين مِن أَهلِ الذِّمَّةِ', '', 'الَّذي يَقَعُ في النَّاسِ ويَعيبُهم ويَقَعُ في أعْراضِهم ويَغتابُهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'جُملةُ: «مَن أعتَقَ رقبةً مُسلِمةً» مَعناها:', '', 'مَن أعتَقَ عبدًا أو أَمَةً', '', 'مَن تَملَّك  رَقَبةَ امرأةٍ مُسلِمةٍ فتَزوَّجَها ليُعِفَّها', '', 'مَن أنجَى مُسلِمًا منَ القَتلِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رسولُ الله ﷺ: ولا يَتَمَنَّيَنَّ أحَدُكُمُ المَوْتَ: إمَّا مُحْسِنًا', '', 'فَلَعَلَّهُ أنْ يَزْدادَ خَيْرًا', '', 'فَلَهُ إِحسانُه', '', 'أو مُسِيئًا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «لَمْ يَكُنِ النبيُّ ﷺ فَاحِشًا ولَا مُتَفَحِّشًا» الفُحشُ هو:', '', 'الزِّنا', '', 'زِيادةُ الشَّيءِ على المألوفِ مِن مِقْدارِه', '', 'الكَذِبُ الذي يَبلُغُ الآفاقَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: جَعَلَ اللَّهُ الرَّحْمَةَ مِئَةَ جُزْءٍ:', '', 'فأنزَلَها كُلَّها إلى الأَرضِ يَتراحَمُون بِها', '', 'فأَمسَكَ عنده تِسعةً وتِسعِينَ جُزءًا، وأَنزلَ فِي الأرضِ جُزءًا واحِدًا', '', 'فأمسَكَها كُلَّها عِندَه لرَحمَةِ المُؤمِنينَ يومَ القِيامَة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسُولُ اللهِ ﷺ: اتَّقُوا اللَّعَّانَيْنِ قالوا: وَما اللَّعَّانَانِ يا رَسُولَ اللهِ؟ قالَ:', '', 'الَّذي يَلعَنُ والِدَيه', '', 'الَّذِي يَسُبُّ النَّاسَ ويُؤذيهِم', '', 'الَّذي يَتَخَلَّى في طَرِيقِ النَّاسِ، أَوْ في ظِلِّهِمْ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَن رَدَّد مِثلَ ما يَقولُ المُؤذِّنُ يَقينًا، كان أجْرُه على ذلك:', '', 'أن يُغفَرَ له ما تَقدَّمَ من ذَنبِه', '', 'دُخولَ الجنَّةِ', '', 'أن يُلبِسَه اللهُ تَعالَى تاجَ الوَقارِ يَومَ القِيامةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِه ﷺ: «لا جَنَبَ»:', '', 'لا تَبتَعِدوا بأمْوالِكم عن أماكِنِ رَعيِها فيَشُقَّ ذلك على السُّعاةِ', '', 'لا تَتَجَنَّبوا أَرحامَكُم', '', 'لا يَتفاخَر بَعضُكم على بَعضٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُستَنبَطُ من حَديثِ خُفَافِ بْنِ إِيمَاءٍ: رَكَعَ رَسُولُ اللهِ ﷺ، ثُمَّ رَفَعَ رَأْسَهُ، فَقَالَ: «غِفَارُ غَفَرَ اللهُ لَهَا، وَأَسْلَمُ سَالَمَهَا اللهُ...» :', '', 'مَشروعِيَّةُ الكَلامِ بغَيرِ الذِّكرِ في الصَّلاةِ', '', 'مَشروعيَّةُ قُنوتِ النَّوازلِ في الصَّلاةِ', '', 'مَشرُوعِيَّة الفَأْلِ الحَسَنِ بالاسمِ الحَسَنِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالغِرارِ في حَديثِ أبي هُرَيرةَ رَضِيَ اللهُ عَنهُ: «لَا غِرارَ في صَلاةٍ، ولا تَسليمٍ»:', '', 'الخِداعُ', '', 'النَّقصُ', '', 'الإِسراعُ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسُولُ اللهِ ﷺ: الصَّلَاةُ الْخَمْسُ، وَالْجُمُعَةُ إِلَى الْجُمُعَةِ:', '', 'مَطهَرةٌ منَ الذُّنوبِ', '', 'عُلُوٌّ في الدَّرَجاتِ مَنجَاٌة عن السَّيِّئاتِ', '', 'كَفَّارةٌ لِمَا بَينَهُنَّ مَا لَمْ تُغشَ الكَبائِرُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنْ عَائِشَةَ رَضِيَ اللهُ عَنها: أنَّ قَوْمًا قالوا للنبيِّ ﷺ: إنَّ قَوْمًا يَأْتُونَا باللَّحْمِ، لا نَدْرِي: أذُكِرَ اسْمُ اللَّهِ عليه أمْ لَا؟ فَقالَ:', '', 'دَعوهُ لا تَأكُلوه', '', 'سَمُّوا اللَّهَ عليه وكُلُوهُ', '', 'أطْعِمُوه الطَّيرَ والسِّباعَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«لا يَكونُ اللَّعَّانُونَ شُفَعَاءَ وَلَا شُهَدَاءَ، يَومَ القِيَامَةِ» معنى «وَلا شُهداءَ»', '', 'لا يَنالونَ ثَوابَ الشَّهادةِ في المَعارِك لسوءِ فِعالِهم', '', 'لا تُؤخَذُ مِنهمُ الشَّهادةُ عَلى الأُممِ بتَبليغِ رُسلِهم يَومَ القيامَةِ', '', 'أنَّهُم فُسَّاقٌ فلا تُقبَلُ مِنهُم شَهادةٌ في الدُّنيا ولا في الآخِرةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: التَّسْبِيحُ لِلرِّجَالِ:', '', 'والتَّكبيرُ للإمامِ', '', 'والتَّهليلُ للصِّبيانِ', '', 'وَالتَّصْفِيقُ للنِّساءِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يَنْبغي على الشَّاكِّ في الصَّلاةِ أنْ يَبْنيَ على:', '', 'أقلِّ العدَدِ، ثُمَّ يُكمِلَ بِناءً عَليهِ', '', 'أكثَرِ العَدَدِ', '', 'أَغلبِ الظَّنِّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: مَنْ نَسِيَ صَلاةً فَلْيُصَلِّ إِذَا:', '', 'دَخَلَ وَقتُها مِن يَومِها التَّالي', '', 'أَوَى إلى فِراشِه', '', 'ذَكَرَها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'حَرَصَ الإسلامُ على أن يَكونَ للمُسلِمِ شَخصِيَّتُه المُمَيَّزةُ عن غَيرِه؛ وذلك:', '', 'ليَكونَ أشَدَّ النَّاسِ زينةً وجَمالًا', '', 'ليَكونَ بمَظْهَرِه وجَوهَرِه داعيةً إلى اللهِ، وإن لم يتكَلَّمْ بلِسانِه', '', 'لأنَّ الإسلامَ دِينُ الأَخلاقِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«نَهى رَسولُ اللهِ ﷺ عن اشتمالِ الصَّمَّاءِ». مَعنَى «اشتِمالِ الصَّمَّاءِ»:', '', 'أن يَلبَسَ مَلابِسَ صَفراءَ بالكامِلِ مِثلَ الصَّخرةِ الصَّماءِ', '', 'أنْ يَلُفَّ الإنسانُ جَميعَ جَسَدِه بالثَّوبِ، ولا يَرفَعَ شيئًا مِن جَوانبِه، فلا يُمكِنَه إخراجُ يدِه إلَّا مِن أسفَلِه', '', 'أن يَلُفَّ حَولَ بَدَنِه الصَّخورَ الصَّمَّاءَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«لا تَلاعَنوا بلَعنةِ اللهِ ولا بغَضبِ اللهِ ولا بالنَّارِ» من فَوائدِ هذا الحديثِ:', '', 'إبعادُ أسبابِ دُخولِ الشَّرِّ بين المُسلِمين', '', 'الحَثُّ على التَّنَزُّه عن التَّشَبُّهِ بالفُسَّاقِ', '', 'النهيُ عن الإفَسادِ في الأرضِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ النَّبيِّ ﷺ: «لَعَن اللهُ الواصِلةَ والمَوصولةَ»', '', 'أنَّ قاطِعَ الرَّحِمِ مَلعونٌ', '', 'تَحريمُ العُقوقِ والتَّحذيرُ مِنه', '', 'أنَّ وَصْلَ الشَّعرِ من كَبائرِ الذُّنوبِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كلمة "يَسْتَعْتِب" مَعناها:', '', 'يَطلُبُ رِضا اللهِ بالتَّوبةِ وردِّ المَظالِمِ وتَدارُكِ الفائتِ', '', 'يَعلَمُ أنَّ العُتبَى للهِ فلا يُذنِبُ مَرَّةً أُخرى', '', 'يَعتِبُ على مَن أخطَأَ في حَقِّه ويُراجِعُه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'سُمِّيَتِ القارورةُ بهذا الاسمِ؛ وذلك:', '', 'لبُعدِ قَعرِها وقَرارِها', '', 'باسمِ القَرارِ الَّذي في أسفَلِها', '', 'لاستِقرارِ الشَّرابِ فيها', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: إِنَّ اللهَ كتبَ الإحْسانَ على كلِّ شيءٍ:', '', 'فَإِذا قَتلتُم فأحسِنوا القِتْلةَ، وَإِذا ذَبحتُم فأحْسِنوا الذَّبحَ', '', 'فأَحسِنُوا إنَّ الله يحبُّ المُحسِنينَ', '', 'فإذا صَلَّيتُم في رِحالِكُم ثُمَّ أتَيتُم مَسجِدَ جَماعةٍ فَصَلُّوا مَعَهم', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«نَهَى رسولُ اللهِ ﷺ عن الاحتِباءِ في ثَوبٍ واحِدٍ». ما هو «الاحتِباءُ»:', '', 'أن يَرتَكِزَ الإنسانُ على أربَعٍ -يَدَيهِ ورِجلَيه-، ويَحبِي كما يَفعَلُ الطِّفلُ الصَّغيرُ', '', 'أنْ يَقعُدَ الإنسانُ على أَليَتَيْهِ ويَنصِبَ ساقَيْهِ، فيَضُمَّ رِجلَيْهِ إلى بَطنِه بثَوبٍ، ويَجمَعَهما مع ظهْرِه، ويَشُدَّ الثَّوبَ عليه بهذه الهَيئةِ، أو يَشُدَّ على ساقَيْهِ بيَدِه', '', 'النَّومُ على البَطنِ في ثَوبٍ واحِدٍ؛ لأنَّ ذلك يُظهِر العَورَة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن عَبدِ اللهِ بنِ عبَّاسٍ  رَضِيَ اللهُ عَنهُما قال: فَرَضَ اللهُ الصَّلاةَ على لِسانِ نَبيِّكُمْ في الحَضَرِ أَربعًا، وفي السَّفَرِ رَكعَتَين، وفي الخَوفِ:', '', 'ثَلاثًا', '', 'رَكعةً', '', 'أربَعًا', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'نَهى النَّبيُّ ﷺ في الحَديثِ عنِ اشتِمالِ الصَّمَّاءِ لأنَّ فيه تَشبُّهًا بـ:', '', 'اليَهودِ', '', 'النَّصارَى', '', 'المَجوسِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أَفضلُ الصَّلاةِ بَعدَ الصَّلاةِ المَكتوبةِ:', '', 'الصَّلاةُ في جَوفِ اللَّيلِ', '', 'صَلاةُ الضُّحى', '', 'صَلاةُ الفَجرِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'مَن مات بالطَّاعونِ منَ المُؤمِنين، وهو صابِرٌ مُحتَسِبٌ، فإنَّ له أجْرَ:', '', 'الشَّهيدِ', '', 'الصِّدِّيقين', '', 'سَبعِينَ شَفِيعًا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللهِ ﷺ: تَصَدَّقْنَ، يَا مَعْشَرَ النِّسَاءِ، وَلَوْ مِنْ:', '', 'حُلِيِّكُنَّ', '', 'الطَعامِ والشَّرابِ', '', 'أموالِ أزواجِكُنَّ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'الحِكمةُ من قَولِه ﷺ: «ما سَقَى كافِرًا منها شَرْبةَ ماءٍ»', '', 'لعَدَمِ المُبالاةِ به أو بما يَفعَلُ', '', 'للدَّلالةِ على أنَّ أَفضلَ الصَّدقَةِ سُقْيا الماءِ', '', 'جَزاءً له وعِقابًا على كُفرِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من قَولِه ﷺ: «اللهُ أرحَمُ بعِبادِهِ مِن هَذِه المَرأَةِ بوَلَدِها»', '', 'أنَّ اللهَ تَعالَى لا يُعذِّبُ أحَدًا مِن خَلقِه', '', 'ضَرْبُ المثَلِ بما يُدرَكُ بالحَواسِّ لِمَا لا يُدرَكُ بها؛ لتَحصيلِ مَعرفةِ الشَّيءِ على وجْهِه', '', 'أنَّ حُبَّ الأبناءِ مِن فِطرَةِ الله التي فَطرَ الناسَ عليها', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالعَينِ الوارِدةِ في حَديثِ أَبِي هُرَيْرَةَ رَضِيَ اللهُ عَنهُ، عَنِ النَّبِيِّ ﷺ قَالَ: «العَيْنُ حَقٌّ»:', '', 'عَينُ الماءِ التي يُفجِّرها اللهُ مِن بين الصُّخورِ آيةً للناسِ', '', 'الرُّؤَى التي يراها المُؤمِنُ في مَنامِه هي حَقٌّ مِن الله تعالى', '', 'الإصابةُ نَتيجةَ نَظْرةِ العينِ والحَسَدِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'خَيرُ الصَّدَقةِ ما كان على:', '', 'ذَوي الحاجةِ منَ الأقارِبِ', '', 'ذَوي الحاجةِ منَ الأصدِقاءِ', '', 'ذَوي الحاجةِ منَ الجيرانِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المَقصودُ بالكَمْأةِ في حَديثِ: «الكَمْأَةُ مِنَ المَنِّ، وماؤُها شِفاءٌ لِلْعَيْنِ»:', '', 'نَباتٌ كَثيرُ الأغصانِ يَنبُتُ في الجَزيرةِ العَرَبِيَّةِ', '', 'شَجَرٌ غَليظٌ مُلتَفٌّ يَنبُتُ بالشَّامِ', '', 'نَوعٌ منَ النَّباتِ لا وَرَقَ له ولا ساقَ، يَخرُجُ في الأرضِ بدونِ زَرعٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما مِن نَبيٍّ يَبعَثُه اللهُ تَعالَى إلَّا:', '', 'أعْطاه مُعجِزةً يُستَدَلُّ بها على نُبوَّتِه', '', 'بَشَّرَ أُمَّتَه بِنَبِيِّ آخرِ الزَّمانِ محمدٍ ﷺ', '', 'أيَّدَه اللهُ بمَلَكٍ يُكلِّمُ النَّاسَ عنه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أفضَلُ الأيَّامِ الأُسبوعِيَّةِ عند المُسلِمين', '', 'يومُ عَرفَة', '', 'يومُ الجُمُعةِ', '', 'يومُ فَتحِ مكَّة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالرِّكازِ؛ هو:', '', 'الغَنائمُ التي يَحوزُها المُسلِمونَ في الفُتوحاتِ', '', 'الماءُ المُستَقِرُّ الذي يُسقَى به الزَّرعُ', '', 'المالُ والكُنوزُ الجاهِلِيَّةُ المَدفونةُ في الأرضِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'رَحمةُ الوَلَدِ الصَّغيرِ ومُعانقتُه وتَقبيلُه والرِّفقُ به:', '', 'مِن الأَعمالِ الفِطريَّة التي لا ثَوابَ عليها ولا عِقاب', '', 'مِنَ الأعمالِ الَّتي يَرْضاها اللهُ ويُجازي عَلَيها', '', 'مِن عَلاماتِ حُسنِ الخاتِمة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنِ النَّبِيِّ ﷺ قَالَ: كُلُّ عَيْنٍ زَانِيَةٌ، وَالمَرْأَةُ إِذَا اسْتَعْطَرَتْ فَمَرَّتْ بِالمَجْلِسِ:', '', 'فَهِيَ كَذَا وَكَذَا؛ يَعْنِي: زَانِيَةً', '', 'فرُدُّوها ولا تَأذَنُوا لها', '', 'فقد بَرِئَت منها الذِّمَّةُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المَقصودُ بالعَينِ المَذكورةِ في حَديثِ: «أمَرَ رسولُ اللهِ صلَّى اللهُ عَليهِ وسلَّم أنْ يُسْتَرْقَى مِنَ العَيْنِ»', '', 'أنْ يَنظُرَ شَخصٌ باستِحسانٍ إلى شَيءٍ فيَتطلَّعَ إليه، دونَ أن يَذكُرَ اسمَ اللهِ عَليهِ', '', 'مَرضٌ يُصيبُ الإنسانِ فيُوهِنُ قِواهُ', '', 'عَينُ الماءِ التي تَكدَّرَت وصارَت غَيرَ صالِحةٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُقَيَّضُ لكُلِّ إنسانٍ قَرينٌ منَ الجِنِّ يُغويه، وكذا يُقيَّضُ له قَرينٌ من:', '', 'صُحبَتِه التي يَختارُها في الدُّنيا', '', 'المَلائكةِ يُسدِّدُه ويُرْشدُه', '', 'صالِحي المُؤمِنين ليَهدِيَه إلى سَبيلِ الله', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من قَولِ رَسُولُ اللَّهِ ﷺ: «اخْتَتَنَ إِبْرَاهِيمُ عَلَيْهِ السَّلاَمُ وَهُوَ ابْنُ ثَمَانِينَ سَنَةً بِالقَدُّومِ»', '', 'أنَّ إبراهيمَ عليه السَّلامُ كان مُتَّبِعًا لشَريعَةِ محمدٍ ﷺ', '', 'أنَّ الخِتانَ في حَقِّ الصَّغيرِ والكَبيرِ', '', 'أنَّ الخِتانَ لا يُجزِئ قبلَ بُلوغِ الثَّمانين', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِه ﷺ: «بُعِثتُ بين يَدَيِ السَّاعةِ بالسَّيفِ»', '', 'السَّيفُ المَقصُودُ هنا هو القُرآنُ، وهو مُعجِزتُه الباقِيَة ﷺ', '', 'أنَّ مِن عَلاماتِ السَّاعَة انتِشارَ السَّيفِ؛ أي: القَتل', '', 'أنَّه ﷺ بُعِثَ بالجِهادِ؛ لنَشرِ تَوحيدِ اللهِ وعِبادتِه دون شَريكٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسُولُ اللَّهِ ﷺ: العَجْمَاءُ جُبَارٌ، وَالبِئْرُ جُبَارٌ:', '', 'ولَيسَ فيما دُونَ خَمسِ ذَوْدٍ صَدَقةٌ', '', 'واللهُ طَيِّبٌ لا يَقبَلُ إلَّا طَيِّبًا', '', 'وَالمَعْدِنُ جُبَارٌ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أوَّلُ مَن يَدخُلُ الجَنَّةَ ويَستَفتِحُ بابَها يَومَ القِيامةِ؛ هو:', '', 'إبراهيمُ عَليهِ السَّلامُ', '', 'نَبِيُّنا مُحمَّدٌ ﷺ', '', 'آدَمُ عَليهِ السَّلامُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُشرَعُ استِلامُ الحَجَرِ الأسوَدِ بقَصدِ:', '', 'تَعظيمِه وتَقديسِه', '', 'التأسِّي بعُمرَ بنِ الخطَّابِ رضي الله عنه', '', 'الإخلاصِ والتَّعبُّدِ للهِ عزَّ وجلَّ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ:«مَن وَجَدَ لُقَطةً فليُشهِدْ ذا عَدلٍ أو ذَوَيْ عَدْلٍ، ولا يَكْتُمْ، ولا يغيِّبْ؛ فإنْ وجَدَ صاحِبَها فليَرُدَّها علَيهِ، وإلَّا فَهوَ مالُ اللَّهِ عزَّ وجلَّ يُؤتِيهِ مَن يشاءُ» مَعنَى «مالُ اللَّهِ عزَّ وجلَّ يُؤتِيهِ مَن يشاءُ»:', '', 'يَرُدُّ اللُّقَطةَ لبَيتِ مالِ المُسلِمين؛ لأنَّه المالَ للهِ عزَّ وجلَّ', '', 'يَعني: يَتصَدَّقُ بثَمَنِ اللُّقَطةِ إن لم يَجِدْ صاحِبَها؛ لأنَّه مالُ اللهِ عزَّ وجلَّ', '', 'أصبَحَت مِن رِزقِه الَّذي رزَقَه اللهُ عزَّ وجلَّ إيَّاه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بقَولِ عَمرِو بنِ عَبْسةَ رَضِيَ اللهُ عَنهُ، حين سَأل النَّبيَّ ﷺ: «أيُّ اللَّيلِ أسمَعُ؟»', '', 'في أيِّ ساعَةٍ مِن اللَّيلِ أَقرأُ القُرآنَ فتَسمَعُ تِلاوتِي المَلائِكةُ', '', 'أيُّ اللَّيلِ أكثَرُ هُدوءًا من غَيرِه', '', 'يَعني: أيُّ أوقاتِ الصَّلاةِ يَكونُ فيها الدُّعاءُ فيها أكثرَ استِجابةً مِن اللهِ سُبحانَه؟', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'ما المُرادُ بالتَّساخينِ في حَديثِ «بَعَثَ رسولُ اللَّهِ ﷺ سَرِيَّةً فأصابَهُمُ البَردُ فلمَّا قدِموا علَى رسولِ اللَّهِ ﷺ أمرَهُم أن يَمسَحوا علَى العَصائبِ والتَّساخينِ»', '', 'جَمعُ تِسخَان وهو الإزارُ المَصنُوعُ مِن الجِلدِ', '', 'كلُّ ما يُسْتَدْفأُ به مِن خُفٍّ وجَوْرَبٍ وغيرِه', '', 'العَمائمُ ذاتُ الذَّوائبِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «مَن سَرَقَ منهُ شيئًا بعدَ أن يُؤْويَهُ الجَرينُ فبلغَ ثَمَنَ المِجَنِّ فعلَيهِ القَطعُ» مَعنَى "المِجنِّ":', '', 'هو الثَّوبُ الَّذي يَلبَسُه المَجنونُ', '', 'هو ما يَستَتِرُ به المُحارِبُ منَ التُّرسِ وغَيرِه', '', 'إناءٌ تُجفَّفُ فيه الثِّمارُ أو تُخزَنُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «إنَّ العبدَ إذا لَعَن شَيئًا صَعِدَتِ اللَّعنةُ إلى السَّماءِ فتُغلَقُ أَبوابُ السَّماءِ دُونَها»: معنَى «فتُغلَقُ أبوابُ السَّماءِ دونَها»:', '', 'لَيسَ على حَقيقَتِه، بلِ المُرادُ أنَّه يُعذَّبُ على مَقالَتِه', '', 'تُغلَقُ أبوابُ السَّماءِ في وجْهِ هذا الدُّعاءِ، ويُمنَعُ مِن مُرورِه إلى اللهِ عزَّ وجلَّ ولا يُرْفعُ', '', 'تُغلَقُ أبوابُ السَّماءِ أمامَ رُوحِ صاحِبِها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ اللهِ ﷺ: مَا مِن مَولُودٍ إلَّا يُولَدُ عَلَى:', '', 'الاستِقامةِ', '', 'اليَقينِ', '', 'الفِطرةِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«سِبَابُ المُسْلِمِ فُسُوقٌ، وقِتَالُهُ كُفْرٌ» في هذا الحَديثِ رَدٌّ على فِرقةٍ يَقولونَ: لا يضُرُّ مع الإيمانِ مَعصِيةٌ، وهُم:', '', 'المُرجئةُ', '', 'الجَهمِيَّةُ', '', 'الخَوارِجُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عن قَتادةَ: «قُلنا لأنَسٍ: أيُّ الثِّيَابِ كانَ أحَبَّ إلى النَّبيِّ ﷺ أنْ يَلْبَسَهَا؟ قالَ:', '', 'الخَشِنَةُ', '', 'الصُّفرةُ', '', 'الحِبَرَةُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', '«مَن آوَى ضالَّةً فَهو ضالٌّ، ما لَمْ يُعَرِّفْها» يُستَفادُ من هذا الحَديثِ:', '', 'جَميعُ ما سَبَق', '', 'أنَّ مَن أخَذَها لِيَذْهَبَ بها فهو ضالٌّ', '', 'مَن أخَذَها لِيَرُدَّها أو لِيُعَرِّفَها ويَطلُبَ صاحبَها فلا بَأْسَ به', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: إذا جَلَس أحدُكُم على حَاجَتِه:', '', 'فلا يَستَقبِلِ القِبلةَ ولا يَستَدبِرْها', '', 'فليُتِمَّ ما بَدأَ ولا يَعجَل', '', 'فإنْ استَطاعَ ألَّا يُغلَب فليَفعَل', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصودُ بالمُغِيبةِ في نَهيِ النَّبيِّ ﷺ عن طُروقِ المُسافِرِ أهلَهُ لَيلًا، وقال: «لِكَيْ تَمْتَشِطَ الشَّعِثَةُ وَتَسْتَحِدَّ المُغِيبَةُ»', '', 'المَرأةُ الَّتي غابَت حَيضَتُها أكثرَ مِن عادةِ النِّساءِ', '', 'المَرأةُ التي غابَ عَنها زَوجُها', '', 'المَرأةُ العَقيمُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «جَاءَ رَجُلٌ إلى النَّبيِّ ﷺ فقَالَ: يا رَسولَ اللهِ، مَن أَحَقُّ النَّاسِ بحُسنِ صَحَابَتِي؟ فقال ﷺ:', '', 'وَلدُك', '', 'أبوكَ', '', 'أُمُّكَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'لَمْ يَكُنِ النبيُّ ﷺ فَاحِشًا ولَا مُتَفَحِّشًا، وكانَ يقولُ: «إنَّ مِن خِيَارِكُمْ أحْسَنَكُمْ أخْلَاقًا». حَقيقةُ حُسنِ الخُلقِ:', '', 'الإخلاصُ في الأَقوالِ والأَعمالِ والأَحوالِ', '', 'بِرُّ الوالدَينِ', '', 'كَفُّ الأذى وبَذلُ المَعروفِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «سِبَابُ المُسْلِمِ فُسُوقٌ، وقِتَالُهُ كُفْرٌ». المُرادُ بالكُفرِ هنا:', '', 'مُبالَغةً في التَّحذيرِ؛ لِيَنزجِرَ السَّامعُ عنِ الإقدامِ عَليهِ', '', 'حَقيقَتُه الَّتي هي الخُروجُ عنِ المِلَّةِ', '', 'الشِّركُ باللهِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في حَديثِ أُمِّ خالِد: «أَتَيْتُ رَسولَ اللَّهِ ﷺ مع أبِي وعَلَيَّ قَمِيصٌ أصْفَرُ، قَالَ رَسولُ اللَّهِ ﷺ: سَنَهْ سَنَهْ! قَالَتْ: فَذَهَبْتُ ألْعَبُ:', '', 'بخَاتَمِ النُّبُوَّةِ', '', 'مع الغِلمانِ', '', 'بأَلعابِي', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'كانَ النبيُّ ﷺ مَرْبُوعًا، بَعِيدَ ما بيْنَ المَنْكِبَيْنِ، له شَعَرٌ:', '', 'كأنَّه الجُمانُ', '', 'طَويلٌ مِثل ذُؤابَةِ العِمامَة', '', 'يَبْلُغُ شَحْمَةَ أُذُنِهِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ: «أنَّ رَسولَ اللَّهِ ﷺ سَابَقَ بيْنَ الخَيْلِ الَّتي أُضْمِرَتْ...»:', '', 'تشريفُ الإسلامِ للخَيلِ مِن أَجلِ الجِهادِ عليها في سَبيلِ الله', '', 'مَشروعِيَّةُ تَضميرِ الخَيلِ، وإعدادِها لإعزازِ كَلِمةِ الله تَعالَى ونُصرةِ دِينِه', '', 'أنَّ الأَصلَ في العِباداتِ الإباحةُ إلَّا ما حَرَّمهُ الشَّرعُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من قَولِ النَّبيِّ ﷺ: «ستُفْتَحُ عَلَيكُم أرَضُونَ، ويَكفِيكُمُ اللهُ، فَلَا يَعْجِزْ أَحدُكُم أن يَلْهُوَ بأَسْهُمِه»:', '', 'مَشروعِيَّةُ اللَّهوِ واللَّعِبِ مُطلَقًا', '', 'أنَّ المُسلِمين إذا رَهِبَهُمُ الأعداءُ استَعانُوا عليهم بالسِّهامِ', '', 'الحَضُّ على تَعلُّمِ الرَّميِ والتَّحذيرُ من نِسيانِه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'أهْدى دِحْيةُ الكلبيُّ لرَسولِ اللَّهِ ﷺ خُفَّينِ:', '', 'فأَهْدَاهُما عائِشةَ رضي الله عنها', '', 'فلبِسَهُما', '', 'فتَرَكَهُما', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ أبي قَتادةَ رَضِيَ اللهُ عَنهُ: «إِذَا بَالَ أحَدُكم فلا يَمَسَّ ذَكَره بيَمِينِه»:', '', 'كَراهِيَةُ التَّبوُّلِ واقِفًا', '', 'أنَّ اليَدَ اليُسْرى أَقدرُ على مُمارسَةِ الأَعمالِ من اليُمْنى غالِبًا', '', 'أنَّ اليَمينَ تُستخدَمُ لِفضائلِ الأعمالِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ أَبِي سَعِيدٍ الْخُدْرِيِّ، أَنَّ رَسُولَ اللهِ ﷺ قَالَ: «لَا يَنْظُرُ الرَّجُلُ إِلَى عَوْرَةِ الرَّجُلِ»:', '', 'أنَّ النَّهيَ خاصٌّ بالرِّجالِ أمَّا النِّساءُ فلا يَشمَلهُنَّ النَّهيُ', '', 'عَدَمُ مَشروعِيَّةِ رُؤيةِ العَوْراتِ ولو بين الأزواجِ', '', 'عَدَمُ مَشروعيَّةِ النَّظرِ إلى العَورةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: «لا يَنْبَغِي لِصِدِّيقٍ أنْ يَكونَ لَعَّانًا» معنى «صِدِّيق»:', '', 'الصِّديقُ هو لَقبُ أبي بكرٍ رضي الله عنه', '', 'مُبالِغًا في الصِّدقِ', '', 'أي: المُؤمِنُ الصَّادِقُ في إيمانِه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في قَولِ النَّبيِّ ﷺ «لا يَنظُرُ اللَّهُ إلى مَن جَرَّ إزارَهُ بَطَرًا» مَعنَى «بَطَرًا» هو:', '', 'إظهارُ الإيمانِ وإبطانُ الكُفرِ', '', 'الكِبرُ والخُيَلاءُ، والعُجْبُ بالنَّفسِ', '', 'مُخالَفةٌ للباطِنِ؛ حَيث يَلبَسُ زِيَّ التَّقوى وهو مُصِرٌّ على العِصيانِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالمُخالَفةِ بين الطَّرَفينِ في حَديثِ: «مَن صلَّى في ثَوبٍ فلْيُخَالِفْ بينَ طَرَفيهِ»:', '', 'أن يَجعلَ طرَفَه الأيمنَ على عاتقِه الأيسرِ، وطَرَفَه الأيسرَ على عاتقِه الأيمنِ', '', 'أن يُحوِّلَ رِداءَه كما يُفعَل في صَلاةِ الاستِسقاءِ', '', 'أن يَجعَلَ أحَدَ طَرَفَيهِ تَحتَ إبْطِه وطَرَفَه الآخَرَ على مَنكِبِه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصودُ بذِمَّةِ اللهِ، الَّتي وَرَد ذِكرُها في حَديثِ: «مَن صَلَّى الصُّبحَ فهُوَ في ذِمَّةِ اللهِ»:', '', 'أي: في عَفوِه ومَغفِرَتِه', '', 'أي: في أمانِه وضَمانتِه', '', 'أي: في نَعيمِ جَنَّتِه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: «نَظَرَ أنَسٌ إلى النَّاسِ يَومَ الجُمُعَةِ، فَرَأَى طَيَالِسَةً، فَقالَ: كَأنَّهُمُ السَّاعَةَ يَهُودُ خَيْبَرَ» ما معنى «طَيالِسةً»:', '', 'نَوعٌ من ثِيابِ كان يَلبَسُها نَصارَى نَجران', '', 'نَوعٌ مِن الثِّيابِ العَرَبيَّةً، كانت مُنتَشِرَة في المَدينَة', '', 'نَوعٌ منَ الثِّيابِ لَيستْ عَرَبيَّةً، كان يَلبَسُها اليَهودُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المَقصودُ بالِاستِحدادِ في حَديثِ النَّبيِّ ﷺ: «الفِطْرَةُ خَمْسٌ، أَوْ خَمْسٌ مِنَ الفِطْرَةِ: الخِتَانُ، وَالِاسْتِحْدَادُ؟»', '', 'امتِهانُ الحِدادةِ مثل نَبيِّ الله داودَ عليه السلامُ', '', 'صِناعَة السُّيوفِ مِن الحَديدِ مِن أَجلِ الجِهادِ في سَبيلِ الله', '', 'استِعمالُ المُوسَى في حَلْقِ العَانَةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يَرجِعُ اختِلافُ الرِّواياتِ في تَحديدِ الدَّرَجاتِ التي تَفضُلُها صَلاةُ الجَماعةِ على صَلاةِ الفَذِّ إلى:', '', 'جِنسِ الصَّلاةِ', '', 'تَكاثُر الجَماعاتِ', '', 'اختِلافِ أحوالِ المُصلِّين والصَّلاةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحديثِ: «كانَ أنَسٌ يَنْعَتُ لَنَا صَلَاةَ النبيِّ ﷺ، فَكانَ يُصَلِّي وإذَا رَفَعَ رَأْسَهُ مِنَ الرُّكُوعِ، قَامَ حتَّى نَقُولَ:', '', 'هل يَسجُد؟', '', 'أَطالَ الوُقوفَ', '', 'قدْ نَسِيَ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بالبَردَينِ، الوارِدِ ذِكرُهُما في حَديثِ: «مَنْ صلَّى البَرْدَين دَخَل الجَنَّةَ»:', '', 'صَلاةُ العِشاءِ وصَلاةُ الصُّبحِ', '', 'صَلاةُ الظُّهرِ وصَلاةُ العَصرِ', '', 'صَلاةُ الصُّبحِ وصَلاةُ العَصرِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حديثِ: «َلَا يُفْضِي الرَّجُلُ إِلَى الرَّجُلِ فِي ثَوْبٍ وَاحِدٍ، وَلَا تُفْضِي الْمَرْأَةُ إِلَى الْمَرْأَةِ فِي الثَّوْبِ الْوَاحِدِ»', '', 'عَدَمُ مَشروعِيَّةِ أن يَخلُوَ الرَّجُلُ إلى الرَّجُلِ أوِ المَرأةُ إلى المَرأةِ في ثَوبٍ واحِدٍ مُتَجَرِّدَيْنِ', '', 'عَدَمُ مَشروعِيَّةِ نَظَرِ الرَّجُلِ إلى عَورةِ المَرأةِ وإنْ كانت زَوجَتَه', '', 'الحَثُّ على التَستُّرِ بالإزارِ والرِّداءِ لأنَّه أَستَرُ للعَورةِ وأَحفَظُ للبَدَنِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'عَنْ عِمْرَانَ بْنِ حُصَيْنٍ رَضِيَ اللهُ عَنهُ، قَالَ: كَانَتْ بِي بَوَاسِيرُ، فَسَأَلْتُ النَّبِيَّ ﷺ عَنِ الصَّلَاةِ، فَقَالَ: صَلِّ قَائِمًا، فَإِنْ لَمْ تَسْتَطِعْ فَقَاعِدًا، فَإِنْ لَمْ تَسْتَطِعْ:', '', 'فعلى جَنبٍ', '', 'فمُتَّكِئًا', '', 'فمُضطَجِعًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'إذا أرادَ المُصَلِّي أن يَبصُقَ في الصَّلاةِ، فإنَّه يَبصُقُ:', '', 'عن يَمينِه', '', 'عن يَسارِه أو تَحتَ قَدَمِه', '', 'تِلقاءَ وَجهِه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'في الحَديثِ: قالَت أمُّ سَلمةَ: فَكَيفَ يصنَعُ النِّساءُ بذُيولِهِنَّ؟ فقالَ ﷺ: يُرخينَ شبرًا، فقالت: إذنْ تَنكَشِفُ أقدامُهُنَّ، فقالَ ﷺ:', '', 'افْعَلْنَ ما آمُركُنَّ إنَّما السَّترُ مِن الله', '', 'لا تَنكَشِفُ إن شاءَ الله', '', 'فيُرخينَهُ ذراعًا', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قَالَ رَسُولُ اللَّهِ ﷺ: حُبِّبَ إِلَيَّ مِنَ الدُّنْيَا النِّسَاءُ وَالطِّيبُ:', '', 'والخَيلُ', '', 'وَجُعِلَت قُرَّةُ عَيْنِي فِي الصَّلَاةِ', '', 'والجِهادُ في سَبيلِ اللهِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يَدُلُّ قَولُه ﷺ: «قُمْتُ عَلَى بَابِ الجَنَّةِ، فَكَانَ عَامَّةَ مَنْ دَخَلَهَا المَسَاكِينُ، وَأَصْحَابُ الجَدِّ مَحْبوسونَ...» على أنَّ:', '', 'الأغنياءَ منَ المُؤمِنين يَدخُلون الجَنَّةَ بلا حِسابٍ', '', 'الفُقَراءَ منَ المُؤمِنين لا حِسابَ عَلَيهِم مُطلَقًا', '', 'حلالَ الدُّنيا له حسابٌ، كما أنَّ لحَرامِها عِقابٌ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'يُؤخَذُ من حَديثِ أبي جُحَيفةَ رَضِيَ اللهُ عَنهُ: «أَنَّهُ رَأَى بِلاَلًا يُؤَذِّنُ فَجَعَلْتُ أَتَتَبَّعُ فَاهُ هَا هُنَا وَهَا هُنَا يقولُ: يَمينًا وشِمالًا يقولُ: حيَّ على الصَّلاةِ، حيَّ على الفَلاحِ»', '', 'إِباحَةُ الأذانِ قائِمًا أو قاعِدًا أو على جَنبٍ؛ كهَيئاتِ ذِكرِ الله', '', 'مَشروعيَّةُ الالْتِفاتِ يمينًا ويَسارًا في الأذانِ عِندَ الحَيْعَلتَينِ', '', 'استِحبابُ النَّظَرِ إلى المُؤذِّنِ وهو يُنادي بالصَّلاةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسُولُ اللهِ ﷺ: رُبَّ أَشْعَثَ، مَدْفُوعٍ بِالْأَبْوَابِ لَوْ:', '', 'أقْسَمَ علَى اللهِ لأَبَرَّهُ', '', 'رَفعَ يَدهُ إلى السَّماءِ لأَمطرَت سَبعِينَ خَريفًا', '', 'تَمنَّى على اللهِ لَأعطاهُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'المُرادُ بـ"ذِي المِرَّةِ السَّوِيِّ" في قَولِ النَّبِيِّ ﷺ: «لا تحِلُّ الصَّدقةُ لغنيٍّ، ولا لذي مِرَّةٍ سَوِيٍّ»:', '', 'صاحِبُ المالِ', '', 'صَحيحُ البَدَنِ، الذي يَقدِرُ على الكَسبِ لتَحصيلِ قُوتِ يَومِه', '', 'ذو النَّسَبِ والشَّرَفِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@hadith_category_id, '', 'قال رَسولُ الله ﷺ: خَمْسٌ مِنَ الفِطْرَةِ:', '', 'تَهذيبُ الشَّعرِ، وحَلقُ العانةِ، ونَتفُ الإبْطِ، وتَنظيفُ الأظْفارِ، وقَصُّ الشَّارِبِ', '', 'الخِتانُ، والِاستِحدَادُ، ونَتفُ الإبْطِ، وتَقْلِيمُ الأَظفَارِ، وقَصُّ الشَّارِبِ', '', 'الدُّعاءُ عِندَ نُزولِ البَلاءِ، وإقامةُ الصَّلاةِ، وإيتاءُ الزَّكاةِ، وصَومُ رَمَضانَ، والحَجُّ،', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'تَجديدُ الوُضوءِ لكلِّ صَلاةٍ:', '', 'لا دليلَ عليه', '', 'يُسَنُّ باتِّفاقِ المذاهبِ الفقهيَّةِ الأربعةِ', '', 'واجبٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'يُستحَبُّ عندَ دخولُ الخَلاءِ:', '', 'قولُ: اللهُمَّ طهِّرْني ظاهرًا وباطنًا', '', 'التسوُّكُ قبلَ الدخولِ وبعدَ الخروجِ', '', 'تقديمُ الرِّجلِ اليُسرى عندَ الدخولِ، واليُمنى عندَ الخروجِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'في بابِ الطهارةِ ينقسِمُ الحدَثُ إلى قِسمينِ هما:', '', 'حدَثٌ أوَّلُ، وحدَثٌ ثانٍ', '', 'حدَثٌ خفيٌّ، وحدَثٌ ظاهرٌ', '', 'حدَثٌ أصغرُ، وحدَثٌ أكبرُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'ما هو حُكمُ المسحِ على الجَبيرةِ؟', '', 'لا يجوزُ', '', 'يجوزُ معَ الكَراهةِ', '', 'جائزٌ باتِّفاقِ المذاهبِ الفقهيَّةِ الأربعةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'ما حُكمُ ذِكرِ اللهِ تعالى باللِّسانِ في الخَلاءِ؟', '', 'جائزٌ', '', 'يُكرَهُ باتِّفاقِ المذاهِبِ الفقهيَّةِ الأربعةِ', '', 'يحرُمُ باتِّفاقِ المذاهِبِ الفقهيَّةِ الأربعةِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'ما حُكمُ الخِتانِ للرِّجالِ؟', '', 'واجبٌ', '', 'مَكْروهٌ', '', 'مُستحَبٌّ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'من فَضائلِه أنَّه شَطرُ الإيمانِ:', '', 'ذِكرُ اللهِ عزَّ وجلَّ', '', 'الصِّدقُ', '', 'الوُضوءُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'يُسَنُّ عندَ دخولِ الخَلاءِ قولُ:', '', 'ربِّ أعوذُ بكَ مِن هَمَزاتِ الشياطينِ', '', 'اللهُمَّ احفَظْني مِن بينِ يدَيَّ ومِن خَلْفي', '', 'اللهُمَّ إني أعوذُ بكَ مِنَ الخُبُثِ والخبائثِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'قراءةُ القرآنِ للمُحدِثِ حدَثًا أصغَرَ دونَ مَسِّ المصحفِ:', '', 'يُكرَهُ', '', 'لا يجوزُ', '', 'يجوزُ بالإجماعِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'تَعريفُ المَيتةِ شَرعًا هي:', '', 'كلُّ ما مات بغيرِ ذَكاةٍ', '', 'كلُّ ما مات بطَريقِ الصَّيدِ', '', 'كلُّ ما مات بذَكاةٍ شرعيَّةٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'ما حُكمُ مَيتةِ السَّمكِ؟', '', 'طاهرةٌ بالإجماعِ', '', 'غيرُ طاهرةٍ', '', 'نَجِسةٌ باتِّفاقِ المذاهبِ الفقهيَّةِ الأربعةِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'ما حُكمُ تَقليمِ الأظْفارِ؟', '', 'لا يُشرَعُ', '', 'يُسَنُّ باتِّفاقِ المذاهبِ الفقهيَّةِ الأربعةِ', '', 'يجِبُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'الماءُ النجِسُ هو:', '', 'هو الماءُ المُستَعمَلُ في رفعِ الحدَثِ', '', 'ما تَغيَّر بغيرِ نجاسةٍ', '', 'ما تَغيَّر بنجاسةٍ، بحيث يَتغيَّر بها طعمُه، أو لونُه، أو رِيحُه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@fiqh_category_id, '', 'ما حُكمُ استعمالِ الأواني الثمينةِ غيرِ الذهَبِ والفِضةِ؟', '', 'يجوزُ اتِّخاذُها باتِّفاقِ المذاهبِ الفقهيَّةِ الأربعةِ', '', 'لا يجوزُ اتِّخاذُها باتِّفاقِ المذاهبِ الفقهيَّةِ الأربعةِ', '', 'يُكرَهُ اتِّخاذُها', '', '', 'a', 'easy');
