(@arabia_category_id, '', 'الألْماسٌ حَجَرٌ مِن الجَواهِرِ؛ وهو لَفظٌ مَنقُولٌ مِن:', '', 'العِبرِيَّةِ', '', 'اليونانيَّةِ', '', 'الآرامِيَّةِ', '', '', 'b', 'easy');
