(@arabia_category_id, '', 'قسَّمَ العُلَماءُ الشُّعَراءَ خَمْسةَ أنْواعٍ، منها: "شُعْرورٌ" ويُقصَدُ به:', '', 'الشَّاعِرُ المُتَمَكِّنُ واسِعُ الخَيالِ', '', 'الشَّاعِرُ الرَّديءُ؛ كأنَّه لا شيءَ', '', 'الشَّاعِرُ الذي يَسجَعُ كسَجْعِ الكُهَّانِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'تَتابَعَ مُؤرِّخو الأدَبِ أن يَضمُّوا عَصْرَ النُّبوَّةِ والخِلافةِ الرَّاشِدةِ معَ عَصْرِ الدَّوْلةِ الأُمَويَّةِ ويُطلِقوا عليه:', '', 'العَصرَ الذَّهبِيَّ', '', 'عَصرَ صَدرِ الإسْلامِ', '', 'عَصرَ الرُّشدِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'شاعِرٌ اشْتَهرَ في الجاهِليَّةِ، ولمَّا ظَهَرَ الإسْلامُ هَجا النَّبيَّ صلَّى اللهُ عليه وسلَّمَ وأقامَ يُشبِّبُ بنِساءِ المُسْلِمينَ، فأهْدَرَ النَّبيُّ دَمَه:', '', 'كَعبُ بنُ زُهَير', '', 'عبدُ الله بنُ خَطلَ', '', 'الحُوَيْرِثُ بنُ نقيذٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'عرَفَتِ القَصيدةُ الجاهِليَّةُ شَكلًا مُوَحَّدًا لا تَخرُجُ عنه ومنها:', '', 'إِطلاقُ الخَيالِ وعَدمُ تَقيِيدِه', '', 'الفَخرُ بالقَبيلَةِ وأَنسابِها', '', 'ذِكرُ المُقدِّمةِ الطَّلَليَّةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'كانت سَبَبًا في رَواجِ الشِّعْرِ وانتِشارِه وازِدْهارِه:', '', 'الفُتُوحاتُ الإِسلامِيَّةُ', '', 'الأسواقُ الأدَبِيَّةُ', '', 'رِحلَتَا الشِّتاءِ والصَّيفِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'تُعَدُّ كُتُبُ "الحَماسةِ" جُزْءًا مِن كُتُبِ:', '', 'كُتُبِ البَلاغةِ', '', 'آدابِ التَّغْلبيِّينَ', '', 'الاخْتِياراتِ الشِّعْريَّةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'النَّقيضةُ في الشِّعْرِ  هي:', '', 'التَّنافُسُ المَحمُودُ في الارتِجالِ', '', 'أن يَنقُضَ الشَّاعِرُ الآخَرُ ما قالَه الأوَّلُ', '', 'المُناظَرةِ في النِّحَلِ السِّياسيَّةِ والعَقَديَّةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@arabia_category_id, '', 'أوَّلَ مَن قسَّمَ الشِّعْرَ إلى مَوْضوعاتٍ:', '', 'النَّابِغةُ الذُّبيانِيُّ', '', 'أبو تَمَّامٍ', '', 'البُحتُرِيُّ', '', '', 'b', 'hard');